	/*
    * 입찰서접수개시일시 변경 이벤트
    */
	scwin.ica_bid_start_ymd_onviewchange = function(info) {
		if(scwin.data.pur_grp_cd == "EGF030") return;

		if(this.getValue() != ""){
			scwin.setYmdByBidStart(sbx_ord_helper.getValue());	
		}
	};

	/*
    * 용역구분 변경 이벤트
    */
	scwin.sbx_service_type_cd_onviewchange = function(info) {
		if(this.getValue() ==  "2"){ // 기술용역
			com.biz.getG2bComCd({upper_cd:"S90", compID : "sbx_contrct_method" }, function(){
				sbx_contrct_method.setValue("35");
				sbx_bid_succ_method.hide();

				// 계약방법에 따른 폼 셋팅		
				scwin.setFormVendByConMethod();
			}); 
		}else{ // 일반용역
			com.biz.getG2bComCd({upper_cd:"L17", str_index : 3, str_length : 2, cd:"00", compID : "sbx_contrct_method" }, function(){
				sbx_bid_succ_method.show("");

				sbx_contrct_method.trigger("viewchange");
			}); 
		}

		// 낙찰방법 셋팅
		scwin.setSuccType();
	};

	/*
    * 배정예산변경 이벤트
    */
	scwin.ibx_assign_amount_onviewchange = function(info) {
		ibx_budget_amt.setValue( Math.round(com.parseInt(ibx_assign_amount.getValue()) * 10/11) );
	};

	/*
    * 투찰제한 - 공종별 전체 지분율 추가 버튼 클릭 이벤트
    */
	scwin.btn_const_add_onclick = function(e) {
		com.addRow(grd_const_clsf);
	};

	/*
    * 투찰제한 - 공종별 전체 지분율 삭제 버튼 클릭 이벤트
    */
	scwin.btn_const_del_onclick = function(e) {
		var row = grd_const_clsf.getFocusedRowIndex();
		if (row < 0) {
			alert("삭제할 행을 선택하여 주십시오.");
			return;
		}

		com.deleteRow(grd_const_clsf);
	};

	/*
    * 삭제 버튼 클릭 이벤트
    */
	scwin.btn_del_onclick = function(e) {
		if(dma_ord.get("bid_noti_no") != ""){
			alert("입찰공고서가 존재하므로 삭제할 수 없습니다.");
			return;
		}

		com.confirm("message.xom.wq.022"/*삭제 하시겠습니까?*/, function(ret) {
			if (ret) {
				com.file.deleteAll( function(){
					com.execute(sbm_deleteData, {
						successCallback : function(e) { // 성공 콜백
							com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
								com.moveList("P301", true);
							});
						}
					});
				});
			}
		});
	};
