	/*
	 *  발주품목 추가 팝업 오픈
	 */
	scwin.btn_addItem_onclick = function(){
		scwin.openPop(scwin.pop.TYPE.EPU_REQ_ADD, null, 'item-add');
	};	

	/*
	 *  발주품목 삭제
	 */
	scwin.btn_item_del_onclick = function(row, col, colId){
		var rows = grd_item.getCheckedIndex('chk');
		if(rows.length < 1){
			com.alert("삭제할 행을 선택하세요.");
			return;
		}

		rows.reverse().forEach(function(idx) {
			dlt_item.removeRow(idx);
		});
		// 폼목내역 합계 재계산
		scwin.setStylePurItem("");
	};

	/*
	* 구매요구내역 셀 클릭 이벤트
	*/
	scwin.grd_item_oncellclick = function(row, col, colId){
		if(colId == "btn_itemCd"){
			biz.epu.pop.open(biz.epu.pop.TYPE.ITEM_SRC, {}, function(ret) {
				dlt_item.setCellData(row, "item_cd", ret.item_cd);
				dlt_item.setCellData(row, "item_nm", ret.item_nm);
				dlt_item.setCellData(row, "cntst_prod_yn", ret.cntst_prod_yn);
			});
		}else if(colId == "req_attach_file"){
			biz.epu.pop.open(biz.epu.pop.TYPE.PUR_REQ_ATTACH, {pur_req_no : dlt_item.getCellData(row, "pur_req_no")}, function(ret){
			});
		}
	};

	/*
	* PQ심사여부 viewchange 이벤트
	*/
	scwin.rdo_pq_eval_yn_onviewchange = function(info) {		
		if (rdo_pq_eval_yn.getValue() != "Y") {
			dma_ord.set("pq_score", "");
		}
	};

	/*
	* PQ심사여부change 이벤트
	*/
	scwin.rdo_pq_eval_yn_onchange = function(info) {
		if(this.getValue() == "Y"){
			$("._pq_eval_yn").wq("setStyle", "display", "");
		}else{
			$("._pq_eval_yn").wq("setStyle", "display", "none");
		}
	};

	/*
	* 공고일자 변경 이벤트
	*/
	scwin.ica_noti_ymd_onviewchange = function(info) {
		if(info.newValue != ""){
			scwin.setBidDefVal();
		}
	};	

	/*
	* 저장 버튼 클릭 이벤트
	*/
	scwin.btn_save_onclick = function(e) {	
		// 전문 계약 방법 세팅 (A- 최저가 입찰, B-기술가격 분리 동시, C-협상에 의한 계약, D-적격심사)
		dma_ord.set("contract_type_cd", scwin.setContrctType());	

		if(sbx_bid_clsf_cd.getValue() != "PRB050"){ // 외자직거래일 경우 체크하지
			if(!scwin.pre_save()) return;
		}		

		var msg = "발주계획 내역을 다시 확인하시기 바랍니다. \n\n";	
		if (sbx_bid_clsf_cd.getValue() == "PRB040"){ // 비연계거래
			msg += "입찰공고일 : "+ com.formatDate(ica_noti_ymd.getValue(), "dash") +"\n"+
					"입찰서 접수개시일시 : "+com.formatDate(ica_bid_start_ymd.getValue(), "dash")+" "+ibx_bid_start_hour.getValue()+":"+ibx_bid_start_min.getValue()+"\n"+
					"입찰서 접수마감일시 : "+com.formatDate(ica_bid_end_ymd.getValue(), "dash")+" "+ibx_bid_end_hour.getValue()+":"+ibx_bid_end_min.getValue()+"\n"+
					"개찰일시 : "+com.formatDate(ica_open_ymd.getValue(), "dash") +" "+ibx_open_hour.getValue()+":"+ibx_open_min.getValue()+"\n";
		}

		com.confirm(msg + "\n" + "진행하시겠습니까?", function(ret){
			if(ret){
				scwin.save();
			}
		});			
	};

	/*
	* 입찰설명회여부 viewchange 이벤트
	*/
	scwin.sbx_bid_expl_yn_onviewchange = function(info) {		
		if (sbx_bid_expl_yn.getValue() == "1" || sbx_bid_expl_yn.getValue() == "") {
			ibx_bid_expl_loc.setValue("");
			ica_bid_expl_ymd.setValue("");
			ibx_bid_expl_hour.setValue("");
			ibx_bid_expl_min.setValue("");
		}
	};

	/*
	* 입찰설명회여부 viewchange 이벤트
	*/
	scwin.sbx_bid_expl_yn_onchange = function(info) {
		if (sbx_bid_expl_yn.getValue() == "1" || sbx_bid_expl_yn.getValue() == "") {			
			grp_bid_expl.hide();
		}else{			
			grp_bid_expl.show("");
		}
	};

	/*
	* 단일예가 온라인여부 viewchange 이벤트
