	/*
	* 견적요청서 사용구분 변경 이벤트
	*/
	scwin.rdo_oz_chk_onviewchange = function(info) {
		if(this.getValue() == "1"){
			ta_ozText.setDisabled(false);
		}else{
			ta_ozText.setDisabled(true);
		}
	};

	/*
     * 결재신청 버튼 클릭 이벤트
     */
	scwin.btn_apr_onclick = function(e) {
		if(dma_ord.get("bid_ord_no") == ""){
			alert("발주계획 작성이 아직 되지 않았습니다.");
			return;
		}		

		dma_apr.set("bid_clsf_cd", sbx_bid_clsf_cd.getText());
		dma_apr.set("contrct_method", sbx_contrct_method.getText()); // 계약방법(1)
		dma_apr.set("bid_succ_method", sbx_bid_succ_method.getText()); // 계약방법(2)
		dma_apr.set("bid_succ_type", sbx_bid_succ_type.getText()); // 입찰방식
		dma_apr.set("rebid_possible_yn", rdo_rebid_possible_yn.getText()); // 재입찰가능여부
		dma_apr.set("succ_type", sbx_succ_type.getText()); // 낙찰방법
		dma_apr.set("join_possible_yn", sbx_join_possible_yn.getText()); // 공동수급협정서 접수 및 구성방식
		dma_apr.set("join_method", sbx_join_method.getText()); // 공동수급협정서 접수 및 구성방식(2)
		dma_apr.set("same_suc_lot_program_yn", rdo_same_suc_lot_program_yn.getText()); // 동가입찰 낙찰자 자동추첨프로그램	
		dma_apr.set("limit_loc", sbx_limit_loc.getText());
		dma_apr.set("tender_yn", rdo_tender_yn.getText());
		dma_apr.set("limit_category", sbx_limit_category.getText());
		dma_apr.set("item_limit_yn", sbx_item_limit_yn.getText()); // 물품분류제한여부	
		dma_apr.set("join_mem_loc_limit_yn", sbx_join_mem_loc_limit_yn.getText()); // 공동수급체 구성원 지역제한적용여부	
		dma_apr.set("multi_esti_pr_yn", rdo_multi_esti_pr_yn.getText());
		dma_apr.set("emer_bid_yn", rdo_emer_bid_yn.getText()); // 긴급입찰여부
		dma_apr.set("pq_eval_yn", rdo_pq_eval_yn.getText()); // PQ심사여부
		dma_apr.set("bid_expl_yn", sbx_bid_expl_yn.getText()); // 입찰설명회 여부
		dma_apr.set("vat_include_yn", rdo_vat_include_yn.getText()); // 부가세 포함여부

		dma_ord.set("approval", "true");

		if(sbx_bid_clsf_cd.getValue() != "PRB050"){ // 외자직거래일 경우 체크하지
			if(!scwin.pre_save()) return;
		}		

		scwin.save();		
	};

	/*
     * 견적요청서 버튼 클릭 이벤트
     */
	scwin.btn_print_01_onclick = function(e) {
		if(rdo_oz_chk.getValue() == "1" && ta_ozText.getValue() == ""){
			com.alert("수정할 문구를 작성되어 있지 않습니다.");
			return;
		}

		// /epu/ord/EpuOrdPlanGoods.ozr
		var node = "BidOrdNo="+spn_bid_ord_no.getValue()
				 +"&ChgNo="+spn_chg_no.getValue()
				 +"&UseTextChk="+rdo_oz_chk.getValue()
				 +"&UseInsertText="+ta_ozText.getValue();

		com.oz.openPopup({
			ozrCode: "P035",    
			param: node
		});
	};

	/*
     * 수의계약체결제한여부확인서 버튼 클릭 이벤트
     */
	scwin.btn_print_02_onclick = function(e) {
		// EpuOrdPlanConfirmation.ozr
		var node = "BidOrdNo="+spn_bid_ord_no.getValue()
					+"&ChgNo="+spn_chg_no.getValue()
					+"&UseTextChk="+rdo_oz_chk.getValue()
					+"&UseInsertText="+ta_ozText.getValue()
					+"&ordNm="+ibx_bid_title.getValue();
		com.oz.openPopup({
			ozrCode: "P034",    
			param: node
		});		
	};

	/*
     * 입찰방식 변경 이벤트
