	scwin.sbx_bid_succ_type_onviewchange = function(info) {
		scwin.setFormVendByConMethod();		
	};

	/*
     * 발주품목추가 버튼 클릭 이벤트
     */
	scwin.btn_item_add_onclick = function(e) {		
		var data={
			inout_psn_clsf_cd : dma_ord.get("inout_psn_clsf_cd")
		};

		biz.epu.pop.open(biz.epu.pop.TYPE.EPU_REQ_ADD, data, function(ret){
			var rowCnt = dlt_item.getRowCount();
			var cnt = 0;
			for(var i=0; i<ret.length; i++){
				var rowData = ret[i];
				rowData.delvr_demnd_daycnt = "계약일로부터 " + rowData.delvr_demnd_daycnt + "일 이내";
				rowData.cud_type = "C";
				if(rowCnt == 0){
					dlt_item.setRowJSON(i, rowData);
				}else{
					if(scwin.checkDupData(rowData)){
						dlt_item.setRowJSON(rowCnt+cnt, rowData);
						cnt++;
					}
				}
			}

			scwin.setStylePurItem("new_add");		

			scwin.setExeGuar(); // 하자보증 초기값 설정
		});
	};


	/*
    * 낙찰방법 변경 이벤트
    */
	scwin.sbx_succ_type_onviewchange = function(info) {
		ibx_succ_min_rate.setDisabled(false);

		if(rdo_succ_pivot.getValue() == "1"){ // 조달청기준		
			var val = "";
			switch(sbx_succ_type.getValue()){
				case "1": ibx_succ_min_rate.setDisabled(true); break;
				case "1-3": ibx_succ_min_rate.setDisabled(true); break;
				case "103-1": val = "80.495"; break;
				case "103-4": val = "80.495"; break;
				case "103-5": val = "80.495"; break;
				case "103-8": val = "87.995"; break;
				case "103-9": val = "87.995"; break;
				case "105-4": val = "87.995"; break;
				case "105-5": val = "87.995"; break;
				case "13": val = "87.995"; break;
				default: break;
			}	

			ibx_succ_min_rate.setValue(val);
		}else{
			ibx_succ_min_rate.setValue("");

			if(info.newValue == "1"){
				ibx_succ_min_rate.setDisabled(true);
			}
		}
	};

	/*
    * 지명업체 추가 버튼 클릭 이벤트
    */
	scwin.btn_vend_add_onclick = function(e) {
		biz.epu.pop.open(biz.epu.pop.TYPE.EPU_BID_G2B, {}, function(ret) {
			var rowCnt = dlt_bid_vend.getRowCount();
			var cnt = 0;
			for(var i=0; i<ret.length; i++){
				var rowData = ret[i];
				rowData.cud_type = "C";
				if(rowCnt == 0){
					dlt_bid_vend.setRowJSON(i, rowData);
				}else{
					if(scwin.checkVendAdd(rowData)){
						dlt_bid_vend.setRowJSON(rowCnt+cnt, rowData);
						cnt++;
					}
				}
			}
		});
	};

	/*
    * 지명업체 삭제 버튼 클릭 이벤트
    */
	scwin.btn_vend_del_onclick = function(e) {
		var row = grd_vend.getFocusedRowIndex();
		if (row < 0) {
			alert("삭제할 행을 선택하여 주십시오.");
			return;
		}

		dlt_bid_vend.removeRow(row);
	};

