head.script:
	/****************************************************
 	 * 화면 설명 : 소액물품 검수결의
 	 * 변경 이력 : 2023-03-27	이영희	최초 작성
 	 * 서비스아이디 : F429
 	 ****************************************************/


 	 scwin.data={
		typeCode : "F429",
		allowExtension : "jpg,pptx,tiff,jpeg,hwp,hwpx,bmp,gif,mht,pdf,doc,rtf,tif,ppt,html,docx,txt,png"
	};

 	/*
     * 페이지 열기
     */  
	scwin.onpageload = function() {
		// 초기화
		grp_card.hide();
		com.file.init();

		// 공통코드
		var codeOptions = [ { code : "FAA", compID :"sbx_bank" }, // 은행
		                    { code : "FAX", compID :"sbx_pay_clsf" } // 지급구분
		                ];
		com.setCmnCd(codeOptions, function() {
			var param = com.getParameter();
			dma_search.setJSON(param);

			if(param.param4 == "apr"){
				if(param.req_no.substring(0,3) == "F27"){
					dma_search.set("check_no", param.req_no);
				}else{
					dma_search.set("unslip_no", param.req_no);
				}
			}

			if(dma_search.get("check_no") != "" || dma_search.get("unslip_no") != ""){
				scwin.search();
			} else {
				// 첨부파일(소액물품검수조서에서 저장된 결제정보)
				com.file.create(upload_A.getID(), {fileKey: "F429", mode: 'view'});
			}
		});
	};

	scwin.onpageunload = function() {

	};

	/*
     * 조회
     */
	scwin.search = function(){
		com.execute(sbm_selectData, {
			successCallback : function(e){
				if(dma_check.get("card_no").trim() != ""){
					sbx_evid_clsf.setValue("Card");	
				}else{
					sbx_evid_clsf.setValue("Bill");
					if(dma_slipH.get("unslip_no") == "") {
						if(dma_check.evid_cd == "FAH611"){
							dma_check.set("pay_clsf", "FAX100");

							ibx_depstr_nm.setDisabled(false);
							sbx_bank.setDisabled(false);
						}else{
							dma_check.set("vend_cd", "");

							ibx_depstr_nm.setDisabled(true);
							sbx_bank.setDisabled(true);
						}
					}
				}

				if(dma_check.get("syspayno").trim() != ""){
					ibx_vend_emp_no.setValue(dma_check.get("empno"));
					ibx_vend_emp_nm.setValue(dma_check.get("nm"));
				}else{
					ibx_vend_emp_no.setValue(dma_check.get("vend_cd"));
					ibx_vend_emp_nm.setValue(dma_check.get("vend_nm"));
				}

				// 거래명세서 유무에 따른 미리보기 버튼 제어
				if(dma_check.get("evid_attach_file_no") != "") {
					evidImage.setDisabled(false);
				}

				// 결재상태에 따른 버튼 제어
				com.biz.disabledButtons(dma_slipH.get("apr_state"), dma_slipH.get("unslip_no"));

				if(dma_slipH.get("unslip_no") == ""){
					sbx_evid_clsf.setDisabled(false);

					var session = com.getLoginInfo();

					dma_slipH.set("slip_typ_cd", "119");
					dma_slipH.set("empno", session.empno);
					dma_slipH.set("nm", session.cn);
					dma_slipH.set("wrte_psn", session.sn);
					dma_slipH.set("wrte_dept", session.deptCode);
					dma_slipH.set("dept_nm", session.deptName);
					dma_slipH.set("wrte_dept_new_ymd", session.deptNewYmd);				
					dma_slipH.set("slip_ymd", com.getServerDateTime());

				}else{
					btn_preView.setDisabled(false);
					btn_save.setDisabled(true);
					sbx_evid_clsf.setDisabled(true);
					dma_search.set("check_no", dma_check.get("check_no"));
					btn_card.setDisabled(true);
					btn_bill.setDisabled(true);
					btn_evid.setDisabled(true);
					btn_elecBill.setDisabled(true);
				}	

				spn_emp_nm.setValue(dma_slipH.get("nm") + " (" + dma_slipH.get("empno") + " )");

				// 기타(결제)첨부파일 세팅
				var etcAttachFileNo = dma_check.get("etc_attach_file_no").trim();
				com.file.create(upload_A.getID(), { fileKey : com.isEmpty(etcAttachFileNo) ? scwin.data.typeCode : etcAttachFileNo, mode : 'view' });
			}
		});
	};

	/*
     * 증빙데이터 초기화
     */
	scwin.clearData = function(){
		var cols = ["card_no", "apprvl_no", "use_ymd", "card_clsf", "card_knd", "bill_no","pay_clsf","syspayno", "vend_cd","vend_nm","bankaccnt_no","depstr_nm","bank","evid_cd"];

		cols.forEach(function(elt) {
			dma_check.set(elt, "");
		});		
	};

	/*
     * 결의생성전 체크
     */
	scwin.preSave = function(){
		if(dma_check.get("card_no").trim() == "" && dma_check.get("bill_no").trim() == ""){
			alert("카드번호 또는 계산서번호가 반드시 존재해야합니다.");
			return false;
		}

		var checkData = [
					    { id : "pay_clsf", mandatory : true}
					   ,{ id : "evid_cd", mandatory : true } 				   
					];
		if(dma_check.get("card_no").trim() != ""){
			checkData.push({ id : "apprvl_no", mandatory : true});
			checkData.push({ id : "use_ymd", mandatory : true});
		}else{
			if(sbx_pay_clsf.getValue() == "FAX010"){ // 개인
				checkData.push({ id : "syspayno", mandatory : true});

			}else if(sbx_pay_clsf.getValue() == "FAX020"){  // 거래처
				checkData.push({ id : "vend_nm", mandatory : true});

			}else if(sbx_pay_clsf.getValue() == "FAX100"){  // 지로
				checkData.push({ id : "depstr_nm", mandatory : true});
			}
		}		

		var validData = [
			{dataObj : dma_slipH
			   , valInfoArr :[
				    { id : "slip_ymd", mandatory : true}
				   ,{ id : "slip_typ_cd", mandatory : true } 
				   ,{ id : "wrte_psn", mandatory : true } 
				   ,{ id : "wrte_dept", mandatory : true } 		   
				]
			  , isCheckModified : false
			},
			{dataObj : dma_check
			   , valInfoArr : checkData
			   , isCheckModified : false
			}
		];		

		if(!com.validateData(validData)) return false;

		if(/FAX090|FAX020/.test(sbx_pay_clsf.getValue())){ // FAX090 기타(송금)지급, FAX020 거래처지급
			if(dma_check.get("bank") == "" || dma_check.get("depstr_nm") == "" || dma_check.get("bankaccnt_no") == ""){
				com.alert(com.getMsg("message.xom.co.com.0001", "계좌정보"));	
				return false;
			}	
		}

		if(dma_check.get("card_no").trim() != ""){
			var useYmd = dma_check.get("use_ymd");
			// 날짜 검증
			if(!scwin.slip_date_check(useYmd, "Card")) return false;				

		}else if(dma_check.get("bill_no").trim() != ""){
			// 날짜 검증
			if(!scwin.slip_date_check(dma_check.get("issu_ymd"), "Bill")) return false;
		}

		if(com.parseInt(dma_check.get("total_amt")) != com.parseInt(dma_check.get("check_amt"))){
			alert("검수금액이 증빙금액과 일치 하지 않습니다. 증빙 금액[" + com.formatMoney(dma_check.get("total_amt")) + "]");
			return false;	
		}

		return true;
	};

	/*
     * 날짜 체크
     */
	scwin.slip_date_check = function(eivdYmd, clsf){
		if(dma_slipH.get("slip_ymd").substring(0, 6) != eivdYmd.substring(0, 6)){			
			alert(((clsf == "Card")? "카드승인월" : "계산서발행월") + "과 결의생성월은 일치해야 합니다.");
			return false;
		}

		var msg = (clsf == "Card")? "카드승인일자" : "계산서발행일자";		

		// 2018.10.15 증빙일자, 결의일자 체크(재정운영실 이유애)
		if (dma_slipH.get("slip_ymd") < eivdYmd.substring(0, 8)) {
			com.alert(com.getMsg("message.xom.ui.fin.010", msg, "결의일자"));
			return false;
		}

		// 2017.06.29. 입회검수일자 증빙날자+10일 이내로 (재정운영실 권정은 통화)
		// 2017.07.24. 입회검수일자 증빙날자+-10일 이내로 (구매자산실 강원채 확인)
		// 2023.06.12. 카드인 경우에는 10일 검증로직이 필요없음 (재정운영실 권정은 통화)
		// 2023.06.26. 증빙 10일 체크 주석 요청(정보화추진실 이윤민)
//		var chkYmdTo = WebSquare.date.dateAdd(eivdYmd.substring(0, 8), 10);
//		var chkYmdFr = WebSquare.date.dateAdd(eivdYmd.substring(0, 8), -10);
//		
//		if (clsf != "Card" && (dma_check.get("entsch_check_ymd") > chkYmdTo || dma_check.get("entsch_check_ymd") < chkYmdFr)) {
//			alert("입회(검수)일자는 " + msg + " 전후 10 일 이내여야 합니다. 검수담당자에게 수정을 요청하시기 바랍니다.");
//			return false;
//		}

		return true;
	};

	/*
     * 결의금액 셋팅
     */
	scwin.setUnslipAmt = function(chrgAmt, taxAmt){
		if(dma_check.get("tax_cd") == "FTX001"){
			dma_check.set("unslip_amt", chrgAmt);
			dma_check.set("tax_amt", taxAmt);
		}else{
			dma_check.set("unslip_amt", dma_check.get("check_amt"));
			dma_check.set("tax_amt", 0);
		}
		dma_check.set("total_amt", dma_check.get("check_amt"));		
	};

	//-------------------------------------- event --------------------------------//

	/*
     * 증빙구분 변경이벤트
     */
	scwin.sbx_evid_clsf_onchange = function(info) {
		if(this.getValue() == "Card"){
			grp_card.show("");
			grp_bill.hide();

			btn_evid.hide();
			btn_elecBill.hide();
		}else{
			grp_card.hide();
			grp_bill.show("");

			btn_evid.show("");
			btn_elecBill.show("");
		}
	};

	/*
     * 카드번호 검색 버튼 클릭 이벤트
     */
	scwin.btn_card_onclick = function(e) {
		biz.fin.pop.open(biz.fin.pop.TYPE.CARD_USE, {}, function(ret){
			if(ret){
				if(dma_check.get("card_use_ex") == "FAJ006"){
					if(ret.card_clsf != "FAI003" || ret.card_knd != "FAJ006"){
						alert("e나라도움 과제의 경우 신한(국고보조금)연구비 카드만 등록 가능합니다.");
						return;
					}
				}

				// 증빙데이터 초기화
				scwin.clearData();

				if(ret.un_amt != dma_check.get("check_amt")){
					alert("카드승인금액[" + ret.un_amt + "]이 검수금액과 일치하지 않습니다.");
					return;
				}

				dma_check.set("card_no", ret.card_no);
				dma_check.set("apprvl_no", ret.apprvl_no);
				dma_check.set("use_ymd", ret.use_ymd);
				dma_check.set("card_clsf", ret.card_clsf);
				dma_check.set("card_knd", ret.card_knd);
				dma_check.set("pay_clsf", "FAX900");
				dma_check.set("evid_cd", "FAH500");
				dma_check.set("vend_nm", ret.joinsto_nm);

				// 결의금액 셋팅
				scwin.setUnslipAmt(ret.supply_amt, ret.tax_amt);						
			}
		});
	};

	/*
     * 계산서번호 검색 버튼 클릭 이벤트
     */
	scwin.btn_bill_onclick = function(e) {
		var data={
			issu_seqno : dma_check.get("bill_no").trim(),
			vend_nm : dma_check.get("vend_nm"),
			relat_no : com.getLoginInfo("sn")
		};

		biz.fin.pop.open(biz.fin.pop.TYPE.BILL, data, function(ret) {
			if(ret){
				if(com.parseInt(ret.totl_amt) != com.parseInt(dma_check.get("check_amt"))){
					alert("검수금액이 증빙금액과 일치 하지 않습니다. 증빙 금액[" + com.formatMoney(ret.totl_amt) + "]");
					return;	
				}

				// 증빙데이터 초기화
				scwin.clearData();

				dma_check.set("bill_no", ret.issu_seqno);
				dma_check.set("evid_cd", ret.evid_cd);
				dma_check.set("issu_ymd", ret.regs_date);			
				dma_check.set("bsns_psn_regst_no", ret.bsns_psn_regst_no);
				dma_check.set("vend_nm", ret.vend_nm);
				// 2023.03.28.거래처지급일 경우에는 계좌정보를 셋팅해도 될것 같긴한데... 확실치가 않아서 보류
//				dma_check.set("bank", ret.bank);
//				dma_check.set("depstr_nm", ret.depstr_nm);
//				dma_check.set("bankaccnt_no", ret.bankaccnt_no);

				if(ret.evid_cd == "FAH611"){
					dma_check.set("vend_cd", ret.vend_cd);				
					dma_check.set("pay_clsf", "FAX100");

					ibx_depstr_nm.setDisabled(false);
					sbx_bank.setDisabled(false);
				}else{
					ibx_depstr_nm.setDisabled(true);
					sbx_bank.setDisabled(true);
				}

				// 결의금액 셋팅				
				scwin.setUnslipAmt(ret.chrg_amt, ret.tax_amt);	
			}			
		});
	};

	/*
     * 개인(거래처) 검색 버튼 클릭 이벤트
     */
	scwin.btn_vend_emp_onclick = function(e) {
		if(sbx_pay_clsf.getValue() == ""){
			alert("지급구분을 먼저 선택하세요.");
			return;
		}

		if(sbx_pay_clsf.getValue() == "FAX010"){
			var data={clsf : "apart", ref_yn : "Y"};
			com.pop.open(com.pop.TYPE.BNKAC, data, function(ret) {
				if(ret){
					dma_check.set("syspayno", ret.syspayno);
					dma_check.set("vend_nm", ret.nm);

					ibx_vend_emp_no.setValue(ret.empno);
					ibx_vend_emp_nm.setValue(ret.nm);

					dma_check.set("bankaccnt_no", ret.bankaccnt_no);
					dma_check.set("depstr_nm", ret.depstr_nm);
					dma_check.set("bank", ret.bank);
				}
			});
		}else if(sbx_pay_clsf.getValue() == "FAX020"){
			var data={
				bsns_psn_regst_no : dma_check.get("bsns_psn_regst_no"),
				ref_yn : "Y"
			};
			com.pop.open(com.pop.TYPE.VEND, data, function(ret) {
				if(ret){
					if(ret.is_account == "") {
						alert("계좌검증이 되지 않은 거래처입니다.\n 거래처관리 메뉴에서 계좌검증을 진행해주세요");
						return;
					}
					dma_check.set("vend_cd", ret.vend_cd);
					dma_check.set("vend_nm", ret.vend_abbr);
					ibx_vend_emp_no.setValue(ret.vend_cd);
					ibx_vend_emp_nm.setValue(ret.vend_abbr);

					dma_check.set("bankaccnt_no", ret.bankaccnt_no);
					dma_check.set("depstr_nm", ret.depstr);
					dma_check.set("bank", ret.tran_bank);
				}
			});
		}
	};

	/*
     * 증빙 버튼 클릭 이벤트
     */
	scwin.btn_evid_onclick = function(e) {
		biz.fin.pop.open(biz.fin.pop.TYPE.EVID_BILL, {}, function(ret) {
			if(ret){
				dma_check.set("vend_nm", ret.vend_nm);
				dma_check.set("bill_no", ret.issu_seqno);				

				btn_bill.trigger("click");
			}
		});
	};

	/*
     * 전자증빙 버튼 클릭 이벤트
     */
	scwin.btn_elecBill_onclick = function(e) {
		var data = {pur_con_user_id : com.getLoginInfo("sn")
				  , pur_con_org_code : com.getLoginInfo("deptCode")
				  , ref_yn : "Y"
		};

		com.pop.open(com.pop.TYPE.TAXBILL, data, function(retObj) {
			var billNo = retObj.dma_bill.getJSON().issu_seqno;

			if(billNo != null && billNo != "") {
				dma_check.set("bill_no", billNo);
			}
		});
	};

	/*
     * 미리보기 버튼 클릭 이벤트
     */
	scwin.btn_preView_onclick = function(e) {
		if(spn_unslip_no.getValue() == "") return;
		com.biz.openEvidWindow(spn_unslip_no.getValue());
	};

	/*
     * 결재신청 버튼 클릭 이벤트
     */
	scwin.btn_apr_onclick = function(e) {
		if(dma_slipH.get("apr_state") >= "XAD020") return;

		var data={
			unslip_no : spn_unslip_no.getValue(),
			slip_typ_cd : "119"
		};
		com.openTabMenu("F203", data);
	};

	/*
     * 검수번호 클릭 이벤트
     */
	scwin.ibx_check_no_onclick = function(e) {
		var data={
			reqNo : this.getValue(),
			ref_yn : "Y",
			viewMode : "read",
			instStatus : "Completed",
			endpoint : com.getLoginInfo('uid'),
			check_no : dma_check.get("check_no")
		};
		com.openTabMenu("F428", data);
	};

	/*
     * 목록버튼 클릭 이벤트.
     */
	scwin.btn_pre_onclick = function(e) {
		com.moveList("F427");
	};

	/*
     * 결의생성 버튼 클릭 이벤트
     */
	scwin.btn_save_onclick = function(e) {
		if(!scwin.preSave()) return;

		// 소액물품 첨부파일 증빙 파일 연계되도록 수정
		if (confirm("소액물품검사검수서의 첨부파일을 결의서로 연계하시겠습니까?")) {
			dma_check.set("attach_yn", "Y");
		}else{
			dma_check.set("attach_yn", "N");
		}

		//20210105 결의생성 전 과세계정인 경우 부가세 입력 필수 알림메시지 상신자가 확인하면 그대로 진행 
		if(dma_check.get("tax_cd") == "FTX001" && dma_check.get("tax_amt") == 0){
			if (!confirm("과세 계정인경우 부가세 입력이 필수 입니다. 부가세가 0원이 맞습니까?")){
				return;
			}
		}

		if(confirm("선택한 자료에 대하여 결의를 생성하시겠습니까 ?")) {
			com.execute(sbm_saveData, {
				successCallback : function(e){					
					com.confirm("message.xom.ui.fin.069", function(ret) {						
						if(ret){
							var data = { unslip_no : dma_search.get("unslip_no"),
										slip_type_cd : "119"
							};
							com.openTabMenu("F203", data);
						}else{
							com.alert("취소하셨습니다.\n해당 문서는 작성중 문서함에서 확인하실 수 있습니다.", function() {
								setTimeout(function() {
									com.movePageMenu("A004");
								}, 100);
							});
						}
					});			
				}
			});
		}
	};

	/*
     * 결의취소 버튼 클릭 이벤트
     */
	scwin.btn_del_onclick = function(e) {	

		if(dma_slipH.get("decsn_ex") == "Y"){
			alert("[결의확정]인 건은 결의를 취소할 수 없습니다.");
			return;
		}

		if(confirm("선택한 자료에 대하여 결의를 취소하시겠습니까 ?")){
			com.execute(sbm_deleteData, {
				successCallback : function(e){
					com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
						com.moveList("F427",{});
					});
				}
			});
		}
	};

	/*
     * 지급구분 변경 이벤트
     */
	scwin.sbx_pay_clsf_onviewchange = function(info) {
		if(this.getValue() == "FAX090"){ // 기타(송금)지급
			ibx_vend_emp_nm.setDisabled(false);
			sbx_bank.setDisabled(false);
			ibx_depstr_nm.setDisabled(false);
			ibx_bankaccnt_no.setDisabled(false);			
		}else{
			ibx_vend_emp_nm.setDisabled(true);
			sbx_bank.setDisabled(true);
			ibx_depstr_nm.setDisabled(true);
			ibx_bankaccnt_no.setDisabled(true);
		}

		ibx_vend_emp_no.setValue("");
		ibx_vend_emp_nm.setValue("");
		dma_check.set("vend_cd", "");
		dma_check.set("vend_nm", "");
		dma_check.set("bank", "");
		dma_check.set("bankaccnt_no", "");
		dma_check.set("depstr_nm", "");
	};

	/*
     * 거래명세서 미리보기 이벤트
     */
	scwin.evidImage_onclick = function(e) {
		evidAttachFileNo = dma_check.get("evid_attach_file_no");
		biz.apr.previewFilePop(evidAttachFileNo);
	};
