head.script:
	/****************************************************
	 * 화면 설명 : 계정번호 검색, 관리
	 * 변경 이력 : 2023-09-06	안화용	최초 작성
	 * serviceId : 계정번호관리(F112)
	 ****************************************************/
	/*
     *  날짜 설정
     */
	var year = com.getServerDateTime().substring(0, 4);

	/*
     *  전역변수
     */
	var accntNoGb = "";
	scwin.service_id = "";

	/*
     * 페이지 열기
     */
	scwin.onpageload = function() {
		// 초기변수 세팅
		dma_mast.set("rowStatus", "C");
		ica_start_year.setValue(year);

		scwin.service_id = com.getParameter("menuObj").serviceID;

		// 공통코드 조회(FAK 계정그룹, FCC 편성단위, RAK 계정상태, FBS 재무사업구분 ,FEF 용도구분, FTX 과세구분)
		var codeOptions = [ { code : "FAK", compID :"sbx_accnt_grp"}	// 계정그룹
						,	{ code : "FAK", compID :"sbx_tb_accnt_grp" } // 상세정보 계정그룹
						,	{ code : "FCC", compID :"sbx_fomat_unit" } // 편성단위
						,	{ code : "RAK", compID :"sbx_accnt_state" } // 계정상태
						,	{ code : "FBS", compID :"sbx_fin_bsns_clsf" } // 재무사업구분
						,	{ code : "FEF", compID :"sbx_usg_clsf" } // 용도구분
						,	{ code : "FTX", compID :"sbx_tax_cd" } // 과세구분
			];
		com.setCmnCd(codeOptions, function() {
		});

		// 엔터 키 이벤트
		com.setEnterKeyEvent(grp_accnt_no, scwin.btn_accnt_no_onclick);	// 조회조건 계정검색
		com.setEnterKeyEvent(grp_accnt_rspns, scwin.btn_accnt_rspns_onclick);	// 조회조건 계정책임자 검색
		com.setEnterKeyEvent(grp_bugt_ctrl_psn, scwin.btn_bugt_ctrl_psn_onclick);	// 조회조건 예산통제자 검색
		com.setEnterKeyEvent(grp_tb_accnt_rspns, scwin.btn_tb_accnt_rspns_onclick);	// 테이블 계정책임자 검색
		com.setEnterKeyEvent(grp_tb_bugt_ctrl_psn, scwin.btn_tb_bugt_ctrl_psn_onclick);	// 테이블 예산통제자 검색
		com.setEnterKeyEvent(grp_tb_itm_solve_base, scwin.btn_tb_itm_solve_base_onclick);	// 테이블 비목해소기준 검색

	};

	scwin.onpageunload = function() {

	};

	/*
     *  조회버튼 클릭 이벤트
     */
	scwin.btn_search_onclick = function(e) {
		scwin.search();
	};

	/*
    *  조회
    */
	scwin.search = function() {
		if (dma_search.get("accnt_no").trim() != "") {	// 계정번호 입력시 시작연도 조건 적용 X
			dma_search.set("start_ymd", "");
		} else {
			dma_search.set("start_ymd", year);
		}

		com.execute(sbm_selectData, {
			successCallback : function(e){
				dlt_grd.setJSON(e.responseJSON.resBgAcctMVoList);	// 조회해온 리스트 그리드에 세팅
				tbx_result.setValue(dlt_grd.getRowCount());	// 조회결과 건수 표시
				scwin.grd_list_oncellclick(0);	// 조회 시 첫번째 로우 상세정보 자동 세팅
			}
		});
	};

	/*
     *  계정번호 변경 이벤트
     */
	scwin.inp_accnt_no_onchange = function(info) {
		if (this.getValue() == "") {
			inp_accnt_no_nm.setValue("");
		}
	};

	/*
     *  계정번호 검색 버튼 클릭 이벤트
     */
	scwin.btn_accnt_no_onclick = function(e) {
		var data = {
			accnt_no : inp_accnt_no.getValue().toUpperCase(),
			ref_yn : (inp_accnt_no.getValue() == "")? "Y" : "N"
		};

		com.pop.open(com.pop.TYPE.ACNNO, data, function(ret){
			inp_accnt_no.setValue(ret.accnt_no);
			inp_accnt_no_nm.setValue(ret.accnt_no_nm);
		});
	};

	/*
     *  계정책임자 변경 이벤트(조회조건)
     */
	scwin.inp_accnt_rspns_empno_onchange = function(info) {
		if (this.getValue() == "") {
			scwin.changeEmpInfo("inp_accnt_rspns");
		}
	};

	/*
     *  계정책임자 변경 이벤트(상세정보)
     */
	scwin.ibx_accnt_rspns_empno_onchange = function(info) {
		if (this.getValue() == "") {
			scwin.changeEmpInfo("ibx_accnt_rspns");
		}
	};

	/*
     *  계정책임자 변경 이벤트(조회조건)
     */
	scwin.inp_bugt_empno_onchange = function(info) {
		if (this.getValue() == "") {
			scwin.changeEmpInfo("inp_bugt_ctrl");
		}
	};

	/*
     *  계정책임자 변경 이벤트(상세정보)
     */
	scwin.ibx_bugt_empno_onchange = function(info) {
		if (this.getValue() == "") {
			scwin.changeEmpInfo("ibx_bugt_ctrl");
		}
	};

	/*
     *  사용자 변경 초기화 이벤트
     */
	scwin.changeEmpInfo = function(clsf){
		if (clsf == "inp_accnt_rspns"){	// 조회조건 계정책임자
			inp_accnt_rspns_empno.setValue("");
			inp_accnt_rspns_nm.setValue("");
			dma_search.set("accnt_rspns", "");
		} else if (clsf == "ibx_accnt_rspns"){	// 상세정보 계정책임자
			dma_mast.set("accnt_rspns", "");
			dma_mast.set("accnt_rspns_empno", "");
			dma_mast.set("accnt_rspns_nm", "");
		} else if (clsf == "inp_bugt_ctrl"){	// 조회조건 예산통제자
			inp_bugt_empno.setValue("");
			inp_bugt_nm.setValue("");
			dma_search.set("bugt_ctrl_psn", "");
		} else if (clsf == "ibx_bugt_ctrl"){	// 상세정보 예산통제자
			dma_mast.set("bugt_ctrl_psn", "");
			dma_mast.set("bugt_empno", "");
			dma_mast.set("bugt_nm", "");
		}
	};

	/*
     *  계정책임자 검색 버튼 클릭 이벤트(조회조건)
     */
	scwin.btn_accnt_rspns_onclick = function(e) {
		scwin.getEmpInfo("inp_accnt_rspns");
	};

	/*
     *  계정책임자 검색 버튼 클릭 이벤트(상세정보)
     */
	scwin.btn_tb_accnt_rspns_onclick = function(e) {
		scwin.getEmpInfo("ibx_accnt_rspns");
	};

	/*
     *  예산통제자 검색 버튼 클릭 이벤트(조회조건)
     */
	scwin.btn_bugt_ctrl_psn_onclick = function(e) {
		scwin.getEmpInfo("inp_bugt_ctrl");
	};

	/*
     *  예산통제자 검색 버튼 클릭 이벤트(상세정보)
     */
	scwin.btn_tb_bugt_ctrl_psn_onclick = function(e) {
		scwin.getEmpInfo("ibx_bugt_ctrl");
	};

	/*
     * 사용자 검색
     */
	scwin.getEmpInfo = function(clsf){
		var data={work_clsf : "HAG010"};
		if (clsf == "inp_accnt_rspns"){	// 조회조건 계정책임자
			data.empno = inp_accnt_rspns_empno.getValue();
		} else if (clsf == "ibx_accnt_rspns"){	// 상세정보 계정책임자
			data.empno = ibx_accnt_rspns_empno.getValue();
		} else if (clsf == "inp_bugt_ctrl"){	// 조회조건 예산통제자
			data.empno = inp_bugt_empno.getValue();
		} else if (clsf == "ibx_bugt_ctrl"){	// 상세정보 예산통제자
			data.empno = ibx_bugt_empno.getValue();
		}

		data.ref_yn = (data.empno == "")? "Y" : "N";

		com.pop.open(com.pop.TYPE.HUM, data, function(ret){
			if (clsf == "inp_accnt_rspns"){
				dma_search.set("accnt_rspns", ret.syspayno);
				inp_accnt_rspns_empno.setValue(ret.empno);
				inp_accnt_rspns_nm.setValue(ret.nm);
			} else if (clsf == "ibx_accnt_rspns"){
				dma_mast.set("accnt_rspns", ret.syspayno);
				dma_mast.set("accnt_rspns_empno", ret.empno);
				dma_mast.set("accnt_rspns_nm", ret.nm);
			} else if (clsf == "inp_bugt_ctrl"){
				dma_search.set("bugt_ctrl_psn", ret.syspayno);
				inp_bugt_empno.setValue(ret.empno);
				inp_bugt_nm.setValue(ret.nm);
			} else if (clsf == "ibx_bugt_ctrl"){
				dma_mast.set("bugt_ctrl_psn", ret.syspayno);
				dma_mast.set("bugt_empno", ret.empno);
				dma_mast.set("bugt_nm", ret.nm);
			}
		});
	};

	/*
     *  비목해소기준 검색 버튼 클릭 이벤트
     */
	scwin.btn_tb_itm_solve_base_onclick = function(e) {
		if (sbx_fin_bsns_clsf.getValue() == "") {
			alert("재무사업구분을 먼저 선택하여 주시기 바랍니다.");
			return;
		} 

		var data = { fin_bsns_clsf : dma_mast.get("fin_bsns_clsf")
					, itm_solve_base : dma_mast.get("itm_solve_base").trim()
					, disable_yn : "Y"
					, ref_yn : (dma_mast.get("itm_solve_base").trim() == "") ? "Y" : "N"
					};
		com.pop.open(com.pop.TYPE.ITM_BASE, data, function(ret) {
			dma_mast.set("itm_solve_base", ret.itm_solve_base);
			dma_mast.set("itm_solve_base_nm", ret.itm_solve_base_nm);
		});	
	};

	/*
     *  그리드 셀 클릭 이벤트
     */
	scwin.grd_list_oncellclick = function(row,col,colId) {
		dma_mast.setEmptyValue();
		dma_mast.setJSON(dlt_grd.getRowJSON(row));

		if (dma_mast.get("acnnt_no") != "") {
			btn_del.setDisabled(false);
		}

		// 계정 종류에 따른 그리드 ui 수정
		scwin.grd_list_set_ui(0);
	};

	/*
     *  추가 버튼 클릭 이벤트
     */
	scwin.btn_add_onclick = function(e) {
		dma_mast.setEmptyValue();
		dma_mast.set("rowStatus", "C");
		dma_mast.set("mngmt_clsf", "F");
		sbx_tax_cd.setDisabled(false);

		// 계정 종류에 따른 그리드 ui 수정
		scwin.grd_list_set_ui("N");
	};

	/*
     * 저장 전 체크
     */
	scwin.preSave = function(){
		var mngmtClsf = dma_mast.get("mngmt_clsf");
		var validData=[
			{dataObj : dma_mast
			   , valInfoArr :[
				    { id : "accnt_no_nm", mandatory : true } 
				   ,{ id : "accnt_grp", mandatory : true } 
				   ,{ id : "accnt_state", mandatory : true } 
				   ,{ id : "fomat_unit", mandatory : true }
				   ,{ id : "accnt_rspns", mandatory : true}
				   ,{ id : "bugt_ctrl_psn", mandatory : true}
				   ,{ id : "fin_bsns_clsf", mandatory : true}
				   ,{ id : "itm_solve_base", mandatory : true}
				   ,{ id : "ctrl_lmt_rt", mandatory : true}
				   ,{ id : "start_ymd", mandatory : true}
				]
			  , isCheckModified : false
			}
		];

		if (mngmtClsf != "R") {
			if (dma_mast.get("rowStatus") == "C") {
				var accntGrp = sbx_tb_accnt_grp.getValue();
				var taxCd = sbx_tax_cd.getValue().trim();

				if (accntGrp == "FAK041" || accntGrp == "FAK042" || accntGrp == "FAK061"){
					alert("PD계정, PA계정, PX계정은 좌측매뉴 [과제관리/과제수행/A.D.X계정신청]을 이용하시기 바랍니다.");
					return false;
				}

				if (accntGrp == "FAK043" || accntGrp == "FAK044" || accntGrp == "FAK011" || accntGrp == "FAK005" || accntGrp == "FAK004" || accntGrp == "FAK003"){
					if (taxCd == ""){
						alert("과세구분을 입력해 주세요.");
						return false;
					}
				}

				if (accntGrp == "FAK011" || accntGrp == "FAK005" || accntGrp == "FAK004" || accntGrp == "FAK003"){
					if (taxCd == "FTX001"){
						alert("BG, BH, BK, DM 계정은 비과세로만 설정가능합니다.");
						return false;
					}
				}
			} else {
				if (accntGrp != "FAK051" && accntGrp != "FAK911" && accntGrp != "FAK995"){  // 특수사업계정번호, 연구사업계정번호, 재무관리계정번호 제외하고 모두 검증
					if (ica_start_ymd.getValue().substring(2,4) != ibx_accnt_no.getValue().substring(2,4)){
						alert(ibx_accnt_no.getValue().substring(2,4)+"년도 계정의 시작일자를 "+ica_start_ymd.substring(2,4)+"년으로 수정할 수 없습니다.");
						return false;
					}
				}
			}

			if (!com.validateData(validData)) return false;
		}
		return true;
	};

	/*
     *  저장 이벤트
     */
	scwin.save = function(e) {
		com.execute(sbm_saveData, {
			successCallback : function(e){
				com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function(){
					debugger;
					dma_mast.get("rowStatus");
					// 조회
					scwin.search();
				});
			}
		});
	};

	/*
     *  저장 버튼 클릭 이벤트
     */
	scwin.btn_save_onclick = function(e) {
		var accntGrp = sbx_tb_accnt_grp.getValue();

		if (!scwin.preSave()) {
			return;
		}

		if(scwin.service_id == "F112"){	// 화면ID 세팅
			dma_mast.set("serviceId", scwin.service_id);
		}

		if (dma_mast.get("rowStatus") == "U") {	// 수정 로직
			if (accntGrp == "FAK911") {	// 2015.09.02 연구계정에는 accnt_clsf에 계정구분이, card_use_ex에는 카드종류가 들어가야해서 연구계정을 수정할 경우 데이터를 가지고 가지 못하도록 수정 
				dma_mast.set("accnt_clsf", "");
				dma_mast.set("card_use_ex", "");
			}

			if (accntNoGb != "PX"){
	     		dma_mast.set("tot_prj_start_ymd", "");
	     		dma_mast.set("tot_prj_cls_ymd", "");
			}
		}

		com.confirm("message.xom.wq.017"/*저장 하시겠습니까?*/, function(ret){
			if (ret) {
				scwin.save();
			}
		});		
	};

	/*
     *  삭제 버튼 클릭 이벤트
     */
	scwin.btn_del_onclick = function(e) {
		com.confirm("message.xom.wq.022"/*삭제 하시겠습니까?*/, function(ret) {
			if (ret) {
				com.execute(sbm_deleteData, {
					successCallback : function(e) { // 성공 콜백
						com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
							dma_search.set("accnt_no", "");	// 기 삭제된 계정번호 검색을 막기 위한 공백 세팅
							scwin.search();
						});
					}
				});
			}
		});
	};

	/*
     *  계정에 따른 그리드 UI 동적 변경
     */
	scwin.grd_list_set_ui = function(row) {
		var accntNo = row == "N" ? "" : grd_list.getCellData(row, "accnt_no");
		var mngmtClsf = row == "N" ? "" : grd_list.getCellData(row, "mngmt_clsf");
        accntNoGb = row == "N" ? "" : accntNo.substr(0,2);

		if (accntNoGb == "PX"){
			grp_px_th.setStyle("display", "");
			grp_px_td.setStyle("display", "");
		}else{
			grp_px_th.setStyle("display", "none");
			grp_px_td.setStyle("display", "none");
		}

		if (accntNoGb == "PA" || accntNoGb == "PD"){
			grp_pa_th.setStyle("display", "");
			grp_pa_td.setStyle("display", "");
		}else{
			grp_pa_th.setStyle("display", "none");
			grp_pa_td.setStyle("display", "none");
		}  	

		if (/PL|PQ|BG|BH|BK|DM/.test(accntNoGb)){  //20150107 BG, BH, BK, DM 계정추가
			sbx_tax_cd.setDisabled(true);                
		}			

		if (mngmtClsf == "R"){
			if (com.getLoginInfo("roleCode").indexOf("1002138") < 0){ // 재무시스템 관리자
				ibx_accnt_no_nm.setDisabled(true);
                sbx_tb_accnt_grp.setDisabled(true);
                sbx_usg_clsf.setDisabled(true);
                grp_tb_accnt_rspns.setDisabled(true); //계정책임자 그룹
                grp_tb_bugt_ctrl_psn.setDisabled(true); // 예산통제자 그룹
                grp_tb_itm_solve_base.setDisabled(true); // 비목해소기준 그룹
                sbx_fin_bsns_clsf.setDisabled(true);
                ibx_ctrl_lmt_rt.setDisabled(true);
                grp_pbs_ex.setDisabled(true); // pbs유무 그룹
                grp_card_use_ex.setDisabled(true); // 카드유무 그룹
                ibx_rmk.setDisabled(true);
            }               
        } else {
        	ibx_accnt_no_nm.setDisabled(false);
            sbx_usg_clsf.setDisabled(false);
            grp_tb_accnt_rspns.setDisabled(false); //계정책임자 그룹
            grp_tb_bugt_ctrl_psn.setDisabled(false); // 예산통제자 그룹
            grp_tb_itm_solve_base.setDisabled(false); // 비목해소기준 그룹
            sbx_fin_bsns_clsf.setDisabled(false);
            ibx_ctrl_lmt_rt.setDisabled(false);
            grp_pbs_ex.setDisabled(false); // pbs유무 그룹
            grp_card_use_ex.setDisabled(false); // 카드유무 그룹
            ibx_rmk.setDisabled(false);
            ica_start_ymd.setReadOnly(false);
            ica_cls_ymd.setReadOnly(false);
	        if (mngmtClsf == "F"){
	        	sbx_tb_accnt_grp.setDisabled(true);  
	        } else {                	
	            sbx_tb_accnt_grp.setDisabled(false);  
	        }
		}
	};

	/**
	 * 엑셀 다운로드 버튼 클릭 이벤트
	 */
	scwin.btn_excel_onclick = function(e) {
		if(dlt_grd.getRowCount() > 0){
			var option = {
				    fileName : "계정번호관리.xls"
				  , useDataFormat : true
//				  , hiddenVisible : true
			};
			com.downloadGridExcel(grd_list, option);
		}
	};

	scwin.sbx_tb_accnt_grp_onchange = function(info) {
		var accntGrp = sbx_tb_accnt_grp.getValue();

		if(dma_mast.get("rowStatus") == "C"){
			if (accntGrp == "FAK041" || accntGrp == "FAK042" || accntGrp == "FAK061"){
				alert("PD계정, PA계정, PX계정은 좌측메뉴 [과제관리/과제수행/A.D.X계정신청]을 이용하시기 바랍니다.");
				sbx_tb_accnt_grp.setSelectedIndex(0);
			}

			if (accntGrp == "FAK043" || accntGrp == "FAK044" || accntGrp == "FAK011" || accntGrp == "FAK005" || accntGrp == "FAK004" || accntGrp == "FAK003" 
				|| accntGrp == "FAK007"){
				sbx_tax_cd.setDisabled(false);	
			} else {
				sbx_tax_cd.setDisabled(true);	
			}
		} else {
			sbx_tax_cd.setDisabled(true);	
		}

		if (accntGrp =="FAK043") {
			sbx_fin_bsns_clsf.setValue("FBS105");
		} else if (accntGrp =="FAK044") {
			sbx_fin_bsns_clsf.setValue("FBS101");
		}
	};

