head.script:
    /****************************************************
 	 * 화면 설명 : 매출계산서(관) 조회
	 * 변경 이력 : 2022-09-25	이영희	최초 작성
	 * serviceId : F812
 	 ****************************************************/  
 	/**
	 * 페이지 로딩 이벤트
	 */
	scwin.onpageload = function() {		
		var today = com.getServerDateTime();
		dma_search.set("from_ymd", com.addDate(today, -30));
		dma_search.set("to_ymd", today);

		// 기간 역전 체크
		com.checkPeriod(ica_from, ica_to);

		// 공통코드
		var codeOptions = [ { code : "FTX", compID :"grd_bill:tax_cd" }
		                   ,{ code : "FBF", compID :"sbx_bill_clsf,grd_bill:bill_clsf" }
		                   ,{ code : "FBE", compID :"sbx_bill_clsf_1" }
		                ];
		com.setCmnCd(codeOptions, function() {
		});

		var session = com.getLoginInfo();
		if(session.deptCode != "H740" && !/1010001|1002138/.test(session.roleCode)){ // H740 재정운영실, 1002138 재무관리시스템관리자, 1010001 시스템관리자A
			ibx_empno.setValue(session.empno);
			ibx_nm.setValue(session.cn);
			dma_search.set("wrte_psn", session.sn);
		}

		// enter key event
		com.setEnterKeyEvent(grp_emp, scwin.btn_emp_onclick);
		com.setEnterKeyEvent(grp_wrte_psn, scwin.btn_wrte_psn_onclick);
		com.setEnterKeyEvent(grp_accnt_no, scwin.btn_accnt_no_onclick);	 
	};

	scwin.onpageunload = function() {

	};

	/**
	 * 계산서 리스트 조회
	 */
	scwin.search = function(){
		dma_search.set("tax_bill_no", ibx_bill_no.getValue());

		com.execute(sbm_selectData, {
			isImplExcel : true,
			successCallback : function(e) {
				dlt_bill.setJSON(e.responseJSON.ftxBillHVoList);
				tbx_cnt.setValue(dlt_bill.getRowCount());

				dma_bill.setEmptyValue();
				dlt_billD.removeAll();
			},
			excelCallback : function(e) {
				// DB 다운로드
				var option = {
					fileName : "매출계산서_" + com.getServerDateTime() + ".xls"
					//, hiddenVisible : true
					, useDataFormat : true
					, providerInfo : {
						  service : "finTaxService"
						, method : "selectFtxBillHExcelDown"
						, vo : "fin.tax.vo.FinTaxSearchVo"
						, paramMap : dma_search
					}
				};
				com.downloadGridExcel(grd_excel_down, option);
			}
		});
	};

	/**
	 * 계산서 상세 조회
	 */
	scwin.searchBill = function(){
		com.execute(sbm_selectBill, {
			successCallback : function(e){
				var session = com.getLoginInfo();
				var unslipNoSub = dma_bill.get("unslip_no").substring(0,3);				

				if(dma_bill.get("unslip_no") != "" && !(/1002207/.test(session.roleCode) && unslipNoSub == "110")){	// 110결의이면서 1002207 재무관리 세무담당 권한자는 수정 가능 2023.11.08
					ibx_unslip_no.setDisabled(true);
				}else{
					ibx_unslip_no.setDisabled(false);
				}

				if(dma_bill.get("tax_bill_no").substring(0, 3) == "F30"){
					btn_del.setDisabled(true);
				}else{
					btn_del.setDisabled(false);
				}
			}
		});
	};

	/**
	 * 계산서발행 팝업 오픈
	 */
	scwin.openBillPop = function(data){
		data.ref_yn = "Y"; // 조회없이 팝업 호출
		// eip2.0 >> 계산서 : FinBillRegst1Mnt, 세금계산서  : FinTaxBillRegst1Mnt
		com.pop.open(com.pop.TYPE.RVN_BILL, data, function(ret) {
			btn_search.trigger("click");

			dma_search.set("tax_bill_no", ret.tax_bill_no);			
			scwin.searchBill();
		});
	};

	//--------------------------------------------------------------------------- event --------------------------------//

	/**
	 * 작성자 검색 팝업
	 */
	scwin.btn_emp_onclick = function(e) {
		var data = { empno : ibx_empno.getValue(),
					 work_clsf : "HAG010",
					 ref_yn : (ibx_empno.getValue()=="")?"Y" : "N"
		};

		com.pop.open( com.pop.TYPE.HUM, data, function(ret){
			ibx_empno.setValue(ret.empno);
			ibx_nm.setValue(ret.nm);
			dma_search.set("wrte_psn", ret.syspayno);
		});
	};

	/**
	 * 조회
	 */
	scwin.btn_search_onclick = function(e) {
		if(dma_search.get("from_ymd") == "" || dma_search.get("to_ymd") == ""){
			com.alert(com.getMsg("message.xom.ui.fin.026", "발행일자"));
			return;
		}

		scwin.search();			
	};	

	/**
	 * 계정번호 검색 버튼 클릭
	 */
	scwin.btn_accnt_no_onclick = function(e) {
		var data={
			accnt_no : ibx_accnt_no.getValue()
		};
		if(ibx_accnt_no.getValue() == ""){
			data.ref_yn = "Y";	
		}
		com.pop.open(com.pop.TYPE.ACNNO, data, function(ret) {
			dma_search.set("accnt_no", ret.accnt_no);
			ibx_accnt_no_nm.setValue(ret.accnt_no_nm);
		});
	};

	/**
	 * grid cell click event
	 */
	scwin.grd_bill_oncellclick = function(row,col,colId) {
		if(colId == "unslip_no"){
			if(dlt_bill.getCellData(row, "unslip_no") == "") return;			
			com.biz.openEvidWindow(dlt_bill.getCellData(row, "unslip_no"));
		}else if(colId == "slip_no"){
			if(dlt_bill.getCellData(row, "slip_no") == "") return;			
			com.biz.openEvidWindow(dlt_bill.getCellData(row, "slip_no"));
		}else if(colId == "tax_bill_no"){
			dma_search.set("tax_bill_no", dlt_bill.getCellData(row, "tax_bill_no"));			

			scwin.searchBill();
		}
	};

	/**
	 * 엑셀다운로드 버튼 클릭 event
	 */
	scwin.btn_excel_onclick = function(e) {
		var option = {
			    fileName : "매출계산서.xls"
			  , useDataFormat : true
//			  , hiddenVisible : true
		};
		com.downloadGridExcel(grd_bill, option);
	};

	/**
	 * 신규 버튼 클릭 event
	 */
	scwin.btn_new_onclick = function(e) {		
		scwin.openBillPop({biz_clsf : "F", type : "C"}); // 03 계산서, 01 세금계산서		
	};

	/**
	 * 작성자 변경 event
	 */
	scwin.ibx_empno_onviewchange = function(info) {
		if(this.getValue() == ""){
			ibx_nm.setValue("");
			dma_search.set("wrte_psn", "");
		}
	};

	/**
	 * 계정번호 변경 event
	 */
	scwin.ibx_accnt_no_onviewchange = function(info) {
		ibx_accnt_no_nm.setValue("");
	};

	/**
	 * 인쇄버튼 클릭 이벤트
	 */
	scwin.btn_print_onclick = function(e) {
		var chkTaxBillKnd = dma_billCnt.get("bill_knd");
		var chkUpdtCnt = dma_billCnt.get("updt_cnt");
		var chkOrgCnt = dma_billCnt.get("org_cnt");
		var chkUpdtNewCnt = dma_billCnt.get("updt_new_cnt");
		var chkUpdtOfUpdtCnt = dma_billCnt.get("updt_of_updt_cnt");
		var taxType = dma_bill.get("tax_type");

		var node = "tax_bill_no=" + dma_bill.get("tax_bill_no") 
					+ "&barcode_1=" + com.getServerDateTime("yyyyMMddHHmm")
					+ "&barcode_2=" + com.getLoginInfo("sn")
					+ "&barcode_3=" + com.getServerDateTime()
					+ "&scrn_id=" + "F812";


		// 레포트 양식 항목코드
		debugger;
		var reportArr = [];
		if(chkTaxBillKnd.trim() == "" && chkUpdtCnt == 0 && chkOrgCnt == 0 && chkUpdtNewCnt == 0) { // 계산서 단건			
			reportArr.push({
				ozrCode : (taxType == "03")? "F060": "F070",
				ozrPath : (taxType == "03")? "/fin/fin/tax/FinBillDetls.ozr" : "/fin/fin/tax/FinTaxBillDetls.ozr",
				odiNm : "FinBillDetls",
				displayNm : (taxType == "03")? "계산서(공급자보관용)" : "세금계산서(공급자보관용)",
				param: node
			});

			reportArr.push({
				ozrCode : (taxType == "03")? "F066": "F076",
				ozrPath : (taxType == "03")? "/fin/fin/tax/FinBillDetls1.ozr" : "/fin/fin/tax/FinTaxBillDetls1.ozr",
				odiNm : "FinBillDetls",
				displayNm : (taxType == "03")? "계산서(공급받는자보관용)" : "세금계산서(공급받는자보관용)",
				param: node
			});
		} else if((chkTaxBillKnd.trim() == "" && chkUpdtCnt == 1 && chkOrgCnt == 0 && chkUpdtNewCnt == 0)
		         || (chkTaxBillKnd.trim() == "U" && chkUpdtCnt == 0 && chkOrgCnt == 1 && chkUpdtNewCnt == 0)) { // 마이너스만발행한 최초세금계산서에서 출력
			reportArr.push({
				ozrCode : (taxType == "03")? "F061": "F071",
				ozrPath : (taxType == "03")? "/fin/fin/tax/FinUpdtMinusBillDetls.ozr" : "/fin/fin/tax/FinUpdtMinusTaxBillDetls.ozr",
				odiNm : (taxType == "03")? "FinUpdtMinusBillDetls" : "FinUpdtMinusTaxBillDetls",
				displayNm : (taxType == "03")? "계산서(공급자보관용)" : "세금계산서(공급자보관용)",
				param: node
			});

			reportArr.push({
				ozrCode : (taxType == "03")? "F068": "F077",
				ozrPath : (taxType == "03")? "/fin/fin/tax/FinUpdtMinusBillDetls1.ozr" : "/fin/fin/tax/FinUpdtMinusTaxBillDetls1.ozr",
				odiNm : (taxType == "03")? "FinUpdtMinusBillDetls" : "FinUpdtMinusTaxBillDetls",
				displayNm : (taxType == "03")? "계산서(공급받는자보관용)" : "세금계산서(공급받는자보관용)",
				param: node
			});
		} else { // 수정발행 건 - 기재사항 착오정정
			reportArr.push({
				ozrCode : (taxType == "03")? "F065": "F075",
				ozrPath : (taxType == "03")? "/fin/fin/tax/FinUpdtNewBillDetls.ozr" : "/fin/fin/tax/FinUpdtNewTaxBillDetls.ozr",
				odiNm : (taxType == "03")? "FinUpdtNewBillDetls" : "FinUpdtNewTaxBillDetls",
				displayNm : (taxType == "03")? "계산서(공급자보관용)" : "세금계산서(공급자보관용)",
				param: node
			});

			reportArr.push({
				ozrCode : (taxType == "03")? "F069": "F078",
				ozrPath : (taxType == "03")? "/fin/fin/tax/FinUpdtNewBillDetls1.ozr" : "/fin/fin/tax/FinUpdtNewTaxBillDetls1.ozr",
				odiNm : (taxType == "03")? "FinUpdtNewBillDetls" : "FinUpdtNewTaxBillDetls",
				displayNm : (taxType == "03")? "계산서(공급받는자보관용)" : "세금계산서(공급받는자보관용)" ,
				param: node
			});		
		}

		com.oz.openPopup(reportArr, true);
	};

	/**
	 * 엑셀내리기 버튼 클릭 이벤트
	 */
	scwin.btn_noti_file_onclick = function(e) {
		var option = {
			  fileName : "매출계산서-세무신고용.xlsx"
			, hiddenVisible : true
			, useDataFormat : true
			, providerInfo : {
				  service : "finTaxService"
				, method : "selectFtxBillHExcel"
				, vo : "fin.tax.vo.FinTaxSearchVo"
				, paramMap : dma_search
			}
		};

		com.downloadGridExcel(grd_excel, option);
	};

	/**
	 * 계정번호 검색 버튼 클릭 이벤트
	 */
	scwin.btn_accnt_onclick = function(e) {
		var data={
			accnt_no : ibx_accnt_no_detl.getValue().trim(),
			ref_yn : (ibx_accnt_no_detl.getValue().trim() == "")?"Y" : "N"
		};

		com.pop.open(com.pop.TYPE.ACNNO, data, function(ret) {
			if(ret.accnt_no.substring(0, 2) != "FF"){
				if(dma_bill.get("tax_type") == "03" && ret.tax_cd == "FTX001"){
					alert("과세계정은 계산서를 발행할 수 없습니다.");
					return;	
				}

				if(dma_bill.get("tax_type") == "01" && ret.tax_cd == "FTX002"){
					alert("비과세계정은 세금계산서를 발행할 수 없습니다.");
					return;	
				}
			}

			dma_bill.set("accnt_no", ret.accnt_no);
			dma_bill.set("accnt_no_nm", ret.accnt_no_nm);
		});
	};

	/**
	 * 공급받는자 검색 버튼 클릭 이벤트
	 */
	scwin.btn_vend_onclick = function(e) {
		var data ={ref_yn:"Y",
				   vend_cd : ibx_vend_cd.getValue()
		};
		com.pop.open(com.pop.TYPE.VEND, data, function(ret) {
			if(ret.vend_cd != undefined && ret.use_yn != "Y"){
				alert("(사용중지 거래처) - 계산서 발행이 불가합니다.\n문의: 재정운영실 내선152번");
				return;
			}
			dma_bill.set("sude_psn", ret.vend_cd);
			dma_bill.set("vend_abbr", ret.vend_abbr);
			dma_bill.set("reprs_psn_nm", ret.reprs_psn_nm);
			if(ret.bsns_psn_regst_no.trim() == ""){
				dma_bill.set("bsns_psn_regst_no", ret.corp_resid_no);
			}else{
				dma_bill.set("bsns_psn_regst_no", ret.bsns_psn_regst_no);
			}			
			dma_bill.set("addr", ret.addr);
			dma_bill.set("bizcatg", ret.bizcatg); // 업태
			dma_bill.set("biztyp", ret.biztyp); // 업종
		});
	};

	/**
	 * 저장 버튼 클릭 이벤트
	 */
	scwin.btn_save_onclick = function(e) {
		dma_bill.set("prcs_clsf", (dma_bill.get("unslip_no") == "")? "N" : "Y");

		// 필수값체크
		if(!com.validateData(dma_bill,[
			// 2023.11.21 임자경 사무원 요청에 의한 주석
//			{ id : "accnt_no", mandatory : true, allowChar : "A-Z0-9", maxLength : 8, minLength : 8},			
//			{ id : "bill_clsf1", mandatory : true},
			{ id : "wrte_psn", mandatory : true}
		], false)) return;

		com.confirm("message.xom.wq.017"/*저장 하시겠습니까?*/, function(ret){
			if(ret){
				com.execute(sbm_saveData, {
					successCallback : function(e){
						com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
							scwin.search();
						});
					}
				});
			}
		});
	};

	/**
	 * 폐기/복구 버튼 클릭 이벤트
	 */
	scwin.btn_del_onclick = function(e) {
		if(dma_bill.get("tax_bill_no") == ""){
			alert("폐기/복구 하실 데이터를 선택하세요.");
			return;
		}

		if(dma_bill.get("decsn_ex") == "Y"){
			alert("확정된 결의건이므로 폐기하실수 없습니다.");
			return;
		}

		var msg = "";
		var clsf = (dma_bill.get("prcs_clsf") == "D")? "R" : "D"; // 폐기 D, 복구 R
		if(clsf == "D"){
			if(/미발행|전송오류/.test(dma_bill.get("state_nm"))){
				msg = "폐기 하시겠습니까?";
			}else{
				alert("세금계산서는 폐기할 수 없습니다. 재정운영실로 문의바랍니다.(내선 157)");
				return;
			}
		}else{
			msg = "복구 하시겠습니까?";
		}

		if(confirm(msg)){
			if(clsf == "D"){
				dma_bill.set("prcs_clsf", "D");
			}else{
				dma_bill.set("prcs_clsf", "N");
			}

			com.execute(sbm_savePrcs, {
				successCallback : function(e){
					com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
						scwin.search();
					});	
				}
			});
		}
	};

	/**
	 * 미확인조회 버튼 클릭 이벤트
	 */
	scwin.btn_unConfirm_onclick = function(e) {
		var data={
			from_ymd : ica_from.getValue(),
			to_ymd : ica_to.getValue(),
			bill_type : "1", // 1 매출계산서, 2 매입계산서
		};

		biz.fin.pop.open(biz.fin.pop.TYPE.BILL_UNCONF, data);
	};

	/**
	 * 확인 버튼 클릭 이벤트
	 */
	scwin.btn_confirm_onclick = function(e) {
		var row = grd_bill.getFocusedRowIndex();
		if(row < 0){
			alert("확정결의번호를 선택하세요.");
			return;
		}

		if(dlt_bill.getCellData(row, "slip_no") == ""){
			alert("선택한 행은 결의서가 아직 확정되지 않았습니다.");
			return;
		}

		dma_confirm.set("slip_no", dlt_bill.getCellData(row, "slip_no"));
		dma_confirm.set("tax_confirm_yn", "Y");

		com.execute(sbm_saveConfirm, {
			successCallback : function(e){
				scwin.search();
			}
		});
	};

	/**
	 * 미처리이메일 버튼 클릭 이벤트
	 */
	scwin.btn_email_onclick = function(e) {
		var rows = grd_bill.getCheckedIndex("chk");
		if(rows.length == 0){
			alert("자료를 선택하지 않았습니다.");
			return;
		}

		com.confirm("미처리 이메일을 발송하시겠습니까?", function(ret){
			if(ret){
				var billNos = "";
				for(var i=0; i<rows.length; i++){
					billNos += (i==0)? dlt_bill.getCellData(rows[i], "tax_bill_no") : ("," + dlt_bill.getCellData(rows[i], "tax_bill_no"));
				}

				dma_search.set("bill_no_list", billNos);

				com.execute(sbm_sendMail, {
					successCallback : function(e){
						alert("메일 발송을 하였습니다.");
					}
				});
			}
		});
	};

	/**
	 * 작성자 검색 클릭 이벤트
	 */
	scwin.btn_wrte_psn_onclick = function(e) {
		var data = { empno : ibx_wrte_empno.getValue(),
					 work_clsf : "HAG010",
					 ref_yn : (ibx_wrte_empno.getValue()=="")?"Y" : "N"
		};

		com.pop.open( com.pop.TYPE.HUM, data, function(ret){
			ibx_wrte_empno.setValue(ret.empno);
			ibx_wrte_nm.setValue(ret.nm);
			dma_bill.set("wrte_psn", ret.syspayno);
		});
	};

	/**
	 * 작성자 변경 이벤트
	 */
	scwin.ibx_wrte_empno_onchange = function(info) {
		if(this.getValue() == ""){
			ibx_wrte_nm.setValue("");
			dma_bill.set("wrte_psn", "");
		}
	};

	/**
	 * 엑셀내리기 버튼 클릭 이벤트
	 */
	scwin.btn_excel_down_onclick = function(e) {
		var option = {
			  fileName : "매출계산서_" + com.getServerDateTime() + ".xls"
			, hiddenVisible : true
			, useDataFormat : true
			, providerInfo : {
				  service : "finTaxService"
				, method : "selectFtxBillHExcelDown"
				, vo : "fin.tax.vo.FinTaxSearchVo"
				, paramMap : dma_search
			}
		};

		com.downloadGridExcel(grd_excel_down, option);
	};
