head.script:
    /****************************************************
 	 * 화면 설명 : 매입계산서(관) 조회
	 * 변경 이력 : 2022-10-05	이영희	최초 작성
	 * serviceId : F813
 	 ****************************************************/  
 	scwin.data ={};

 	/**
	 * 페이지 로딩 이벤트
	 */
	scwin.onpageload = function() {		
		var today = com.getServerDateTime();
		dma_search.set("from_ymd", today);
		dma_search.set("to_ymd", today);

		// 기간 역전 체크
		com.checkPeriod(ica_from, ica_to);

		if(!/1002207/.test(com.getLoginInfo("roleCode"))){ // 1002207 세무담당자
			grp_regst.hide();
			btn_del.hide();
		}

		// enter key event
		com.setEnterKeyEvent(grp_emp, scwin.btn_emp_onclick);
		com.setEnterKeyEvent(grp_decsn, scwin.btn_decsn_onclick);
		com.setEnterKeyEvent(grp_chng_psn, scwin.btn_chng_psn_onclick);	 

		// 공통코드
		var codeOptions = [ { code : "FAH", compID :"sbx_evid_cd,grd_bill:evid_cd", filter:"mngmtItem1=='Y'" }
		                ];
		com.setCmnCd(codeOptions, function() {
		});
	};

	scwin.onpageunload = function() {

	};

	/**
	 * 계산서 리스트 조회
	 */
	scwin.search = function(){		
		if(ibx_selr_corp_no.getValue() != ""){
			dma_search.set("selr_corp_no_1", ibx_selr_corp_no.getDisplayValue());
		}

		com.execute(sbm_selectData, {
			isImplExcel : true,
			successCallback : function(e) {
				dlt_bill.setJSON(e.responseJSON.ktxIssuMstrVoList);
				tbx_cnt.setValue(dlt_bill.getRowCount());
			},
			excelCallback : function(e) {
				// DB 다운로드
				var option = {
					fileName : "매입계산서_" + com.getServerDateTime() + ".xls"
					, hiddenVisible : true
					, useDataFormat : true
					, providerInfo : {
						  service : "finPurTaxService"
						, method : "selectKtxIssuMstrList"
						, vo : "fin.tax.vo.FinPurTaxSrcVo"
						, paramMap : dma_search
					}
					//, removeColumns : "0" // 데이터가 깨져서 생략
				};
				com.downloadGridExcel(grd_bill, option);
			}
		});
	};

	/**
	 * 작성자 및 확정자 검색 
	 */
	scwin.getEmpInfo = function(clsf, empNo){
		var data = { empno : empNo,
					 work_clsf : "HAG010",
					 ref_yn : (empNo=="")?"Y" : "N"
		};

		com.pop.open( com.pop.TYPE.HUM, data, function(ret){
			if(clsf == 1){ // 작성자
				ibx_wrte_psn_empno.setValue(ret.empno);
				ibx_wrte_psn_nm.setValue(ret.nm);
				dma_search.set("wrte_psn", ret.syspayno);
			}else if(clsf == 2){ // 확정자
				ibx_decsn_empno.setValue(ret.empno);
				ibx_decsn_nm.setValue(ret.nm);
				dma_search.set("decsn_psn", ret.syspayno);
			}else{
				ibx_empno.setValue(ret.empno);
				ibx_nm.setValue(ret.nm);
				scwin.data.wrte_psn = ret.syspayno;
				scwin.data.wrte_dept = ret.dept_cd;
			}
		});
	};	

	/**
	 * 작성자 및 확정자 클리어
	 */
	scwin.clearEmpInfo = function(clsf){
		if(clsf == 1){ // 작성자
			ibx_wrte_psn_nm.setValue("");
			dma_search.set("wrte_psn", "");
		}else if(clsf == 2){ // 확정자
			ibx_decsn_nm.setValue("");
			dma_search.set("decsn_psn", "");
		}else{
			ibx_nm.setValue("");
			scwin.data.wrte_psn = "";
			scwin.data.wrte_dept = "";
		}
	};

	/**
	 * 전자증빙 팝업 오픈
	 */
	scwin.openElecBill = function(data){
		com.pop.open(com.pop.TYPE.TAXBILL, data, function(retObj) {
			if(retObj){
				scwin.search();	
			}
		});
	};

	/**
	 * 증빙 팝업 오픈
	 */
	scwin.openBillPop = function(data){
		biz.fin.pop.open(biz.fin.pop.TYPE.EVID_BILL, data, function(ret) {
			if(ret){
				dma_search.set("selr_corp_nm", ret.vend_nm);

				btn_search.trigger("click");
			}
		});
	};

	//--------------------------------------------------------------------------- event --------------------------------//
	/**
	 * 작성자 검색 버튼 클릭 이벤트
	 */
	scwin.btn_emp_onclick = function(e) {
		scwin.getEmpInfo(1, ibx_wrte_psn_empno.getValue());
	};

	/**
	 * 확정자 검색 버튼 클릭 이벤트
	 */
	scwin.btn_decsn_onclick = function(e) {
		scwin.getEmpInfo(2, ibx_decsn_empno.getValue());
	};

	/**
	 * 조회
	 */
	scwin.btn_search_onclick = function(e) {
		if(dma_search.get("from_ymd") == "" || dma_search.get("to_ymd") == ""){
			com.alert(com.getMsg("message.xom.ui.fin.026", "발행일자"));
			return;
		}

		scwin.search();			
	};	


	/**
	 * grid cell click event
	 */
	scwin.grd_bill_oncellclick = function(row,col,colId) {
		if(colId == "unslip_no"){
			if(dlt_bill.getCellData(row, "unslip_no") == "") return;			
			com.biz.openEvidWindow(dlt_bill.getCellData(row, "unslip_no"));
		}else if(colId == "slip_no"){
			if(dlt_bill.getCellData(row, "slip_no") == "") return;			
			com.biz.openEvidWindow(dlt_bill.getCellData(row, "slip_no"));
		}else if(colId == "issu_seqno"){
			if(dlt_bill.getCellData(row, "stat_code") != "N"){
				var data = {issu_id : dlt_bill.getCellData(row, "issu_seqno")
						  , mode: "view"
						  , wrk_clsf : "mnt"
						  , ref_yn : "Y"
				};

				scwin.openElecBill(data);
			}else{
				var data = {issu_seqno : dlt_bill.getCellData(row, "issu_seqno")
						  , unslip_no: dlt_bill.getCellData(row, "unslip_no")
				};

				scwin.openBillPop(data);
			}
		}else if(colId == "wrte_psn_nm"){
			dma_tax_returns.set("issu_seqno", dlt_bill.getCellData(row, "issu_seqno"));
			com.execute(sbm_selectCnt, {
				successCallback : function(e){
					if(e.responseJSON.cnt == '0'){
						alert("변경 이력이 없습니다.");
					}else{
						biz.fin.pop.open(biz.fin.pop.TYPE.PSN_HIS, {issu_seqno: dlt_bill.getCellData(row, "issu_seqno")});
					}
				}
			});
		}
	};

	/**
	 * 엑셀다운로드 버튼 클릭 event
	 */
	scwin.btn_excel_onclick = function(e) {
//		var option = {
//			    fileName : "매입계산서_" + com.getServerDateTime() + ".xls"
//			  , useDataFormat : true
//			  , hiddenVisible : true
//		};
//		com.downloadGridExcel(grd_bill, option);

		var option = {
			    fileName : "매입계산서_" + com.getServerDateTime() + ".xls"
			  , useDataFormat : true
			  , hiddenVisible : true
			  , providerInfo : {
				  service : "finPurTaxService"
				, method : "selectKtxIssuMstrListExcel"
				, vo : "fin.tax.vo.FinPurTaxSrcVo"
				, paramMap : dma_search
			}
		};
		com.downloadGridExcel(grd_bill, option);
	};	

	/**
	 * 작성자 변경 event
	 */
	scwin.ibx_empno_onviewchange = function(info) {
		if(this.getValue() == ""){
			scwin.clearEmpInfo(3);	
		}
	};

	/**
	 * 계정번호 변경 event
	 */
	scwin.ibx_accnt_no_onviewchange = function(info) {
		ibx_accnt_no_nm.setValue("");
	};

	/**
	 * 인쇄버튼 클릭 이벤트
	 */
	scwin.btn_print_onclick = function(e) {
		var chkTaxBillKnd = dma_billCnt.get("bill_knd");
		var chkUpdtCnt = dma_billCnt.get("updt_cnt");
		var chkOrgCnt = dma_billCnt.get("org_cnt");
		var chkUpdtNewCnt = dma_billCnt.get("updt_new_cnt");
		var chkUpdtOfUpdtCnt = dma_billCnt.get("updt_of_updt_cnt");
		var taxType = dma_bill.get("tax_type");

		// 레포트 양식 항목코드
		var reportArr = [];
		if(chkTaxBillKnd.trim() == "" && chkUpdtCnt == 0 && chkOrgCnt == 0 && chkUpdtNewCnt == 0) { // 계산서 단건			
			reportArr.push({
				ozrCode : (taxType == "03")? "F060": "F070",
				ozrPath : (taxType == "03")? "/fin/fin/tax/FinBillDetls.ozr" : "/fin/fin/tax/FinTaxBillDetls.ozr",
				odiNm : "FinBillDetls",
				displayNm : "계산서(공급자보관용)",
				param: "tax_bill_no=" + dma_bill.get("tax_bill_no")
			});

			reportArr.push({
				ozrCode : (taxType == "03")? "F066": "F076",
				ozrPath : (taxType == "03")? "/fin/fin/tax/FinBillDetls1.ozr" : "/fin/fin/tax/FinTaxBillDetls1.ozr",
				odiNm : "FinBillDetls",
				displayNm : "계산서(공급받는자보관용)",
				param: "tax_bill_no=" + dma_bill.get("tax_bill_no")
			});
		} else if((chkTaxBillKnd.trim() == "" && chkUpdtCnt == 1 && chkOrgCnt == 0 && chkUpdtNewCnt == 0)
		         || (chkTaxBillKnd.trim() == "U" && chkUpdtCnt == 0 && chkOrgCnt == 1 && chkUpdtNewCnt == 0)) { // 마이너스만발행한 최초세금계산서에서 출력
			reportArr.push({
				ozrCode : (taxType == "03")? "F061": "F071",
				ozrPath : (taxType == "03")? "/fin/fin/tax/FinUpdtMinusBillDetls.ozr" : "/fin/fin/tax/FinUpdtMinusTaxBillDetls.ozr",
				odiNm : "FinUpdtMinusBillDetls",
				displayNm : "계산서(공급자보관용)",
				param: "tax_bill_no=" + dma_bill.get("tax_bill_no")
			});

			reportArr.push({
				ozrCode : (taxType == "03")? "F068": "F077",
				ozrPath : (taxType == "03")? "/fin/fin/tax/FinUpdtMinusBillDetls1.ozr" : "/fin/fin/tax/FinTaxBillDetls1.ozr",
				odiNm : "FinUpdtMinusBillDetls",
				displayNm : "계산서(공급받는자보관용)",
				param: "tax_bill_no=" + dma_bill.get("tax_bill_no")
			});
		} else { // 수정발행 건 - 기재사항 착오정정
			reportArr.push({
				ozrCode : (taxType == "03")? "F065": "F075",
				ozrPath : (taxType == "03")? "/fin/fin/tax/FinUpdtNewBillDetls.ozr" : "/fin/fin/tax/FinUpdtNewTaxBillDetls.ozr",
				odiNm : "FinUpdtNewBillDetls",
				displayNm : "계산서(공급자보관용)",
				param: "tax_bill_no=" + dma_bill.get("tax_bill_no")
			});

			reportArr.push({
				ozrCode : (taxType == "03")? "F069": "F078",
				ozrPath : (taxType == "03")? "/fin/fin/tax/FinUpdtNewBillDetls1.ozr" : "/fin/fin/tax/FinUpdtNewTaxBillDetls1.ozr",
				odiNm : "FinUpdtNewTaxBillDetls",
				displayNm : "계산서(공급받는자보관용)",
				param: "tax_bill_no=" + dma_bill.get("tax_bill_no")
			});		
		}

		com.oz.openPopup(reportArr, true);
	};

	/**
	 * 엑셀내리기 버튼 클릭 이벤트
	 */
	scwin.btn_excel_down_onclick = function(e) {
		var option = {
			  fileName : "매출계산서-세무신고용.xlsx"
			, hiddenVisible : true
			, useDataFormat : true
			, providerInfo : {
