				, method : "selectFtxBillHExcel"
				, vo : "fin.tax.vo.FinTaxSearchVo"
				, paramMap : dma_search
			}
		};

		com.downloadGridExcel(grd_excel, option);
	};

	/**
	 * 계정번호 검색 버튼 클릭 이벤트
	 */
	scwin.btn_accnt_onclick = function(e) {
		var data={
			accnt_no : ibx_accnt_no_detl.getValue().trim(),
			ref_yn : (ibx_accnt_no_detl.getValue().trim() == "")?"Y" : "N"
		};

		com.pop.open(com.pop.TYPE.ACNNO, data, function(ret) {
			if(ret.accnt_no.substring(0, 2) != "FF"){
				if(dma_bill.get("tax_type") == "03" && ret.tax_cd == "FTX001"){
					alert("과세계정은 계산서를 발행할 수 없습니다.");
					return;	
				}

				if(dma_bill.get("tax_type") == "01" && ret.tax_cd == "FTX002"){
					alert("비과세계정은 세금계산서를 발행할 수 없습니다.");
					return;	
				}
			}

			dma_bill.set("accnt_no", ret.accnt_no);
			dma_bill.set("accnt_no_nm", ret.accnt_no_nm);
		});
	};

	/**
	 * 공급받는자 검색 버튼 클릭 이벤트
	 */
	scwin.btn_vend_onclick = function(e) {
		var data ={ref_yn:"Y",
				   vend_cd : ibx_vend_cd.getValue()
		};
		com.pop.open(com.pop.TYPE.VEND, data, function(ret) {
			if(ret.vend_cd != undefined && ret.use_yn != "Y"){
				alert("(사용중지 거래처) - 계산서 발행이 불가합니다.\n문의: 재정운영실 내선152번");
				return;
			}
			dma_bill.set("sude_psn", ret.vend_cd);
			dma_bill.set("vend_abbr", ret.vend_abbr);
			dma_bill.set("reprs_psn_nm", ret.reprs_psn_nm);
			if(ret.bsns_psn_regst_no.trim() == ""){
				dma_bill.set("bsns_psn_regst_no", ret.corp_resid_no);
			}else{
				dma_bill.set("bsns_psn_regst_no", ret.bsns_psn_regst_no);
			}			
			dma_bill.set("addr", ret.addr);
			dma_bill.set("bizcatg", ret.bizcatg); // 업태
			dma_bill.set("biztyp", ret.biztyp); // 업종
		});
	};

	/**
	 * 저장 버튼 클릭 이벤트
	 */
	scwin.btn_save_onclick = function(e) {
		dma_bill.set("prcs_clsf", (dma_bill.get("unslip_no") == "")? "N" : "Y");

		// 필수값체크
		if(!com.validateData(dma_bill,[
			{ id : "accnt_no", mandatory : true, allowChar : "A-Z0-9", maxLength : 8, minLength : 8},			
			{ id : "bill_clsf1", mandatory : true}
		], false)) return;

		com.confirm("message.xom.wq.017"/*저장 하시겠습니까?*/, function(ret){
			if(ret){
				com.execute(sbm_saveData, {
					successCallback : function(e){
						com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
							scwin.search();
						});
					}
				});
			}
		});
	};

	/**
	 * 폐기/복구 버튼 클릭 이벤트
	 */
	scwin.btn_del_onclick = function(e) {
		if(!/1002207/.test(com.getLoginInfo("roleCode"))){
			alert("계산서 삭제는 재정운영실 세무담당자만 할 수 있습니다. 세무담당자에게 문의바랍니다.");
			return;
		}

		var rows = grd_bill.getCheckedIndex("chk");
		if(rows.length == 0){
			alert("삭제할 데이터를 선택하세요.");
			return;
		}

		dlt_save.removeAll();
		for(var i=0; i<rows.length; i++){
			if(dlt_bill.getCellData(rows[i], "issu_seqno").substring(0, 3) == "F24"){
				if(dlt_bill.getCellData(rows[i], "unslip_no") != ""){
					alert("[" + (rows[i]+1) + "]번째행의 자료는 결의번호가 생성되어 삭제할 수 없습니다.");
					return;
				}

				if(dlt_bill.getCellData(rows[i], "tax_returns_yn") == "Y"){
					alert("[" + (rows[i]+1) + "]번째행의 자료는 부가세신고 확인처리가 되었으므로 삭제하실 수 없습니다.");
					return;
				}
			}else{
				alert("[" + (rows[i]+1) + "]번째행의 자료는 삭제할 수 없습니다.");
				return;
			}

			dlt_save.setRowJSON(i, {issu_seqno : dlt_bill.getCellData(rows[i], "issu_seqno")});
		}

		dlt_save.reform();

		com.confirm("삭제하시겠습니까?", function(ret){
			if(ret){				
				com.execute(sbm_deleteData, {
					successCallback : function(e){
						com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
							scwin.search();
						});	
					}
				});
			}
		});			
	};

	/**
	 * 미확인조회 버튼 클릭 이벤트
	 */
	scwin.btn_unConfirm_onclick = function(e) {
		var data={
			from_ymd : ica_from.getValue(),
			to_ymd : ica_to.getValue(),
			bill_type : "2", // 1 매출계산서, 2 매입계산서
		};

		biz.fin.pop.open(biz.fin.pop.TYPE.BILL_UNCONF, data);
	};

	/**
	 * 확인 버튼 클릭 이벤트
	 */
	scwin.btn_confirm_onclick = function(e) {
		var row = grd_bill.getFocusedRowIndex();
		if(row < 0){
			alert("확정결의번호를 선택하세요.");
			return;
		}

		if(dlt_bill.getCellData(row, "slip_no") == ""){
			alert("선택한 행은 결의서가 아직 확정되지 않았습니다.");
			return;
		}

		dma_confirm.set("slip_no", dlt_bill.getCellData(row, "slip_no"));
		dma_confirm.set("tax_confirm_yn", "Y");

		com.execute(sbm_saveConfirm, {
			successCallback : function(e){
				scwin.search();
			}
		});
	};

	/**
	 * 미처리이메일 버튼 클릭 이벤트
	 */
	scwin.btn_email_onclick = function(e) {
		com.confirm("미처리 이메일을 발송하시겠습니까?", function(ret){
			if(ret){
				com.execute(sbm_sendMail, {
					successCallback : function(e){
						alert("메일발송을 하였습니다.");
					}
				});
			}
		});
	};	

	/**
	 * 전자증빙 버튼 클릭 event
	 */
	scwin.btn_elecBill_onclick = function(e) {
		var data = {pur_con_user_id : com.getLoginInfo("sn")
				  , pur_con_org_code : com.getLoginInfo("deptCode")
				  , ref_yn : "Y"
		};

		scwin.openElecBill(data);
	};

	/**
	 * 증빙 버튼 클릭 event
	 */
	scwin.btn_evid_onclick = function(e) {
		scwin.openBillPop({});
	};

	/**
	 * 작성자 변경 이벤트
	 */
	scwin.ibx_wrte_psn_empno_onviewchange = function(info) {
		if(info.newValue == ""){
			scwin.clearEmpInfo(1);	
		}
	};

	/**
	 * 확정자 변경 이벤트
	 */
	scwin.ibx_decsn_empno_onviewchange = function(info) {
		if(info.newValue == ""){
			scwin.clearEmpInfo(2);	
		}
	};

	/**
	 * 신고파일 엑셀내리기 버튼 클릭 이벤트
	 */
	scwin.btn_noti_file_onclick = function(e) {
		var option = {
			  fileName : "매입계산서-세무신고용.xlsx"
			, hiddenVisible : true
			, useDataFormat : true
			, providerInfo : {
				  service : "finPurTaxService"
				, method : "selectKtxIssuMstrForReport"
				, vo : "fin.tax.vo.FinPurTaxSrcVo"
				, paramMap : dma_search
			}
		};

		com.downloadGridExcel(grd_report, option);
	};

