	};

	/**
	 * 부가세신고확인 버튼 클릭 이벤트
	 */
	scwin.btn_tax_confirm_onclick = function(e) {
		var rows = grd_bill.getCheckedIndex("chk");

		if(rows.length == 0){
			alert("부가세신고 처리할 목록을 선택하세요.");
			return;
		}

		var issuSeqNos = [];
		for(var i=0; i<rows.length; i++){
			issuSeqNos.push(dlt_bill.getCellData(rows[i], "issu_seqno"));
		}

		com.confirm("부가세신고 확인 처리 하시겠습니까 ?", function(ret){
			if(ret){
				dma_tax_returns.set("issu_seqno", issuSeqNos.join(","));
				dma_tax_returns.set("tax_returns_yn", "Y");

				com.execute(sbm_saveTaxReturns, {
					successCallback : function(e){
						scwin.search();
					}
				});
			}
		});
	};

	/**
	 * 부가세신고취소 버튼 클릭 이벤트
	 */
	scwin.btn_tax_cancel_onclick = function(e) {
		var rows = grd_bill.getCheckedIndex("chk");

		if(rows.length == 0){
			alert("부가세신고 확인취소할 목록을 선택하세요.");
			return;
		}

		var issuSeqNos = [];
		for(var i=0; i<rows.length; i++){
			issuSeqNos.push(dlt_bill.getCellData(rows[i], "issu_seqno"));
		}

		com.confirm("부가세신고 확인취소 처리 하시겠습니까 ?", function(ret){
			if(ret){
				dma_tax_returns.set("issu_seqno", issuSeqNos.join(","));
				dma_tax_returns.set("tax_returns_yn", "N");

				com.execute(sbm_saveTaxReturns, {
					successCallback : function(e){
						scwin.search();
					}
				});
			}
		});
	};

	/**
	 * 등록자변경 검색 버튼 클릭 이벤트
	 */
	scwin.btn_chng_psn_onclick = function(e) {
		scwin.getEmpInfo(3, ibx_empno.getValue());
	};

	/**
	 * 등록자변경 버튼 클릭 이벤트
	 */
	scwin.btn_savePsn_onclick = function(e) {
		if(!/1002207/.test(com.getLoginInfo("roleCode"))){
			alert("등록자 변경은 재정운영실 세무담당자만 할 수 있습니다. 세무담당자에게 문의바랍니다.");
			return;
		}

		var rows = grd_bill.getCheckedIndex("chk");

		if(rows.length == 0){
			alert("선택된 목록이 없습니다.");
			return;
		}

		if(scwin.data.wrte_psn == ""){
			alert("변경할 등록자를 입력하시기 바랍니다.");
			return;
		}

		for(var i=0; i<rows.length; i++){
			if(dlt_bill.getCellData(rows[i], "unslip_no") != ""){
				alert("[" + (rows[i]+1) + "]번째행의 자료는 결의번호가 생성되어 삭제할 수 없습니다.");
				return;
			}

			if(dlt_bill.getCellData(rows[i], "wrte_psn") == scwin.data.wrte_psn){
				alert("[" + (rows[i]+1) + "]번째행의 자료의 등록자와 변경할 등록자가 동일합니다.");
				return;
			}				
		}

		com.confirm("등록자를 변경하시겠습니까?", function(ret){
			if(ret){
				dlt_save.setJSON(grd_bill.getCheckedJSON("chk"));
				for(var i=0; i<dlt_save.getRowCount(); i++){
					dlt_save.setCellData(i, "pur_con_user_id", scwin.data.wrte_psn);
					dlt_save.setCellData(i, "pur_con_org_code", scwin.data.wrte_dept);
				}

				com.execute(sbm_saveWrtePsn, {
					successCallback : function(e){
						scwin.search();
					}
				});
			}
		});
	};
