head.script:

	scwin.onpageload = function() {

		var today = com.getServerDateTime();

		var codeOptions = [ { code : "HAG", compID : "sbx_work_clsf"},
							{ code : "HAA", compID : "sbx_occugrp_cd"}];

		com.setCmnCd(codeOptions, function() {			
			//퇴직자 제외조건 공통코드 외 추가				
			dlt_cmnCd_HAG.setRowJSON(dlt_cmnCd_HAG.getRowCount(), {comCd : "HAG091", comNm: "퇴직자제외", useEx : "Y"}, false);							
		});


		sbx_work_clsf.setSelectedIndex(1);

		grd_list_excel.hide(); //엑셀 바로내리기용 그리드 숨김
		if(!/1003001|1010001/.test(com.getLoginInfo("roleCode"))){ // 인사담당자, 시스템담당자만 엑셀바로내리기 버튼 활용
			btn_excel_detail.hide();
		}
	};

	scwin.onpageunload = function() {

	};

	scwin.btn_search_onclick = function(e) {
		scwin.search();
	};

	scwin.grd_cert_oncellclick = function(row, col, colId) {
		if (colId == "empno") {
			var data = { req_no : dlt_cert.getCellData(row, "empno")};
			scwin.openDetail(data);
		}
	};

	/**
	*	엑셀다운로드
	**/
	scwin.btn_down_excel_onclick = function() {
		var now = com.getServerDateTime("yyyyMMdd");
		var name = '직원명부총괄표' + now + '.xlsx';

		var option = {
			    fileName : name
		};
		com.downloadGridExcel(grd_cert, option);
	};


	scwin.search = function() {		
		 if (!scwin.isValidData) {
			return;	
		}		

		if(sbx_work_clsf.getValue() == "HAG090"){
			//퇴직자 조회시 퇴직일자로
			if(inp_ymd_from.getValue() == "" || inp_ymd_to.getValue() == ""){
				alert("퇴직자 검색인경우 퇴직일자를 입력해주세요");	
				return;
			}		 
			dma_search.set("from_date", inp_ymd_from.getValue());	
			dma_search.set("to_date", inp_ymd_to.getValue());
			dma_search.set("from_entr_date", "");	
			dma_search.set("to_entr_date", "");
		}else{
			//조회시 입사일자로
			if(inp_ymd_from.getValue() != "" || inp_ymd_to.getValue() != ""){
				if(inp_ymd_from.getValue() == "" || inp_ymd_to.getValue() == ""){
					alert("입사일 조회의 경우 시작, 종료일 모두 입력해주세요");	
					return;
				}
			}

			dma_search.set("from_date", "");	
			dma_search.set("to_date", "");
			dma_search.set("from_entr_date", inp_ymd_from.getValue());	
			dma_search.set("to_entr_date", inp_ymd_to.getValue());

		}
		com.execute(sbm_selectList, {
			successCallback : function(e) {
				dlt_cert.setJSON(e.responseJSON.humAnaUserVoList);				
				tbx_result.setValue(dlt_cert.getTotalRow());
			},

			isImplExcel: true,
			excelCallback : function(e) {
				// DB 다운로드
				var option = {
					fileName : "직원명부총괄표.xlsx"
					, providerInfo : {
						  service : "humAnaUserInfoServiceImpl"
						, method : "selectAnaUserInfoList"
						, vo : "hum.ana.vo.HumAnaUserVo"
						, paramMap : dma_search
					}
				};
				com.downloadGridExcel(grd_cert, option);
			}
		});
	};



	scwin.isValidData = function() {
		return com.validateData(dma_search, [
			{ id : "dept_cd", valFunc : function(data) {
					// TODO : roleCode를 이용해 권한이 있는지 체크해야함
					var deptCd = com.getLoginInfo("deptCode");
					deptCd = com.isEmpty(deptCd) ? "" : deptCd;
					var roleCd = com.getLoginInfo("roleCode");

                	if ( !scwin.isAllowRole(roleCd) && inp_dept_cd.getValue().trim() != deptCd) {
                		com.alert("소속부서만 조회할수 있습니다.");
						inp_dept_cd.setValue(deptCd);
						return ;
                	}
				}
			},
		]);
	}


	scwin.sbx_work_clsf_onchange = function(info) {
		if(sbx_work_clsf.getValue() == "HAG090"){
			inp_date_nm.setValue("퇴직일");			
		}else{
			inp_date_nm.setValue("입사일");
		}
	};

	//엑셀 상세 조회
	scwin.btn_excel_detail_onclick = function(e) {
		// DB 다운로드
		alert("개인정보 보안에 유의하시기 바랍니다.");
		var session = com.getLoginInfo();

		dma_search.set("regst_syspayno", session.sn);
		dma_search.set("scrn_id", "H911");
		//dma_search.set("param", dma_search.getXML());
		var option = {
			fileName : "직원명부총괄표(상세).xlsx"
			, hiddenVisible : false
			, useDataFormat : true
			, providerInfo : {
				  service : "humAnaUserInfoServiceImpl"
				, method : "selectAnaUserInfoExcel"
				, vo : "hum.ana.vo.HumAnaUserVo"
				, paramMap : dma_search
			},
			hiddenVisible : false
		};

		com.downloadGridExcel(grd_list_excel, option);
	};


	scwin.sbx_occugrp_cd_onchange = function(info) {
		com.execute(sbm_selectOccutypCd, {
			successCallback : function(e) {
				dlt_occutyp_cd.setJSON(e.responseJSON.humAnaUserVoList);
			}
		});
	};

	scwin.sbx_occuty_cd_onchange = function(info) {
		com.execute(sbm_selectPosiCd, {
			successCallback : function(e) {
				dlt_posi_cd.setJSON(e.responseJSON.humAnaUserVoList);
			}
		});
	};
