head.script:
 	/****************************************************
	 * 화면 설명 : 휴복직신청 신청서
	 * 변경 이력 : 2023-09-15 swt 최초생성
	 * serviceId : H544
	 ****************************************************/
	scwin.data={};
    /*************************************************************************************************************
     *
     *	페이지 이벤트 함수
     *
     *************************************************************************************************************/
	/**
	 * 페이지 열기
	 */
	scwin.onpageload = function() {
		
		//권한 (권한 제대로 세팅안되는 경우가 있어서 com.biz.checkRole안에 로직 작성)
		com.biz.checkRole({ serviceId : "H544", roleCodes : com.getLoginInfo('roleCode')}, function(e){
			//권한 세팅
			scwin.data.isRoleYn = e.isRoleYn;
			
			//첨부파일 초기화  *필수*
			com.file.init();
			
			//숨김처리
			tb_empno.hide();				//신청자 사번
			tb_nm.hide();					//신청자 이름
			tb_syspayno.hide();				//신청자 개인번호
			tb_dept_cd.hide();				//신청자 부서코드
			inp_month_no.hide();			//휴직기간 총 일수
			tb_deptNewYmd.hide();			//신청자 부서신설일
			tb_job_clsf.hide();				//신청자 직무구분코드
			inp_cmbk_dept_new_ymd.hide();	//신청서 내 복직부서 부서신설일
			btn_prt_2.hide();				//반려,처리 버튼 (그룹영역)
			
			//조회날짜 기간역전 체크
			com.checkPeriod(inp_cal_tf_orgn_ymd, inp_cal_tf_cls_ymd);
	
			//엔터키 이벤트
			com.setEnterKeyEvent(grp_cmbk_dept, scwin.btn_cmbk_dept_search_onclick);
			
			//공통코드 (selectbox)
			var codeOptions = [ { code : "HBL", compID :"sbx_absnc" } ];//신청유형"HBL"
			com.setCmnCd(codeOptions, function() {	
				//공통 가져온 후 '휴직, 복직' 경우 직접 핸들링해서 데이터 세팅
				let arr = dlt_cmnCd_HBL.getAllJSON();
				dlt_cmnCd_HBL.removeAll();
				dlt_cmnCd_HBL.reform();
				  
				var i=0;
				arr.forEach(function(xad){
					if (/휴직|복직/.test(xad.mngmtItem1)){
						dlt_cmnCd_HBL.insertRow(i);
						dlt_cmnCd_HBL.setRowJSON(i, xad, true);
						i++;
					}
				});	
				
				//상세조회 구분 (목록 통한 상세조회 or 신규 구분 -> 신청번호 유무로 확인)
				var req_no = com.getParameter("req_no");
	
				//if : 신규작성(신청번호가 없는경우)
				if (com.isEmpty(req_no)) {
				
					//첨부파일 셋팅
					com.file.create(upload_A.getID(), { fileKey : "H544", mode : 'edit' });
				
					//현재 결재 상태에 따라 버튼의 활성화 유무 컨트롤
					com.biz.disabledButtons();
	
					//신청자 정보 세팅
					var data = { empno : com.getLoginInfo("empno"), work_clsf : "HAG010" }
					com.pop.open( com.pop.TYPE.HUM, data, function(ret){
						
						tb_empno.setValue(ret.empno);					//사원번호
						tb_nm.setValue(ret.nm);							//이름
						tb_syspayno.setValue(ret.syspayno);				//개인번호
						//사번(성명) 형식
						tb_nm_empno.setValue(ret.empno + "(" + ret.nm +")");
						
						tb_dept_nm.setValue(ret.dept_nm);				//부서명
						tb_dept_cd.setValue(ret.dept_cd);				//부서코드
						tb_deptNewYmd.setValue(ret.dept_new_ymd);		//부서신설일
						
						tb_posi_nm.setValue(ret.posi_nm);				//직급
						
						tb_job_clsf_nm.setValue(ret.job_clsf_nm);		//직무구분명
						tb_job_clsf.setValue(ret.job_clsf);				//직무구분
						
						cal_req_ymd.setValue(com.getServerDateTime());	//신청일
						
					});
					
					
				//else if : 신청번호 있는경우 -> 상세조회	
				} else {
					
					//상세 조회
					scwin.search(req_no);
					
				}
	
			});
		
		});
		
	};
	
	/**
	 * 페이지 닫기
	 */     
	scwin.onpageunload = function() {
		
	};
	
    /**
     * 상세 조회
     */	
	scwin.search = function(req_no) {
		
		//조회 조건
		dma_search.set("req_no", req_no);
		
		//신청서 기본 정보 상세 조회
		com.execute(sbm_outHumApyLndInfo, {
			successCallback : function(e) {
				
				//상세정보 데이터 세팅
				dma_info.setJSON(e.responseJSON.elData);
				
				//사번(성명) 형식
				tb_nm_empno.setValue(tb_empno.getValue() + "(" + tb_nm.getValue() +")");
				
				//버튼제어					
				com.biz.disabledButtons(dma_info.get("apr_state"), req_no);
				
				//첨부파일 조회 (진행상태에 따라 비활성화, 활성화 처리)
				var vMode = "edit";		//첨부파일 수정모드
				if (dma_info.get("apr_state") >= "XAD020") {
					//비활성화
					vMode = "view";							//첨부파일 보기모드
				}	
				var attachFileNo = dma_info.get("attach_file_no");
				com.file.create(upload_A.getID(), {fileKey: com.isNotEmpty(attachFileNo) ? attachFileNo : 'H544', mode: vMode});
				
				
				//관리자인 경우 && 결재상태 "담당자 확인중"인 경우 버튼 처리
				if(scwin.data.isRoleYn && dma_info.get("apr_state") == "XAD070") {
					btn_prt_1.hide();	//삭제, 저장 버튼 숨김 (그룹영역)
					btn_prt_2.show();	//반려, 처리 버튼 보임 (그룹영역)
				} else {
					btn_prt_1.show();	//삭제, 저장 버튼 보임 (그룹영역)
					btn_prt_2.hide();	//반려, 처리 버튼 숨김 (그룹영역)				
				} 
				
				
				//"담당자 확인중"인 경우 버튼 비활성화	
				if(dma_info.get("apr_state") == "XAD070" || dma_info.get("apr_state") == "XAD100") {
					//cal_req_ymd.setReadOnly(true);			//신청일
					sbx_absnc.setDisabled(true);			//신청유형
					inp_cal_tf_orgn_ymd.setReadOnly(true);	//휴직기간 시작일
					inp_cal_tf_cls_ymd.setReadOnly(true);	//휴직기간 종료일
					inp_cal_cmbk_dept.setReadOnly(true);	//복직일
					inp_cmbk_dept_cd.setReadOnly(true);		//복직부서 부서코드
					btn_cmbk_dept_search.setReadOnly(true);	//복직부서 부서조회버튼
					inp_rsn.setReadOnly(true);				//사유
				} 
	
			}
		});	
		
	};

    /**
     * 목록 버튼 클릭 이벤트
     */	
	scwin.btn_list_onclick = function(e) {
		com.moveList("H534", "");
	};

    /**
     * 휴직기간 변경 시 이벤트
     */	
	scwin.inp_cal_tf_orgn_ymd_onchange = function() {
		
		//시작일 ~ 종료일 일수 계산 후 세팅
		var startYmd = inp_cal_tf_orgn_ymd.getValue();
		var clsYmd = inp_cal_tf_cls_ymd.getValue();

		if (startYmd == "" || clsYmd == "") {
			return;
		}

		var dDate = WebSquare.date.dateDiff(startYmd, clsYmd);
		
		//~년~개월~일 형식으로 보여주기 위함
		var viewDate = scwin.getDateHan(dDate+1);
		
		//복직이 아닌 경우에 작동 ("복직"으로 저장 후 화면 새로고침되면 NaN으로 표시돼서 if문 처리)
		if (dma_info.get("req_typ") != "HBL240") {
			
			if (viewDate.substr(0, 1) != "-") {
				inp_month_no.setValue(dDate+1);		//hide처리 : 저장시 사용하기 위해 값 담아둠
				inp_view_day.setValue(viewDate);	//show처리 : ~년~개월~일 형식으로 보여주기위함
				
			} 
		
		}

	};
	
    /**
     * 휴직기간 계산시 사용 (~년~개월~일 형식으로 세팅)
     */	
	scwin.getDateHan = function(reDate) {
		
		var myDate = new Date("0001", "1", "0");
	
		myDate.setDate(reDate);
	
		var giyear = (myDate.getYear() - 1) + "";
		var gimonth = myDate.getMonth() + "";
		var giday = myDate.getDate() + "";
	
		var ymd = "";
		if (giyear != "0") {
			ymd = ymd + giyear + "년";
		}
	
		if (gimonth != "0") {
			ymd = ymd + gimonth + "개월";
		}
	
		if (giday != "0") {
			ymd = ymd + giday + "일";
		}
	
		if (gimonth == "11" && giday == "31") {
			giyear = Number(giyear) + 1;
			ymd = giyear + "년";
		}
	
		return ymd;
	};
	
    /**
     * 신청유형 변경 시 이벤트
     */	
	scwin.sbx_absnc_onchange = function(info) {
		scwin.chkLandr();
	};
	
    /**
     * 신청유형 변경 시 선택 값에 따른 활성화, 비활성화 처리
     */	
	scwin.chkLandr = function(info) {
		
		//신청유형 값
		var reqTyp = sbx_absnc.getValue();
		
		//복직		
		if (reqTyp == "HBL240") {
			
			inp_cal_tf_orgn_ymd.setDisabled(true);	//휴직기간 시작일
			inp_cal_tf_cls_ymd.setDisabled(true);	//휴직기간 종료일
			inp_cmbk_dept_cd.setDisabled(false);	//복직부서코드
			btn_cmbk_dept_search.setDisabled(false);//복직부서 조회버튼
			inp_cal_cmbk_dept.setDisabled(false);	//복직일

			inp_cal_tf_orgn_ymd.setValue("");
			inp_cal_tf_cls_ymd.setValue("");
			inp_month_no.setValue("");
			
			//휴직기간 일수 빈값 세팅
			inp_view_day.setValue("");
			inp_month_no.setValue("");	
			
			tb_1.changeClass("blue", "");		//휴직기간 텍스트
			span_1.changeClass("bul_req", "");
			
			tb_2.changeClass("", "blue");		//복직일 텍스트
			span_2.changeClass("", "bul_req");
			
			tb_3.changeClass("", "blue");		//복직부서 텍스트
			span_3.changeClass("", "bul_req");
			
			
		//휴직
		} else {

			inp_cal_tf_orgn_ymd.setDisabled(false);	//휴직기간 시작일
			inp_cal_tf_cls_ymd.setDisabled(false);	//휴직기간 종료일
			inp_cmbk_dept_cd.setDisabled(true);		//복직부서코드
			btn_cmbk_dept_search.setDisabled(true);	//복직부서 조회버튼
			inp_cal_cmbk_dept.setDisabled(true);	//복직일 
			
			inp_cal_cmbk_dept.setValue("");		//복징일
			inp_cmbk_dept_cd.setValue("");		//복직부서
			inp_cmbk_dept_nm.setValue("");		//복직부서명

			tb_1.changeClass("", "blue");		//휴직기간 텍스트
			span_1.changeClass("", "bul_req");
			
			tb_2.changeClass("blue", "");		//복직일 텍스트
			span_2.changeClass("bul_req", "");
			
			tb_3.changeClass("blue", "");		//복직부서 텍스트
			span_3.changeClass("bul_req", "");

			// 20230125 육아휴직의 경우 사유 자동설정, 수정가능
			if (reqTyp == "HBL180" && dma_info.get("req_no") == "") {
				inp_rsn.setValue( "「취업규칙」제28조의 2에 근거하여 만8세이하 또는 초등학교 2학년이하 자녀의 양육을 위한 육아휴직 신청" );
				
			}else{
				if (dma_info.get("req_no") == "" && inp_rsn.getValue() == "「취업규칙」제28조의 2에 근거하여 만8세이하 또는 초등학교 2학년이하 자녀의 양육을 위한 육아휴직 신청"){
					inp_rsn.setValue("");
				}
			}
			
			//육아휴직인 경우 첨부파일란 필수입력 표시
			if (reqTyp == "HBL180") {
				tb_4.changeClass("", "blue");		//첨부파일 텍스트
				span_4.changeClass("", "bul_req");
			}else{
				tb_4.changeClass("blue", "");		//첨부파일 텍스트
				span_4.changeClass("bul_req", "");
			}
			
		}
		
	};
	
	/**
	 * 복직부서 검색 버튼 클릭 이벤트
	 */
	scwin.btn_cmbk_dept_search_onclick = function(e) {
		var data={
			dept_cd : inp_cmbk_dept_cd.getValue(),
			ref_yn : (inp_cmbk_dept_cd.getValue()=="") ? "Y" : "N"
		};
		
		com.pop.open(com.pop.TYPE.DEPT, data, function(ret) {
			inp_cmbk_dept_cd.setValue(ret.dept_cd);					//부서코드
			inp_cmbk_dept_nm.setValue(ret.dept_nm);					//부서명
			inp_cmbk_dept_new_ymd.setValue(ret.divsn_dept_new_ymd);	//부서 신설일
			
		});		
	};

	/**
	 * 삭제 버튼 클릭 이벤트
	 */
	scwin.btn_del_onclick = function(e) {
		
		com.confirm("message.xom.wq.022"/*삭제 하시겠습니까?*/, function( ret) {
			if (ret) {
				com.file.deleteAll( function(){
					
					//삭제조건 세팅
					var req_no = dma_info.get("req_no");
					dma_search.set("req_no", req_no);
					
					com.execute(sbm_deleteData, {
						successCallback : function(e) { // 성공 콜백
							com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
								//삭제 후 목록창 이동
								com.moveList("H534", {mod :"delete"});
							});
						}
					});
					
				});	
			}
		});

	};

	/**
	 * 결재신청 버튼 클릭 이벤트
	 */
	scwin.btn_apr_onclick = function(e) {
		
		//반려상태인 경우
		if(dma_info.get("apr_state") == "XAD015"){
			com.alert("저장 후 신청바랍니다.");	
			return;
		};
		
		//필수값체크 실행
		if (!scwin.preSave()) {
			return false;
		}
		
		//결재신청 로직
		com.confirm("결재신청 하시겠습니까?", function(ret){
			if (ret) {
				scwin.save("apr");
			}
		});
		
	};

	/**
	 * 저장 버튼 클릭 이벤트
	 */
	scwin.btn_save_onclick = function(e) {
		
		//필수값체크 실행
		if (!scwin.preSave()) {
			return false;
		}
		
		//저장 로직
		com.confirm("message.xom.wq.017"/*저장 하시겠습니까?*/, function(ret) {
			if (ret) {
				scwin.save("save");
			}
		});
		
	};
	
    /**
     * 저장, 결재신청 전 필수값체크
     */
	scwin.preSave = function() {

		var reqYmd = cal_req_ymd.getValue();						//신청일
		var reqTyp = sbx_absnc.getValue();							//신청유형
		var ttoffOrignYmd = inp_cal_tf_orgn_ymd.getValue();			//휴직기간 신청일
		var TtoffClsYmd = inp_cal_tf_cls_ymd.getValue();			//휴직기간 종료일
		var combkYmd = inp_cal_cmbk_dept.getValue();				//복직일
		var combkDeptCd = inp_cmbk_dept_cd.getValue();				//복직부서코드
		var combkDeptNm = inp_cmbk_dept_nm.getValue();				//복직부서명
	
		if (reqYmd == "") {
			cal_req_ymd.focus();
	
			com.alert("신청일 를(을) 입력하세요.");
	
			return false;
		}
	
		if (reqTyp == "") {
			sbx_absnc.focus();
	
			com.alert("신청유형 를(을) 입력하세요.");
	
			return false;
		}
	
		if (reqTyp != "HBL240") {
			if (ttoffOrignYmd == "") {
				inp_cal_tf_orgn_ymd.focus();
	
				com.alert("휴직기간 를(을) 입력하세요.");
	
				return false;
			}
	
			if (TtoffClsYmd == "") {
				inp_cal_tf_cls_ymd.focus();
	
				com.alert("휴직기간 종료일 를(을) 입력하세요.");
	
				return false;
			}
		}
	
		if (reqTyp == "HBL240") {
			if (combkYmd == "") {
				inp_cal_cmbk_dept.focus();
	
				com.alert("복직일 를(을) 입력하세요.");
	
				return false;
			}
	
			if (combkDeptCd == "") {
				inp_cmbk_dept_cd.focus();
	
				com.alert("복직부서 를(을) 입력하세요.");
	
				return false;
			}
			
			if (combkDeptNm == "") {
				inp_cmbk_dept_cd.focus();
	
				com.alert("복직부서 를(을) 입력하세요.");
	
				return false;
			}
		}
		
		//육아휴직인 경우 첨부파일을 필수 입력
		if (reqTyp == "HBL180" && com.file.action.getTotalFileCount(upload_A.getID()) < 1) {
			com.alert("육아휴직인 경우 첨부파일을 첨부하시기 바랍니다.");

			return false;
		}
	
		return true;

	};
		
    /**
     * 저장, 수정, 결재신청 로직
     */	
	scwin.save = function(saveType) {

		com.file.transport( function( attachObj){
			
			//저장 데이터 세팅
			dma_info.set("attach_file_no"	, attachObj.upload_A);		//첨부파일번호
			dma_info.set("req_typ_nm"		, sbx_absnc.getText()); 	//신청유형 텍스트 (저장시 formInit에 사용)
			
			
			//결재신청인 경우 추가 데이터 세팅
			if (saveType == "apr") {
				
				//스냅샷 세팅 데이터
				var apyDate = "";
				
				if (inp_cal_cmbk_dept.getValue().trim() == "") {
					var startYmd = inp_cal_tf_orgn_ymd.getValue().substr(0, 4);		//휴직 시작일
					var clsYmd = inp_cal_tf_cls_ymd.getValue().substr(0, 4);		//휴직 종료일

					startYmd += "-" + inp_cal_tf_orgn_ymd.getValue().substr(4, 2);
					startYmd += "-" + inp_cal_tf_orgn_ymd.getValue().substr(6, 2);

					clsYmd += "-"+ inp_cal_tf_cls_ymd.getValue().substr(4, 2);
					clsYmd += "-"+ inp_cal_tf_cls_ymd.getValue().substr(6, 2);

					apyDate = startYmd + " ~ " + clsYmd + "";
					
				} else {
					var apyDate = inp_cal_cmbk_dept.getValue().substr(0, 4);

					apyDate += "-"+ inp_cal_cmbk_dept.getValue().substr(4, 2);
					apyDate += "-"+ inp_cal_cmbk_dept.getValue().substr(6, 2);
				}

				
				//approvalRequest 전달 데이터
				var Req_typ = sbx_absnc.getText();			//신청유형
				var Resn 	= inp_rsn.getValue().trim();	//사유
				var stdYnCd = "";
				var posiNm 	= "";

				posiNm = tb_posi_nm.getValue().trim();
				
				if (posiNm == "UST학생(근로)" || posiNm == "근로연수생" || posiNm == "학연협동과정생(근로)") { // 학생연구원[UST학생(근로) / 학연협동과정생(근로) / 근로연수생]일 경우
					stdYnCd = "Y";
				} else {
					stdYnCd = "N";
				}
				
				//결재신청정보 세팅 (스냅샷)			
				dma_info.set("approval"				, true); 		//결재여부
				dma_info.set("save_type"			, "apr");		//저장타입
				
				dma_info.set("sh_dept_nm"			, tb_dept_nm.getValue());		//신청자 부서명
				dma_info.set("sh_posi_nm"			, tb_posi_nm.getValue());		//신청자 직급
				dma_info.set("sh_nm"				, tb_nm.getValue());			//신청자 이름
				dma_info.set("sh_job_clsf_nm"		, tb_job_clsf_nm.getValue());	//신청자 직무구분
				dma_info.set("sh_req_typ_nm"		, sbx_absnc.getText());			//신청유형명
				dma_info.set("sh_req_typ"			, sbx_absnc.getValue());		//신청유형 코드
				
				dma_info.set("sh_combk_ymd"			, apyDate);						//복지일
				dma_info.set("sh_combk_dept_nm"		, inp_cmbk_dept_nm.getValue());	//복직부서명
				dma_info.set("sh_ttoff_orign_ymd"	, apyDate);						//휴직기간
				dma_info.set("sh_month_no"			, inp_month_no.getValue());		//휴직 일수
				dma_info.set("sh_resn"				, inp_rsn.getValue());			//사유
				
			}
			
			com.execute(sbm_saveData, {
				successCallback : function(e) { // 성공 콜백					
					
					if(dma_info.get("approval")){
						// 결재화면 이동
						com.movePageMenu("A001", { "reqNo" :  dma_info.get("req_no")});
					}else{
						com.alert("message.xom.wq.023" /*정상적으로 처리되었습니다.*/, function() {	
							//리턴받은 신청번호로 상세조회
							scwin.search(e.responseJSON.elData.req_no);
						});	
					}

				}
			});
			
		});
		
	};

    /**
     * 처리 버튼 클릭 이벤트
     */		
	scwin.btn_confirm_onclick = function(e) {
		
		if(!scwin.data.isRoleYn){
			com.alert("관리자외에는 처리를 하실 수 없습니다.");
			return;
		}

		//승인처리 후 insert시 필요한 정보 세팅 (발령정보관리 입력)		
		var prcStartYmd = "";

		if (sbx_absnc.getValue() == "HBL240") { // 복직일경우
			prcStartYmd = inp_cal_cmbk_dept.getValue(); // 복직일
		} else {
			prcStartYmd = inp_cal_tf_orgn_ymd.getValue(); // 휴직시작일
		}
		
		dma_accept.set("appont_typ"			, sbx_absnc.getValue());			//발령유형
		dma_accept.set("req_no"				, tb_req_no.getValue());			//신청번호
		dma_accept.set("appont_prcs_ymd"	, prcStartYmd);						//발령처리일
		dma_accept.set("appont_start_ymd"	, prcStartYmd);						//발령시작일
		dma_accept.set("appont_cls_ymd"		, inp_cal_tf_cls_ymd.getValue());	//발령종료일
		dma_accept.set("syspayno"			, tb_syspayno.getValue());			//시스템개인번호
		dma_accept.set("appont_resn"		, inp_rsn.getValue());				//사유
		dma_accept.set("appont_prcs"		, "HCX010");						//사유
		dma_accept.set("ref_tab"			, "humapylandr");					//참조테이블
		
		var data = { empno : tb_empno.getValue(), work_clsf : "HAG010" }
		com.pop.open( com.pop.TYPE.HUM, data, function(ret){
			//인사정보 세팅(의미모르는 컬럼들 주석 생략함)
			//dma_accept.set("appont_no"				, "");								//채번 -> 자바단 처리
			dma_accept.set("ref_data"					, tb_req_no.getValue());			//신청번호
			dma_accept.set("empno"						, ret.empno);						//사원번호
			dma_accept.set("nm"							, ret.nm);							//이름
			
			dma_accept.set("appont_befr_dept_cd"		, ret.dept_cd);						//부서코드
			dma_accept.set("appont_befr_dept_new_ymd"	, ret.dept_new_ymd);				//부서신설일
			dma_accept.set("appont_befr_occugrp_cd"		, ret.occugrp_cd);					//
			dma_accept.set("appont_befr_occutyp_cd"		, ret.occutyp_cd);					//
			dma_accept.set("appont_befr_posi_cd"		, ret.posi_cd);						//직급코드
			dma_accept.set("appont_befr_cd"				, ret.duty_cd);						//
			dma_accept.set("appont_befr_job_cd"			, ret.job_cd);						//직무코드
			dma_accept.set("appont_befr_job_clsf"		, ret.job_clsf);					//
			dma_accept.set("appont_befr_work_land"		, ret.work_land);					//
			
			dma_accept.set("dept_cd"					, ret.dept_cd);						//부서코드
			dma_accept.set("dept_new_ymd"				, ret.dept_new_ymd);				//부서신설명
			dma_accept.set("occugrp_cd"					, ret.occugrp_cd);					//
			dma_accept.set("occutyp_cd"					, ret.occutyp_cd);					//
			
			dma_accept.set("posi_cd"					, ret.posi_cd);						//
			dma_accept.set("duty_cd"					, ret.duty_cd);						//
			dma_accept.set("job_cd"						, ret.job_cd);						//직무코드
			dma_accept.set("job_clsf"					, ret.job_clsf);					//
			dma_accept.set("work_land"					, ret.work_land);					//
		});
		
		com.confirm("승인처리 하시겠습니까?", function(ret) { 		
			if(ret) {
				com.execute(sbm_aprAgr, {
					successCallback : function(e) {
						com.alert("승인처리 되었습니다");
						com.openTabMenu("A004", {});
					}
				});
			}
		});
		
	};
	
    /**
     * 반려 버튼 클릭 이벤트
     */	
	scwin.btn_reject_onclick = function(e) {
		
		if(!scwin.data.isRoleYn){
			com.alert("관리자외에는 반려를 하실 수 없습니다.");
			return;
		}
		
		var options = {
		    id: "APR02P",
		    popupName: "반려의견",
		    width: 500,
		    height: 300
		};
		
		com.openPopup("/ui/apr/APR_C001_03P.xml", options, "", function(retObj) {
		    if (retObj.comments != null) {
		        dma_info.set("comments", retObj.comments);
		        com.execute(sbm_aprReject, {
		            successCallback: function(e) {
		                com.alert("반려처리 되었습니다");
		                com.openTabMenu("A004", {});
		            }
		        });
		    }
		});
		
	};
