	/**
	 * 페이지 열기
	 */                  
	scwin.onpageload = function() {
		
		//권한
		com.biz.checkRole({ serviceId : "H548", roleCodes : com.getLoginInfo('roleCode')}, function(e){
			scwin.data.isRoleYn = e.isRoleYn;
		});

		//첨부파일 초기화  *필수*
		com.file.init();
		
		// 엔터키 이벤트
		com.setEnterKeyEvent(grp_rspns, scwin.btn_hum_search_onclick);
		
		//숨김처리
		tb_empno.hide();				//신청자 사번
		tb_nm.hide();					//신청자 이름
		tb_syspayno.hide();				//신청자 개인번호
		tb_dept_cd.hide();				//신청자 부서코드
		tb_deptNewYmd.hide();			//신청자 부서코드
		inp_syspayno.hide();			//지도교수 개인번호
		hidden_tb_enfrcAmt.hide();		//지출예정액
		inp_prof_entr_ymd.hide();		//지도교수 입사일
		inp_dept_cd.hide();				//부서코드(save시 사용)
		inp_dept_new_ymd.hide();		//부서 개설일(save시 사용)
		inp_depthed_syspayno.hide();	//부서장 시스템개인번호(save시 사용)
		inp_divsn_rspns_syspayno.hide();//본부장 시스템개인번호(save시 사용)
		
		//공통코드 (selectbox)
		var codeOptions = [{ code : "HAN", compID : "grd_list_1:major" }];	//전공
		com.setCmnCd(codeOptions, function() {	

			//상세조회 구분 (목록 통한 상세조회 or 신규 구분 -> 신청번호 유무로 확인)
			var req_no = com.getParameter("req_no");
			
			//if : 신규작성(신청번호가 없는경우)
			if (com.isEmpty(req_no)) {
			
				//첨부파일 셋팅
				com.file.create(upload_A.getID(), { fileKey : "H548", mode : 'edit' });
			
				//현재 결재 상태에 따라 버튼의 활성화 유무 컨트롤
				com.biz.disabledButtons();
				
				//신청서 등록자 기본정보 세팅
				tb_empno.setValue(com.getLoginInfo("empno"));		//사원번호
				tb_nm.setValue(com.getLoginInfo("cn"));				//이름
				tb_syspayno.setValue(com.getLoginInfo("sn"));		//개인번호
				tb_dept_nm.setValue(com.getLoginInfo("deptName"));	//부서명
				tb_posi_nm.setValue(com.getLoginInfo("posiName"));	//직급
				cal_req_ymd.setValue(com.getServerDateTime());		//신청일
				tb_dept_cd.setValue(com.getLoginInfo("deptCode"));	//부서코드
				tb_deptNewYmd.setValue(com.getLoginInfo("deptNewYmd"));	//부서 신설일
				
				//사번(성명) 형식
				tb_nm_empno.setValue(com.getLoginInfo("empno") + "(" + com.getLoginInfo("cn") +")");
				
				
			//else if : 신청번호 있는경우 -> 상세조회	
			} else {
				
				//상세 조회
				scwin.search(req_no);
				
			}
		});
		
	};
	
	/**
