	scwin.onpageunload = function() {
		
	};
	
    /**
     * 상세 조회
     */	
	scwin.search = function(req_no) {
		
		//if : 신규작성(신청번호가 없는경우)
		if (com.isEmpty(req_no)) {
			
			//팝업에서 지도교수 선택시 작동 -> [연수책임자 과제현황], [연수책임자 지도 학생연구원 현황], [연수책임자 학생 인건비 현황] 조회하기 위함
			scwin.search_detail();

			
		//else if : 신청번호 있는경우 -> 상세조회
		} else {
			
			//조회 조건
			dma_search.set("req_no", req_no);
			
			//신청서 기본 정보 상세 조회
			com.execute(sbm_outHumApyStuInfo, {
				successCallback : function(e) {
					
					//상세정보 데이터 세팅
					dma_info.setJSON(e.responseJSON.elData);
					
					//사번(성명) 형식
					tb_nm_empno.setValue(tb_empno.getValue() + "(" + tb_nm.getValue() +")");
					
					var empno = dma_info.get("map_prof_emp_no");
					var data = { empno : empno, work_clsf : "HAG010" }
					com.pop.open( com.pop.TYPE.HUM, data, function(ret){
						inp_dept_nm.setValue(ret.dept_nm);					//지도교수 부서명
						inp_posi_nm.setValue(ret.posi_nm);					//지도교수 직급
						inp_depthed_nm.setValue(ret.depthed_nm);			//지도교수 부서장
						inp_divsn_rspns_nm.setValue(ret.divsn_rspns_nm);	//지도교수 본부장
					});
					
					scwin.search_detail(req_no);				
					
				}
			});				
			
		}
		
	};
	
    /**
     * 상세조회2 ("신규"인 경우와 "상세조회"의 같은 로직 타는 부분 -> [연수책임자 과제현황], [연수책임자 지도 학생연구원 현황] 2개 grid)
     */	
	scwin.search_detail = function(req_no) {
		
		//신청번호 있는 경우 -> 해당 신청번호에 해당하는 값으로 조회조건 세팅 
		if (!com.isEmpty(req_no)) {
		
			//조회용 데이터 세팅
			dma_search.set("syspayno", dma_info.get("map_prof_syspayno"));	//지도교수sn
			dma_search.set("cls_ymd", com.getServerDateTime());				//오늘날짜
		}
		
		//[학생연구원 정보] 상세 조회
		com.execute(sbm_outHumApyStuInfoList, {
			successCallback : function(e) {
				
				dlt_stu_info.setJSON(e.responseJSON.humApyStuInfoVoList);
		
				//[연수책임자 과제현황] 목록 조회
				com.execute(sbm_outHumApyPrj, {
					successCallback : function(e) {
						
						dlt_prj.setJSON(e.responseJSON.humApyStuInfoVoList);
						
						//연구기간 세팅
						for (var i = 0; i < dlt_prj.getTotalRow(); i++) {
						
					    	var startYmd = dlt_prj.getCellData(i, "start_ymd");
					    	var clsYmd = dlt_prj.getCellData(i, "cls_ymd");
					    			
							dlt_prj.setCellData( i , "prj_ymd" , startYmd + "~" + clsYmd);
						}
						
						//[연수책임자 지도 학생 연구원 현황] 목록 조회
						com.execute(sbm_outHumApyStu, {
							successCallback : function(e) {
								
								dlt_stu.setJSON(e.responseJSON.humApyStuInfoVoList);
								
								//학생인건비 현황을 가져오기 위한 세팅
								dma_search.set("accnt_rspns", inp_syspayno.getValue());
								dma_search.set("absrp_yr", com.getServerDateTime().substr(0, 4));		//현재년도
								
								//[연수책임자 학생인건비 현황] 조회
								com.execute(sbm_outHumApyStuExpns, {
									successCallback : function(e) {
										
										dlt_expns.setJSON(e.responseJSON.humApyStuInfoVoList);
										
										//<<잔액(tot_ramt), 지출예정액(input_schdl_amt) 세팅>>
										//잔액 계산
										var tot_ramt 		= dlt_expns.getCellData(0, "tot_ramt");
										var input_schdl_amt = dlt_expns.getCellData(0, "input_schdl_amt");
										//잔액 세팅
										tb_ramtAmt.setValue(Number(tot_ramt) + Number(input_schdl_amt));
										
										//지출예정액 계산
										var countGrid3 = dlt_stu.getRowCount();
										var tempAmt 		= 0;
										var totAmt 			= 0;
										
										for (var i = 0; i < countGrid3; i++) {
										
											var month 		= scwin.monthCalc(com.getServerDateTime(), dlt_stu.getCellData(i, "contrct_cls_ymd"),"M" );
											var tempUnslip 	=  dlt_stu.getCellData(i, "unslip_no");
											
											var tempAmt = 0;
											if ( month >= 0 ) {
												if( tempUnslip == "" ){
													tempAmt = eval(dlt_stu.getCellData(i, "month_fxdamt_amt")) * (eval(month) + 1);
												}else{
													tempAmt = eval(dlt_stu.getCellData(i, "month_fxdamt_amt")) * eval(month);
												}
											}
											totAmt += eval(tempAmt);
										}
										
									    if (totAmt == "") {
									    	totAmt = 0;
									    }
									    
									    //지출예정액 세팅
										tb_enfrcAmt.setValue(totAmt);			//지출예정액 = 지출예정금액 + (인건비 * 당해 활용기간(월))
										hidden_tb_enfrcAmt.setValue(totAmt);	//동적으로 변하는 상황을 위해 
										
										//신청번호 있는 경우 추가 로직
										if (!com.isEmpty(req_no)) {
											
											//지출예정액 세팅 로직
										    var stuAmtYr = 0;
											var rowCnt = dlt_stu_info.getRowCount();	//[학생연구원 정보] 행의 갯수
											
											for (var i = 0; i < rowCnt; i++) {
												
												if(dlt_stu_info.getCellData(i, "stu_amt_yr")/dlt_stu_info.getCellData(i, "stu_amt_mnt") > 12){
													stuAmtYr += com.parseInt(dlt_stu_info.getCellData(i, "stu_amt_mnt") * 12, 0);
												} else{
													stuAmtYr += com.parseInt(dlt_stu_info.getCellData(i, "stu_amt_yr"), 0);
												}		
												
											}
										
											var hdnEnfrcAmt = hidden_tb_enfrcAmt.getValue();
											if(hdnEnfrcAmt == "") { 
												hdnEnfrcAmt = 0; 
											}
											//지출예정액 세팅
											tb_enfrcAmt.setValue(Number(hdnEnfrcAmt) + stuAmtYr);
											
											//버튼 제어
											if (dma_info.get("apr_state") >= "XAD020") btn_state.setDisabled(false);	//결재진행상황 버튼
											com.biz.disabledButtons(dma_info.get("apr_state"), req_no);					//결재신청, 삭제, 저장 버튼
											
											//진행상태에 따라 비활성화, 활성화 처리
											var vMode = "edit";		//첨부파일 수정모드
											var aprChk = false;		//입력란 비활성호
											if (dma_info.get("apr_state") >= "XAD020") {
												//비활성화
												vMode = "view";							//첨부파일 보기모드
												aprChk = true;
											}		
											//cal_req_ymd.setReadOnly(aprChk);		//신청일
											grd_list_1.setReadOnly(aprChk);			//학생연구원 정보 그리드
											trggier_add.setDisabled(aprChk);		//학생연구원 정보 그리드 내 "+" 버튼
											trggier_del.setDisabled(aprChk);		//학생연구원 정보 그리드 내 "-" 버튼
											grd_list_2.setReadOnly(aprChk);			//면담결과 그리드
											tta_relatInvnt.setReadOnly(aprChk);		//확보계획
											grp_rspns.setDisabled(aprChk);			//지도교수 팝업 조회 버튼 영역
											
											//첨부파일 조회
											var attachFileNo = dma_info.get("attach_file_no");
											com.file.create(upload_A.getID(), {fileKey: com.isNotEmpty(attachFileNo) ? attachFileNo : 'H548', mode: vMode});

										}
			
									}
								});		
													
							}
						});
						
					}
				});
		
			}
		});		
		
	};	
	
