	/**	
	 * 변경자 사용자검색 클릭 이벤트
	 */
	scwin.btn_hum_search_onclick = function(e) {
		
		var data = { empno : inp_empno.getValue(), work_clsf : "HAG010" }
		com.pop.open( com.pop.TYPE.HUM, data, function(ret){
			
			inp_empno.setValue(ret.empno);					//지도교수 사원번호
			inp_nm.setValue(ret.nm);						//지도교수 성명
			inp_dept_nm.setValue(ret.dept_nm);				//지도교수 부서명
			inp_posi_nm.setValue(ret.posi_nm);				//지도교수 직급
			inp_syspayno.setValue(ret.syspayno);			//지도교수 시스템 개인번호
			inp_depthed_nm.setValue(ret.depthed_nm);		//지도교수 부서장명
			inp_divsn_rspns_nm.setValue(ret.divsn_rspns_nm);//지도교수 본부장명
			
			//숨김처리한tb에 데이터 세팅 (저장, 수정, 결재신청시 사용하기 위함)
			inp_prof_entr_ymd.setValue(ret.entr_ymd);						//지도교수 입사일
			inp_dept_cd.setValue(ret.dept_cd);								//지도교수 부서코드
			inp_dept_new_ymd.setValue(ret.dept_new_ymd);					//지도교수 부서 개설일
			inp_depthed_syspayno.setValue(ret.depthed);						//지도교수 부서장 시스템개인번호
			inp_divsn_rspns_syspayno.setValue(ret.divsn_rspns_syspayno);	//지도교수 본부장 시스템개인번호
			
			//조회용 데이터 세팅
			dma_search.set("syspayno", ret.syspayno);			//지도교수sn
			dma_search.set("cls_ymd", com.getServerDateTime());	//오늘날짜
			
			//변경전 후 지도교수(활용책임자) 조회
			scwin.search("");
			
		});

	};
	
	/**	
	 * 지출예정액 계산시 날짜 계산 함수
	 */
	scwin.monthCalc = function(pStartDate, pEndDate, pType) {
	
		var strSDT = new Date(pStartDate.substring(0,4),pStartDate.substring(4,6)-1,pStartDate.substring(6,8));
		var strEDT = new Date(pEndDate.substring(0,4),pEndDate.substring(4,6)-1,pEndDate.substring(6,8));
		var strGapDT = 0;

		if (pType == 'D') {  // 일수 차이
			strGapDT = (strEDT.getTime()-strSDT.getTime())/(1000*60*60*24);
		} else {            // 개월수 차이
			// 년도가 같으면 단순히 월을 마이너스 한다.
			// => 20090301-20090201 의 경우 아래 else의 로직으로는 정상적인 1이 리턴되지 않는다.
			if (pEndDate.substring(0,4) == pStartDate.substring(0,4)) {
				strGapDT = ( pEndDate.substring(4,6) * 1 - pStartDate.substring(4,6) * 1 ) + 1;	//현재 로직에서는 1을 더해줘야 함.
			} else {
				// 20140930 김상희요청 - 잔여개월이 소수점자리까지 나올 경우에는 무조건 올림처리
				var division = (1000 * 60 * 60 * 24 * 365.25 / 12);
				strGapDT = Math.floor(Math.ceil((strEDT.getTime()-strSDT.getTime()) / division ));
			}
		}
		return strGapDT;	
	
	};
	
	/**	
	 * 확보계획 텍스트영역 -> 포커스 사라질 때
	 */
	scwin.tta_relatInvnt_onblur = function(e) {
		
		var relatInvnt = tta_relatInvnt.getValue();
		if (relatInvnt.trim().length > 1000) {
			com.warn('확보계획의 내용은 1000자를 넘을 수 없습니다.');
			return;
		}

	};
	
	/**	
	 * 확보계획 텍스트영역 -> 키보드 입력되는 때
	 */
	scwin.tta_relatInvnt_onkeyup = function(e) {
		
		var ctnt = tta_relatInvnt.getValue();
		var msglen = ctnt.length;
		tb_relatinvnt_cnt.setValue(msglen + "자");

	};
	
	/**	
	 * "+" 버튼 클릭 이벤트 (학생연구원 정보(grd_list_1) 내 위치)
	 */
	scwin.trggier_add_onclick = function(e) {
		
		if (inp_nm.getValue() == "") {
			com.alert("지도교수 선택 후 가능합니다.");
			return;
		}
		
		com.addRow(grd_list_1);	//[학생연구원 정보], [면담결과] 같은 dlt 바라보기에 한줄만 추가
		
	};

	/**	
	 * "-" 버튼 클릭 이벤트 (학생연구원 정보(grd_list_1) 내 위치)
	 */
	scwin.trggier_del_onclick = function(e) {
		
		if (grd_list_1.getRowCount() > 0) {
			
			if (grd_list_1.getFocusedRowIndex() < 0) {
				com.alert('삭제할 학생연구원활용예정확인서 정보를 선택하셔야 합니다.');
				return;
				
			} else {
				com.alert("삭제내용은 저장하셔야 반영됩니다.");
				var gridRow = grd_list_1.getFocusedRowIndex();
				dlt_stu_info.removeRow(gridRow);	// 학생연구원 정보 행 삭제 시 면담결과 행도 함께 삭제됨
			}
		
		}		
		
	};
		
    /**
     * 학생연구원 정보(grd_list_1) 각 cell에 대한 값 변경 이벤트
     */	
	scwin.grd_list_1_onafteredit = function(row,col,value) {

		var colId 		= this.getColumnID(col);
		var posi_nm 	= this.getCellData(row, "posi_nm").split("(");
		var procs 		= this.getCellData(row, "procs").substr(0,1);	//석·박사학위통합과정도 석사로 하면되기 때문
		var prjStartYmd = this.getCellData(row, "prj_start_ymd");
		var prjClsYmd 	= this.getCellData(row, "prj_cls_ymd");	
		
		//금액이 설정된 후에 금액에 관련된 정보가 바뀔때  근무시간 및 금액을 초기화 하여 재설정하게 함.
		if ((dlt_stu_info.getCellData(row, "work_time") != "") && (colId == "posi_nm" || colId == "procs" || colId == "prj_start_ymd" || colId == "prj_cls_ymd")) {
			//빈값 세팅
			dlt_stu_info.setCellData(row, "work_time" 	  , "");	//근무시간
			dlt_stu_info.setCellData(row, "stu_amt_mnt"   , "");	//학생인건비지출예정액(월)
			dlt_stu_info.setCellData(row, "stu_amt_yr" 	  , "");	//학생인건비지출예정총액
		}
		
		if (colId == "work_time") {	//근무시간(주)에 따른 월, 연 지급액 설정
		
			if (posi_nm == "" || procs == "") {
				com.warn("직급과 지원학위과정을 먼저 선택해주세요.");
				dlt_stu_info.setCellData(row, colId , "");
				return;
				
			} else if (prjStartYmd == "" || prjClsYmd == "") {
				com.warn("활용예정시작일과 종료일을 입력해주세요.");
				dlt_stu_info.setCellData(row, colId , "");
				return;
				
			} else {	
				
				if (dlt_stu_info.getCellData(row, "membr_burdn").trim() == "") {	//grd_list_1의 "membr_burdn"은 현재 숨김 처리
					
					//년도별 보험률기준 조회 로직
					scwin.salInsur(row);
					
				}
				
				//학생연구원 급여기준표 급여 조회 -> 시간바꿀때마다 급여 세팅
				dma_search.set("procs"	, procs);		//과정
				dma_search.set("posi_nm", posi_nm[0]);	//직급명
				
				//급여기준표 조회 로직
				scwin.salAnuPay(row, procs, posi_nm[0]);
				
			}     
			
			
		//else if : 시작일 설정
		} else if (colId == "prj_start_ymd") {
			
			var prjStartYmd = this.getCellData(row, colId);
			
			if (prjStartYmd.length == 6) {
				dlt_stu_info.setCellData(row, colId , prjStartYmd+"01");
			}
			
			var prjStartYmdChk = this.getCellData(row, "prj_start_ymd").trim();
			
			//grd_list_1의 "entr_ymd"은 현재 숨김 처리
			dlt_stu_info.setCellData(row, "entr_ymd" , prjStartYmdChk);	//추천서 발급을 위한 입소일자 자동세팅
			
			if (prjStartYmdChk != "") {
				
				var prj_Start_Ymd_chk = WebSquare.date.isValidDate(prjStartYmdChk, prjStartYmdChk);
				
				if (! prj_Start_Ymd_chk) {
					grd_list_1.setFocusedCell(row, "prj_start_ymd");
					return false;
				}
			
			}
			
			//기간 체크
			if (this.getCellData(row, "prj_start_ymd").trim() != "" && this.getCellData(row, "prj_cls_ymd").trim() != "" && this.getCellData(row, "prj_start_ymd") > this.getCellData(row, "prj_cls_ymd")) {
				com.warn("시작일이 종료일 보다 뒷 날짜입니다.");
				dlt_stu_info.setCellData(row, colId , "");	//시작일 빈값설정
				return false;
			}

			//년도별 보험률기준 조회 로직
			scwin.salInsur(row);
			
			
		//else if : 종료일 체크
		} else if (colId == "prj_cls_ymd") {
			
			var prjClsYmd 	= dlt_stu_info.getCellData(row, "prj_cls_ymd");
			
			if (prjClsYmd != "") {
				var yyyy 		= prjClsYmd.substr(0,4);
				var mm 			= prjClsYmd.substr(4,2);
				var dd 			= "01";
			
			//여기서 mm에 -1을 하지 않는 이유는 해당월의 마지막 날을 추출하기 위함
			var com_ymd = new Date(yyyy, mm, dd);
			com_ymd.setDate(com_ymd.getDate() - 1);
			
			prjClsYmd = com.formatDateTime(com_ymd, "yyyyMMdd");	//날짜형식 세팅
			dlt_stu_info.setCellData(row, colId , prjClsYmd);
			
			var prjClsYmdChk = this.getCellData(row, "prj_cls_ymd");
			}
			
			if (prjClsYmdChk.trim() != "") {
			
				var prj_Cls_Ymd_chk = WebSquare.date.isValidDate(prjClsYmdChk, prjClsYmdChk);
	
				if (! prj_Cls_Ymd_chk) {
					grd_list_1.setFocusedCell(row, "prj_cls_ymd");
					return false;
				}
				
			}
			
			if(this.getCellData(row, "prj_start_ymd").trim() != "" && this.getCellData(row, "prj_cls_ymd").trim() != "" && this.getCellData(row, "prj_start_ymd") > this.getCellData(row, "prj_cls_ymd")){
				com.warn("종료일이 시작일 보다 앞 날짜입니다.");
				dlt_stu_info.setCellData(row, colId , "");	//종료일 빈값설정
				return false;
			}
			
			
		//else if : 전기/후기 선택여부
		} else if (colId == "former_latter") {
			
			if (this.getCellData(row, "posi_nm").trim() != "학연협동과정생(근로)") {
				com.warn("해당 정보는 [학연협동과정생(근로)] 직급에만 해당 됩니다.");
				dlt_stu_info.setCellData(row, colId , "");
				return false;
			}
			
			
		//else if : 직급 변경시
		} else if (colId == "posi_nm") {
			
			//"학연협동과정생(근로)"인 경우 -> "전기/후기" 선택란 활성화
			if (this.getCellData(row, "posi_nm") == "학연협동과정생(근로)") {
				grd_list_1.setCellDisabled(row, "former_latter", false);
			
			//그 외의 경우 -> "전기/후기" 선택란 비활성화	
			} else {
				grd_list_1.setCellDisabled(row, "former_latter", true);
			}
			
			if (this.getCellData(row, "posi_nm").trim() != "학연협동과정생(근로)") {
				dlt_stu_info.setCellData(row, "former_latter" , "");
				return false;
			}
			
		}
			
	};
	
	/**	
	 * 년도별 보험률기준 조회 로직 (grd_list_1 내 cell 클릭 시)
	 */
	scwin.salInsur = function(row) {
		
		//보험율 산정을 위한 귀속년도 세팅
		var prjStartYmdChk = dlt_stu_info.getCellData(row, "prj_start_ymd").trim();
		//보혐율 산정 방식 조회
		dma_search.set("belng_yr", prjStartYmdChk.substr(0,4));
		
		if (prjStartYmdChk != "") {
			
			com.execute(sbm_outHumApyStuSalInsur, {
				successCallback : function(e) {
					
					//보험율 산정 데이터 세팅
					var membrBurdn = e.responseJSON.elData.membr_burdn;
					dlt_stu_info.setCellData(row, "membr_burdn", membrBurdn);
					
				}
			});	
			
		}
		
	};
	
	/**	
	 * 급여기준표 조회 로직 (grd_list_1 내 cell 클릭 시)
	 */
	scwin.salAnuPay = function(row, procs, posi_nm) {
		
		dma_search.set("procs"	, procs);		//과정
		dma_search.set("posi_nm", posi_nm);		//직급명
		
		com.execute(sbm_outHumApyStuSalAnuPay, {
			successCallback : function(e) {
				
				//급여기준표 데이터 세팅
				dma_salAnuPay.setJSON(e.responseJSON.elData);
				
				var analsal = dma_salAnuPay.get("analsal"); 
				
				if (analsal != "" || analsal != null) {
					
					var workTime 	= dlt_stu_info.getCellData(row, "work_time");
					var membrBurdn 	= Number(dlt_stu_info.getCellData(row, "membr_burdn"));
					
					if(workTime == "") workTime = 0;
									
					//(주근무시간/40*급여기준표) + (주근무시간/40*급여기준표) + 200,000 * (1+보험료율(%)+퇴직충당금비율(%))
					var cal_1 = workTime / 40 * analsal;
					var cal_2 = workTime / 40 * analsal + 200000;
					var cal_3 = cal_2 * (Math.floor(membrBurdn + 8.333333) / 100);
					
					//var stuAmtMnt = (workTime / 40 * analsal) + (workTime / 40 * analsal + 200000) * (Math.floor(membrBurdn + 8.333333) / 100); 
					var stuAmtMnt = cal_1 + cal_3;	//위 코드와 같은 의미 -> 수식때문에 에러 떠서 변수에 담아서 나눠둠
					
					var tempAmt 		= 0;
					var tempAmtSub 		= 0;
					var totAmt 			= 0;						
					var month = scwin.monthCalc(dlt_stu_info.getCellData(row, "prj_start_ymd"),dlt_stu_info.getCellData(row, "prj_cls_ymd"),"M" );
	
					if ( month >= 0 ) {
					
						tempAmt = eval(Math.round(stuAmtMnt)) * (eval(month));
						tempAmtSub = eval(Math.round(stuAmtMnt)) * (eval(month));
						
						if (month > 12) {
						
							month = 12;
							tempAmtSub = eval(Math.round(stuAmtMnt)) * (eval(month));
							
						}
						
					}						
										
					dlt_stu_info.setCellData(row, "stu_amt_mnt" 	, Math.round(stuAmtMnt));	//소수점 반올림, 정수변환
					dlt_stu_info.setCellData(row, "stu_amt_yr" 		, tempAmt);					//소수점 반올림, 정수변환
					dlt_stu_info.setCellData(row, "stu_amt_yr_sub"	, tempAmtSub);				//소수점 반올림, 정수변환, 최대 12개월
					
					var stuAmtYr = 0;
					var rowCnt = dlt_stu_info.getRowCount();
					
					for (var i=0; i < rowCnt; i++) {
						//WebSquare.util.parseInt 사용해야 as-is와 동일하게 정상작동
						if (WebSquare.util.parseInt(dlt_stu_info.getCellData(i, "stu_amt_yr_sub"), 0) == 0 && WebSquare.util.parseInt(dlt_stu_info.getCellData(i, "stu_amt_mnt"), 0) != 0){
						
							if(dlt_stu_info.getCellData(i, "stu_amt_yr")/dlt_stu_info.getCellData(i, "stu_amt_mnt") > 12){
								stuAmtYr += WebSquare.util.parseInt(dlt_stu_info.getCellData(i, "stu_amt_mnt") * 12, 0);
							} else{
								stuAmtYr += WebSquare.util.parseInt(dlt_stu_info.getCellData(i, "stu_amt_yr"), 0);
							}
							
						} else{
							stuAmtYr += WebSquare.util.parseInt(dlt_stu_info.getCellData(i, "stu_amt_yr_sub"), 0);
						}
						
					}
					
					var hdnEnfrcAmt = hidden_tb_enfrcAmt.getValue();
					
					if (hdnEnfrcAmt == "") { 
						hdnEnfrcAmt = 0; 
					}
					
					//지출예정액 세팅
					tb_enfrcAmt.setValue(Number(hdnEnfrcAmt) + stuAmtYr);
					
				}
				
			}
		});	
		
		
	};
	
    /**
     * 결재신청 버튼 클릭 이벤트
     */	
	scwin.btn_apr_onclick = function(e) {
				
		//반려상태인 경우
		if(dma_info.get("apr_state") == "XAD015"){
			com.alert("저장 후 신청바랍니다.");	
			return;
		};
		
		//필수값체크 실행
		if (!scwin.preSave()) {
			return false;
		}
		
		//결재신청 로직
		com.confirm("결재신청 하시겠습니까?", function(ret){
			if (ret) {
				scwin.save("apr");
			}
		});
		
	};
	
    /**
     * 저장 버튼 클릭 이벤트
     */	
	scwin.btn_save_onclick = function(e) {
		
		//필수값체크 실행
		if (!scwin.preSave()) {
			return false;
		}
		
		com.confirm("message.xom.wq.017"/*저장 하시겠습니까?*/, function(ret) {
			if (ret) {
				scwin.save("save");
			}
		});
		
	};
	
    /**
     * 저장, 결재신청 전 필수값체크
     */
	scwin.preSave = function() {
		
		var ramtAmt		= tb_ramtAmt.getValue();		//잔액		
		var enfrcAmt	= tb_enfrcAmt.getValue();		//지출예정액	
		var relatInvnt	= tta_relatInvnt.getValue();	//확보계획	
		
		//필수체크
		if (!com.validateData(dma_info, [{ id : "req_ymd", mandatory : true }			//신청일
										,{ id : "map_prof_emp_no", mandatory : true }	//지도교수번호
										,{ id : "map_prof_nm", mandatory : true }		//지도교수명
										], false)) return false;
		
		if (dlt_stu_info.getRowCount() == 0) {
			com.warn("학생연구원 정보를 입력해 주십시오.");
			return false;
		}
		
		//[학생연구원 정보] 행 개수만큼 반복
		var cnt = grd_list_1.getTotalRow();
		for (var row = 0; row < cnt; row++) {
		
			var nm 				= grd_list_1.getCellData( row , "nm" );				//성명
			var nm_chchar 		= grd_list_1.getCellData( row , "nm_chchar" );		//한자
			var email 			= grd_list_1.getCellData( row , "email" );			//이메일
			var mobile 			= grd_list_1.getCellData( row , "mobile" );			//핸드폰
			var supt_schl 		= grd_list_1.getCellData( row , "supt_schl" );		//지원학교
			var major 			= grd_list_1.getCellData( row , "major" );			//전공
			var procs 			= grd_list_1.getCellData( row , "procs" );			//과정
			var posi_nm 		= grd_list_1.getCellData( row , "posi_nm" );		//직급
			var intrv_result	= grd_list_1.getCellData( row , "intrv_result" );	//면담결과
			var former_latter	= grd_list_1.getCellData( row , "former_latter" );	//전기or후기
			var prj_start_ymd	= grd_list_1.getCellData( row , "prj_start_ymd" );	//활용예정시작일
			var prj_cls_ymd		= grd_list_1.getCellData( row , "prj_cls_ymd" );	//활용예정종료일
			var work_time		= grd_list_1.getCellData( row , "work_time" );		//근무시간
			var pruse_contnt	= grd_list_1.getCellData( row , "pruse_contnt" );	//연수분야

			if (nm.trim() == "") {
				com.alert("[그리드] 성명(한글)을 입력해 주십시오.");
				return false;
			}

			if (nm_chchar.trim() == "") {
				com.alert("[그리드] 성명(한자)을 입력해 주십시오.");
				return false;
			}
			if (email.trim() == "") {
				com.alert("[그리드] 이메일을 입력해 주십시오.");
				return false;
			}
			
			if (mobile.trim().length != 11) {
				com.alert("[그리드] 핸드폰 번호 입력형식이 잘못되었습니다.");
				return false;
			}
		
			if (supt_schl.trim() == "") {
				com.alert("[그리드] 지원학교를 입력해 주십시오.");
				return false;
			}

			if (major.trim() == "") {
				com.alert("[그리드] 전공을 입력해 주십시오.");
				return false;
			}

			if (procs.trim() == "") {
				com.alert("[그리드] 과정을 선택해 주십시오.");
				return false;
			}

			if (posi_nm.trim() == "") {
				com.alert("[그리드] 직급을 선택해 주십시오.");
				return false;
			}

			if (intrv_result.trim() == "") {
				com.alert("면담결과를 입력해 주십시오.");
				return false;
			}
		
			if(posi_nm.trim() == "학연협동과정생(근로)"){
				if (former_latter.trim() == "") {
					com.alert("[그리드] 전기/후기를 선택해 주십시오.\n전기 : 3월 ~ 8월\n후기 : 9월 ~ 익년2월");
					return false;
				}
			}

			if(prj_start_ymd.trim() == ""){
				com.alert("활용예정시작일을 입력해주시기 바랍니다.");
				return false;
			}
			if(prj_cls_ymd.trim() == ""){
				com.alert("활용예정종료일을 입력해주시기 바랍니다.");
				return false;
			}
			if (prj_start_ymd.trim() > prj_cls_ymd.trim()) {
				com.alert( row + 1 + "번 학생의 활용예정시작일을 활용예정종료일 이전으로 입력해주시기 바랍니다.");
				return false;
			}
			
			if (work_time.trim() == "") {
				com.alert("근무시간(주)을 선택하여 주시기 바랍니다.");
				return false;
			}

			var dateCnt = gcm.date.diffDate(inp_prof_entr_ymd.getValue(), prj_start_ymd)
			if (dateCnt < 365) {
				com.alert("지도교수의 근무년수가 학생연구원 입사일자 기준 1년 미만입니다.");
				return false;
			} 

			if (pruse_contnt.trim() == "") {
				com.alert("[그리드] 연수분야를 입력해 주십시오.");
				return false;
			}
			
		}
		
		if(enfrcAmt > ramtAmt && relatInvnt.trim() == "") {
			com.alert("확보계획을 입력해 주십시오.");
			return false;
		}
	
		return true;

	};

    /**
     * 저장, 수정, 결재신청 로직
     */	
	scwin.save = function(saveType) {
		
		com.file.transport( function( attachObj){
			
			//저장시 : forminit의 infoCd에 넣어주는 값 세팅
			//학생연구원. 이름
			var aprNm = "";
			var i = "";
			for (i = 0; i < dlt_stu_info.getRowCount() - 1; i++) {	//마지막 하나 남겨두고 for문 돌리고 마지막에 if문으로  "," 안붙이고 추가
				aprNm += dlt_stu_info.getCellData(i, "nm") + ", ";
			}
			if (i+1 == dlt_stu_info.getRowCount()) {
				aprNm += dlt_stu_info.getCellData(i, "nm");
			}
			
			//저장정보 추가세팅
			dma_info.set("dept_cd"				, tb_dept_cd.getValue());				//신청자 부서코드
			dma_info.set("dept_new_ymd"			, tb_deptNewYmd.getValue());			//신청자 부서신설일
			
			dma_info.set("depthed_syspayno"		, inp_depthed_syspayno.getValue());		//지도교수 부서장시스템개인번호
			dma_info.set("divsn_rspns_syspayno"	, inp_divsn_rspns_syspayno.getValue());	//지도교수 본부장시스템개인번호
			
			dma_info.set("attach_file_no"		, attachObj.upload_A);					//첨부파일
			dma_info.set("apr_nm"				, aprNm);								//inser시 forminit의 infoCd 데이터, 결재신청시 infoCd 사용
			
			
			//결재신청시 추가되는 데이터 세팅
			if(saveType == "apr"){
			
				//학생연구원 정보의 지원학교
				var aprSchl = "";
				var j = "";

				for (j = 0; j < dlt_stu_info.getRowCount()-1; j++) {
					aprSchl += dlt_stu_info.getCellData(j, "supt_schl") + ", ";
				}

				if ((j+1) == dlt_stu_info.getRowCount()) {
					aprSchl += dlt_stu_info.getCellData(j, "supt_schl");
				}
							
				//결재신청정보 세팅				
				dma_info.set("approval"				, true); 		//결재여부
				dma_info.set("save_type"			, "apr");		//저장타입
				dma_info.set("apr_state"			, "XAD010");	//신청상태
				
				dma_info.set("apr_schl"				, aprSchl);		//지원학교
				
				//지도교수이름 데이터 세팅
				if (inp_syspayno.getValue() == tb_syspayno.getValue()) {		//inp_syspayno:지도교수 개인번호, tb_syspayno:신청자 개인번호
					dma_info.set("apr_map_prof"			, "");
				} else {
					dma_info.set("apr_map_prof"			, inp_nm.getValue());
				}
					
				//스냅샷에 보여주는 데이터 세팅
				dma_info.set("rspns_map_prof_nm"	, inp_nm.getValue());					//지도교수
				dma_info.set("rspns_empno"			, inp_empno.getValue());				//지도교수 개인번호
				dma_info.set("rspns_posi_nm"		, inp_posi_nm.getValue());				//지도교수 직급
				dma_info.set("rspns_dept_nm"		, inp_dept_nm.getValue());				//지도교수 부서
				dma_info.set("rspns_depthed_nm"		, inp_depthed_nm.getValue());			//지도교수 부서장명
				dma_info.set("rspns_divsn_rspns_nm"	, inp_divsn_rspns_nm.getValue());		//지도교수 본부장명
				
				dma_info.set("ramt_amt"				, tb_ramtAmt.getValue());				//결재당시 잔액
				dma_info.set("enfrc_amt"			, tb_enfrcAmt.getValue());				//결재당시 지출예정액
				
				//전공명 세팅
				for (var i = 0; i < dlt_stu_info.getRowCount(); i++) {
					dlt_stu_info.setCellData( i , "major_nm" , grd_list_1.getCellDisplayData(i, "major"));
				}						
				
			}
			
			com.execute(sbm_saveData, {
				successCallback : function(e) { // 성공 콜백					
					com.alert("message.xom.wq.023", function() {
						
						//저장, 수정
						if(saveType == "save"){
						
							//팝업에서 변경자 선택시 값이 세팅되어있어서 빈값으로 재설정
							dma_search.set("syspayno", "");
							//리턴받은 신청번호로 상세조회
							scwin.search(e.responseJSON.elData.req_no);
						
						
						//결재신청
						} else if (saveType == "apr") {
						
							if(dma_info.get("approval")){
								// 결재화면 이동
								com.movePageMenu("A001", { "reqNo" :  dma_info.get("req_no")});
							}else{
								com.alert("message.xom.wq.023" /*정상적으로 처리되었습니다.*/, function() {	
									//팝업에서 변경자 선택시 값이 세팅되어있어서 빈값으로 재설정
									dma_search.set("syspayno", "");
									//리턴받은 신청번호로 상세조회
									scwin.search(e.responseJSON.elData.req_no);
								});	
							}
						
						}

					});										
				}
			});
			
		});

		
	};
	
    /**
     * 삭제 버튼 클릭 이벤트
     */	
	scwin.btn_del_onclick = function(e) {
		
		//추천서가 생성된 건이 있으면 해당 신청서 삭제 불가
		for (var j = 0; j < dlt_stu_info.getRowCount(); j++) {
		
			if ("" != dlt_stu_info.getCellData(j, "rcmd_no").trim()) {
				com.warn("추천서가 생성된 신청 건은 삭제가 불가능 합니다.");
				return false;
			} 
			
		}
		
		com.confirm("message.xom.wq.022"/*삭제 하시겠습니까?*/, function( ret) {
			if (ret) {
				com.file.deleteAll( function(){
					
					var req_no = com.getParameter("req_no");
					dma_search.set("req_no", req_no);
				
					com.execute(sbm_deleteData, {
						successCallback : function(e) { // 성공 콜백
							com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
								//삭제 후 목록창 이동
								com.moveList("H538", {mod :"delete"});
							});
						}
					});
					
				});	
			}
		});

	};

	/**
	* ※ 학생연구원 결재서류 양식 다운 버튼 클릭 이벤트
	*/
	scwin.btn_docu_down_onclick = function(e) {
		scwin.downFileinfo("학생연구원활용계획서_양식.hwp", "10000023");
	};
	
	/**
     * 파일 다운로드
     */
	scwin.downFileinfo = function downFileinfo(file_nm,file_no){
		var url = "/SvcAttachDown.do?fileName=web_folder/bizdoc/hum/"+file_no+"&realName="+encodeURIComponent(file_nm)+"&bizKind=OLD";		
		window.open(url, '_blank').focus();
	};
	
    /**
     * 결재진행상황 버튼 클릭 이벤트
     */		
	scwin.btn_state_onclick = function(e) {
		
		var reqNo = dma_info.get("req_no");
		var aprNm = dma_info.get("apr_state_nm");
		
		if (reqNo != "" && aprNm != "저장중" ) {
			var url = "/ui/apr/layoutSimple.xml";
			var option = { type: "browserPopup", width: 1350, height: 1480, id: "snapshotPop", popupName: "결재문서"};
			var data = { reqNo: reqNo, viewMode : "read", instStatus : (dma_info.get("apr_state") != "XAD100" ? "Running" : "Completed") } // instStatus" : "Completed",
			com.openPopup(url, option, data);
			
		} else {
			com.alert("결재 후 열람이 가능합니다.");
			return;
		}

	};
