	/**
	 * 결재신청버튼 클릭 이벤트
	 */
	scwin.btn_apr_onclick = function(e) {
		if (!scwin.pre_save("APR")) {
			return false;
		}
		
		scwin.data.apr_yn = "Y";
		scwin.data.state = "APR";
		dma_info.set("approval", "true");
		
		com.confirm("결재신청 하시겠습니까?" , function(ret) {
			if (ret) {
				scwin.checkAmt(scwin.data.state);				
				scwin.save();
			}
		});	
	};
	
