	scwin.search = function() {
		scwin.data.dataSetFlg = true;
		
		//if(scwin.data.reqNo != ""){	//sbm_selectApyTrainBasicInfo02
			//WebSquare.ModelUtil.setInstanceValue("request/APYTRAINBASICINFO2/req_no/@value", globalVar.reqNo);
