			//dma_search.set("req_no", scwin.data.reqNo);
			//gfnExecuteSubmission("apyTrainBasicInfo2");
			com.execute(sbm_selectApyTrainBasicInfo, {
				successCallback : function(e) {
					console.log("======eldata==="+JSON.stringify(e.responseJSON.elData));
					dma_info.setJSON(e.responseJSON.elData.humApyEducVo);
					dlt_rslt.setJSON(e.responseJSON.elData.attentList, false);
					
					if (dma_info.get("apr_state")=="XAD015") {
						dlt_reject.setJSON(e.responseJSON.elData.docList, false);
						if (dlt_reject.getTotalRow() > 0) {
							grd_reject.setStyle("display", "none");
						}	
					}		
								 
					console.log("결과req_no=="+dma_info.get("req_no"));
					scwin.data.loadForm = false;
					
					//첨부파일 세팅
					var vMode = "edit";
					if (dma_info.get("apr_state") >= "XAD020") {
						vMode = "view";
					}			
							
					
					
					if(dma_info.get("req_no") != ""){	// 리스트에서 불러온 경우 baseInfo02
						hidd_aprState.setValue(dma_info.get("apr_state")); //hidden value
						
						var rtsSyspayno = dma_info.get("syspayno");
						
						console.log("==hidd_aprState=="+hidd_aprState.getValue() + "*****rtsSyspayno===" + rtsSyspayno);
						if (hidd_aprState.getValue() == "XAD100") {	//결재완료
							btn_rsltReport.setDisabled(false);	//trigger2 결과보고 버튼
						}
						
						var attachFileNo = dma_info.get("attach_file_no").trim();
						com.file.create(upload_A.getID(), {fileKey: (attachFileNo == "") ? scwin.data.service_id :attachFileNo, mode: vMode});
	
						if (rtsSyspayno == com.getLoginInfo("sn") && hidd_aprState.getValue() == "XAD005") {
							console.log("111");
							btn_fileU.setStyle("display", "");					// 엑셀업로드 버튼
							btn_sampleDown.setStyle("display", "");		// 양식다운로드 버튼
							btn_fileD.setStyle("display", "none");			// 엑셀다운로드 버튼
							//document.getElementById("btn_fileU").style.display = "";					// 엑셀업로드 버튼
							//document.getElementById("btn_sampleDown").style.display = "";		// 양식다운로드 버튼
							//document.getElementById("btn_fileD").style.display = "none";				// 엑셀다운로드 버튼
						} else if (rtsSyspayno == com.getLoginInfo("sn") && hidd_aprState.getValue() == "XAD010") {
							console.log("222");
							btn_fileU.setStyle("display", "");
							btn_sampleDown.setStyle("display", "");
							btn_fileD.setStyle("display", "none");	
							//document.getElementById("btn_fileU").style.display = "";
							//document.getElementById("btn_sampleDown").style.display = "";
							//document.getElementById("btn_fileD").style.display = "none";
						} else {
							console.log("333");
							btn_fileU.setStyle("display", "none");
							btn_sampleDown.setStyle("display", "none");
							btn_fileD.setStyle("display", "");
							//document.getElementById("btn_fileU").style.display = "none";
							//document.getElementById("btn_sampleDown").style.display = "none";
							//document.getElementById("btn_fileD").style.display = "";
						}
						
						if(dlt_rslt.getRowCount() > 0){
							//grid_rslt.setfFocusedCell(0, 1, true);
							grid_rslt.setFocusedCell(0, 1, true);
							//dlt_rslt.setFocusedCell(0, 1, true);
							scwin.gridToForm(0);
						} else {
							dlt_rslt.removeAll();
							scwin.data.add_flg = "ADD";
