					} else { //신규로 넘어온 경우 baseInfo
						console.log("신규case");
						btn_fileU.setStyle("display", "");
						btn_sampleDown.setStyle("display", "");
						btn_fileD.setStyle("display", "none");
					}

					scwin.data.setSyspayNo = dma_info.get("syspayno");
					
					//scwin.searchSELECT();

					//scwin.gfnBtnControll(hidd_aprState.getValue(), scwin.data.roleChk, rtsSyspayno, com.getLoginInfo("sn"), "300");
					//화면 컨트롤
