	/**
		 * 전자결재 버튼 제어
		 *
		 * @param aprState
		 *            전자결재 상태
		 * @param roleChk
		 *            권한상태 상태(true:권한있음, false:권한없음)
		 * @param reqSyspayno
		 *            등록자 시스템개인번호
		 * @param syspayno
		 *            세션의 시스템개인번호
		 * @param startLeft
		 *            버튼 style left 위치 지정(px)
	*/
	scwin.gfnBtnControll = function() { //aprState, roleChk, reqSyspayno, syspayno, startLeft
		com.biz.disabledButtons(dma_info.get("apr_state"), dma_info.get("req_no"));
		
		if (dma_info.get("apr_state") >= "XAD020") {
			//관리자이고, 담당자확인 상태일 때만 반려/처리버튼 노출.
			com.biz.checkRole({ serviceId : scwin.data.service_id, roleCodes : com.getLoginInfo('roleCode')}, function(e) {
				scwin.data.isRoleYn = e.isRoleYn;
				if (scwin.data.isRoleYn && dma_info.get("apr_state") == "XAD070") {
					btn_confirm.setStyle("display", "none");
					btn_reject.setStyle("display", "none");
				}				
			});			

//			ibx_empno.setDisabled(true);
//			btn_search_emp.setDisabled(true);
//			ica_req_ymd.setDisabled(true);
//			grd_after_fam.setGridReadOnly(true);
			
			
			if (dma_info.get("apr_state") == "XAD100") {
				btn_print.setDisabled(false);
			}
		} else {
			btn_save.setStyle("display", "");	
			btn_add.setStyle("display", "");	
			btn_del.setStyle("display", "");	
					console.log("scwin.data.isRoleYn=="+scwin.data.isRoleYn);
					console.log("com.getLoginInfo(empno)=="+com.getLoginInfo("empno"));	
			if (tbx_empNo.getValue() != com.getLoginInfo("empno")) {// && !scwin.data.isRoleYn
				btn_save.setDisabled(true);
				btn_apr.setDisabled(true);
				btn_del.setDisabled(true);
			}
		}		
	};
	
 	/**
	 * 계정번호 버튼 클릭 이벤트
	 */	
	scwin.btn_searchAccnt_onclick = function(e) {
		var data={
			accnt_no : ibx_accntNo.getValue(),
			ref_yn : (ibx_accntNo.getValue() == "")? "Y" : "N"
		};
		
		com.pop.open(com.pop.TYPE.ACNNO, data, function(ret){
			console.log("ret======"+JSON.stringify(ret));
			scwin.setAccntRow(ret);
			//ibx_accntNo.setValue(ret.accnt_no);
			//ibx_accnt_no_nm.setValue(ret.accnt_no_nm);
		});
	};
	
	/**
	 * 계정팝업에서 선택한 Data를 세팅
	 */	
	scwin.setAccntRow = function(ret){
		console.log("ret.accnt_state===="+ret.accnt_state);
		if(/RAK041/.test(ret.accnt_state)){ // RAK041 집행가능
		//if(ret.accnt_state == "RAK041"){ // RAK041 집행가능
			ibx_accntNo.setValue(ret.accnt_no);
			//return;
		} else {
			com.alert("해당 계정은 집행가능 상태가 아님으로 입력할 수 없습니다.");
			return false;
		}
	};
	
	/**
	 * 비용코드 버튼 클릭 이벤트
	 */	
	scwin.btn_searchExpnsCd_onclick = function(e) {
		var accountNo = ibx_accntNo.getValue(); //.trim()
		var ref_yn = "N";
		//ref_yn:"Y"
				
		var data = {
				accnt_no : accountNo,
				ref_yn : ref_yn
			};

		com.pop.open(com.pop.TYPE.EXPNS, data, function(retObj) {
				if (retObj) {
					// 비용코드 팝업에서 선택한 Data를 세팅
					ibx_accntCd.setValue(retObj.accnt_cd);	//회계코드
					ibx_expnsCd.setValue(retObj.expns_cd);	//비용코드
					ibx_expnsCdNm.setValue(retObj.expns_cd_nm);	//비용명
				}
			});
	};
	
