				var searchYn = "N";
			} else {
				var searchYn = "A";
			}
			
			var getNodeUser = "searchYn="+searchYn+"&"+"gfnYn="+gfnYn+"&"+"codeId="+"inputAddEmpNo"+"&"+"descId="+"inputAddNm"+"&"+
				"suvDept_cd="+suvDept_cd+"&"+"syscodeId="+"inputAddSyspayno"+"&"+"inputEmpNoNm="+empNo_Trim+"&"+"selectboxAuthority="+auth_Hum+"&"
				+"outputDept_Cd="+outputDept_Cd+"&"+"outputDept_New_Ymd="+outputDept_New_Ymd+"&"+"outputDept_Shape="+outputDept_Shape+"&"
				+"SigMul="+sigmul+"&"+"work_clsf="+work_clsf+"&"+"KorEng="+koreng+"&";
				**/		
		var data = { empno : ibx_addEmpNo.getValue()
						 , work_clsf : "HAG010" 
						 , ref_yn : (ibx_addEmpNo.getValue() == "")? "Y" : "N" }
		com.pop.open( com.pop.TYPE.HUM, data, function(ret){
			
			ibx_addEmpNo.setValue(ret.empno);				//개인번호
			ibx_AddNm.setValue(ret.nm);							//개인번호 성명
			hidd_addSyspayno.setValue(ret.syspayno);		//hidden value 
			hidd_addDeptCd.setValue(ret.dept_cd);			//hidden value
			hidd_addDeptNewYmd.setValue(ret.dept_new_ymd);		//hidden value
			hidd_addPosiCd.setValue(ret.posi_cd);		//hidden value
			tbx_addDeptNm.setValue(ret.dept_nm);	//부서 명
			tbx_addPosiNm.setValue(ret.posi_nm);		//직급 명
			hidd_addDutyCd.setValue(ret.duty_cd);		//hidden value
			hidd_addJobCd.setValue(ret.job_cd);		//hidden value 직무코드
		});
	};
	
