	/**
	 * 반려/처리 프로세스
	 */	
	scwin.retProcess = function(aprState) {
		var msg = "반려처리 하시겠습니까?";
		var su_flg = "";

		if (aprState == "XAD100") {
			msg = "처리하시겠습니까?";
			su_flg = "0";
		} else {
			msg = "반려처리 하시겠습니까?";
			su_flg = "2";
		}

		if (!confirm(msg)) {
			return false;
		}
		
		if(aprState == "XAD100"){	//처리
			if (!scwin.data.isRoleYn) {
				com.alert("관리자외에는 처리 하실 수 없습니다.");
			}
			
			com.confirm(msg, function(ret) { 		
				if (ret) {
					com.execute(sbm_aprAgr, {
						successCallback : function(e) {
							com.alert("승인처리 되었습니다");
							com.openTabMenu("A004", {});
						}
					});
				}
			});			
				
		} else {	//반려 
				var options = {
			    id: "APR02P",
			    popupName: "반려의견",
			    width: 500,
			    height: 300
			};
			com.openPopup("/ui/apr/APR_C001_03P.xml", options, "", function(retObj) {
			    if (retObj.comments != null) {
			        dma_info.set("comments", retObj.comments);
			        com.execute(sbm_aprReject, {
			            successCallback: function(e) {
			                com.alert("처리 되었습니다");
							com.openTabMenu("A004", {});
			            }
			        });
			    }
			});		
		}	
	} 

	/**
	 * 저장 버튼 클릭 이벤트
	 */		
	scwin.btn_save_onclick = function(e) {
		if (!scwin.pre_save("SAVE")) {
			return false;
		}
		
		com.confirm("message.xom.wq.017" /*저장 하시겠습니까?*/, function(ret) {
			if (ret) {
				scwin.data.state = "SAVE";
				scwin.data.apr_yn = "N";
				scwin.save();
			}
		});	
	};
	
	/**
	 * 저장전 실행
	 */	
	scwin.pre_save = function(prcType) { 	
		if (dma_info.get("req_ymd") == "") { // 신청일 inputCalendarReqYmd
			//alert(gfnGetMsg("r", "insert", "신청일"));
			com.alert("신청일을 입력하세요.");
			return false;
		}

		if (dma_info.get("educ_typ").trim() == "") { // 교육유형 selectboxEducTyp
			//alert(gfnGetMsg("r", "insert", "교육유형"));
			com.alert("교육유형을 입력하세요.");
			return false;
		}

		if (dma_info.get("educ_nm").trim() == "") { // 교육명
			//alert(gfnGetMsg("r", "insert", "교육명"));
			com.alert("교육명을 입력하세요.");
			return false;
		}

		if (dma_info.get("educ_agncy").trim() == "") { // 교육기관
			//alert(gfnGetMsg("r", "insert", "교육기관"));
			com.alert("교육기관을 입력하세요.");
			return false;
		}

		if (dma_info.get("expns_cd").trim() == "") { // 비용코드 체크
			//alert(gfnGetMsg("r", "insert", "비용코드"));
			com.alert("비용코드를 입력하세요.");
			return false;
		}

		if (dma_info.get("accnt_cd").trim() == "") { // 회계코드 체크
			//alert(gfnGetMsg("r", "insert", "회계코드"));
			com.alert("회계코드를 입력하세요.");
			return false;
		}

		if (dma_info.get("accnt_no").trim() == "") { // 계정번호
			//alert(gfnGetMsg("r", "insert", "계정번호"));
			com.alert("계정번호를 입력하세요.");
			return false;
		}
		
		if (dma_info.get("accnt_no").length != 8) { // 계정번호
			//alert("계정번호를 정확히 입력하세요.");
			com.alert("계정번호를 정확히 입력하세요.");
			dma_info.set("accnt_no", "");			
			return false;
		}

		if (dma_info.get("start_ymd").trim() == "") { // 교육시작일
			//alert(gfnGetMsg("r", "insert", "교육시작일"));
			com.alert("교육시작일을 정확히 입력하세요.");
			return false;
		}

		if (dma_info.get("cls_ymd").trim() == "") { // 교육종료일
			//alert(gfnGetMsg("r", "insert", "교육종료일"));
			com.alert("교육종료일을 정확히 입력하세요.");
			return false;
		}

		if (dma_info.get("educ_time").trim() == "") { // 교육시간
			//alert(gfnGetMsg("r", "insert", "교육시간"));
			com.alert("교육시간을 정확히 입력하세요.");
			return false;
		}

		// 기간역전 체크
		// 교육기간 체크
		com.checkPeriod(ica_startYmd, ica_clsYmd);	
		
		/**if (!WebSquare.date.isValidDate(inputCalendarStartYmd,inputCalendarStartYmd.getValue())) {
			return false;
		}

		if (!WebSquare.date.isValidDate(inputCalendarClsYmd,inputCalendarClsYmd.getValue())) {
			return false;
		}

		
		if (WebSquare.date.dateDiff(inputCalendarStartYmd.getValue(), inputCalendarClsYmd.getValue()) < 0) {
			com.alert("시작일이 종료일 보다 후일로 설정되어 있습니다.");

			return false;
		}**/

		if (hidd_addSyspayno.getValue().trim() != "") { //hidd value
			if (ica_addStartYmd.getValue().trim() == "" || ica_addClsYmd.getValue().trim() == "") {
				//com.alert(gfnGetMsg("r", "insert", "참가자 교육기간"));
				com.alert("참가자 교육기간을 입력하세요.");
				return false;
			}

			if (ibx_addEducTime.getValue().trim() == "") {
				//alert(gfnGetMsg("r", "insert", "참가자 교육시간"));
				com.alert("참가자 교육시간을 입력하세요.");
				return false;
			}

			if (ibx_addEducAmt.getValue().trim() == "") {
				//alert(gfnGetMsg("r", "insert", "참가자 개인교육비"));
				com.alert("참가자 개인교육비을 입력하세요.");
				return false;
			} 
		}
		// 업로드된 그리드의 Status를 임의로 U(수정)으로 바꾸어줌
		for (var i= 0; i < grid_rslt.getRowCount(); i++) {
			var empnoVal = grid_rslt.getCellData(i, "empno").trim();
			var startYmdVal = grid_rslt.getCellData(i, "start_ymd").trim().replace("-","");
			var clsYmdVal = grid_rslt.getCellData(i, "cls_ymd").trim().replace("-","");
			var timeVal = grid_rslt.getCellData(i, "educ_time").trim();
			var amtVal = grid_rslt.getCellData(i, "educ_amt").trim();
			//isNumeric 문자열이 숫자형태인지 판단.
			if (empnoVal.length != 6 || empnoVal.trim() == "" || !gcm.num.isNumber(empnoVal)) {
				com.alert("입력 데이터 에러 : ROW " + parseInt(i+1) + ", 잘못된 개인번호 입니다. 반드시 6자리여야 합니다.");
				
				grid_rslt.setCellChecked(i, "chk", true);

				return false;
			}

			if (startYmdVal.trim() != "" && (!gcm.num.isNumber(startYmdVal) || startYmdVal.length != 8)) {
				com.alert("입력 데이터 에러 : ROW " + parseInt(i+1) + ", 교육시작일 입력이 잘못되었습니다");

				grid_rslt.setCellChecked(i, "chk", true);

				return false;
			}

			if (clsYmdVal.trim() != "" && (!gcm.num.isNumber(clsYmdVal) || clsYmdVal.length != 8)) {
				com.alert("입력 데이터 에러 : ROW " + parseInt(i+1) + ", 교육종료일 입력이 잘못되었습니다");

				grid_rslt.setCellChecked(i, "chk", true);

				return false;
			}

			if (timeVal.length > 3 || !gcm.num.isNumber(timeVal)) {
				com.alert("입력 데이터 에러 : ROW " + parseInt(i+1) + ", 교육시간 입력이 잘못되었습니다");

				grid_rslt.setCellChecked(i, "chk", true);

				return false;
			}

			if (amtVal.length > 15 || !gcm.num.isNumber(amtVal)) {
				com.alert("입력 데이터 에러 : ROW " + parseInt(i+1) + ", 금액 입력이 잘못되었습니다");

				grid_rslt.setCellChecked(i, "chk", true);

				return false;
			}
		}

		if (prcType == "APR") {
			if (grid_rslt.getRowCount()== 0) {
				com.alert("참석자 정보는 적어도 한명이상 존재 해야합니다.");
				return false;
			}

			// 참석자 그리드 교육비 체크
			for (var i=0; i<grid_rslt.getRowCount(); i++) {
				if (grid_rslt.getCellData(i, "educ_amt") == "") {
					com.alert((i+1) + "번째 참석자 교육비를 입력하여 주세요");

					grid_rslt.setFocusedCell(i, "educ_amt", false);

					return false;
				}
			}
			
			// 첨부파일 필수 처리 예제
			if (1 >com.file.action.getTotalFileCount(upload_A.getID())) {
				//alert(gfnGetMsg("r", "insert", "첨부파일"));
				com.alert("첨부파일을 입력하세요.");
				//wfrAttach.focus();
				return false;
			}
		}

		return true;		
	};
	
	/**
	 * 저장/결재신청 전 체크 함수
	 */	
	 scwin.checkAmt = function(prcType) {
		scwin.data.prcType = prcType;

		var syspaynos = new Array();

		for (var i = 0; i < grid_rslt.getRowCount(); i++) {
			var syspayno = grid_rslt.getCellData(i, "syspayno").trim();

			syspaynos.push(syspayno);
		}

		if (hidd_addSyspayno.getValue() != "") {
			syspaynos.push(hidd_addSyspayno.getValue());
		}
		
		dma_search.set("cls_ymd", com.getServerDateTime("yyyy"));
		dma_search.set("syspaynos", syspaynos.join("','"));
		//WebSquare.ModelUtil.setInstanceValue("request/CHECKEDUAMT/cls_ymd/@value", gfnGetCurrentDate("yyyy"));
		//WebSquare.ModelUtil.setInstanceValue("request/CHECKEDUAMT/syspaynos/@value", "'" + syspaynos.join("','") + "'");
		//gfnExecuteSubmission("checkEduAmt");
		com.execute(sbm_getAmtTotalList, {
				successCallback : function(e) {
					console.log("eldata==sbm_getAmtTotalList="+e.responseJSON.elData);
					dlt_amtTotal.setJSON(e.responseJSON.elData.humApyEducVoList);
					
					var checkAmtStr = "";
					var checkAmt = "";

					for (var i = 0; i < grid_rslt.getRowCount(); i++) {
						checkAmt = parseInt(grid_rslt.getCellData(i, "educ_amt"));

						for (var j = 0; j < grd_AmtTotal.getRowCount(); j++) {
							if (grd_AmtTotal.getCellData(j, "empno") == grid_rslt.getCellData(i, "empno")) {
								checkAmt = parseInt(CheckAmt) + parseInt(grd_AmtTotal.getCellData(j, "sumamt"));
							}
						}
						console.log("====checkAmt===="+checkAmt);
						if (checkAmt > 500000) {
							if (checkAmtStr != "") {
								checkAmtStr += ", ";
							}

							checkAmtStr += grid_rslt.getCellData(i, "nm") + "(" + grid_rslt.getCellData(i, "empno") + ")";
						}
					}

					if (checkAmtStr != "") {
						com.confirm(checkAmtStr + " 참석자 개인사용 금액 초과 입니다.\n 계속 진행하시겠습니까?", function(ret) {
							if(ret){
								scwin.save();
							} else {
								return;
							} 
						});
					}					
				}
		});		
	};
	
	/**
	 * 저장
	 */
	scwin.save = function() {
		com.file.transport(function(attachObj){	
			var prcType = scwin.data.state;
			
			// instance java로 넘길 데이터 설정
			var edu_member = "";
			
			for (var i=0;i<grid_rslt.getRowCount();i++) {
				if (i == 0) {
					edu_member = grid_rslt.getCellData(i, "nm");
				} else {
					edu_member = edu_member+","+grid_rslt.getCellData(i, "nm");
				}
			}
			
			dma_info.set("row_del_flg", "");
			dma_info.set("add_flg", scwin.data.add_flg);
			dma_info.set("add_syspayno", hidd_addSyspayno.getValue());
			dma_info.set("add_dept_cd", hidd_addDeptCd.getValue());
			dma_info.set("add_dept_new_ymd", hidd_addDeptNewYmd.getValue());
			dma_info.set("add_posi_cd", hidd_addPosiCd.getValue());
			dma_info.set("add_duty_cd", hidd_addDutyCd.getValue());
			dma_info.set("add_job_cd", hidd_addJobCd.getValue());
			dma_info.set("add_educ_amt", ibx_addEducAmt.getValue());
			dma_info.set("add_start_ymd", ica_addStartYmd.getValue());
			dma_info.set("add_cls_ymd", ica_addClsYmd.getValue());
			dma_info.set("add_educ_time", ibx_addEducTime.getValue());
			dma_info.set("add_educ_contnt", txa_addEducContnt.getValue());
			dma_info.set("edu_member", edu_member);
			
			//WebSquare.ModelUtil.setInstanceValue("request/SAVEAPYTRAIN/ui_id/@value", serviceId);
			//WebSquare.ModelUtil.setInstanceValue("request/SAVEAPYTRAIN/endpoints/@value", uid);
			dma_info.set("save_typ", prcType);
			
//			console.log("해외 체크박스 value=="+cbx_overseaOpenYn.getValue());
//			if (cbx_overseaOpenYn.getValue() == "Y") {
//				var open_yn = "Y";
//			} else {
//				var open_yn = "N";
//			}
			
			if (sbx_educTyp.getValue() == "HDO200") {
				if (cbx_IsOnline.getValue() == "") {
					dma_info.set("is_online", "f");  //교육형태(온/오프라인)
				} else {
					dma_info.set("is_online", cbx_IsOnline.getValue()); //교육형태(온/오프라인)
				}
			} else {
				dma_info.set("is_online", "f");  //교육형태(온/오프라인)
			}
			
			console.log("attach_file!!");
			
			dma_info.set("attach_file_no", attachObj.upload_A);
			var attaches = [];
			if (attachObj.upload_A !="") {
				attaches.push(attachObj.upload_A);
			}
			console.log("attach_file!end!");
			dma_info.set("validAttachNo", attaches.join(","));		
			dma_info.set("regst_syspayno", com.getLoginInfo("sn"));
			dma_info.set("updt_syspayno", com.getLoginInfo("sn")); 
			
			
			
			if (scwin.data.reqNo == "") {
				dma_info.set("apr_state", com.getLoginInfo("XAD005")); 
			}
			console.log("prcType!!"+prcType);
			console.log("scwin.data.state!!"+scwin.data.state);
			
			if (prcType == "APR") {
				var educStartYmd = ica_startYmd.getValue().substr(0,4)+"-"+ica_startYmd.getValue().substr(4,2)+"-"+ica_startYmd.getValue().substr(6,2);
				var educClsYmd = ica_clsYmd.getValue().substr(0,4)+"-"+ica_clsYmd.getValue().substr(4,2)+"-"+ica_clsYmd.getValue().substr(6,2);
				var educYmd = educStartYmd + "~" + educClsYmd;
				
				dma_info.set("sh_nm", dma_info.get("nm"));
				dma_info.set("sh_posi_nm", dma_info.get("posi_nm"));
				dma_info.set("sh_dept_nm", dma_info.get("dept_nm"));
				dma_info.set("sh_educ_typ_nm", dma_info.get("educ_typ_nm"));
				dma_info.set("sh_educ_cd_nm", dma_info.get("educ_cd_nm"));
				dma_info.set("sh_educ_nm", dma_info.get("educ_nm"));
				dma_info.set("sh_educ_agncy", dma_info.get("educ_agncy"));
				dma_info.set("sh_educ_ymd", educYmd);
				dma_info.set("sh_educ_time", dma_info.get("educ_time"));
				dma_info.set("sh_educ_amt", dma_info.get("educ_amt"));
				dma_info.set("sh_accnt_no", dma_info.get("accnt_no"));
				dma_info.set("sh_expns_cd_nm", dma_info.get("expns_cd_nm"));
				dma_info.set("sh_expns_cd", dma_info.get("expns_cd"));
				dma_info.set("sh_educ_contnt", dma_info.get("educ_contnt"));
				dma_info.set("sh_is_online", dma_info.get("is_online"));
				dma_info.set("sh_oversea_open_yn", dma_info.get("oversea_open_yn"));
				
			} else {
				console.log("dksla!!"+dma_info.get("apr_state"));
				if (dma_info.get("apr_state") == "XAD015" || dma_info.get("apr_state") == "XAD013") {
					dma_info.set("apr_state", "XAD015");
					//WebSquare.ModelUtil.setInstanceValue("request/SAVEAPYTRAIN/apr_state/@value", "XAD015"); // 반려/회수일 일경우
				}
				console.log("dksla!!");
			}
			
			console.log("저장직전 == ");
			console.log("scwin.data.apr_yn == "+scwin.data.apr_yn);
			
			com.execute(sbm_saveHumApyTrain, {
				successCallback : function(e) { // 성공 콜백
					if(scwin.data.apr_yn != "Y"){
						com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
							scwin.search();
						});
					} else {
						//	결재신청 후에는 결재진행함으로 이동
						com.movePageMenu("A001", { "reqNo" :  dma_info.get("req_no")});	
					}
				} 
			});
		});
	};
	
