	scwin.btn_del_onclick = function(e) {
		if (!scwin.preDelete()) {
			return;
		}
		
		scwin.remove();
	};
	
	/**
	* 삭제 전 체크 실행
	*/
	scwin.preDelete = function() {
		scwin.data.msg = "";
		console.log("grid_rslt.getRowCount()==="+grid_rslt.getRowCount());
		if (grid_rslt.getRowCount() == "0") {
			scwin.data.msg = "신청서를 삭제 하시겠습니까?";

			scwin.data.delTyp = "apy";
		} else {
			var chkIdx = grid_rslt.getCheckedIndex("chk");
			console.log("chkIdx==="+chkIdx);
			if (chkIdx.length == 0) {
				com.alert("삭제할 참석자를 선택해 주세요");

				return false;
			}

			scwin.data.msg = "참석자 정보를 삭제하시겠습니까?";

			scwin.data.delTyp = "row";
		}

		
		return true;
	};
	
	/**
	* 삭제 실행
	*/
	scwin.remove = function() {
		var reEducAmt = 0;

		if (scwin.data.delTyp == "row") {
			reEducAmt = Number(ibx_educAmt.getValue()) - Number(ibx_addEducAmt.getValue());
		}
		
		console.log("reEducAmt====="+reEducAmt);
		
		dma_info.set("req_no", scwin.data.reqNo);
		dma_info.set("del_typ", scwin.data.delTyp);					// 삭제 타입
		dma_info.set("educ_amt", reEducAmt);			// 교육금액
		dma_info.set("updt_syspayno", com.getLoginInfo("sn"));	// 변경자

		var rowSyspayno = "";
		var chkIdx = grid_rslt.getCheckedIndex("chk");

		for (var i=0; i<chkIdx.length; i++) {
			if (i>0) {
				rowSyspayno += ",";
			}

			rowSyspayno += "'" + grid_rslt.getCellData(chkIdx[i], "syspayno") + "'";
		}
		
		console.log("rowSyspayno==="+rowSyspayno);
		dma_info.set("row_syspayno", rowSyspayno);			// 참석자 대상
		//WebSquare.ModelUtil.setInstanceValue("request/DELETEAPYTRAIN/row_syspayno/@value", rowSyspayno); // 참석자 대상

		//scwin.addGridClear(); // 삭제후 프리폼 클리어
		
		/**if (globalVar.delTyp == "apy") {
			deleteAprVec(uid, inputAprState.getValue(), globalVar.reqNo, sn, serviceId, "request/DELETEAPYTRAIN"); // 전자결재 삭제API 호출
		}**/
		com.confirm(scwin.data.msg, function(ret) {
			if (ret) {
				if(scwin.data.delTyp == "apy"){				
					com.file.deleteAll(function(){
							
					});		
				}
					com.execute(sbm_deleteHumApyTrain, {
						successCallback : function(e) { // 성공 콜백
							com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
								if(scwin.data.delTyp == "apy"){
									//com.openTabMenu("A010", {});
									scwin.backToList();
								} else {
									scwin.search();
								}
							});
						}
					});
				
			}
		//gfnExecuteSubmission("removeApyTrain");
		});	
	};
	
