	/**
	* 참석자 정보 교육비 포커스 이벤트
	*/
	scwin.ibx_addEducAmt_onblur = function(e) {
		var tempEducAmt = 0;
		var tempAmt = 0;
		var tempGridAmt = 0;

		if (grid_rslt.getRowCount() == 0) {
			tempEducAmt = (ibx_addEducAmt.getValue() == "") ? 0 : parseInt(ibx_addEducAmt.getValue());

			ibx_educAmt.setValue(tempEducAmt);

			return;
		} else {
			for (var i = 0; i < grid_rslt.getRowCount(); i++) {
				tempEducAmt = (grid_rslt.getCellData(i, "educ_amt") == "") ? 0 : parseInt(grid_rslt.getCellData(i, "educ_amt"));
				tempGridAmt += tempEducAmt;
			}

			if (scwin.data.add_flg == "ADD") {
				tempEducAmt = (ibx_addEducAmt.getValue() == "") ? "0" : parseInt(ibx_addEducAmt.getValue());
				tempAmt = parseInt(tempEducAmt);
				tempEducAmt = tempAmt + tempGridAmt;
			} else {
				scwin.data.addEduAmt = (scwin.data.addEduAmt == "") ? 0 : parseInt(scwin.data.addEduAmt);
				tempEducAmt = tempGridAmt - scwin.data.addEduAmt + parseInt(ibx_addEducAmt.getValue());
			}
		}

		ibx_educAmt.setValue(tempEducAmt.toString());
	};
	
	/**
	* 참석자 정보 교육시간 포커스 이벤트
