		for (var i=0; i<rowCnt; i++) {
			dlt_rslt.setCellData(i, "req_no", tbx_freqNo.getValue());
			dlt_rslt.setCellData(i,"regst_syspayno", com.getLoginInfo("sn"));
			dlt_rslt.setCellData(i,"updt_syspayno", com.getLoginInfo("sn"));
			
			//WebSquare.ModelUtil.setInstanceValue("request/SAVEEDUCPACP/SAVE/vector/data/msgHumApyEduCpacp01[@id="+i+"]/req_no/@value", outputFreqNo.getValue());
			//WebSquare.ModelUtil.setInstanceValue("request/SAVEEDUCPACP/SAVE/vector/data/msgHumApyEduCpacp01[@id="+i+"]/regst_syspayno/@value", sn);
			//WebSquare.ModelUtil.setInstanceValue("request/SAVEEDUCPACP/SAVE/vector/data/msgHumApyEduCpacp01[@id="+i+"]/updt_syspayno/@value", sn);
		}

