head.script:
 	/****************************************************
	 * 화면 설명 : 지도교수(활용책임자)변경신청조회(목록)
	 * 변경 이력 : 2023-08-14 swt 최초생성
	 * serviceId : H579
	 ****************************************************/
	scwin.data={};
    /*************************************************************************************************************
     *
     *	페이지 이벤트 함수
     *
     *************************************************************************************************************/
	
    /**
     * 페이지 열기
     */
	scwin.onpageload = function() {
		
		com.biz.checkRole({ serviceId : "H579", roleCodes : com.getLoginInfo('roleCode')}, function(e){
			scwin.data.isRoleYn = e.isRoleYn;
		});
		
		var radioSelect_x = com.getParameter("param5");					//신청서양식함에서 받아온 값
		var radioSelect_u = com.getParameter("selectRadio");			//목록 radio에서 넘어오는 값
		var radioSelect_s = com.getParameter("req_no").substr(0, 3);	//목록에서 넘어온 값

		//기존 분기처리
		if (radioSelect_x!="") {
			scwin.data.radioChk = radioSelect_x;
		} else if (radioSelect_u!="") {
			scwin.data.radioChk = radioSelect_u;
		} else {
			scwin.data.radioChk = radioSelect_s;
		}
		
		//선택화면에 따른 화면 제목 세팅
		if (scwin.data.radioChk == "H3A") {
			com.getParameter().menuObj.cn = '지도교수 변경신청서';
		} else if (scwin.data.radioChk == "H3B") {
			com.getParameter().menuObj.cn = '활용책임자 변경신청서';
		}
		
		//첨부파일 초기화  *필수*
		com.file.init();

		// 엔터키 이벤트
		com.setEnterKeyEvent(grp_rspns, scwin.btn_hum_search_onclick);
		
		//숨김처리
		tb_empno.hide();			//신청자 사번
		tb_nm.hide();				//신청자 이름
		inp_tar_syspayno.hide();	//변경자 개인번호
		btn_prt_2.hide();			//반려,처리 버튼 숨김 (그룹영역)
		
		//상세조회 구분 (목록 통한 상세조회 or 신규 구분 -> 신청번호 유무로 확인)
		var req_no = com.getParameter("req_no");
		
		//if : 신규작성(신청번호가 없는경우)
		if (com.isEmpty(req_no)) {
			
			com.biz.disabledButtons();
			
			//첨부파일 셋팅
			com.file.create(upload_A.getID(), { fileKey : "H579", mode : 'edit' });
			
			//변경자 인사조회팝업 활성화
			inp_empno.setDisabled(false);
			btn_hum_search.setDisabled(false);
			
			//기본정보 세팅
			tb_empno.setValue(com.getLoginInfo("empno"));		//개인번호
			tb_nm.setValue(com.getLoginInfo("cn"));				//이름
			tb_dept_nm.setValue(com.getLoginInfo("deptName"));	//부서명
			tb_posi_nm.setValue(com.getLoginInfo("posiName"));	//직급
			cal_req_ymd.setValue(com.getServerDateTime());		//신청일

			//사번(성명) 형식
			tb_nm_empno.setValue(com.getLoginInfo("empno") + "(" + com.getLoginInfo("cn") +")");

			//기본 화면단 세팅
			if (scwin.data.radioChk == "H3A") {
				scwin.viewSetting("H3A");
			} else if (scwin.data.radioChk == "H3B") {
				scwin.viewSetting("H3B");
			}
			
			
		//else if : 지도교수 화면 세팅
		} else if (scwin.data.radioChk == "H3A") {
		
			//기본 화면단 세팅	
			scwin.viewSetting("H3A");
			//상세 조회
			scwin.search(req_no);


		//else if : 활용책임자 화면 세팅 
		} else if (scwin.data.radioChk == "H3B") {

			//기본 화면단 세팅	
			scwin.viewSetting("H3B");
			//상세 조회
			scwin.search(req_no);
			
		}
		
		
	};
	
    /**
     * 2가지화면의 화면단 세팅정보
     */
	scwin.viewSetting = function(req_type) {

		//if : 지도교수
		if (req_type == "H3A") {
			
			//grd의 안내말 변경
			tbx_grd_1.setValue("변경전 지도교수");
			tbx_grd_2.setValue("변경전 지도교수");
			//grid 헤더 변경
			grd_list_1.setHeaderValue("column37", "지도교수구분", "")
			grd_list_2.setHeaderValue("column27", "지도교수구분", "")
			grd_list_2.setHeaderValue("column37", "등록/해지", "")

			//grid1의 selectBox 값 설정
			grd_list_1.setColumnNodeSet("respn_clsf", "data:dlt_H3A", "label", "value");
			//grid2의 selectBox 값 설정
			grd_list_2.setColumnNodeSet("respn_clsf", "data:dlt_H3A", "label", "value");
			
		//else if : 활용책임자
		} else if (req_type == "H3B") {
			
			//안내문구 숨김처리
			tbx_notice_1.hide();
			tbx_notice_2.hide();

			//grid1의 selectBox 값 설정
			grd_list_1.setColumnNodeSet("respn_clsf", "data:dlt_H3B", "label", "value");
			//grid2의 selectBox 값 설정
			grd_list_2.setColumnNodeSet("respn_clsf", "data:dlt_H3B", "label", "value");
		}
	};	
	
    /**
     * 페이지 닫기
     */
	scwin.onpageunload = function() {
		
	};
	
    /**
     * 상세 조회 (팝업에서 사원 선택시(신규신청) or 신청번호가 있을 경우(상세조회))
     */	
	scwin.search = function(req_no) {
		
		//조회 조건 세팅
		dma_search.set("req_no", req_no);
		
		//if : req_no 없는 경우
		if(req_no == "") {
		
			//상세조회 -> 팝업으로 변경전,후 데이터 가져오는 경우
			com.execute(sbm_outHumApyPrs, {
				successCallback : function(e) {
	
					//데이터 세팅
					dlt_list_1.setJSON(e.responseJSON.humApyPrsInfoVoList);
					grd_list_1.setFocusedCell(0,0);
					
					dlt_list_2.setJSON(e.responseJSON.humApyPrsInfoVoList);
					grd_list_2.setFocusedCell(0,0);
					
				}
			});
		
		//else : req_no 있는 경우  
		} else {

			//변경자 인사조회팝업 비활성화. (저장후 재조회시 비활성화 하기 위함)
			inp_empno.setDisabled(true);
			btn_hum_search.setDisabled(true);

			//상세 조회 1 -> 신청번호로 조회하는 경우
			com.execute(sbm_outHumApyDtl, {
				successCallback : function(e) {
	
					//신청정보 : 데이터 세팅
					dma_dtl.setJSON(e.responseJSON.elData);
					
					//사번(성명) 형식
					tb_nm_empno.setValue(tb_empno.getValue() + "(" + tb_nm.getValue() +")");
					
					//상세 조회 2 -> 변경전 지도교수(활용책임자)
					com.execute(sbm_outHumApyPrsBefore, {
						successCallback : function(e) {
			
							//변경전 grid : 데이터 세팅
							dlt_list_1.setJSON(e.responseJSON.humApyPrsInfoVoList);
							grd_list_1.setFocusedCell(0,0);
							
							//상세 조회 3 -> 변경후 지도교수(활용책임자)
							com.execute(sbm_outHumApyPrsAfter, {
								successCallback : function(e) {
									
									//변경후 gird : 데이터 세팅
									dlt_list_2.setJSON(e.responseJSON.humApyPrsInfoVoList);
									grd_list_2.setFocusedCell(0,0);
									
									//변경후 지도교수(활용책임자) -> 퇴직자일경우 자동해지로 변경
									for (var i = 0; i < grd_list_2.getTotalRow(); i++) {
										if (dlt_list_2.getCellData(i, "use_yn") == "N") {
											grd_list_2.setCellReadOnly(i, "use_yn", true);	////"N"인 경우 해당 cell 비활성화
										}	
									}

									//결재상태코드, 신청번호 보내서 버튼 컨트롤
									com.biz.disabledButtons(dma_dtl.get("apr_state"), req_no);
									
									// (XAD015)반려, (XAD010)작성중, (XAD005)저장중이 아닐시 결재버튼
									var aprStateNm = dma_dtl.get("apr_state_nm"); 
				   					if (aprStateNm == "반려" || aprStateNm == "작성중" || aprStateNm == "저장중") {
										btn_state.setDisabled(true);	//결재상태 버튼
									} else {
										btn_state.setDisabled(false);	//결재상태 버튼
									}
									
									//관리자인 경우 && 결재상태 "담당자 확인중"인 경우 버튼 처리
									if(scwin.data.isRoleYn && dma_dtl.get("apr_state") == "XAD070") {
										btn_prt_2.show();	//반려, 처리 버튼 보임 (그룹영역)
										btn_prt_1.hide();	//삭제, 저장 버튼 숨김 (그룹영역)
									}
									
									//첨부파일 조회
									var vMode = "edit";		//첨부파일 수정모드
									var aprChk = false;		
									if (dma_dtl.get("apr_state") >= "XAD020") {
										//비활성화
										vMode = "view";							//첨부파일 보기모드
										aprChk = true;		//버튼 비활성화 값 세팅
									}	
									
									//첨부파일 조회 관련
									var attachFileNo = dma_dtl.get("attach_file_no");
									com.file.create(upload_A.getID(), {fileKey: com.isNotEmpty(attachFileNo) ? attachFileNo : 'H579', mode: vMode});
									//입력창 활성화, 비활성화
									inp_rmk.setReadOnly(aprChk);		//변경사유
									grd_list_2.setReadOnly(aprChk);		//변경후 활용책임자
									trggier_add.setDisabled(aprChk);	//변경후 학력사항 그리드 내 "+" 버튼
									trggier_del.setDisabled(aprChk);	//변경후 학력사항 그리드 내 "-" 버튼
									
								}
							});
							
						}
					});					
					
				}
			});
		
		}
	
	};
	
    /**
     * 목록 버튼 클릭 이벤트
     */	
	scwin.btn_list_onclick = function(e) {
		scwin.backToList();
	};
	
	/**	
	 * 목록 화면 이동
	 */
	scwin.backToList = function(data) {
		com.moveList("H570", data);
	};

	/**	
	 * 변경자 사용자검색 클릭 이벤트
	 */
	scwin.btn_hum_search_onclick = function(e) {
		
		var data = { empno : inp_empno.getValue(), work_clsf : "HAG010" }
		com.pop.open( com.pop.TYPE.HUM, data, function(ret){
			
			inp_empno.setValue(ret.empno);				//변경자 사원번호
			inp_nm.setValue(ret.nm);					//변경자 성명
			inp_dept_nm.setValue(ret.dept_nm);			//변경자 부서명
			inp_posi_nm.setValue(ret.posi_nm);			//변경자 직급
			inp_tar_syspayno.setValue(ret.syspayno);	//변경자 시스템 개인번호
			
			dma_search.set("syspayno", ret.syspayno);
			
			//변경전 후 지도교수(활용책임자) 조회
			scwin.search("");
			
		});
		
	};
	
    /**
     * 삭제 버튼 클릭 이벤트
     */	
	scwin.btn_del_onclick = function(e) {
		
		com.confirm("message.xom.wq.022"/*삭제 하시겠습니까?*/, function( ret) {
			if (ret) {
				com.file.deleteAll( function(){
					
					var req_no = com.getParameter("req_no");
					dma_search.set("req_no", req_no);
				
					com.execute(sbm_deleteHumApy, {
						successCallback : function(e) { // 성공 콜백
							com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
								//삭제 후 목록창 이동
								com.moveList("H570", {mod :"delete"});
							});
						}
					});
				});	
			}
		});

	};
	
    /**
     * grid2 내 "+" 버튼 클릭 이벤트
     */	
	scwin.trggier_add_onclick = function(e) {
		
		//지도교수
		if (scwin.data.radioChk == "H3A") {
			
			//새로 행 추가한거에 기본 데이터 세팅
			com.addRow(grd_list_2);
			
			dlt_list_2.setCellData(grd_list_2.getTotalRow() - 1, "empno", "");
			grd_list_2.setCellReadOnly(grd_list_2.getTotalRow() - 1, "empno", false);	//개인번호 입력란 활성화
			
			dlt_list_2.setCellData(grd_list_2.getTotalRow() - 1, "data_clsf", "N");
			dlt_list_2.setCellData(grd_list_2.getTotalRow() - 1, "use_yn", "Y");
			dlt_list_2.setCellData(grd_list_2.getTotalRow() - 1, "respn_clsf", 1);
			
		//활용책임자
		} else if (scwin.data.radioChk == "H3B") {
			
			//새로 행 추가한거에 기본 데이터 세팅
			com.addRow(grd_list_2);
			
			dlt_list_2.setCellData(grd_list_2.getTotalRow() - 1, "empno", "");
			grd_list_2.setCellReadOnly(grd_list_2.getTotalRow() - 1, "empno", false);	//개인번호 입력란 활성화
			
			dlt_list_2.setCellData(grd_list_2.getTotalRow() - 1, "data_clsf", "N");
			dlt_list_2.setCellData(grd_list_2.getTotalRow() - 1, "use_yn", "Y");
			dlt_list_2.setCellData(grd_list_2.getTotalRow() - 1, "respn_clsf", 1);

		}
		
	};
	
    /**
     * grid2 내 "-" 버튼 클릭 이벤트
     */	
	scwin.trggier_del_onclick = function(e) {
		
		var rowIdx = grd_list_2.getCheckedIndex("chk");
		var deleteCnt = 0;
		
		if(rowIdx.length == 0){
			com.alert(com.getMsg("message.xom.co.com.0003", "삭제할 데이터"));
			return;
		}

		for(var i = 0; i < grd_list_2.getTotalRow(); i++) {
		
			//체크가 된 정보
			if (dlt_list_2.getCellData(i, "chk") == "Y" ) {
			
				//"data_clsf"는 "데이터 구분"
				if (dlt_list_2.getCellData( i , "data_clsf" ) != "N") {

					com.alert(dlt_list_2.getCellData( i , "nm" )+"는 삭제가 불가능 합니다. \n 신규 추가 인원만 삭제 가능 합니다.");
					return;
				}

				//기존에 있던게 아닌 경우 deleteCnt = 1 설정하여 DB바로 삭제				
				if (dlt_list_2.getRowStatus( i ) != "C") {
					deleteCnt = 1;
					
				//그 외의 경우 단순 행 삭제
				} else {
					//삭제 실행
					rowIdx.reverse().forEach(function(rowIndex){
						dlt_list_2.removeRow(rowIndex);
					});
				}
				
			}
			
    	}

	    if (deleteCnt != 0) {		
			com.confirm("삭제하시겠습니까?\nDB에 바로 삭제됩니다.", function( ret) {
				if (ret) {
					com.execute(sbm_deleteData, {
						successCallback : function(e) { // 성공 콜백
							com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
								
							});
						}
					});
				}
			});
		}

	};
	
    /**
     * 변경후 지도교수(활용책임자) 행
     */
	scwin.grd_list_2_onafteredit = function(row,col,value) {
		
		//"1" -> 개인번호
		if (col == "1") {
			
			//값 입력한 경우 -> alert + 개인정보 조회해서 띄우기
			if (value.length > 0) {
				com.alert("지도교수(연수책임자) 자격\n- 연구원 근무년수 3년 이상이며, 선임급 이상인 직원\n(단, 학연협동과정 연수책임자의 경우 연구원 근무년수 1년 이상, 선임급 이상인 직원)\n"
						+ "※ 관련규정 : 타업종사 운영지침", function() {
					//alert 팝업 닫으면 -> 검색한이름 팝업통해서 상세정보 가져오기
					scwin.openUserInfo(row, col, value);
				});
			}
			
			//빈값 입력한 경우 -> 빈값 세팅
			if (value.trim() == "") {
				
				dlt_list_2.setCellData(row, "empno", "");
				dlt_list_2.setCellData(row, "nm", "");
				dlt_list_2.setCellData(row, "dept_nm", "");
				dlt_list_2.setCellData(row, "dept_cd", "");
				dlt_list_2.setCellData(row, "dept_new_ymd", "");
				dlt_list_2.setCellData(row, "posi_nm", "");
				dlt_list_2.setCellData(row, "pruse_respn_psn", "");
			}
					
		}

	};
	
    /**
     * grid_2의 개인번호 이미지 클릭 시 작동
     */
	scwin.openUserInfo = function(row,col,value) {
		
		//신규행이 아닌경우 return
		if (dlt_list_2.getRowStatus( row ) != "C") {
			return;
		}
			
		var data = { empno : value, work_clsf : "HAG010" }
		com.pop.open( com.pop.TYPE.HUM, data, function(ret){
			
			dlt_list_2.setCellData(row, "empno", ret.empno);
			dlt_list_2.setCellData(row, "nm", ret.nm);
			dlt_list_2.setCellData(row, "dept_nm", ret.dept_nm);
			dlt_list_2.setCellData(row, "dept_cd", ret.dept_cd);
			dlt_list_2.setCellData(row, "dept_new_ymd", ret.dept_new_ymd);
			dlt_list_2.setCellData(row, "posi_nm", ret.posi_nm);
			dlt_list_2.setCellData(row, "pruse_respn_psn", ret.syspayno);
			
		});
		
	};

    /**
     * 결재신청 버튼 클릭 이벤트
     */	
	scwin.btn_apr_onclick = function(e) {
		
		if (grd_list_1.getRowCount() == 0) {
			alert("변경전 지도교수(활용책임자) 내역이 없으면 결재신청 하실수 없습니다.");
			return;
		} else {
			
			//필수값체크 실행
			if (!scwin.preSave()) {
				return false;
			}
					
			//결재신청 로직
			com.confirm("결재신청 하시겠습니까?", function(ret){
				if (ret) {
					scwin.data.status = "apr";
					scwin.save(scwin.data.status);
				}
			});
			
		}
		
	};
	
    /**
     * 저장 버튼 클릭 이벤트
     */	
	scwin.btn_save_onclick = function(e) {
		
		//필수값체크 실행
		if (!scwin.preSave()) {
			return false;
		}
		
		com.confirm("message.xom.wq.017"/*저장 하시겠습니까?*/, function(ret) {
			if (ret) {
				scwin.data.status = "save";
				//저장로직 실행
				scwin.save(scwin.data.status);
			}
		});
		
		
	};
	
    /**
     * 저장, 결재신청 전 필수값체크
     */
	scwin.preSave = function() {
		
		//필수체크
		if (!com.validateData(dma_dtl, [{ id : "tar_empno", mandatory : true}
									  , { id : "tar_nm", mandatory : true}
									  , { id : "rmk", mandatory : true}
										], false)) return;	//물리명 변경
		
		//첨부파일 필수체크
		if(com.file.action.getTotalFileCount(upload_A.getID()) < 1){
			com.warn("첨부파일을 첨부하시기 바랍니다.");
			return;
		}
		
		var saveChk=0;		//지도교수 구분
		var saveChk2=0;		//등록/해지 구분
		scwin.data.pruseRespnPsnList = "";
		
		//변경후 지도교수(활용책임자) 행 개수만큼 반복
		var cnt = grd_list_2.getTotalRow();
		for (var row = 0; row < cnt; row++) {
			
			var gPruseRespnPsn	= dlt_list_2.getCellData(row, "pruse_respn_psn");
			var gEmpno 			= dlt_list_2.getCellData(row, "empno" );
			var gRespnClsf 		= dlt_list_2.getCellData(row, "respn_clsf" ); 		//지도교수구분
			var gdataClsf		= dlt_list_2.getCellData(row, "data_clsf" );
			var gUseYn			= dlt_list_2.getCellData(row, "use_yn" ); 			//등록해지
			var workClsf		= dlt_list_2.getCellData(row, "work_clsf" ); 		//근무상태
			
			if (gPruseRespnPsn == "") {
				com.alert("검색후 저장하세요");
				grd_list_2.setFocusedCell(row, "empno");
				return false;
			}
			
			if (gEmpno == "") {
				com.alert("개인번호를 입력하여 주시기 바랍니다.");
				grd_list_2.setFocusedCell(row, "empno");
				return false;
			}
			
			// 등록해지
			if (gUseYn == "") {
				com.alert("등록/해지를 선택하여 주세요.");
				return false;
			}
			
			// 활용책임자/지도교수
			if (grd_list_2.getHeaderValue("column27") == "활용책임자구분") {
				
				if (gRespnClsf == "") {
					com.alert("활용책임자구분을 선택하여 주세요.");
					return false;			
				}
				
			} else if (grd_list_2.getHeaderValue("column27") == "지도교수구분") {
			
				if (gRespnClsf == "") {
					com.alert("지도교수구분을 선택하여 주세요.");
					return false;			
				}
			
			}
			
			//"등록"인 경우	
			if (gUseYn == "Y") {
			    saveChk2++; // 등록/해지 구분

				if (gRespnClsf == 1) {
					saveChk++; // 지도교수구분
				}
			}

			// respn_clsf : M 지도교수, S 공동지도교수
			// data_clsf : U 수정, N 추가, O 신규
			if (gdataClsf == "") {
				dlt_list_2.modifyRowStatus(row, "C")
				dlt_list_2.setCellData(row, "data_clsf", "U" );
			}
			
			//결재라인 구성 교수리스트
			if(workClsf != "HAG090") { // 퇴직자는 결재라인 형성하지 않음. 
				if (row == 0 ) {
					scwin.data.pruseRespnPsnList = gPruseRespnPsn;
				} else {
					scwin.data.pruseRespnPsnList = scwin.data.pruseRespnPsnList + ";" + gPruseRespnPsn;
				}
			}
			
		}
		
		//지도교수변경신청일 경우
		if (scwin.data.radioChk == "H3A") {
			
			if (saveChk == 0) { // 지도교수구분
				com.alert("지도교수 한 명을 선택하세요.");
				return false;
			}
		 
			if (saveChk > 1) { // 지도교수구분
				com.alert("지도교수는 한 명 이상일 수 없습니다.");
				return false;
			}
		
			if (saveChk2 == 0) { // saveChk2(등록/해지) 구분
				com.alert("지도교수 등록 및 해지를 선택하세요.");
				return false;
			}
		
			if (inp_posi_nm.getValue() != "학생연구원" && inp_posi_nm.getValue() != "연구생" && inp_posi_nm.getValue() != "연수생") {
		    	if (saveChk2 > 1 && saveChk > 1) {
		     		com.alert("지도교수 등록은 한 명 이상일 수 없습니다.");
		     		return false;
		    	}
			}
			
		//활용책임자변경신청일 경우
		} else if (scwin.data.radioChk == "H3B") { 
			
			if (saveChk == 0) { // 활용책임자구분
				com.alert("활용책임자 한 명을 선택하세요.");
				return false;
			}
		
			if (saveChk > 1) {
				com.alert("활용책임자는 한 명 이상일 수 없습니다.");
				return false;
			}
			
			if (saveChk2 == 0) { // saveChk2(등록/해지) 구분
				com.alert("활용책임자 등록 및 해지를 선택하세요.");
				return false;
			}
			
		}
		
		return true;
	};
		
    /**
     * 저장, 결재신청 로직
     */	
	scwin.save = function(e) {
		
		//저장, 수정
		if(e == "save"){
		
			com.file.transport( function( attachObj){
				//저장정보 추가세팅
				dma_dtl.set("req_typ"			, scwin.data.radioChk);				//기본신청유형(문서종류)
				dma_dtl.set("syspayno"			, com.getLoginInfo("sn"));			//시스템개인번호
				dma_dtl.set("dept_cd"			, com.getLoginInfo("deptCode"));	//부서코드
				dma_dtl.set("dept_new_ymd"		, com.getLoginInfo("deptNewYmd"));	//부서신설일
				dma_dtl.set("tar_syspayno"		, inp_tar_syspayno.getValue());		//변경자 시스템개인번호
				dma_dtl.set("attach_file_no"	, attachObj.upload_A);				//첨부파일번호
				
				com.execute(sbm_saveData, {
					successCallback : function(e) { // 성공 콜백					
						com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
						
							//팝업에서 변경자 선택시 값이 세팅되어있어서 빈값으로 재설정
							dma_search.set("syspayno", "");
							//리턴받은 신청번호로 상세조회
							scwin.search(e.responseJSON.elData.req_no);
	
						});										
					}
				});
			});
		
		}
		
		//결재신청
		if(e == "apr"){
			
			var app_type_cd=0;
			
			com.file.transport( function( attachObj){
			
				// 20180912 김지현 요청으로 근로연수생 조건 포함
				if (inp_posi_nm.getValue() == "학생연구원"
				 || inp_posi_nm.getValue() == "연구생" 
				 || inp_posi_nm.getValue() == "연수생" 
				 || inp_posi_nm.getValue() == "근로연수생" 
				 || inp_posi_nm.getValue() == "학연협동과정생(근로)"
				 || inp_posi_nm.getValue() == "UST학생(근로)"
				 || inp_posi_nm.getValue() == "현장실습생") {
							app_type_cd=1;
				}
				
				//저장정보 추가세팅
				dma_dtl.set("approval"			, true); 	//결재여부
				
				dma_dtl.set("save_type"			, "apr");		//저장타입
				dma_dtl.set("apr_state"			, "XAD010");	//신청상태
				dma_dtl.set("app_type_cd"		, app_type_cd);
				dma_dtl.set("manager_id"  		, scwin.data.pruseRespnPsnList);	//교수리스트
				dma_dtl.set("info_cd"  			, inp_nm.getValue());				//변경자 이름
				
				dma_dtl.set("req_typ"			, scwin.data.radioChk);				//기본신청유형(문서종류)
				dma_dtl.set("syspayno"			, com.getLoginInfo("sn"));			//시스템개인번호
				dma_dtl.set("dept_cd"			, com.getLoginInfo("deptCode"));	//부서코드
				dma_dtl.set("dept_new_ymd"		, com.getLoginInfo("deptNewYmd"));	//부서신설일
				dma_dtl.set("tar_syspayno"		, inp_tar_syspayno.getValue());		//변경자 시스템개인번호
				dma_dtl.set("attach_file_no"	, attachObj.upload_A);				//첨부파일번호
				
				//"dlt_list_1"과 "dlt_list_2"에 "respn_clsf_nm"값을 설정
				for(var i = 0; i < grd_list_1.getTotalRow(); i++) {
					dlt_list_1.setCellData(i, "respn_clsf_nm", grd_list_1.getCellDisplayData(i, "respn_clsf"));
				}
				
				for(var i = 0; i < grd_list_2.getTotalRow(); i++) {
					dlt_list_2.setCellData(i, "respn_clsf_nm", grd_list_2.getCellDisplayData(i, "respn_clsf"));
				}				
				
				com.execute(sbm_saveData, {
					successCallback : function(e) { // 성공 콜백					
						if(dma_dtl.get("approval")){
							// 결재화면 이동
							com.movePageMenu("A001", { "reqNo" :  dma_dtl.get("req_no")});
						}else{
							com.alert("message.xom.wq.023" /*정상적으로 처리되었습니다.*/, function() {	
								//팝업에서 변경자 선택시 값이 세팅되어있어서 빈값으로 재설정
								dma_search.set("syspayno", "");
								//리턴받은 신청번호로 상세조회
								scwin.search(e.responseJSON.elData.req_no);
							});	
						}
					}
				});
				
			});
			
		}
		
	};
	
    /**
     * 반려 버튼 클릭 이벤트
     */	
	scwin.btn_reject_onclick = function(e) {
		
		if(!scwin.data.isRoleYn){
			com.alert("관리자외에는 반려를 하실 수 없습니다.");
			return;
		}
		
		var options = {
		    id: "APR02P",
		    popupName: "반려의견",
		    width: 500,
		    height: 300
		};
		
		com.openPopup("/ui/apr/APR_C001_03P.xml", options, "", function(retObj) {
		    if (retObj.comments != null) {
		        dma_dtl.set("comments", retObj.comments);
		        com.execute(sbm_aprReject, {
		            successCallback: function(e) {
		                com.alert("반려처리 되었습니다");
		                com.openTabMenu("A004", {});
		            }
		        });
		    }
		});

	};

    /**
     * 처리 버튼 클릭 이벤트
     */		
	scwin.btn_confirm_onclick = function(e) {
	
		if(!scwin.data.isRoleYn){
			com.alert("관리자외에는 처리를 하실 수 없습니다.");
			return;
		}

		com.confirm("승인처리 하시겠습니까?", function(ret) { 		
			if(ret) {
				com.execute(sbm_aprAgr, {
					successCallback : function(e) {
						com.alert("승인처리 되었습니다");
						com.openTabMenu("A004", {});
					}
				});
			}
		});
		
	};
	

    /**
     * 결재상태 버튼 클릭 이벤트
     */		
	scwin.btn_state_onclick = function(e) {
	
		var reqNo = dma_dtl.get("req_no");
		var aprNm = dma_dtl.get("apr_state_nm");
		
		if(reqNo != "" && aprNm != "저장중" || aprNm != "작성중"){
			var url = "/ui/apr/layoutSimple.xml";
			var option = { type: "browserPopup", width: 1350, height: 1480, id: "snapshotPop", popupName: "결재문서"};
			var data = { reqNo: reqNo, viewMode : "read", instStatus : (dma_dtl.get("apr_state") != "XAD100" ? "Running" : "Completed") } // instStatus" : "Completed",
			com.openPopup(url, option, data);
			
		}else{
			com.alert("결재 후 열람이 가능합니다.");
		}
		
	};
