head.script:
    /****************************************************
 	 * 화면 설명 : 퇴직금중간정산신청
 	 * 변경 이력 : 2023-08-28 김상미	최초 작성
 	 * 서비스아이디 : H584
 	 ****************************************************/ 
 	         
 	scwin.data ={};
 	
 	 /**
	 * 페이지 로딩 이벤트
	 */  	          
	scwin.onpageload = function() {
		var param = com.getParameter();	
		scwin.data.service_id="H584";
		scwin.data.doc_id="H15";
		scwin.data.flag = false; //사용자정보 또는 신청정보가 화면에 세팅된 후에 컴포넌트 이벤트 처리가 적용되도록 하기위한 flag
		scwin.data.save = true;
		scwin.data.today = com.getServerDateTime();
		
		grd_reject.hide();	
		btn_confirm.hide();
		btn_reject.hide();		
		com.biz.disabledButtons(dma_info.get("apr_state"), dma_info.get("req_no"));
		grp_conct.hide();
		scwin.initPage(param);	
		
		com.setValidGroup(grp_base_info, [
			 { id : "ica_req_ymd", mandatory : true}
			,{ id : "sbx_bank_accnt_typ", mandatory : true } 
			,{ id : "sbx_middle_adjst_typ", mandatory : true} 	
			,{ id : "ica_conct_ymd", mandatory : true} 
		]);			
	};

	scwin.onpageunload = function() {
		
	};
	
	/**
	* 화면 초기 세팅
	*/
	scwin.initPage = function(param) {
		// 공통코드
		var codeOptions = [ { code : "HMR", compID : "sbx_middle_adjst_typ"} //중간정산사유
		                  , { code : "HSA", compID : "sbx_bank_accnt_typ"} //계좌유형
		               ];		
		com.setCmnCd(codeOptions, function() {
		});

		//관리자권한 확인
		com.biz.checkRole({ serviceId : scwin.data.service_id, roleCodes : com.getLoginInfo('roleCode')}, function(e) {
			scwin.data.isRoleYn = e.isRoleYn;
		});	
			
		// 첨부파일 컴포넌트 초기화
		com.file.init();
		// 첨부파일
		com.file.create(upload_A.getID(), {fileKey: scwin.data.service_id, mode: 'edit'});
	
		var clsCntfrYmdVal = (Number(scwin.data.today.substring(0,4)) -1) + "1231";
		ica_cls_cntfr_ymd.setValue(clsCntfrYmdVal);
		
		//넘어온 파라메터가 없으면 신규 처리
		if (param.req_no == undefined) {
			dma_search.set("syspayno", com.getLoginInfo("sn"));
			dma_info.set("empno", com.getLoginInfo("empno"));
			scwin.searchRetireAppro();
		} else {
			// parameter 설정
			dma_search.setJSON(com.getParameter());	
			scwin.search();	
		}	
	}

	/**
	 * 퇴직충당금 존재여부 조회
	 */		
	scwin.searchRetireAppro = function() {
		com.execute(sbm_selectRetireAppro, {
			successCallback : function(e) {
				console.log("사용자 정보 조회 결과 ===> \n", dma_result.getJSON());
				if (dma_result.get("sci_tech_anuty") != "N" || parseInt(dma_result.get("wrk_days")) < 365) {
					com.alert("퇴직금중간신청 대상자가 아닙니다. 확인바랍니다.");
					scwin.data.save = false;
				}
				scwin.search();	
			}
		});		
	}

	/**
	 * 조회 실행
	 */	
	scwin.search = function() {
		if (dma_search.get("req_no") !="") {
			com.execute(sbm_selectHumApyMidSvr, {
				successCallback : function(e) {
				 	scwin.data.flag=true;
					dma_info.setJSON(e.responseJSON.elData.humApyMidSvrInfoVo);

					if (dma_info.get("apr_state")=="XAD015") {
						dlt_reject.setJSON(e.responseJSON.elData.docList, false);
						if (dlt_reject.getTotalRow() > 0) {
							grd_reject.show();
						}						
					}
					if (dma_info.get("apr_state") < "XAD070") {
						dma_info.set("req_ymd", scwin.data.today);
					}
					//첨부파일 세팅
					var vMode = "edit";
					if (dma_info.get("apr_state") >= "XAD020") {
						vMode = "view";
					}				
					var attachFileNo = dma_info.get("attach_file_no").trim();
					com.file.create(upload_A.getID(), {fileKey: (attachFileNo == "") ? scwin.data.service_id :attachFileNo, mode: vMode});
					//급여기본정보를 조회하여 퇴직충당금정보 세팅
					scwin.searchRetireApproPre();				
				}
			});		
		} else {
			com.execute(sbm_selectEmpInfo, {
				successCallback : function(e) {
					scwin.data.flag=true;
					dma_info.setJSON(e.responseJSON.elData);
					
					//달력세팅
					dma_info.set("req_ymd",scwin.data.today);
				    //급여기본정보를 조회하여 퇴직충당금정보 세팅
					scwin.searchRetireApproPre();
				}
			});					
		}
	}	

	/**
	 * 퇴직충당금 조회
	 */	
	 scwin.searchRetireApproPre = function() {
 		var reqBaseYmdPre = dma_info.get("retire_hope_ymd").substring(0,6);
		var b_reqBaseYy = (Number(scwin.data.today.substring(0,4)) -2) +"12";
		
		if (sbx_middle_adjst_typ.getValue()=="HMR010") {
			reqBaseYmdPre = (Number(scwin.data.today.substring(0, 4)) - 1) + "1231";
		}
		dma_search.set("syspayno", dma_info.get("syspayno"));
		dma_search.set("belng_yrmon", reqBaseYmdPre);
		dma_search.set("b_belng_yy", b_reqBaseYy);
				 
		com.execute(sbm_selectRetireApproPre, {
			successCallback : function(e) {
				var result = e.responseJSON.elData;
				dma_info.set("retire_appro_pre", result.retire_appro_pre);
				scwin.compControll();
			}
		});		 
	 };
	 
	 /**
	  * 중간정산사유가 전세금등 주택임차인 신청횟수 조회
	  */
	 scwin.searchReqCnt = function() {
		com.execute(sbm_selectReqCount, {
			successCallback : function(e) {
				var cnt = dma_result.get("cnt");				
				if (parseInt(cnt) > 1) {
					com.alert("중간정산사유가 [전세금등 주택임차]로는 2회이상 신청할 수 없습니다.");
					sbx_middle_adjst_typ.setValue("");
				}
			}
		});		 	
	 };
	
	/**
	 * 목록 버튼 클릭 이벤트
	 */	
	scwin.btn_list_onclick = function(e) {
		scwin.backToList(); 		
	};

	/**
	 * 목록 페이지로 이동
	 */	
	scwin.backToList = function() {
		var data ={};
		com.moveList("H574");
	};
	
	/**
	 * 인쇄 버튼 클릭 이벤트
	 */		
	scwin.btn_print_onclick = function(e) {
		var ozrCode = "H101";
		//oz팝업 호출시 전달할 파라메터
		var node = "req_no=" + dma_info.get("req_no") 
				+ "&belng_yrmon=" + ica_cls_cntfr_ymd.getValue();		
		com.oz.openPopup({
			ozrCode: ozrCode,
			param: node    
        });	
	};

	/**
	 * 결재요청 버튼 클릭 이벤트
	 */	
	scwin.btn_apr_onclick = function(e) {
		scwin.data.approval = true;
		if (!scwin.preSave())
		{
			return;
		}
				
		com.confirm("결재신청 하시겠습니까?", function(ret) {
			if (ret) {
				dma_info.set("approval", "true");
				scwin.saveTrans();					
			}
		});				
	};
	
	
	/**
	 * 계좌유형 선택 변경 이벤트
	 */		
	scwin.sbx_bank_accnt_typ_onchange = function(info) {
		if (scwin.data.flag) {
			if (this.getValue()=="HSA010") {
				ibx_bank_accnt_no.setValue(dma_info.get("a_bankaccnt_no"));
			} else {
				ibx_bank_accnt_no.setValue(dma_info.get("f_bankaccnt_no"));			
			}
		}
	};

	
	/**
	 * 중간정산사유 선택 변경 이벤트
	 */
	scwin.sbx_middle_adjst_typ_onchange = function(info) {
		if (scwin.data.flag) {
			var clsCntfrYmdVal = "";
			
			// 임금피크제일때는 첨부파일 필수 아님
			if (this.getValue()=="HMR060") {
				spa_file.hide();				
				// 생일에 따라 퇴직금기산일 종료일 다시 계산
				var residNo = dma_info.get("resid_no");
				
				// 신규작성시 퇴직금기산일 종료일자 생일월에 따라 세팅 (2016.07.06 윤인옥 요청)
				if (residNo.substr(2, 2) < "07") { // 1~6월이면 올해 6월 30일
					clsCntfrYmdVal = scwin.data.today.substr(0, 4) + "0630";
				} else { // 7~12월이면 작년 12월31일
					clsCntfrYmdVal = (Number(scwin.data.today.substring(0, 4)) - 1) + "1231";
				}		
			} else {
				spa_file.show("");
				clsCntfrYmdVal = (Number(scwin.data.today.substring(0, 4)) - 1) + "1231";				
			}
			
			ica_cls_cntfr_ymd.setValue(clsCntfrYmdVal);
			
			if (this.getValue()=="HMR020") {
				tbx_conct.setLabel("임대차계약 체결일 및 잔금지급(예정)일");
				grp_conct.show("");
				ica_cls_cntfr_ymd.setReadOnly(true);
				
				scwin.searchReqCnt();
			} else if (this.getValue()=="HMR010") {
				tbx_conct.setLabel("주택매매 계약체결일 및 소유권 이전 등기(예정)일");
				grp_conct.show("");
				
				// 20190916 무주택자의 주택을 구입하는 경우에 한하여 퇴직금 기산일을 신청자가 변경 가능(권민규 요청)
				ica_cls_cntfr_ymd.setReadOnly(false);
			} else if (this.getValue()=="HMR060") {
				grp_conct.hide();
				ica_conct_ymd.setValue("");
				// 개인마다 마지막 근무일이 다르므로 정산 사유가 임금피크제인 경우 퇴직금 기산일을 신청자가 변경 가능해야함(이경인 요청)
				ica_cls_cntfr_ymd.setReadOnly(false);				
			} else {
				grp_conct.hide();
				ica_cls_cntfr_ymd.setReadOnly(true);	
			}
		
		}

	};

	/**
	 * 퇴직금기산일 선택 변경 이벤트
	 */	
	scwin.ica_cls_cntfr_ymd_onviewchange = function() {
		var reqBaseYmdPre = ica_cls_cntfr_ymd.getValue().substring(0,6);
		var b_reqBaseYy = (Number(scwin.data.today.substring(0,4)) -2)  +"12";
		
		if (sbx_middle_adjst_typ.getValue()=="HMR010") {
			reqBaseYmdPre = (Number(scwin.data.today.substring(0,4)) -1)  +"1231";
		}

		dma_search.set("syspayno", dma_info.get("syspayno"));
		dma_search.set("belng_yrmon", reqBaseYmdPre);
		dma_search.set("b_belng_yy", b_reqBaseYy);				
		com.execute(sbm_selectRetireApproPre, {
			successCallback : function(e) {
				var result = e.responseJSON.elData;
				dma_info.set("retire_appro_pre", result.retire_appro_pre);
			}
		});

	};
	
	/**
	* 처리 버튼 클릭 이벤트
	*/	
	scwin.btn_confirm_onclick = function(e) {
		if (!scwin.data.isRoleYn) {
			com.alert("관리자외에는 처리 하실 수 없습니다.");
		}
		
		if (scwin.validate()) {
			com.confirm("승인처리 하시겠습니까?", function(ret) { 		
				if (ret) {
			 		//서비스아이디 세팅				
					com.execute(sbm_aprAgr, {
						successCallback : function(e) {
							com.alert("승인처리 되었습니다");
							com.openTabMenu("A004", {});
						}
					});
				}
			});
		}			
	};

	/**
	* 반려 버튼 클릭 이벤트
	*/	
	scwin.btn_reject_onclick = function(e) {
		if (!scwin.data.isRoleYn) {
			com.alert("관리자외에는 반려를 하실 수 없습니다.");
		}
		var options = {
		    id: "APR02P",
		    popupName: "반려의견",
		    width: 500,
		    height: 300
		};
		com.openPopup("/ui/apr/APR_C001_03P.xml", options, "", function(retObj) {
		    if (retObj.comments != null) {
		        dma_info.set("comments", retObj.comments);        
		        com.execute(sbm_aprReject, {
		            successCallback: function(e) {
		                com.alert("반려처리 되었습니다");
						com.openTabMenu("A004", {});
		            }
		        });
		    }
		});		
	};

	/**
     * 삭제 버튼 클릭 이벤트
     */		
	scwin.btn_del_onclick = function(e) {
		scwin.remove();
	};

	/**
     * 삭제 실행
     */		
	scwin.remove = function() {
		dma_info.set("regst_syspayno", com.getLoginInfo("sn"));
		dma_info.set("updt_syspayno", com.getLoginInfo("sn"));

		com.confirm("message.xom.wq.022"/*삭제 하시겠습니까?*/, function(ret) {
			if (ret) {
				com.file.deleteAll(function(){				
					com.execute(sbm_deleteHumApyMidSrv, {
						successCallback : function(e) { // 성공 콜백
							com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
								if (dma_search.get("param4") == "apr") {
									com.openTabMenu("A010", {});
								} else {
									scwin.backToList();							
								}
							});
						}
					});
				});
			}
		});		
	}

	/**
     * 저장 버튼 클릭 이벤트
     */		
	scwin.btn_save_onclick = function(e) {
		scwin.data.approval = false;
		if (!scwin.preSave())
		{
			return;
		}
		com.confirm("message.xom.wq.017"/*저장 하시겠습니까?*/, function(ret) {
			if (ret) {
				dma_info.set("approval", "false");
				scwin.saveTrans();					
			}
		});					
	};

	/**
     * 저장 실행
     */			
	scwin.saveTrans = function() {
 		//서비스아이디 세팅	
		com.file.transport(function(attachObj) {
			var attaches = [];
			debugger;
			if (JSON.stringify(attachObj.upload_A) != '{}') {
				attaches.push(attachObj.upload_A);
				dma_info.set("attach_file_no", attachObj.upload_A);			
			}
			dma_info.set("validAttachNo", attaches.join(","));			
			scwin.save();
			
		}); 	
	};

	/**
     * 저장 실행
     */			
	scwin.save = function() {
 		//서비스아이디 세팅
		dma_info.set("service_id", scwin.data.service_id);
		dma_info.set("doc_id", scwin.data.doc_id);
		dma_info.set("dept_cd", com.getLoginInfo("deptCode"));
		dma_info.set("dept_new_ymd", com.getLoginInfo("deptNewYmd"));
		dma_info.set("middle_adjst_yn", "Y"); 
		dma_info.set("retire_resn", "HSK010"); 	
			
		if (dma_info.get("approval") == "true") {
			var infoCd = tbx_nm.getValue() +"(" + tbx_empno.getValue() + ") / " + txa_rmk.getValue().replace(/(\n)/g, " ");
			dma_info.set("info_cd", infoCd);			
		}

		console.log(dma_info.getJSON());
				
		com.execute(sbm_saveHumApyMidSvr, {
			successCallback : function(e) { // 성공 콜백
				com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
					scwin.postSave();
				});
			} 
		});
	};
	/**
     * 저장 후 처리
     */			
	scwin.postSave = function() {
		if (!scwin.data.approval) {
			com.confirm("결재 신청 하시겠습니까?", function(ret) {
				if (ret) {
					dma_info.set("approval", "true");
					dma_info.set("req_no", dma_search.get("req_no"));
					scwin.data.approval = true;
					scwin.save();		
				}else{
					scwin.search();
				}
			});
		} else {
			//결재신청 후에는 결재진행함으로 이동
			com.openTabMenu("A005", {});
		}
	};
	
	/**
     * 저장 전 확인
     */			
	scwin.preSave = function() {
		 if (!scwin.data.save) {
			 com.alert("퇴직금 중간정산신청자가 아닙니다. 확인바랍니다.");
			 return false;
		 }
		 
	     if (!scwin.validate()) {
	     	return false;
	     }	
	     
		if (sbx_middle_adjst_typ.getValue()=="HMR010" || sbx_middle_adjst_typ.getValue()=="HMR020") {
			if (ica_conct_ymd.getValue()=="") {
				var msg = "신청 사유가 [" + sbx_middle_adjst_typ.getText() + "]일때 계약체결일을 입력해야 합니다";
				com.alert(msg, function() {
					ica_conct_ymd.focus();
				});
				return false;

			} else {
				if (!com.isDate(ica_conct_ymd.getValue(), false)) {
					return false;
				}	
			}
			
			if (ica_conct_ymd.getValue() < com.addDate(ica_req_ymd.getValue(), -30)) {
				var msg = "신청 사유가 [" + sbx_middle_adjst_typ.getText() + "]일때 계약일이 1개월지난경우 퇴직금 중간정산 신청이 되지않습니다.";
				com.alert(msg, function() {
					ica_conct_ymd.focus();
				});
				return false;
			}
		}   
		
		if (com.file.action.getTotalFileCount(upload_A.getID()) < 1 && sbx_middle_adjst_typ.getValue()!="HMR060") {
			com.alert("첨부파일은 필수 입력 항목입니다.");
			upload_A.focus();
			return false;
		}	  
		
		return true;	     
	};
	
	/*
     * 검증
     */	
	scwin.validate = function(flag) {
		if (!com.validateData(dma_info,[
			{ id : "req_ymd", mandatory : true},
			{ id : "bankaccnt_typ", mandatory : true},
			{ id : "retire_hope_ymd", mandatory : true},
			{ id : "middle_adjst_typ", mandatory : true},					
		], false)) {
			return false;
		}

		if (!com.isDate(ica_req_ymd.getValue(), false)) {
			return false;
		}	
		if (!com.isDate(ica_cls_cntfr_ymd.getValue(), false)) {
			return false;
		}		
		return true;	
	};

	/**
     * 서약서 다운로드
     */	
	scwin.btn_docu_down_onclick = function() {
		scwin.downFileinfo("[양식]무주택자 확인 서약서.hwp", "10000030");	
	};
	
	/**
     * 파일 다운로드
     */
	scwin.downFileinfo = function downFileinfo(file_nm,file_no) {
		var url = "/SvcAttachDown.do?fileName=web_folder/bizdoc/hum/"+file_no+"&realName="+encodeURIComponent(file_nm)+"&bizKind=OLD";		
		window.open(url, '_blank').focus();
	};	

	/**
	 * 화면 콤포넌트 컨트롤
	 */	
	scwin.compControll = function() {
	    com.biz.disabledButtons(dma_info.get("apr_state"), dma_info.get("req_no"));
		
		if (dma_info.get("req_no") != "") {
			//저장되면 인쇄버튼 활성화
			btn_print.setDisabled(false);
		}
			    
		if (dma_info.get("apr_state") >= "XAD020") {
			//관리자이고, 담당자확인 상태일 때만 반려/처리버튼 노출.
			com.biz.checkRole({ serviceId : scwin.data.service_id, roleCodes : com.getLoginInfo('roleCode')}, function(e) {
				scwin.data.isRoleYn = e.isRoleYn;
				if (scwin.data.isRoleYn && dma_info.get("apr_state") == "XAD070") {
					btn_confirm.show("");
					btn_reject.show("");
				}				
			});				

			sbx_middle_adjst_typ.setDisabled(true);
			sbx_bank_accnt_typ.setDisabled(true);
			ica_cls_cntfr_ymd.setReadOnly(true);
			if (dma_info.get("middle_adjst_typ") =="HMR010" || dma_info.get("middle_adjst_typ") =="HMR020") {
				ica_conct_ymd.setReadOnly(true);
			}
			txa_rmk.setReadOnly(true);
		} else {
			if (tbx_empno.getValue() != com.getLoginInfo("empno")) {
				btn_save.setDisabled(true);
				btn_apr.setDisabled(true);
				btn_del.setDisabled(true);
			}
		}
	    
	    if (!scwin.data.save) {
	    	btn_save.setDisabled(true);
	    }
	};
