head.script:
	/*
	*  날짜 설정
	 */
	   
	var now = new Date();
	var defalutday = gcm.date.formatDateTime(now, "yyyyMMddHHmm"); // yyyyMMddHHmm
	var ymd = gcm.date.formatDateTime(now); // yyyyMMdd
	var year = ymd.substr(0, 4); // yyyyMM 
	var yearMm = ymd.substr(0, 6); // yyyyMM
	var Month = ymd.substr(4, 2); // yyyyMM
	var session = com.getLoginInfo();	     
	 
	        
	scwin.onpageload = function() {
		dma_header.set("empno", session.empno);
		dma_header.set("nm", session.cn);
		dma_header.set("syspayno", session.sn);
		dma_search.set("syspayno", session.sn);
		dma_search.set("belng_yrmon", yearMm);
		// 공통코드
		var codeOptions = [ { code : "HYY", compID :"selectbox_userdiv"}  ];
		com.setCmnCd(codeOptions, function() {
													
		});
		
		com.checkPeriod(ica_from, ica_to);
		com.checkPeriod(ica_fromYm, ica_toYm);
		
	};
	
	scwin.onpageunload = function() {
		
	};
	
	
	
	/*
     * 조회
     */
	scwin.search = function(){
		com.execute(sbm_selectData, {
			successCallback : function(e){				
			}
		});
	};
	
	//조회 버튼이벤트
	scwin.btn_search_onclick = function(e) {
		if(inp_belngYrMm.getValue() == ""){
			alert("조회대상 연월을 입력하시기 바랍니다.");	
		}
		scwin.search();
	};
	
	//임금명세서 출력
	scwin.btn_print_onclick = function(e) {

		var node = "syspayno=" + dma_search.get("syspayno") + "&belng_yrmon=" + dma_search.get("belng_yrmon"); // +"&user_div=" + selectboxUserDiv.getValue();
		
		if(dma_search.get("belng_yrmon") != ""){
			com.oz.openPopup({
				ozrCode : "H635",
				param: node
			});
		}else{
			alert("급여연월을 입력해주세요.");	
		}
	};
	
	//급여정보로 화면이동
	scwin.btn_move_onclick = function(e) {
		com.moveList("H611");
	};
	
	//엑셀다운로드
	scwin.btn_excel_onclick = function(e) {
		var now = com.getServerDateTime("yyyyMMdd");
		var name = '급여지급내역' + now + '.xlsx';
		
		var option = {
			    fileName : name
			    , useDataFormat : true
		};
		com.downloadGridExcel(grd_list, option);
	};
	
	
	//급여지급내역 조회
	scwin.btn_searchList_onclick = function(e) {
		if(dma_search.get("fr_ymd") == "" || dma_search.get("to_ymd") == ""){
			alert("지급년월 시작, 종료일을 입력해주세요.");
			return;
		}
		
				
		dma_search.set("slGubn", "%") //EIP2.0 확인결과 일반사용자는 전체. 관리화면에서 담당자는 선택가능
		com.execute(sbm_selectList, {
			successCallback : function(e) {
				dlt_list03.setJSON(e.responseJSON.humSalPayVoList);								
			}
			
		});
	};
	
	//지급내역 출력
	scwin.btn_printList_onclick = function(e) {
		
		if(dma_search.get("fr_ymd") == "" || dma_search.get("to_ymd") == ""){
			alert("지급년월 시작, 종료일을 입력해주세요.");
			return;
		}
		
		var node = "syspayno=" + dma_search.get("syspayno") + "&belng_yrmon_fr=" + dma_search.get("fr_ymd")+"21" + "&belng_yrmon_to=" + dma_search.get("to_ymd")+"21"
				  +"base_ymd="+dma_search.get("to_ymd")+"21"+"slGubn="+"%";
		
		com.oz.openPopup({
			ozrCode : "H640",
			param: node
		});
	};
	
	//총괄표 조회
	scwin.btn_searchTotal_onclick = function(e) {
		if(dma_search.get("fr_ymd") == "" || dma_search.get("to_ymd") == ""){
			alert("지급년월 시작, 종료일을 입력해주세요.");
			return;
		}
		dma_search.set("slGubn", "") 		
		com.execute(sbm_selectTotList, { 
			successCallback : function(e) {
											
			}
			
		});

	};
