head.script:

	scwin.onpageload = function() {

		btn_print_old.hide(); //변경전 원천징수영수증 숨김처리

		var today = com.getServerDateTime();
		var yr = today.substring(0, 4)-1;
		inp_yr.setValue(yr);

		var session = com.getLoginInfo();
		var syspayno = session.sn;
		ibx_empno.setValue(session.empno);
		ibx_nm.setValue(session.cn);
		dma_search.set("syspayno", syspayno);
		dma_log.set("req_ymd", today);		
		dma_log.set("req_syspayno",syspayno);
		dma_log.set("regst_syspayno",syspayno);

		//화면 권한 체크
		com.biz.checkRole({serviceId : "H815", roleCodes : session.roleCode}, function(e){
			if(e.isRoleYn){
				ibx_empno.setDisabled(false);
				btn_meetEmp.setDisabled(false);
			}else{
				ibx_empno.setDisabled(true);
				btn_meetEmp.setDisabled(true);
			}
		});		

		com.setEnterKeyEvent(grp_meet_psn, scwin.btn_meetEmp_onclick);//참여자조회

	};

	scwin.onpageunload = function() {

	};

	scwin.btn_search_onclick = function(e) {
		scwin.search();
	};



	/**
	*	엑셀다운로드
	**/
	scwin.btn_down_excel_onclick = function() {
		var now = com.getServerDateTime("yyyyMMdd");
		var name = '원천징수영수증' + now + '.xlsx';

		var option = {
			    fileName : name
			    , useDataFormat : true
		};
		com.downloadGridExcel(grd_list, option);
	};

	/*
	* 목록 조회
	*/
	scwin.search = function() {		

		dma_search.set("searchGubun", "");	//목록조회


		if (inp_yr.getValue() == "" && ibx_empno.getValue() == "" && ibx_resid_no.getValue() == "") {
			alert("정산년도 또는 개인번호/주민등록번호는 필수 입력사항 입니다.");
			return;	
		}

		if (inp_yr.getValue() != "" && inp_yr.getValue().length != 4) {
			if( inp_yr.getValue() < "2011"){
				alert("2011년 이전 데이터는 존재하지 않습니다.");
				return;
			}	
			alert("정산연도를 바르게 입력하여 주시기 바랍니다.");
			return;	
		}	

		com.execute(sbm_selectList, {
			successCallback : function(e) {
				dlt_list.setJSON(e.responseJSON.humTaxPayVoList);				
				tbx_result.setValue(dlt_list.getTotalRow());
			},

			isImplExcel: true,
			excelCallback : function(e) {
				// DB 다운로드
				var option = {
					fileName : "원천징수영수증.xlsx"
					, providerInfo : {
						  service : "humPayTaxService"
						, method : "selectHumTaxPayInfoList"
						, vo : "hum.tax.vo.HumTaxPayVo"
						, paramMap : dma_search
					}
				};
				com.downloadGridExcel(grd_list, option);
			}
		});
	};


	/*
	* 개인번호 조회조건 초기화
	*/
	scwin.ibx_empno_onchange = function(info) {
		if(info.newValue == "") {
			ibx_nm.setValue("");
			dma_search.set("syspayno", "");
		}
	};

	/*
	* 개인번호 조회
	*/
	scwin.btn_meetEmp_onclick = function(e) {
		var data = { empno : ibx_empno.getValue()
					, work_clsf : "HAG010"
					, ref_yn : (ibx_empno.getValue() == "")?"Y" : "N"
					};

		com.pop.open( com.pop.TYPE.HUM, data, function(ret){
			ibx_empno.setValue(ret.empno);
			ibx_nm.setValue(ret.nm);
			dma_search.set("syspayno", ret.syspayno);
		});
	};

	/*
	* 원천징수부 인쇄
	*/
	scwin.btn_print_onclick = function(e) {	

		var chkArr = grd_list.getCheckedIndex("chk");
		if(chkArr.length == 0){
			alert("출력할 자료를 선택하십시오");
   			return;
		}	

		var prtSyspayno = grd_list.getCellData(chkArr, "syspayno");
		var prtYr = grd_list.getCellData(chkArr, "adjst_yr");

		dma_log.set("prt_knd", "소득자별근로소득원천징수부");		
		dma_log.set("prt_contnt", prtYr+"년도 소득자별근로소득원천징수부(H815)");
		dma_log.set("use_rsn", "원천징수 출력");

		//증명서 출력 로그 입력이후 출력
		com.execute(sbm_insertData, {
			successCallback : function(e) {				
				var node =  "syspayno=" + prtSyspayno + "&yrmon=" + prtYr;
				//"hum/ana/HumAnaEarmCltSel01.ozr", "HumAnaEarmCltSel02"
				com.oz.openPopup({
					ozrCode : "H110",      
					param: node
		        })
			}			
		});			
	};


	//엑셀 상세 다운로드	
	scwin.btn_excel_detail_onclick = function(e) {
		if (inp_yr.getValue() == "" && ibx_empno.getValue() == "" && ibx_resid_no.getValue() == "") {
			alert("정산년도 또는 개인번호/주민등록번호는 필수 입력사항 입니다.");
			return;	
		}

		if (inp_yr.getValue() != "" && inp_yr.getValue().length != 4) {
			alert("정산연도를 바르게 입력하여 주시기 바랍니다.");
			return;
			if( inp_yr.getValue() < "2011"){
				alert("2011년 이전 데이터는 존재하지 않습니다.");
				return;
			}		
		}



		var excelNm = ibx_nm.getValue();
		var excelEmpno = ibx_empno.getValue();
		if(excelNm == ""){
			excelEmpno = "전체";		
			excelNm = "전체";
		}
		dma_search.set("searchGubun", "excel");

		var option = {
			fileName : inp_yr.getValue()+"_세금관련증명서_엑셀상세_"+excelNm+"("+excelEmpno+")"+".xlsx"
			, hiddenVisible : false			
			, providerInfo : {
				  service : "humTaxPayServiceImpl"
				, method : "selectHumTaxPayInfoList"
				, vo : "hum.tax.vo.HumTaxPayVo" 
				, paramMap : dma_search
			},
			hiddenVisible : false
			, useDataFormat : true
		};

		com.downloadGridExcel(grd_list_excel, option);
	};



	//원천징수 영수증	
	scwin.btn_print_rcpt_onclick = function(e) {
		var chkArr = grd_list.getCheckedIndex("chk");
		if(chkArr.length == 0){
			alert("출력할 자료를 선택하십시오");
   			return;
		}	

		var prtSyspayno = grd_list.getCellData(chkArr, "syspayno");
		var prtYr = grd_list.getCellData(chkArr, "adjst_yr");
		var userDiv = grd_list.getCellData(chkArr, "user_div");
		var enterYmd = grd_list.getCellData(chkArr, "entr_ymd");

		if(userDiv == "HYY020" && enterYmd >= "20130301"){
			alert("기타소득자는 인재경영실 담당자에게 문의하세요");
			return;
		}		

		if(prtYr < "2002"){
			alert("2002년 이전 원천징수영수증은 제공되지 않습니다.");	
		}


		dma_log.set("prt_knd", "원천징수영수증");		
		dma_log.set("prt_contnt", prtYr+"년도 원천징수영수증부(H815)");
		dma_log.set("use_rsn", "원천징수영수증 출력");

		var ozCode = "";



		//증명서 출력 로그 입력이후 출력
		com.execute(sbm_insertData, {
			successCallback : function(e) {				
				var node =  "syspayno=" + prtSyspayno + "&yrmon=" + prtYr;
				//"hum/ana/HumAnaEarmCltSel01.ozr", "HumAnaEarmCltSel02"
				com.oz.openPopup({
					ozrCode : "H110",      
					param: node
		        })
			}		
		});			
	};

	scwin.grd_list_oncellclick = function(row,col,colId) {
		grd_list.setCellChecked(row, "chk", true);
	};
