head.script:
    /****************************************************
 	 * 화면 설명 : 국제행사상세
 	 * 변경 이력 : 2023-11-15	김준하	최초 작성
 	 ****************************************************/
 	scwin.data = {};
	/**
	 * 페이지 로딩 이벤트
	 */           
	scwin.onpageload = function() {
		var codeOptions = [ { code : "LCA", compID : "sbx_visit_clsf"} ];//방문구분
        com.setCmnCd(codeOptions, function() {
            // 첨부파일 컴포넌트 초기화
            com.file.init();
            com.file.create(upload_A.getID(), {
                fileKey: "L450",
                mode: 'edit'
            });
            var param = com.getParameter();
            var session = com.getLoginInfo();
            scwin.mode_type = "edit";
            scwin.roleCode = com.getLoginInfo('roleCode');

			scwin.userSyspayno = session.sn;
			scwin.flag = param.flag;
			scwin.eventno = param.eventno;
			scwin.searchCon1 = param.searchCon1;
			scwin.searchCon2 = param.searchCon2;
			scwin.searchCon3 = param.searchCon3;
			scwin.searchCon4 = param.searchCon4;
			scwin.cmd = param.cmd;
			btn_del.hide();            

            // enter key event
            com.setEnterKeyEvent(grp_hum, scwin.btn_hum_onclick);
			// 기간 역전 체크
			com.checkPeriod(ica_event_start_ymd, ica_event_cls_ymd);            
			//화면 권한 체크
			com.biz.checkRole({serviceId : "L450", roleCodes : scwin.roleCode}, function(e){
				scwin.roleChk = e.isRoleYn;
				if (scwin.roleChk) {					
		        	btn_load01.setDisabled(false);
		        	btn_row_delete.setDisabled(false);
		        	btn_hum.setDisabled(false);
		        	btn_load02.setDisabled(false);
		        	btn_delete_row02.setDisabled(false);
		        	ibx_emp_no.setDisabled(false);
		        	btn_save.show("");
		        	if (scwin.flag == "Update") {
			        	btn_del.show("");
			        }
				} 
			});

			if (scwin.flag == "Update") {			
				dma_search.set("event_no", scwin.eventno);
				scwin.selectInfo();//상세
				scwin.selectExpen();//행사비용	
				scwin.selectAgncy();//기관
			} else if (scwin.flag == "Search") {
				dma_search.set("event_no", scwin.eventno);
				scwin.selectInfo();//상세
				scwin.selectAgncy();//기관

				btn_row_delete.setDisabled(true);
				btn_load01.setDisabled(true);
				btn_load02.setDisabled(true);
				btn_delete_row02.setDisabled(true);
				btn_save.setDisabled(true);
				scwin.mode_type = "view";
			} else {
				scwin.eventno = "";
				ibx_event_supvis.setValue("한국생산기술연구원");
				var now = new Date();
			    var year= now.getFullYear();
			    var mon = (now.getMonth()+1)>9 ? ''+(now.getMonth()+1) : '0'+(now.getMonth()+1);
			    var day = now.getDate()>9 ? ''+now.getDate() : '0'+now.getDate();
			    var curdate = year+mon+day;		    
				ica_event_start_ymd.setValue(curdate);				

			}
        });	
	};

    /**	
     *	상세조회
     **/		
	scwin.selectInfo = function(e) {
		com.execute(sbm_selectInfo, {				
			successCallback : function(e){
				//e.responseJSON.elData.lcpOvsResMouVoList
	            // 첨부파일
	            if (dma_form_result.get("attach_file_no").trim() !== '') {
	                com.file.create(upload_A.getID(), {
	                    fileKey: dma_form_result.get("attach_file_no"),
	                    mode: scwin.mode_type
	                });
	            }					
			}	
		});					
	};

    /**	
     *	행사비용조회
     **/		
	scwin.selectExpen = function(e) {
		com.execute(sbm_selectExpen, {				
			successCallback : function(e){
				//e.responseJSON.elData.lcpOvsResMouVoList
			}	
		});					
	};

    /**	
     *	기관조회
     **/		
	scwin.selectAgncy = function(e) {
		com.execute(sbm_selectAgncy, {				
			successCallback : function(e){
				//e.responseJSON.elData.lcpOvsResMouVoList				
			}	
		});					
	};			

	scwin.onpageunload = function() {	
	};

	//담당자 팝업
	scwin.btn_hum_onclick = function(e) {
        var data = { work_clsf : "HAG010", empno : ibx_emp_no.getValue(), nm : ibx_emp_no.getValue() };	
        data.ref_yn = (data.empno == "") ? "Y" : "N";
        com.pop.open(com.pop.TYPE.HUM, data, function(ret) {
			dma_form_result.set("charg_syspayno", ret.syspayno);
			dma_form_result.set("charg_empno", ret.empno);
			dma_form_result.set("charg", ret.nm);
       });
	};

	//접견기관 불러오기
	scwin.btn_load_onclick = function(e) {
		var data = {};
		data.useFlag = "Y";
		data.flag = "OverEvent";
		biz.lpl.pop.open(biz.lpl.pop.TYPE.AGENCY, data, function(ret) {
			dlt_agncy_list.insertRow();

			var tmp = grd_lcp_event_agncy.getRowCount()-1;

			dlt_agncy_list.setCellData(tmp, "natn_nm", ret.natn_nm);
			dlt_agncy_list.setCellData(tmp, "krchar_agncy_nm", ret.cmpy_nm_krchar);
			dlt_agncy_list.setCellData(tmp, "natn_cd", ret.natn_cd);
			dlt_agncy_list.setCellData(tmp, "agncy_cd", ret.cust_no);
			dlt_agncy_list.setCellData(tmp, "reprs_psn_nm", ret.reprs_psn);		
		});			
	};

	// 담당자 빈값일경우 초기화
	scwin.ibx_emp_no_onchange = function(info) {
        if (info.newValue == "") {
            dma_form_result.set("charg_syspayno", "");
            dma_form_result.set("charg_empno", "");
            dma_form_result.set("charg", "");
        }		
	};

	//행 삭제
	scwin.btn_depst_remove_onclick = function(e) {
        var req_amt = 0;
        var arr = grd_lcp_event_agncy.getCheckedIndex("chk");
        var row_cnt = arr.length;
        var delOdrs = "";
        if (row_cnt == 0) {
            alert("선택되지 않았습니다.");
            return;
        } else {
	    dma_form_result.set("agy_row_flag", "D");   
	    }
		for (var i = arr.length; i >0; i--) {
			delOdrs += grd_lcp_event_agncy.getCellData(arr[i-1], "seq")+" ";		
			dlt_agncy_list.setCellData(arr[i-1], "cud_type", "D");
			dlt_agncy_list.removeRow(arr[i-1]);        
		}
		dma_form_result.set("agy_del_row_odr", delOdrs);
		alert("삭제후 저장을 하셔야 반영이 됩니다");		
	};

	//행사비용 행 삭제
	scwin.btn_exp_remove_onclick = function(e) {
        var req_amt = 0;
        var arr = grd_cpp_event_expns.getCheckedIndex("chk");
        var row_cnt = arr.length;
        var delOdrs = "";
        if (row_cnt == 0) {
            alert("선택되지 않았습니다.");
            return;
        } else {
	    dma_form_result.set("exp_row_flag", "D");   
	    }
		for (var i = arr.length; i >0; i--) {
			delOdrs += grd_cpp_event_expns.getCellData(arr[i-1], "seq")+" ";		
			dlt_expen_list.setCellData(arr[i-1], "cud_type", "D");
			dlt_expen_list.removeRow(arr[i-1]);        
		}
		dma_form_result.set("exp_del_row_odr", delOdrs);
		alert("삭제후 저장을 하셔야 반영이 됩니다");		
	};	

	//저장 버튼 클릭
	scwin.btn_save_onclick = function(e) {
		com.file.transport( function( attachObj){
			var attaches = [];
			attaches.push(attachObj.upload_A);
			dma_form_result.set("attach_file_no", attachObj.upload_A);
			dma_form_result.set("validAttachNo", attaches.join(','));				
			if (grd_lcp_event_agncy.getRowCount() > 0) {
				if (!scwin.vaildCheck()) {
					return;
				}

				var count = grd_lcp_event_agncy.getRowCount();
				var	count2 = grd_cpp_event_expns.getRowCount();

				for (var i = 0; i < count; i++) {
					if (!scwin.gridValidChk(grd_lcp_event_agncy, i, "krchar_agncy_nm", "기관명", "협력기관정보")) return false;
				}
				for (var i = 0; i < count2; i++) {
					if (!scwin.gridValidChk(grd_cpp_event_expns, i, "decsn_slip_no", "결의번호", "행사비용")) return false;
					if (!scwin.gridValidChk(grd_cpp_event_expns, i, "cr", "소요금액", "행사비용")) return false;
				}				
				scwin.crudInfo();
			} else {
				alert("협력기관 정보를 입력해주세요");
			}
		});		
	};

	// 저장,수정,삭제
	scwin.crudInfo = function(e) {
		if (scwin.flag == "Update") {
			dma_form_result.set("cud_type", "U");
			dma_form_result.set("updt_psn_syspayno", scwin.userSyspayno); 
		} else if (scwin.flag == "Delete") {
			dma_form_result.set("cud_type", "D");
			dma_form_result.set("event_no", ibx_req_no.getValue());
		} else {
			dma_form_result.set("cud_type", "I");
			dma_form_result.set("regst_psn_syspayno", scwin.userSyspayno);
		}

		var status = "";

		// 협력기관정보		
		for(var i=0; i<dlt_agncy_list.getRowCount(); i++) {			
			status = dlt_agncy_list.getRowStatus(i);
			if (status != "R") { // R : DB에서 가져온 수정되지 않은 data
				if (status == "C") {
					dlt_agncy_list.setCellData(i, "cud_type", "C");
				} else if (status == "U") {
					dlt_agncy_list.setCellData(i, "cud_type", "U");								
				}
			}
		}

		// 행사비용		
		for(var i=0; i<dlt_expen_list.getRowCount(); i++) {			
			status = dlt_expen_list.getRowStatus(i);
			if (status != "R") { // R : DB에서 가져온 수정되지 않은 data
				if (status == "C") {
					dlt_expen_list.setCellData(i, "cud_type", "C");
				} else if (status == "U") {
					dlt_expen_list.setCellData(i, "cud_type", "U");								
				}
			}
		}		

		com.confirm("message.xom.wq.017"/*저장 하시겠습니까?*/, function( ret) {
			com.execute(sbm_saveData, {
				successCallback : function(e) { // 성공 콜백					
					com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
						scwin.data.saveFlag = true;
						dma_search.set("event_no", e.responseJSON.elData.event_no);
						dma_form_result.set("cud_type", "U");
						scwin.flag = "Update"
						dma_form_result.set("agy_row_flag", "");
						dma_form_result.set("exp_row_flag", "");
						dma_form_result.set("exp_del_row_odr", "");
						dma_form_result.set("agy_del_row_odr", "");						
						//조회
						btn_del.show("");
						scwin.search();				
					});										
				}
			});
		});						
	}	


    // 그리드 유효성 검사
    scwin.gridValidChk = function(gridObj, row, colId, colName, title) {
        var cellValue = gridObj.getCellData(row, colId);
        if (cellValue == "") {
            alert("[" + title + "] " + (row + 1) + "번째 " + colName + "을(를) 입력하세요.");
            gridObj.setFocusedCell(row, colId);
            return false;
        }
        return true;
    }	

	// 유효성 검사
	scwin.vaildCheck = function(e) {
		if (!com.validateData(dma_form_result,[
				 { id : "event_start_ymd", mandatory : true }//행사시작일
				,{ id : "event_cls_ymd", mandatory : true }//행사종료일
				,{ id : "event_nm", mandatory : true }//행사명
				,{ id : "event_supvis", mandatory : true }//행사주관
				,{ id : "charg_empno", mandatory : true }//담당자
			], false)
		) return false;
		return true;		
	}	

    /**	
     *	삭제
     **/	
	scwin.btn_del_onclick = function(e) {
		com.confirm("message.xom.wq.022" /*삭제 하시겠습니까?*/ , function(ret) {
          	scwin.flag = "Delete";
          	dma_form_result.set("event_no", ibx_req_no.getValue());
          	dma_form_result.set("cud_type", "D"); 	                    	                        
			if (ret) {
                com.execute(sbm_saveData, {
                    successCallback: function(e) { // 성공 콜백
						$p.parent().com.alert("message.xom.wq.023" /*정상적으로 처리되었습니다.*/, function(){
						    scwin.data.save_flag = true;
							scwin.btn_list_onclick();
						});		                        
                    }
                });
			}			
		});		
	};

	// 목록이동
	scwin.btn_list_onclick = function(e) {
		var data={};
		com.moveList("L451", scwin.data.save_flag);
//		com.openTabUrl("국제행사", "/ui/lpl/lcp/LPL_LCP_L451_00L.xml", 1, data, function() {
//		});		
	};

	scwin.btn_load01_onclick = function(e) {
		var data = {};
		data.eventno = scwin.eventno;
		data.flag = "lcp";
		biz.lpl.pop.open(biz.lpl.pop.TYPE.EVEXPEN, data, function(ret) {
			var cnt = ret;

			for (var i = 0; i <cnt.length; i++) {
					dlt_expen_list.insertRow();
					var tmp = grd_cpp_event_expns.getRowCount()-1;
					dlt_expen_list.setCellData(tmp, "accnt_no", ret[i].accnt_no);
					dlt_expen_list.setCellData(tmp, "ymd", ret[i].slip_ymd);
					dlt_expen_list.setCellData(tmp, "decsn_slip_no", ret[i].unslip_no);
					dlt_expen_list.setCellData(tmp, "rmk", ret[i].rmk_1);
					dlt_expen_list.setCellData(tmp, "vend_nm", ret[i].rmk_2);
					dlt_expen_list.setCellData(tmp, "expns_cd", ret[i].expns_cd);
					dlt_expen_list.setCellData(tmp, "cr", ret[i].unslip_amt);	
			}
		});					
	};
