head.script:
 
    /****************************************************
 	 * 화면 설명 : 언론보도신청
 	 * 변경 이력 : 2023-08-21	김상미	최초 작성
 	 * 서비스아이디 : L521
 	 ****************************************************/  
 	
 	scwin.data = {};
 	
 	/**
	 * 페이지 로딩 이벤트
	 */  	         	        
	scwin.onpageload = function() {
		cbx_rptg_media_clsf3.hide();
		scwin.data.service_id="L521";
		scwin.data.doc_id="L26";	
		scwin.data.req_state="XRR020";	
		spa_rgpt_media_clsf.hide();
		com.biz.disabledButtons(dma_info.get("apr_state"), dma_info.get("req_no"));		
		
		var param = com.getParameter();
		scwin.initPage(param);

	};
	
	scwin.onpageunload = function() {
		
	};
	
	
	/**
	 * 화면 초기화
	 */	
	scwin.initPage = function(param) {		
		//콤보박스세팅
		var codeOptions = [ { code : "XAD", compID : "sbx_req_state"},
							{ code : "LPA", compID : "sbx_req_clsf"},];		
		com.setCmnCd(codeOptions, function() {
		});	
				
		// 첨부파일 컴포넌트 초기화
		com.file.init();
		// 첨부파일
		com.file.create(upload_A.getID(), {fileKey: 'L521', mode: 'edit'});	
		
		//파라메터에 따른 처리
		if (param != undefined && param != null) {
			dma_param.setJSON(param);
		}
		
		
		sbx_req_state.setDisabled(true);
		
		com.biz.checkRole({ serviceId : scwin.data.service_id, roleCodes : com.getLoginInfo('roleCode')}, function(e) {	
			scwin.data.isRoleYn = e.isRoleYn;
		});		
		
		//달력세팅
		var today = com.getServerDateTime();	
		ica_req_ymd.setValue(today);
		ica_rptg_hope_ymd.setValue(today);

		//필수입력항목 세팅
		com.setValidGroup(grp_base_info, [
			 { id : "ica_req_ymd", mandatory : true}
			,{ id : "ica_rptg_hope_ymd", mandatory : true }
			,{ id : "sbx_req_clsf", mandatory : true } 			 	
			,{ id : "txa_rptg_contnt", mandatory : true } 			 									
		])
				
		
		if (dma_param.get("cmd")=="Vw") {
			dma_search.set("req_no", dma_param.get("req_no"));
			scwin.search();
		} else {
			if (dma_param.get("req_no")=="") {
				dma_info.set("dept_nm", com.getLoginInfo("deptName"));
				dma_info.set("req_psn_syspayno_nm", com.getLoginInfo("cn"));
			} else {
				dma_search.set("req_no", dma_param.get("req_no"));	
				dma_search.set("apr_state", dma_param.get("aprStatus"));	
				scwin.search();										
			}		
		}
		
	};
	
	
	/**
	 * 목록버튼 클릭
	 */	
	scwin.btn_list_onclick = function(e) {
		scwin.backToList(); 		
	};

	/**
	 * 목록 페이지로 이동
	 */	
	scwin.backToList = function() {
		var data = dma_param.getJSON();
		com.moveList('L522', data);
	};

	/**
	 * 인쇄 버튼 클릭 이벤트
	 */
	scwin.btn_print_onclick = function(e) {
		com.oz.openPopup( {
		    formAlias : dma_info.get("req_no").substring(0,3),
		    reqNo : dma_info.get("req_no"),
		    snapFrame : grp_base_info.getID()
		});

	};

	/**
	 * 결재신청 버튼 클릭 이벤트
	 */	
	scwin.btn_apr_onclick = function(e) {
		dma_info.set("approval", "true");
		dma_info.set("apr_state", "XAD010");
		dma_info.set("apr_req_ymd",com.formatDate(ica_req_ymd.getValue(), "date"));
		dma_info.set("apr_rptg_hope_ymd",com.formatDate(ica_rptg_hope_ymd.getValue(), "date"));
		dma_info.set("apr_req_clsf",sbx_req_clsf.getText());

		if (ibx_tel.getValue() !="") {
			dma_info.set("apr_tel",com.formatPhone(ibx_tel.getValue()));
		}
		dma_info.set("apr_rptg_contnt",txa_rptg_contnt.getValue().replace(/(\n)/g, "<br/>"));
		dma_info.set("apr_rptg_resn",txa_rptg_resn.getValue().replace(/(\n)/g, "<br/>"));		
		
		var infoCd = com.getLoginInfo("cn") +"("+ com.getLoginInfo("empno") +") / " + txa_rptg_contnt.getValue().replace(/(\n)/g, " ");
		  
		dma_info.set("info_cd", infoCd);
										
		scwin.setRptgMediaClsf();	
		
		if (!scwin.preSave())
		{
			return;
		}		
		com.confirm("결재신청을 하시려면 [확인]을 클릭하세요", function(ret) {
			if (ret) {
				scwin.save();				
			} 
		});	
	};

	/**
	 * 신청구분 변경이벤트
	 */	
	scwin.sbx_req_clsf_onchange = function(info) {
		scwin.triggerReqClsf(info.newValue);
		
	};
	
	scwin.triggerReqClsf = function(option) {
		if (option == "LPA001") {
			spa_rgpt_media_clsf.show("");
			scwin.setCheckBoxDisable(false);	
			scwin.setInputBox(true);		
						
		} else if (option == "LPA002") {
			spa_rgpt_media_clsf.hide();
			scwin.setCheckBoxDisable(true);
			scwin.setInputBox(false);				
		} else {
			spa_rgpt_media_clsf.hide();			
			scwin.setCheckBoxDisable(false);	
			scwin.setInputBox(false);									
		}	
		scwin.setCheckBoxValue(option, dma_info.get("rptg_media_clsf"));
	}
	

	/**
	 * 신청정보 보도매체 체크박스 활성/비활성 처리
	 */		
	scwin.setCheckBoxDisable = function(flag) {
		cbx_rptg_media_clsf1.setDisabled(flag);
		cbx_rptg_media_clsf2.setDisabled(flag);
		cbx_rptg_media_clsf3.setDisabled(flag);
		cbx_rptg_media_clsf4.setDisabled(flag);	
	};
	
	/**
	 * 신청정보 보도매체 체크박스 체크 처리
	 */		
	scwin.setCheckBoxValue = function(reqClsf, rptgMediaClsf) {
		
		if (reqClsf=="LPA001") {
			if (rptgMediaClsf != undefined && rptgMediaClsf.length ==4) {
				if (rptgMediaClsf.substr(0,1)=="1") {
					cbx_rptg_media_clsf1.setValue("Y");
				}
				if (rptgMediaClsf.substr(1,1)=="2") {
					cbx_rptg_media_clsf2.setValue("Y");
				}
				if (rptgMediaClsf.substr(2,1)=="3") {
					cbx_rptg_media_clsf3.setValue("Y");
				}
				if (rptgMediaClsf.substr(3,1)=="4") {
					cbx_rptg_media_clsf4.setValue("Y");
				}					
			}
		} else {
			cbx_rptg_media_clsf1.setValue("N");
			cbx_rptg_media_clsf2.setValue("N");
			cbx_rptg_media_clsf3.setValue("N");
			cbx_rptg_media_clsf4.setValue("N");			
		}
	};


	/**
	 * 취재협조사항 언론사 정보 활성/비활성 처리
	 */	
	scwin.setInputBox = function(flag) {
		if (flag) {
			ibx_press_nm.setValue("");			
			ibx_charg_nm.setValue("");	
			ibx_tel.setValue("");	
			ibx_email.setValue("");	
		}
		ibx_press_nm.setReadOnly(flag);			
		ibx_charg_nm.setReadOnly(flag);
		ibx_tel.setReadOnly(flag);
		ibx_email.setReadOnly(flag);			
	};	

	
	/**
	 * 삭제 버튼 클릭 이벤트
	 */			
	scwin.btn_del_onclick = function(e) {
		scwin.remove(); 
	};

	/**
	 * 삭제 처리
	 */	
	scwin.remove = function() {
		dma_info.set("regst_syspayno", com.getLoginInfo("sn"));
		dma_info.set("updt_syspayno", com.getLoginInfo("sn"));

		com.confirm("[언론보도신청]해당신청 건을 삭제 하시려면 [확인]을 클릭하세요.", function(ret) {
			if (ret) {
				com.file.deleteAll( function() {			
					com.execute(sbm_deletePressPrReq, {
						successCallback : function(e) { // 성공 콜백
							com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
								if (dma_param.get("param4") == "apr") {
									com.openTabMenu("A010", {});
								} else {
									scwin.backToList();							
								}
							});
						}
					});
				});
			}
		});
	};

	/**
	 * 저장 버튼 클릭 이벤트
	 */		
	scwin.btn_save_onclick = function(e) {
		dma_info.set("approval", "false");
		dma_info.set("apr_state", "XAD005");	
		scwin.setRptgMediaClsf();	

		if (!scwin.preSave())
		{
			return;
		}		
		com.confirm("message.xom.wq.017" /*저장 하시겠습니까?*/, function(ret) {
			if (ret) {
				scwin.save();
			}
		});		
	};
			
	/**
	 * 저장
	 */
	scwin.save = function() {
		dma_info.set("req_psn_syspayno", com.getLoginInfo("sn"));
		dma_info.set("regst_syspayno", com.getLoginInfo("sn"));
		dma_info.set("updt_syspayno", com.getLoginInfo("sn"));
		dma_info.set("dept_cd", com.getLoginInfo("deptCode"));
		dma_info.set("dept_new_ymd", com.getLoginInfo("deptNewYmd"));
		dma_info.set("doc_id",scwin.data.doc_id);
		dma_info.set("service_id",scwin.data.service_id);
		dma_info.set("empno", com.getLoginInfo("empno"));
	
		com.file.transport( function(attachObj) {	
			dma_info.set("attach_file_mngmt_no", attachObj.upload_A);
			
			var attaches = [];
			if (attachObj.upload_A !="") {
				attaches.push(attachObj.upload_A);
			}
			dma_info.set("validAttachNo", attaches.join(","));			

			com.execute(sbm_savePressPrReq, {
				successCallback : function(e) { // 성공 콜백
					if (dma_info.get("approval")=="true") {
						com.movePageMenu("A001", {"reqNo" :  dma_info.get("req_no")});						
					} else {
						com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
							scwin.search();
						});						
					}	
				} 
			});
		});
	};
	
	/**
	 * 저장전 확인
	 */
	scwin.preSave = function() {
		var flag = (dma_info.get("req_clsf")=="LPA001") ? true : false;
		if (!scwin.validate(flag)) {
			return false;
		}
		return true;
	}	
	
	/*
     * 검증
     */
	scwin.validate = function(flag) {
		if (!com.validateData(dma_info,[
			{ id : "req_ymd", mandatory : true},	
			{ id : "rptg_hope_ymd", mandatory : true},						
			{ id : "req_clsf", mandatory : true},
			{ id : "rptg_media_clsf", mandatory : flag},								
			{ id : "rptg_contnt", mandatory : true, maxLength : 330},
			{ id : "rptg_resn", mandatory : false, maxLength : 330},						
		], false)) return false;
		
		//날짜 유효성 검증
		if (!com.isDate(ica_req_ymd.getValue(), false)) {
			return false;
		}			
		if (!com.isDate(ica_rptg_hope_ymd.getValue(), false)) {
			return false;
		}
					
		return true;
	};
		
	/**
	 * dma_info 보도매체구분 데이터 세팅
	 */	
	scwin.setRptgMediaClsf = function() {
		var reqClsf = dma_info.get("req_clsf");
		var rptgMediaClsf = "";
		if (reqClsf =="LPA001") {
			if (cbx_rptg_media_clsf1.getValue()=="Y") {
				rptgMediaClsf += "1";
			} else {
				rptgMediaClsf += "0";
			}

			if (cbx_rptg_media_clsf2.getValue()=="Y") {
				rptgMediaClsf += "2";
			} else {
				rptgMediaClsf += "0";
			}

			if (cbx_rptg_media_clsf3.getValue()=="Y") {
				rptgMediaClsf += "3";
			} else {
				rptgMediaClsf += "0";
			}			

			if (cbx_rptg_media_clsf4.getValue()=="Y") {
				rptgMediaClsf += "4";
			} else {
				rptgMediaClsf += "0";
			}
			if (rptgMediaClsf=="0000") {
				rptgMediaClsf="";
			} else {
				dma_info.set("rptg_media_clsf1", cbx_rptg_media_clsf1.getValue());
				dma_info.set("rptg_media_clsf2", cbx_rptg_media_clsf2.getValue());
				dma_info.set("rptg_media_clsf3", cbx_rptg_media_clsf3.getValue());
				dma_info.set("rptg_media_clsf4", cbx_rptg_media_clsf4.getValue());				
			}
		} else {
			rptgMediaClsf="0000";
		}
		dma_info.set("rptg_media_clsf", rptgMediaClsf);
		
	};		

	/**
	 * 주요내용 키입력 이벤트
	 */	
	scwin.txa_rptg_contnt_onkeyup = function(e) {
		tbx_cnt_1.setValue(this.getValue().length);
	};

	/**
	 *  사유 및 기타사항 키입력 이벤트
	 */		
	scwin.txa_rptg_resn_onkeyup = function(e) {
		tbx_cnt_2.setValue(this.getValue().length);
	};
	
	
	/*
     * 조회 실행
     */	
	scwin.search = function() {
		com.execute(sbm_selectPressPrReq, {
			successCallback : function(e) {
				dma_info.setJSON(e.responseJSON.elData);
				scwin.triggerReqClsf(dma_info.get("req_clsf"));
				//첨부파일 세팅
				var vMode = "edit";
				if (dma_info.get("apr_state") >= "XAD020") {
					vMode = "view";
				} else {
					if (dma_info.get("req_psn_syspayno") != com.getLoginInfo("sn")) {
						vMode = "view";
					}
				}				
				
				var attachFileNo = dma_info.get("attach_file_mngmt_no").trim();
				com.file.create(upload_A.getID(), {fileKey: (attachFileNo == "") ? scwin.data.service_id :attachFileNo, mode: vMode});
				
				scwin.compControll(scwin.data.isRoleYn);
			}
		});
	};	

	
	/**
	 * 화면 콤포넌트 컨트롤
	 */	
	scwin.compControll = function() {
	    com.biz.disabledButtons(dma_info.get("apr_state"), dma_info.get("req_no"));

		if (dma_info.get("apr_state") >= "XAD020") {
			ica_req_ymd.setDisabled(true);
			ica_rptg_hope_ymd.setDisabled(true);
			sbx_req_clsf.setDisabled(true);
			ibx_press_nm.setDisabled(true);
			ibx_charg_nm.setDisabled(true);
			ibx_tel.setDisabled(true);
			cbx_rptg_media_clsf4.setDisabled(true);
			cbx_rptg_media_clsf3.setDisabled(true);
			cbx_rptg_media_clsf2.setDisabled(true);
			cbx_rptg_media_clsf1.setDisabled(true);
			ibx_email.setDisabled(true);
			txa_rptg_contnt.setDisabled(true);
			txa_rptg_resn.setDisabled(true);
			
			if (dma_info.get("apr_state") == "XAD100") {
				btn_print.setDisabled(false);
			}
		} else {
			if (dma_info.get("req_psn_syspayno") != com.getLoginInfo("sn")) {
				ica_req_ymd.setDisabled(true);
				ica_rptg_hope_ymd.setDisabled(true);
				sbx_req_clsf.setDisabled(true);
				ibx_press_nm.setDisabled(true);
				ibx_charg_nm.setDisabled(true);
				ibx_tel.setDisabled(true);
				cbx_rptg_media_clsf4.setDisabled(true);
				cbx_rptg_media_clsf3.setDisabled(true);
				cbx_rptg_media_clsf2.setDisabled(true);
				cbx_rptg_media_clsf1.setDisabled(true);
				ibx_email.setDisabled(true);
				txa_rptg_contnt.setDisabled(true);
				txa_rptg_resn.setDisabled(true);				
				btn_save.setDisabled(true);
				btn_apr.setDisabled(true);
				btn_del.setDisabled(true);
			}
		}	
	};
