head.script:
    /****************************************************
 	 * 화면 설명 : 과제정보
 	 * 변경 이력 : 2023-11-02	김준하	최초 작성
 	 ****************************************************/
 	scwin.data = {};
	/**
	 * 페이지 로딩 이벤트
	 */   
	scwin.onpageload = function() {	
		// Default : 기본정보                
        var tabId = "tab01";
        scwin.data.selTab01 = 0;        // 개발목표
        scwin.data.selTab02 = 0;        // 참여 연구원
        scwin.data.selTab03 = 0;        // 참여기관 및 참여 업체
        scwin.data.selTab04 = 0;        // 연구 기자재 및 시작품
        scwin.data.selTab05 = 0;        // 개발성과 및 성과      
		prvPrjNo = (com.isEmpty(com.getParameter("prj_no")))? WebSquare.net.getParameter("prj_no") : com.getParameter("prj_no");
		dma_search.set("tab_id", tabId);
		dma_search.set("prj_no", prvPrjNo);
		scwin.search();
		scwin.setTabData(tabId);
	};	
	scwin.onpageunload = function() {
	};

	/**
	 * 조회
	 */
	scwin.search = function(){
		com.execute(sbm_selectPrjList, {
			successCallback : function(e){
				// 사업구분
				var dispData = "";
				dispData += "관계부처: " + e.responseJSON.elData.bsns_cd_ofic_nm;
				dispData += "          사업: " + e.responseJSON.elData.bsns_cd_bsns_nm;
				dispData += "          세부사업: " + e.responseJSON.elData.bsns_cd_detls_bsns_nm;                    				
				ibx_in_out.setValue(dispData.trim());

                // 과제책임자
                var currReschRspns = "";
                currReschRspns += e.responseJSON.elData.curr_resch_rspns_nm;
                currReschRspns += " 부서: " + e.responseJSON.elData.dept_nm;
                ibx_curr_resch_rspns.setValue(currReschRspns);				
			}
		});
	};

	/**
	 * 닫기버튼클릭 이벤트
	 */
	scwin.btn_close_onclick = function(e) {
		if(window.location.pathname == '/popup.jsp'){
			window.close();
		}else{
			com.closePopup();
		}
	};

	/**
	 * 탭변경 이벤트
	 */	
	scwin.tabContainer_ontabclick = function(tabID,idx) {	    
	    scwin.setTabData(tabID.replace("project_wframe_tabContainer_tab_", ""));
	};	
	scwin.setTabData = function(changeTabId) {
		switch (changeTabId) {
		    case "tab01":
		        // 개발목표
		        scwin.handleTabChange(changeTabId, "selTab01");
		        break;
		    case "tab02":
		        // 참여연구원
		        scwin.handleTabChange(changeTabId, "selTab02");
		        break;
		    case "tab03":
		        // 참여 업체
		        scwin.handleTabChange(changeTabId, "selTab03");
		        break;
		    case "tab04":		    
		        // 연구 기자재
		        scwin.handleTabChange(changeTabId, "selTab04", {
			        "sch_prj_no": dma_search.get("prj_no"),
		            "anal": "",
		            "chng_item": "RCG006"
		        });
		        break;
		    case "tab05":
		        // 개발성과
		        scwin.handleTabChange(changeTabId, "selTab05", {
		            "bsns_req_no_plan": "",
		            "bsns_req_no": ""
		        });
		        break;
		}	
	}
	scwin.handleTabChange = function(tabId, dataKey, extraData) {
	    if (scwin.data[dataKey] === 0) {
	        scwin.data[dataKey] = 1;
	        dma_search.set("tab_id", tabId);
	        dma_search.set("p_prj_no", dma_search.get("prj_no"));

	        // 추가 데이터 설정
	        if (extraData) {
	            Object.entries(extraData).forEach(([key, value]) => {
	                dma_search.set(key, value);
	            });
	        }	
	        scwin.selectTab();
	    }
	}	

	/**
	 * 탭 조회
	 */
	scwin.selectTab = function(){
		com.execute(sbm_selectTabList, {
			successCallback : function(e){
            var data = e.responseJSON.elData;   

			}
		});
	};
