head.script:
    /****************************************************
 	 * 화면 설명 : 기관기본정보등록
 	 * 변경 이력 : 2023-11-06	김준하	최초 작성
 	 ****************************************************/
 	scwin.data = {};
	/**
	 * 페이지 로딩 이벤트
	 */   	        
	scwin.onpageload = function() {
		scwin.data.f_chk_duplicate = "N"; //중복검사 여부 
        scwin.userSyspayno = com.getLoginInfo("sn");
        scwin.flag =  com.getParameter("flag");
       	scwin.agncyCd = com.getParameter("agncyCd");
		// 공통코드
		var codeOptions = [{ code : "XAC", compID :"sbx_issu_tel_gu,sbx_issu_tel_gu02"},
						   { code : "RFK", compID :"sbx_dom_excpt_clsf" },
						   { code : "FDA", compID :"sbx_natn_cd" },
						   { code : "LCC", compID :"sbx_agncy_clsf" }];
		com.setCmnCd(codeOptions, function() {
			// enter key event
			com.setEnterKeyEvent(flex_searh, scwin.btn_agncy_dup_check_onclick);			
	       	if (scwin.flag == "Update") {
	       		dma_search.set("cust_no", scwin.agncyCd);
	       		scwin.searchAgncyInfo();

	       		sbx_dom_excpt_clsf.setDisabled(true);
	       		btn_agncy_dup_check.setDisabled( true );
	       	} else if (scwin.flag == "UserInsert") {  //사용자 입력시(경우의수 : 해외출장신청서, 해외출장복명서)
	       		ibx_relat_vend_cd.setDisabled( true );
	       		btn_vend_cd.setDisabled( true );
	       		sbx_intr_yn.setDisabled( true );
	       		txa_intrc_stop_resn.setDisabled( true );
	       		grp_dom_tel.hide();
	       	   	grp_dom_fax.hide();
	       	   	sbx_intr_yn.setValue("N");

	       	} else {
	       	   //기관기본정보에서 신규버튼 클릭시
	       	   grp_dom_tel.hide();
	       	   grp_dom_fax.hide();
	       	} 								
		});	       	
	};	
	scwin.onpageunload = function() {		
	};

	/**
	 * 조회
	 */
	scwin.searchAgncyInfo = function(){
		com.execute(sbm_selectOrgList, {
			successCallback : function(e){
				scwin.mobileNo = e.responseJSON.elData.lplCustBaseInfoVo.reprs_psn_mobile_no;
				scwin.tel = e.responseJSON.elData.lplCustBaseInfoVo.cmpy_reprs_tel;
				scwin.fax = e.responseJSON.elData.lplCustBaseInfoVo.cmpy_fax_no;
				scwin.cmpPostNo = e.responseJSON.elData.lplCustBaseInfoVo.cmpy_post_no;
				dma_result.setJSON(e.responseJSON.elData.lplCustBaseInfoVo);
				scwin.postSetting();
			}
		});
	};

	scwin.postSetting = function() {
		var pData = [];
		if (scwin.cmpPostNo.trim() != "") {
			pData = scwin.cmpPostNo.split("-");	
			var postNo1 = pData[0].trim();
			var postNo2 = pData[1];
			ibx_post01.setValue(postNo1);
			ibx_post02.setValue(postNo2);
		}

		if (sbx_dom_excpt_clsf.getValue() == "RFK001") {
			grp_dom_tel.show("");
			grp_dom_fax.show("");
			ibx_tel_no.hide();
			ibx_fax_no.hide();
			grp_nm_eng_th.hide();
			grp_nm_eng_td.hide();

			var tData = [];
			var fData = [];
			var mData = [];
			if (scwin.tel.trim() != "") {
				tData = scwin.tel.split("-");	
				var telNo1 = tData[0].trim();
				var telNo2 = tData[1];
				var telNo3 = tData[2];				
				sbx_issu_tel_gu.setText(telNo1);
				ibx_tel_no01.setValue(telNo2);
				ibx_tel_no02.setValue(telNo3);
			}
			//팩스
			if(scwin.fax.trim() != "") {
				fData = scwin.fax.split("-");	
				var faxNo1 = fData[0].trim();
				var faxNo2 = fData[1];
				var faxNo3 = fData[2];				
				sbx_issu_tel_gu02.setText(faxNo1);
				ibx_fax_no01.setValue(faxNo2);
				ibx_fax_no02.setValue(faxNo3);
			}
		}
		else {
			grp_dom_tel.hide();
			grp_dom_fax.hide();
			ibx_tel_no.show("");
			ibx_fax_no.show("");
			grp_nm_eng_th.show("");
			grp_nm_eng_td.show("");

			ibx_tel_no.setValue(scwin.tel);
			ibx_fax_no.setValue(scwin.fax);
		}		
	};		
	scwin.sbx_dom_excpt_clsf_onchange = function(info) {
		ibx_natn_land.setValue("");
		if (sbx_dom_excpt_clsf.getValue() != null) {  			
			if (scwin.flag == "Update") {
				if (sbx_dom_excpt_clsf.getValue() == "RFK001") {
	 				sbx_natn_cd.setValue("FDA001");
					var codeOptions = [{ code : "LCO", compID :"sbx_agncy_clsf"}];		
					com.setCmnCd(codeOptions, function(){
						sbx_agncy_clsf.setValue("");
					});
		       		dma_search.set("cust_no", scwin.agncyCd);
		       		scwin.searchAgncyInfo();
	 			} else {
	 				sbx_natn_cd.setValue("");
	 				var codeOptions = [{ code : "LCC", compID :"sbx_agncy_clsf"}];		
					com.setCmnCd(codeOptions, function(){
						sbx_agncy_clsf.setValue("");
					});				
		       		dma_search.set("cust_no", scwin.agncyCd);
		       		scwin.searchAgncyInfo();;
	 			}
	 		} else {	
	 			if (sbx_dom_excpt_clsf.getValue() == "RFK001") { //국내	
	 				sbx_natn_cd.setValue("FDA001");
	 				sbx_issu_tel_gu.setValue("");
	 				sbx_issu_tel_gu.setDisabled(false);
	 				sbx_issu_tel_gu02.setValue("");
	 				sbx_issu_tel_gu02.setDisabled(false);
	 				ibx_tel_no.setValue("");
	 				ibx_fax_no.setValue("");
					var codeOptions = [{ code : "LCO", compID :"sbx_agncy_clsf"}];		
					com.setCmnCd(codeOptions, function(){
					});
	 				sbx_natn_cd.setDisabled(true);

					grp_dom_tel.show("");
					grp_dom_fax.show("");
					ibx_tel_no.hide();
					ibx_fax_no.hide();
					grp_nm_eng_th.hide();
					grp_nm_eng_td.hide();				
	 			} else { //국외		
	 				sbx_natn_cd.setValue("");
	 				sbx_issu_tel_gu.setValue("");
	 				sbx_issu_tel_gu.setDisabled(true);
	 				sbx_issu_tel_gu02.setValue("");
	 				sbx_issu_tel_gu02.setDisabled(true);
	 				var codeOptions = [{ code : "LCC", compID :"sbx_agncy_clsf"}];		
					com.setCmnCd(codeOptions, function(){
					});		
	 				sbx_natn_cd.setDisabled(false);
					grp_dom_tel.hide();
					grp_dom_fax.hide();
					ibx_tel_no.show("");
					ibx_fax_no.show("");
					grp_nm_eng_th.show("");
					grp_nm_eng_td.show("");			 				
	 			}	    		
	 		}
	 	}		
	};

	//거래처 팝업
	scwin.btn_vend_cd_onclick = function(e) {
		com.pop.open(com.pop.TYPE.VEND, {ref_yn : "Y"}, function(ret){
			ibx_relat_vend_cd.setValue(ret.vend_cd);
		});		
	};
	//중복검사
	scwin.btn_agncy_dup_check_onclick = function(e) {
     	if (ibx_kr_char_agncy_nm.getValue().trim() != "") {
	     	dma_chk_search.set("chk_text", '%'+ibx_kr_char_agncy_nm.getValue().trim()+'%');
			com.execute(sbm_selectChk, {
				successCallback : function(e){
					floatingLayer1.show();
					grd_agncyList.setJSON(e.responseJSON.elData.lplCustBaseInfoVoList);
					scwin.f_chk_duplicate = "Y";
				}
			});
      	} else {
      		alert("[알림] 기관명(한글)을 입력하세요.");
      		ibx_kr_char_agncy_nm.focus();
      	} 			
	};

	scwin.sbx_natn_cd_onchange = function(info) {
		var rows = dlt_cmnCd_FDA.getMatchedJSON("comCd", dma_result.get("natn_cd"), true)[0]; //
		if (rows != null) {				
			ibx_natn_land.setValue(rows.mngmtItem7); 
		}		
	};

	// 저장 버튼 클릭
	scwin.btn_save_onclick = function(e) {
 		if (scwin.flag == "Update") {
			scwin.updateInfo();
		} else {
			if (scwin.f_chk_duplicate == "Y") {
				scwin.insertInfo();
 			} else {
 				alert("[알림] 기관중복검사를 먼저 실행해 주시기 바랍니다.");
 				btn_agncy_dup_check.focus();
 			}
 		}			
	};

	// 수정
	scwin.updateInfo = function(e) {
		dma_mer.set("dom_excpt_clsf", dma_result.get("dom_excpt_clsf")); //국내외구분
		dma_mer.set("agncy_clsf", dma_result.get("agncy_clsf")); //기관분류
		dma_mer.set("cmpy_nm_krchar", dma_result.get("cmpy_nm_krchar")); //기관명(한글)
		dma_mer.set("cmpy_nm_eng", dma_result.get("cmpy_nm_eng")); //기관명(영어)
		dma_mer.set("natn_cd", dma_result.get("natn_cd")); //국가명
		if (!scwin.vaildCheck()) return;
	       	var tmpMobileNo = "";
	       	var telNo = "";
	       	var faxNo = "";
	       	var postNo = ibx_post01.getValue() + "-" + ibx_post02.getValue();

        	if (sbx_dom_excpt_clsf.getValue() == "RFK001") {
        		if (sbx_issu_tel_gu.getValue() !="") {
        			telNo = sbx_issu_tel_gu.getText() +"-"+ ibx_tel_no01.getValue()+"-"+ibx_tel_no02.getValue();
        		}
        		if (sbx_issu_tel_gu02.getValue() !="") {	        		
        			faxNo = sbx_issu_tel_gu02.getText() + "-"+ ibx_fax_no01.getValue()+"-"+ibx_fax_no02.getValue();
        		}
      		} else {
	        		telNo = ibx_tel_no.getValue();
	        		faxNo = ibx_fax_no.getValue();
	        }
	        dma_mer.set("cust_no", scwin.agncyCd); 
	        dma_mer.set("cmpy_typ", "RZB100"); 
	        dma_mer.set("supt_clsf", "000000"); 
	        dma_mer.set("cmpy_nm_eng_short", ibx_cmpy_nm_eng_short.getValue());         
           	if( sbx_natn_cd.getText() != "-선택-"){
	           	dma_mer.set("natn_nm", sbx_natn_cd.getText()); 
           	}else{
	           	dma_mer.set("natn_nm", ""); 
           	}
            dma_mer.set("reprs_psn", ibx_reprs_psn_nm.getValue());
            dma_mer.set("cmpy_post_no", postNo);
            dma_mer.set("cmpy_addr", ibx_addr.getValue());
            dma_mer.set("cmpy_reprs_tel", telNo);
            dma_mer.set("cmpy_fax_no", faxNo);
            dma_mer.set("reprs_psn_mobile_no", tmpMobileNo);
            dma_mer.set("homepg", ibx_hpage.getValue());
            dma_mer.set("closebiz_ex", sbx_intr_yn.getValue());          
            dma_mer.set("factry_use_ex", "N");
            dma_mer.set("reprs_clsf", "Y");
            dma_mer.set("wrte_complt", "Y");
            dma_mer.set("rmk", txa_rmk.getValue());
            dma_mer.set("closebiz_cause", txa_intrc_stop_resn.getValue());
            dma_mer.set("updt_psn_syspayno", scwin.userSyspayno);
            dma_mer.set("relat_vend_cd", ibx_relat_vend_cd.getValue().trim());
            dma_mer.set("cud_type", "U");
			com.confirm("message.xom.wq.017", function(ret) {            
				com.execute(sbm_saveData, {
					successCallback : function(e){
						com.alert("message.xom.wq.023", function() {
							com.closePopup({ research : true });
						});
					}
				});
			});           		

	}
	// 저장
	scwin.insertInfo = function(e) {
		dma_mer.set("dom_excpt_clsf", dma_result.get("dom_excpt_clsf")); //국내외구분
		dma_mer.set("agncy_clsf", dma_result.get("agncy_clsf")); //기관분류
		dma_mer.set("cmpy_nm_krchar", dma_result.get("cmpy_nm_krchar")); //기관명(한글)
		dma_mer.set("cmpy_nm_eng", dma_result.get("cmpy_nm_eng")); //기관명(영어)
		dma_mer.set("natn_cd", dma_result.get("natn_cd")); //국가명

		if (!scwin.vaildCheck()) return;
	       	var tmpMobileNo = "";
	       	var telNo = "";
	       	var faxNo = "";
	       	var postNo = ibx_post01.getValue() + "-" + ibx_post02.getValue();

        	if (sbx_dom_excpt_clsf.getValue() == "RFK001") {
        		if (sbx_issu_tel_gu.getValue() !="") {
        			telNo = sbx_issu_tel_gu.getText() +"-"+ ibx_tel_no01.getValue()+"-"+ibx_tel_no02.getValue();
        		}
        		if (sbx_issu_tel_gu02.getValue() !="") {	        		
        			faxNo = sbx_issu_tel_gu02.getText() + "-"+ ibx_fax_no01.getValue()+"-"+ibx_fax_no02.getValue();
        		}
      		} else {
	        		telNo = ibx_tel_no.getValue();
	        		faxNo = ibx_fax_no.getValue();
	        }
	        dma_mer.set("cust_no", scwin.agncyCd); 
	        dma_mer.set("cmpy_typ", "RZB100"); 
	        dma_mer.set("supt_clsf", "000000"); 
	        dma_mer.set("cmpy_nm_eng_short", ibx_cmpy_nm_eng_short.getValue());         
           	if( sbx_natn_cd.getText() != "-선택-"){
	           	dma_mer.set("natn_nm", sbx_natn_cd.getText()); 
           	}else{
	           	dma_mer.set("natn_nm", ""); 
           	}
            dma_mer.set("reprs_psn", ibx_reprs_psn_nm.getValue());
            dma_mer.set("cmpy_post_no", postNo);
            dma_mer.set("cmpy_addr", ibx_addr.getValue());
            dma_mer.set("cmpy_reprs_tel", telNo);
            dma_mer.set("cmpy_fax_no", faxNo);
            dma_mer.set("reprs_psn_mobile_no", tmpMobileNo);
            dma_mer.set("homepg", ibx_hpage.getValue());
            dma_mer.set("closebiz_ex", sbx_intr_yn.getValue());          
            dma_mer.set("factry_use_ex", "N");
            dma_mer.set("reprs_clsf", "Y");
            dma_mer.set("wrte_complt", "Y");
            dma_mer.set("cmpy_typ_clsf", "");
            dma_mer.set("rmk", txa_rmk.getValue());
            dma_mer.set("closebiz_cause", txa_intrc_stop_resn.getValue());
            dma_mer.set("regst_psn_syspayno", scwin.userSyspayno);
            dma_mer.set("relat_vend_cd", ibx_relat_vend_cd.getValue().trim());
            dma_mer.set("cud_type", "I");
			com.confirm("message.xom.wq.017", function(ret) {            
				com.execute(sbm_saveData, {
					successCallback : function(e){
						com.alert("message.xom.wq.023", function() {
							if (scwin.flag == "UserInsert") {
								com.closePopup({});
							} else {
								com.closePopup({ research : true });
							}						
						});
					}
				});
			});   			
	}	
	// 유효성 검사
	scwin.vaildCheck = function(e) {
		if (!com.validateData(dma_mer,[
				 { id : "dom_excpt_clsf", mandatory : true }//국내외구분
				,{ id : "agncy_clsf", mandatory : true }//기관분류
				,{ id : "cmpy_nm_krchar", mandatory : true }//기관명(한글)
				,{ id : "cmpy_nm_eng", mandatory : true }//기관명(영어)
				,{ id : "natn_cd", mandatory : true }//국가명
			], false)
		) return false;
		return true;		
	}		
	// 닫기
	scwin.btn_cancel_onclick = function(e) {
		if(window.location.pathname == '/popup.jsp'){
			window.close();
		}else{
			com.closePopup();
		}		
	};
