head.script:

			scwin.param = {};
			scwin.onpageload = function() {

				// 첨부파일 컴포넌트 초기화
                com.file.init();

				scwin.param.args = com.getParameter().args || "";
				scwin.param.reqNo = com.getParameter().app_req_no || "";
				scwin.param.xml_id = com.getParameter().xml_id || "";
				scwin.param.reqNoVal = com.getParameter().req_no || ""; // 발명신고서 신청번호
				scwin.param.mngmt_no = com.getParameter().mngmt_no || "";
				scwin.param.his_seq = com.getParameter().his_seq || "";
				scwin.param.skate_clsf = com.getParameter().skate_clsf || "";			

				// 업무 대기함
				scwin.param.param1 = com.getParameter().param1 || ""; // 화면ID
				scwin.param.param2 = com.getParameter().param2 || ""; // 문서번호 
				scwin.param.param3 = com.getParameter().param3 || ""; // 결재상태값
				scwin.param.param4 = com.getParameter().param4 || ""; // 업무대기함의경우 (= apr)

				scwin.param.saveClsf = "I";
				scwin.param.aprState = "XAD005";
				scwin.param.chrgSysPayNo = "";
				scwin.param.scrnClsf = "";

				var codeOptions = [
				    { code: "PDT", compID: "grid1_a:blong_clsf, grid1_b:blong_clsf" },
				    { code: "RHN", compID: "grid1_a:chng_state" },
				    { code: "HAG", compID: "grid1_a:work_clsf" }
				];
				com.setCmnCd(codeOptions);


				const serviceId = com.getLoginInfo("serviceId");


				// 관리자화면(:IB05)   사용자화면(:I042)
				if (serviceId === "IB05") {
				    btn_apr.hide(); // 결재신청
				    btn_del.hide(); // 삭제버튼
				    scwin.param.scrnClsf = "mgr";
				} else {
				    if (scwin.param.reqNo === "" && scwin.param.his_seq !== "") {
				        btn_del.hide();
				        btn_save.hide();
				        btn_apr.hide();
				    }
				}

				if (scwin.param.param4 === "apr" && scwin.param.param2 !== "") {
				    scwin.param.reqNo = scwin.param.param2.trim();
				    const reqNo = scwin.param.reqNo;
				    if (reqNo.substring(0, 3) === "I09") {
				        radioSkateClsf.setValue("PAS099"); // 연구과제
				    } else if (reqNo.substring(0, 3) === "I08") {
				        radioSkateClsf.setValue("PAS020"); // 출원인
				    } else {
				        radioSkateClsf.setValue("PAS010"); // 발명인
				    }   
				}

				if (scwin.param.reqNo !== "" || scwin.param.his_seq !== "") {
				    inputReqNo.setValue(scwin.param.reqNo);
				    scwin.param.saveClsf = "U";
				    radioSkateClsf.setDisabled(true);
				    if (scwin.param.param4 !== "apr") {
				        radioSkateClsf.setValue(scwin.param.skate_clsf);
				    }
				    scwin.search();
				} else {
				    // 초기값 설정
				    const empno = com.getLoginInfo("empno");
				    const cn = com.getLoginInfo("cn");
				    const sn = com.getLoginInfo("sn");

				    radioSkateClsf.setValue("PAS010");

				    dma_search.set("empno", empno);
				    dma_search.set("nm", cn);
				    dma_search.set("syspayno", sn);
				    inputCalendarInputYmd.setValue(com.getServerDateTime("yyyyMMdd"));
				    com.file.create(upload_A.getID(), { fileKey: serviceId, mode: 'edit', target: "IPMS" });
				}

				const info = {newValue: radioSkateClsf.getValue()};
				scwin.skate_clsf_change(info);

			};

			scwin.search = function() {
			    if (scwin.param.reqNo !== "") {
			        dma_search.set("elecapp_docu_no", inputReqNo.getValue());
			    } else {
			        dma_search.set("req_no", scwin.param.reqNo); // 발명신고서 신청번호
			        dma_search.set("mngmt_no", scwin.param.mngmt_no);
			        if (radioSkateClsf.getValue() === "PAS099") {
			            dma_search.set("seq", scwin.param.his_seq);            
			        } else {
			            dma_search.set("his_seq", scwin.param.his_seq);
			        }

			    }
			    dma_search.set("skate_clsf", radioSkateClsf.getValue());
			    com.execute(sbm_svcpatappchnr02);
			};

			scwin.sbm_svcpatappchnr02_done = function(e) {

			      com.trimDataCollection(dlt_before);
			      com.trimDataCollection(dlt_after);
			      com.trimDataCollection(dma_apr_result);      

			      let tmpHisSeq = "";
			      if (radioSkateClsf.getValue() === "PAS099") {

			          tmpHisSeq = dlt_after.getRowJSON(0).seq
			      } else {
			          tmpHisSeq = dlt_after.getRowJSON(0).his_seq
			          scwin.param.mngmt_no = dlt_after.getRowJSON(0).mngmt_no
			      }
			      // 발명신고서 신청번호 및 지재권 관리번호
			      scwin.param.reqNoVal = dlt_after.getRowJSON(0).req_no

			      // 변경차수
			      textboxAfSeq.setValue(tmpHisSeq);
			      textboxBfSeq.setValue(parseInt(tmpHisSeq) - 1);

			      textareaChngCause.setValue( dlt_after.getRowJSON(0).acdt_unusual_item ); // 변경사유
			      dma_search.set("empno", dlt_after.getRowJSON(0).empno); // 신청자사번
				  dma_search.set("nm", dlt_after.getRowJSON(0).nm); // 신청자
				  dma_search.set("syspayno", dlt_after.getRowJSON(0).regst_syspayno); // 신청자개인번호
			      inputCalendarInputYmd.setValue( dlt_after.getRowJSON(0).input_ymd ); //변경일자

			      const attachFileNo = dlt_after.getRowJSON(0).attach_file_no;
			      com.file.create(upload_A.getID(), { fileKey: attachFileNo ? attachFileNo : com.getLoginInfo("serviceId"), mode: 'edit', target: "IPMS" });


			      const skateClsf = radioSkateClsf.getValue();


			      if(skateClsf == "PAS010"){
			            dlt_before_grid1.setJSON(dlt_before.getAllJSON());
			            dlt_after_grid1.setJSON(dlt_after.getAllJSON());
			      }else if(skateClsf == "PAS020"){
			            dlt_before_grid2.setJSON(dlt_before.getAllJSON());
			            dlt_after_grid2.setJSON(dlt_after.getAllJSON());
			      }else{
			            dlt_before_grid3.setJSON(dlt_before.getAllJSON());
			            dlt_after_grid3.setJSON(dlt_after.getAllJSON());
			      }

			      dlt_after_grid1.getAllJSON().forEach((item, index) => {
			      	scwin.post_search(index, scwin.param.skate_clsf);			      	
			      });			

			      scwin.param.aprState = dma_apr_result.get("apr_state") === '' ? scwin.param.aprState : dma_apr_result.get("apr_state");

			      // 기본정보 조회(작성중 문서함에서 화면전환)
			      if (scwin.param.param4 === "apr" && scwin.param.saveClsf === "U") {}
			      // wframe 기본조회
			      WFM_BASE_INFO.getWindow().scwin.retrieve_default_info(scwin.param.reqNoVal, scwin.param.mngmt_no);			
			};


			scwin.post_search = function(index, type) {
				if (type === "PAS010") {
		            if (grid1_a.getCellData(index, "blong_clsf") === "PDT020") {
		                grid1_a.setCellReadOnly(index, "nm_eng", false);
		                grid1_a.setCellReadOnly(index, "resid_regst_no", false);
		                grid1_a.setCellReadOnly(index, "syspayno", false);
		                grid1_a.setCellReadOnly(index, "contac_loc", false);
		                grid1_a.setCellReadOnly(index, "blong_nm", false);
		                grid1_a.setCellReadOnly(index, "email", false);
		                grid1_a.setCellReadOnly(index, "addr", false);
		            } else {
		                grid1_a.setCellReadOnly(index, "nm_eng", true);
		                grid1_a.setCellReadOnly(index, "resid_regst_no", true);
		                grid1_a.setCellReadOnly(index, "syspayno", true);
		                grid1_a.setCellReadOnly(index, "contac_loc", true);
		                grid1_a.setCellReadOnly(index, "blong_nm", true);
		                grid1_a.setCellReadOnly(index, "email", true);
		                grid1_a.setCellReadOnly(index, "addr", true);
		            }
		        }			    
			};


			scwin.approval_click = function() {
				com.confirm("결재 하시겠습니까?", function(ret) {
                    if (ret) {
	                    scwin.pre_approval();                    	
                    }
                });
			};


			scwin.pre_approval = function() {

			    dma_search.set("req_no", scwin.param.reqNoVal);
			    dma_search.set("mngmt_no", scwin.param.mngmtNoVal);

			    if (scwin.param.skateClsf == "PAS020") {			
			        com.execute(sbm_svcpatappchnr04, { // getInvenPsn
			            successCallback: function(e) {
			            	//dlt_before_grid1
			                scwin.approval();
			            }
			        });
			    } else if (scwin.param.skateClsf == "PAS099") {			
			        com.execute(sbm_svcpatappchnr05, { // getMainInvenPsn
			            successCallback: function(e) {
			                scwin.approval();
			            }
			        });
			    } else {
			        scwin.approval();
			    }
			};


			scwin.approval = function() {
			    const skateClsf = radioSkateClsf.getValue();
			    let invntPsnId = "";
			    let outCnt = 0;

				function processGrid(dltObj, conditions, isBefore) {
				    for (var i = 0; i < dltObj.getDataLength(); i++) {
				        var row = dltObj.getRowJSON(i);

				        if (conditions.belongClass && row.blong_clsf !== conditions.belongClass) {
				            outCnt++;
				            continue;
				        }

				        if (row.work_clsf === conditions.workClass) {
				            var sysPayNo = isBefore ? row.syspayno : row.resch_respn_syspayno;
				            if (invntPsnId.indexOf(sysPayNo) < 0) {
				                invntPsnId += sysPayNo + ";";
				            }
				        } else if (conditions.incrementOutCntOnOtherWorkClass) {
				            outCnt++;
				        }
				    }
				}

				if (skateClsf === "PAS010") { // 공동발명자
				    var conditions = { belongClass: "PDT010", workClass: "HAG010", incrementOutCntOnOtherWorkClass: true };
				    processGrid(dlt_before_grid1, conditions, true);
				    processGrid(dlt_after_grid1, conditions, true);
				} else if (skateClsf === "PAS020") { // 출원인
				    var conditions = { workClass: "HAG090", incrementOutCntOnOtherWorkClass: false };
				    processGrid(dlt_before_grid1, conditions, true);
				} else {
				    var conditions = { workClass: "HAG010" };
				    processGrid(dlt_before_grid3, conditions, false);
				    processGrid(dlt_after_grid3, conditions, false);
				}

			    const uid = com.getLoginInfo("uid");
			    const sn = com.getLoginInfo("sn");
			    const serviceId = com.getLoginInfo("serviceId");
			    const aprState = scwin.param.aprState;

			    dma_approval.set("login_user_id", uid);
			    dma_approval.set("endpoint", uid);
			    dma_approval.set("req_no", inputReqNo.getValue());
			    dma_approval.set("reporter_id", sn);
			    dma_approval.set("ui_id", serviceId);
			    dma_approval.set("apr_state", aprState);
			    dma_approval.set("info_cd", textareaChngCause.getValue());
			    dma_approval.set("ext1_cd", "");
			    dma_approval.set("ext2_cd", "");


			    if (skateClsf == "PAS099") {
			        var invntPsnSysPayno = dma_getMainInvenPsn.get("invnt_syspayno")
			        dma_approval.set("main_researcher_id", invntPsnSysPayno);
			    }

			    const chargSyspayno = WFM_BASE_INFO.getWindow().dma_result.get("charg_syspayno");

			    dma_approval.set("researcher_id", invntPsnId);
			    dma_approval.set("manager_id", chargSyspayno);


			    const smitappNo = WFM_BASE_INFO.getWindow().dma_result.get("smitapp_no");
			    const reqPsnNm = dma_search.get("nm") + "(" + dma_search.get("empno") + ")";
				const invntKrnNm = WFM_BASE_INFO.getWindow().dma_result.get("invnt_krn_nm");
				const smitappYmd = WFM_BASE_INFO.getWindow().dma_result.get("smitapp_ymd");

			    if (smitappNo !== "") {
			        dma_approval.set("apply_yn_cd", "Y");
			    } else {
			        dma_approval.set("apply_yn_cd", "N");
			    }

			    dma_approval.set("chng_cause", textareaChngCause.getValue());
			    dma_approval.set("input_ymd", inputCalendarInputYmd.getValue());
			    dma_approval.set("req_psn_nm", reqPsnNm);
			    dma_approval.set("invnt_krn_nm", invntKrnNm);
			    dma_approval.set("smitapp_no", smitappNo);
			    dma_approval.set("smitapp_ymd", smitappYmd);

			    dma_approval.set("fileList_A", dlt_after.getRowJSON(0).attach_file_no);
			    dma_approval.set("skateClsf", skateClsf);


			    if (skateClsf == "PAS010") { // 공동발명자
			        dlt_approval_after.setJSON(dlt_after_grid1.getAllJSON());
			        dlt_approval_before.setJSON(dlt_before_grid1.getAllJSON());
			    } else if (skateClsf == "PAS020") { // 출원인
			        dlt_approval_after.setJSON(dlt_after_grid2.getAllJSON());
			        dlt_approval_before.setJSON(dlt_before_grid2.getAllJSON());
			    } else { // 연구과제
			        dlt_approval_after.setJSON(dlt_after_grid3.getAllJSON());
			        dlt_approval_before.setJSON(dlt_before_grid3.getAllJSON());
			    }

			    com.execute(sbm_svcpatappchnapr);
			};

			scwin.sbm_svcpatappchnapr_done = function(e) {
				const reqNo = e.responseJSON.elData.req_no;
				com.movePageMenu("A001", { "reqNo" : reqNo});
			};

			scwin.skate_clsf_change = function(info) {
				const type = info.newValue;
				$(".I042 .switch-case").wq("setStyle", "display", "none");
				if (type === "PAS010") {
					switchBefore.setSelectedIndex(0);
					switchAfter.setSelectedIndex(0);
					beforeCase1.show();
					afterCase1.show();
				}
				 else if (type === "PAS020") {
				    switchBefore.setSelectedIndex(1);
					switchAfter.setSelectedIndex(1);
					beforeCase2.show();
					afterCase2.show();
				} else {
				    switchBefore.setSelectedIndex(2);
					switchAfter.setSelectedIndex(2);
					beforeCase3.show();
					afterCase3.show();
				}
			};

			scwin.update_grid_row = function(e) {

			    const skateClsf = radioSkateClsf.getValue();
			    const type = $p.getComponentById(e.target.id).getUserData('userData1');

			    if (skateClsf === "PAS010") {
			      	type ? com.addRow(grid1_a) : scwin.delete_row(grid1_a, dlt_after_grid1);
			    }
			    if (skateClsf === "PAS020") {
			      	type ? com.addRow(grid2_a) : scwin.delete_row(grid2_a, dlt_after_grid2);
			    }
			    if (skateClsf === "PAS099") {
					type ? com.addRow(grid3_a) : com.deleteRow(grid3_a);
			    }

			};

			scwin.delete_row = function(gridObj, dltObj) {

				function handleGrid(dltDelGrid, keyField) {
				    let distinctDataArray = dltDelGrid.getColumnDistinctDataArray(keyField);
				    if (distinctDataArray.length > 0) {
				        // keyField 값이 있는 항목들만 필터링합니다.
				        let filteredArray = distinctDataArray.filter(item => item !== null && item !== undefined && item !== '');

				        let newDataArray = filteredArray.map(item => ({
				            [keyField]: item
			            }));
				        dltDelGrid.setJSON(newDataArray);
				    }
				}

				function updateGridData(dltDelGrid) {
				    let gridData = dltDelGrid.getAllJSON();

				    // 각 데이터 행에 대해 필요한 변경을 수행합니다.
				    let updatedData = gridData.map(row => ({
				        ...row, // 기존의 다른 컬럼은 그대로 유지합니다.
				        req_no: scwin.param.reqNo,
				        mngmt_no: scwin.param.mngmt_no,
				        his_seq: textboxAfSeq.getValue(),
				        skate_clsf: radioSkateClsf.getValue()
				    }));

				    dltDelGrid.setJSON(updatedData); // 변경된 데이터로 dltDelGrid를 업데이트합니다.
				}								

			    const data = gridObj.getCheckedJSON('chk');
			    if (data.length < 1) {
			        com.warn('체크된 행이 없습니다.');
			        return;
			    }

			    dlt_deleteList.setJSON(dltObj.getMatchedJSON("chk", "1"), true);

			    // dlt_deleteList 중복 제거
			    handleGrid(dlt_deleteList, "seq");

			    updateGridData(dlt_deleteList);

			    const gridCheckedIndex = gridObj.getCheckedIndex('chk');
			    dltObj.removeRows(gridCheckedIndex);

			};


			scwin.btn_print_onclick = function(e) {
				const node = "";
				com.oz.openPopup({
					ozrCode : "I042",
					param: node
			    });
			};

			scwin.grid1_a_oneditend = function(row, col, value) {
				if(col === 3){ // blong_clsf
					scwin.post_search(row, "PAS010");
				}
			    if (col === 5) {

				    const blongClsf = dlt_after_grid1.getRowJSON(row).blong_clsf;
					if (blongClsf === "") {
					    com.alert("소속구분을 선택해 주시기 바랍니다.");
					    return;
					}

			        const data = {
			            empno: value
			        }
			        com.pop.open(com.pop.TYPE.HUM, data, function(ret) {
			            const retData = {
			                empno: ret.empno,
			                nm_krn: ret.nm,
			                blong_nm: ret.dept_nm,
			                blong_cd: ret.dept_cd,
			                dept_new_ymd: ret.dept_new_ymd,
			                syspayno: ret.syspayno,
			                nm_eng: ret.nm_eng,
			                email: ret.email,
			                addr: ret.addr,
			                resid_regst_no: ret.resid_no,
			                contac_loc: ret.mobile,
			                work_clsf: ret.work_clsf
			            }
			            dlt_after_grid1.setRowJSON(row, retData, true);
			        });
			    }
			};

			scwin.grid2_a_oncellclick = function(row, col, colId) {
				if(colId === "vend_img") scwin.grid2_a_oneditend(row, col);
			};

			scwin.grid2_a_oneditend = function(row, col, value) {

			    if (col === 3 || col === 4) { // vend_nm, vend_img
			        const vendType = dlt_after_grid2.getRowJSON(row).vend_type;
			        if (vendType === "") {
			            com.alert("사업자/개인구분을 선택해 주시기 바랍니다.");
			            //grid2_a.setCellReadOnly(row, "vend_nm", true);
			            return;
			        }

			        const data = {
			            vend_abbr: dlt_after_grid2.getRowJSON(row).vend_nm
			        }
			        com.pop.open(com.pop.TYPE.VEND, data, function(ret) {
			            const retData = {
			                vend_nm: ret.vend_abbr,
			                vend_cd: ret.vend_cd,
			                vend_bsn_no: ret.bsns_psn_regst_no,
			                charg: ret.reprs_psn_nm,
			                contac_loc: ret.tel_no,
			                addr: ret.addr
			            }
			            dlt_after_grid2.setRowJSON(row, retData, true);
			            scwin.grid2_a_onchange(row, col);
			        });
			    }
			};

			scwin.grid2_a_onchange = function(row, col) {
				const vendType = dlt_after_grid2.getRowJSON(row).vend_type;
			    if (col === 2) { // vend_type
			        if (vendType === "2") { // 개인
			            grid2_a.setHeaderValue("col_vend_bsn_no", "주민번호");
			            grid2_a.setHeaderValue("col_vend_nm", "성명");
			            grid2_a.setColumnVisible("vend_img", false);
			            grid2_a.setCellReadOnly(row, "vend_cd", false);
			            grid2_a.setCellReadOnly(row, "vend_bsn_no", false);
			            grid2_a.setCellReadOnly(row, "contac_loc", false);
			            grid2_a.setCellReadOnly(row, "addr", false);
			        } else {
			            grid2_a.setHeaderValue("col_vend_bsn_no", "사업자등록번호");
			            grid2_a.setHeaderValue("col_vend_nm", "업체명");
			            grid2_a.setColumnVisible("vend_img", true);
			            grid2_a.setCellReadOnly(row, "vend_cd", true);
			            grid2_a.setCellReadOnly(row, "vend_bsn_no", true);
			            grid2_a.setCellReadOnly(row, "contac_loc", true);
			            grid2_a.setCellReadOnly(row, "addr", true);
			        }
			    }
			};

			scwin.grid3_a_oncellclick = function(row, col, colId) {

				if(colId !== "accnt_img") return;

				const data = {search_data: ""};

			    com.pop.open(biz.pat.pop.TYPE.PAT_RES, data, function(ret) {

			        const retData = {
			            accnt_no: ret.accnt_no,
			            prj_no: ret.prj_no,
			            prj_nm: ret.prj_nm,
			            prj_term: ret.prj_term,
			            resch_respn_syspayno: ret.resch_rspns_syspayno,
			            resch_respn_syspaynm: ret.resch_rspns_nm
			        };

			        grid3_a.setCellDisabled(row, "accnt_no", true);
			        dlt_after_grid3.setRowJSON(row, retData, true);

			    });
			};

