			scwin.download_file = function() {
				let save = "res_chng_agreement2";
				let real = encodeURIComponent("(양식) 연구과제 변경 요청서.hwp");
				const url = "/SvcAttachDown.do?fileName=/pat/"+save+"&realName="+real+"&bizKind=OLD";
				window.open(url, '_blank').focus();
			};

			scwin.save_click = function() {
			    if (!scwin.pre_save()) return;

			    com.confirm("저장 하시겠습니까?", function(ret) {
                    if (ret) {
	                    scwin.save();                    	
                    }
                });
			};


			scwin.save = function() {

			    const serviceId = com.getLoginInfo("serviceId");
			    const sn = com.getLoginInfo("sn");
			    let uid = com.getLoginInfo("uid");

			    dma_save.set("skate_clsf", radioSkateClsf.getValue());
			    dma_save.set("scrn_clsf", scwin.param.scrnClsf);
			    dma_save.set("ui_id", serviceId);
			    dma_save.set("endpoints", uid);

			    dma_save.set("req_no", scwin.param.reqNoVal);
			    dma_save.set("mngmt_no", scwin.param.mngmt_no);
			    dma_save.set("app_req_no", inputReqNo.getValue());

			    dma_save.set("syspayno", sn);
			    dma_save.set("save_clsf", scwin.param.scrnClsf);
			    dma_save.set("his_seq", textboxAfSeq.getValue());
			    dma_save.set("input_ymd", inputCalendarInputYmd.getValue());
			    dma_save.set("chng_cause", textareaChngCause.getValue());

			    dma_save.set("bf_seq", textboxBfSeq.getValue());

			    // 임시저장
			    dma_save.set("ui_id", serviceId);
			    if (uid === "fin2") uid = "mklee";
			    dma_save.set("endpoints", uid);


			    const skateClsf = radioSkateClsf.getValue();

			    if (skateClsf == "PAS010") {
			        dlt_saveList.setJSON(dlt_after_grid1.getAllJSON());
			        if (textboxBfSeq.getValue() === "0" && inputReqNo.getValue() === "") dlt_bfSaveList.setJSON(dlt_before_grid1.getAllJSON());
			    } else if (skateClsf == "PAS020") {
			        dlt_saveList.setJSON(dlt_after_grid2.getAllJSON());
			        if (textboxBfSeq.getValue() === "0" && inputReqNo.getValue() === "") dlt_bfSaveList.setJSON(dlt_before_grid2.getAllJSON());
			    } else {
			        dlt_saveList.setJSON(dlt_after_grid3.getAllJSON());
			        if (textboxBfSeq.getValue() === "0" && inputReqNo.getValue() === "") dlt_bfSaveList.setJSON(dlt_before_grid3.getAllJSON());
			    }

			    com.file.transport(scwin.save_attach);
			};


			scwin.save_attach = function(attachObj) {
				dma_save.set("attach_file_no", attachObj.upload_A)
			    com.execute(sbm_svcpatappchnm01);
			};

			scwin.sbm_svcpatappchnm01_done = function(e) {
				const reqNo = e.responseJSON.elData.app_req_no;
				inputReqNo.setValue(reqNo);
				scwin.param.saveClsf = "U";
				com.alert("저장하였습니다.");
				scwin.search();
			};


			scwin.pre_save = function() {

			    if (radioSkateClsf.getValue() === "") {
			        com.alert("변경대상을 선택하세요.");
			        return false;
			    }

			    if (textareaChngCause.getValue() === "") {
			        com.alert("변경사유를 입력하세요.");
			        return false;
			    }

			    if (radioSkateClsf.getValue() === "PAS010") {
			        if ( !scwin.pre_save1() ) return false;
			    } else if (radioSkateClsf.getValue() === "PAS020") {
			        if ( !scwin.pre_save2() ) return false;
			    } else if (radioSkateClsf.getValue() === "PAS099") {
			        for (var i = 0; i < dlt_after_grid3.getDataLength(); i++) {

			            if (dlt_after_grid3.getRowJSON(i).accnt_no === "") {
			                com.alert("계정번호를 입력하세요.");
			                return false;
			            }
			        }
			    }
			    return true;

			};


			scwin.pre_save1 = function() {

			    var dataValCnt = 0;
			    var stakeRtSum = 0;
			    var inventPsnClsf = "N";

			    for (var i = 0; i < dlt_after_grid1.getDataLength(); i++) {
			        dataValCnt = 0;
			        // 새로 입력된 데이터 validation 체크
			        if (dlt_after_grid1.getRowJSON(i).blong_clsf === 'PDT010') {
			            if (dlt_after_grid1.getRowJSON(i).syspayno === "") {
			                com.alert("공동발명자정보를 입력하세요.");
			                return false;
			            }
			        } else {
			            if (dlt_after_grid1.getRowJSON(i).nm_krn === "") dataValCnt++;
			            if (dlt_after_grid1.getRowJSON(i).resid_regst_no === "") dataValCnt++;
			            if (dlt_after_grid1.getRowJSON(i).contac_loc === "") dataValCnt++;
			            if (dlt_after_grid1.getRowJSON(i).email === "") dataValCnt++;
			            if (dlt_after_grid1.getRowJSON(i).addr === "") dataValCnt++;
			        }

			        if (dlt_after_grid1.getRowJSON(i).stake_rt === "") {
			            com.alert("지분율을 입력하세요.");
			            return false;
			        }
			        if (dlt_after_grid1.getRowJSON(i).invnt_psn_clsf === '1') {
			            inventPsnClsf = "Y";
			        }

			        stakeRtSum += WebSquare.util.getNumber(dlt_after_grid1.getRowJSON(i).stake_rt);
			    }

			    if (stakeRtSum != 100) {
			        com.alert("지분율의 합을 100%로 입력 바랍니다.");
			        return false;
			    }

			    if (inventPsnClsf === "N") {
			        com.alert("주발명자가 존재하지 않습니다.");
			        return false;
			    }

			    return true;
			};

			scwin.pre_save2 = function() {

			    var dataValCnt = 0;
			    var stakeRtSum = 0;

			    for (var i = 0; i < dlt_after_grid2.getDataLength() ; i++) {
			        // 새로 입력된 데이터 validation 체크
			        if (dlt_after_grid2.getRowJSON(i).stake_rt === "") {
			            com.alert("지분율을 입력하세요");
			            return false;
			        }
			        dataValCnt = 0;
			        if (dlt_after_grid2.getRowJSON(i).vend_type === '1') { // 사업자
			            if (dlt_after_grid2.getRowJSON(i).vend_cd === "") {
			                com.alert("출원업체를 입력하세요");
			                return false;
			            }
			        } else {
			            if (dlt_after_grid2.getRowJSON(i).vend_nm === "") dataValCnt++;
			            if (dlt_after_grid2.getRowJSON(i).vend_bsn_no === "") dataValCnt++;
			            if (dlt_after_grid2.getRowJSON(i).contac_loc === "") dataValCnt++;
			        }
			        stakeRtSum += WebSquare.util.getNumber(dlt_after_grid2.getRowJSON(i).stake_rt);
			    }

			    if (stakeRtSum !== 100) {
			        com.alert("지분율의 합을 100%로 입력 바랍니다.");
			        return false;
			    }
			    return true;
			};

			/**
			 * 소연심위 안건상정삭제
			 */
			scwin.delete_click = function() {
			    com.confirm("삭제 하시겠습니까?", function(ret) {
			        if (ret) {
			            const reqNo = inputReqNo.getValue();
			            if (reqNo === "") {
			                com.alert("신청서가 존재하지 않습니다.");
			                return;
			            }
			            if (scwin.param.aprState >= "XAD020") {
			                com.alert("결재가 진행중이거나 완료된 건입니다.");
			                return;
			            }
			            dma_delete.set("req_no", reqNo);
			            dma_delete.set("skate_clsf", radioSkateClsf.getValue());
			            com.file.deleteAll(scwin.delete_attach);
			        }
			    });
			};

			scwin.delete_attach = function() {
			    com.execute(sbm_svcpatappchnd01, {
			        successCallback: function(e) {
			        	com.alert("삭제하였습니다.");
			        	scwin.movePage();
			        }
			    });
			};


			/*
			 *	사용자 검색
			 */		
			scwin.tb_empno_onkeypress = function(e) {
			    if (event.keyCode == 13) {
			        scwin.selectHumBtn();
			    }
			};

			// 빈값일경우 이름, 시스템사번 데이터셋 초기화
			scwin.tb_empno_onchange = function(info) {
			    if (info.newValue == "") {
			        dma_search.set("nm", "");
			        dma_search.set("empno", "");
			        dma_search.set("syspayno", "");
			    }
			};

			scwin.btn_search_hum_onclick = function(e) {
			    scwin.selectHumBtn("img");
			};


			scwin.selectHumBtn = function(data) {
			    // 버튼(img)으로 팝업함수 바로 호출 처음 변수에 아무것도 넣지 않으면 최초 서브미션도 실행 안함. 
			    data == "img" ? data = { empno: "" } : data = { empno: tb_empno.getValue(), nm: tb_empno.getValue(), work_clsf: "HAG010" };

			    com.pop.open(com.pop.TYPE.HUM, data, function(ret) {
			    	dma_search.set("nm", ret.nm);
			        dma_search.set("empno", ret.empno);
			        dma_search.set("syspayno", ret.syspayno);
			    });
			};

			scwin.onpageunload = function() {

			};
