head.script:
	/****************************************************
 	 * 화면 설명 : 발명신고서 작성
 	 * 변경 이력 : 2022-11-24	박영훈	최초 작성
 	 *          2023-01-18   이영희	수정작성
 	 * 서비스아이디 : I110
 	 ****************************************************/
	scwin.gvar={		
		attachCrtYn : "N", //첨부파일 생성여부
		charg_syspayno : "", // 심의담당자
		charg_nm : "",
		charg_empno : "",
		isApproval : false,
		isTabVisible : false,
		typeCode : "I110"
	}; 
    /*
     * 페이지 열기
     */     
	scwin.onpageload = function() {
		// 초기화
		com.file.init(); // 첨부파일 초기화
		btn_prc.hide(); // 승인
		btn_ret.hide(); // 반려		
		grd_invntPsn.setColumnVisible("resid_no_dis", false);

		var session = com.getLoginInfo();		
		// 관리번호 생성 버튼
		com.biz.checkRole({serviceId : "I110", roleCodes : session.roleCode}, function(e){
			if(e.isRoleYn) btn_crtMngNo.show("");
			else btn_crtMngNo.hide();
		});		

		if(session.roleCode.indexOf("1016102") == -1) ibx_patofic_nm.setDisabled( true ); // 지재권관리자

		// 탭컨트롤
		tabContainer1.hideTab(1);
		tabContainer1.hideTab(2);
		tabContainer1.hideTab(3);

		// parameter
		var param = com.getParameter();
		dma_search.setJSON(param);

		if(param.patLstYn == "Y"){ // PatPsrDefaultDtl.xml 에서 넘어온 경우
			grp_btn.hide();
			btn_crtMngNo.hide();
			btn_pre.hide(); // 목록버튼
			btn_apr.hide();
			btn_smitNo.hide();
		}

		// 산업기술분류(대분류) 조회
		dma_search.set("clsf", "RAQ001");
		com.execute(sbm_selectTecBigClsf, {
			successCallback : function(e) { // 성공 콜백
				dlt_tecBigClsf.setJSON(e.responseJSON.resIndTeccVoList);

				// 공통코드
				var codeOptions = [ { code : "PAT", compID :"sbx_pat_clsf"}
								   ,{ code : "PNT", compID :"sbx_dom_oversea_clsf" }
								   ,{ code : "PDT", compID :"grd_invntPsn:blong_clsf" }
								   ,{ code : "PST", compID :"rad_saleClsf" }						   
								   ,{ code : "PBT", compID :"rad_InvntClsf" ,filter : "mngmtItem2 == 'Y'"} //발명구분
								   ,{ code : "RHA", compID :"grd_prj:bsns_cd_inout" }
								   ,{ code : "PTC", compID :"sbx_tech_clsf" } // 기술분류
								   ,{ code : "PPG", compID :"rdo_pat_grd" } // 발명등급
								   ,{ code : "PUT", compID :"rdo_use_model" } // 사용기종
								   ,{ code : "PUC", compID :"cbx_replica_shape" } // 복제물 제출형태
								   ,{ code : "PUA", compID :"cbx_use_os" }
								   ,{ code : "PUB", compID :"cbx_use_lang" }
								   ,{ code : "HAG", compID :"grd_invntPsn:work_clsf" }
								];

				com.setCmnCd(codeOptions, function() {
					if(dma_search.get("req_no") == ""){
						scwin.init();
					}else{
						scwin.search();
					}
				});
			}
		});			
	};

	scwin.onpageunload = function() {

	};

	/*
     * 첨부파일 셋팅
     */
	scwin.getAttachObjs = function(vMode){
		// 이미 생성되어있는 첨부파일 컴포넌트는 재생성하지 않게 하기위하여 분리
		if(scwin.gvar.attachCrtYn == "Y"){
			var objs = [];
			if(dma_mast.get("plan_attach_file_no") != "") objs.push({uploadId:upload_A.getID(), fileKey : dma_mast.get("plan_attach_file_no"), mode: vMode, target:"IPMS"});
			if(dma_mast.get("attach_file_no_1") != "") objs.push({uploadId:upload_B.getID(), fileKey : dma_mast.get("attach_file_no_1"), mode: vMode, target:"IPMS"});
			if(dma_mast.get("pct_attach_file_no") != "") objs.push({uploadId:upload_C.getID(), fileKey : dma_mast.get("pct_attach_file_no"), mode: vMode, target:"IPMS"});
			if(dma_mast.get("out_deed_attach_file_no") != "") objs.push({uploadId:upload_D.getID(), fileKey : dma_mast.get("out_deed_attach_file_no"), mode: vMode, target:"IPMS"});
			if(dma_mast.get("com_apply_attach_file_no") != "") objs.push({uploadId:upload_E.getID(), fileKey : dma_mast.get("com_apply_attach_file_no"), mode: vMode, target:"IPMS"});
			if(dma_mast.get("etc_attach_file_no") != "") objs.push({uploadId:upload_F.getID(), fileKey : dma_mast.get("etc_attach_file_no"), mode: vMode, target:"IPMS"});
			if(dma_mast.get("one_invnt_attach_file_no") != "") objs.push({uploadId:upload_G.getID(), fileKey : dma_mast.get("one_invnt_attach_file_no"), mode: vMode, target:"IPMS"});
			if(dma_mast.get("open_attach_file_no") != "") objs.push({uploadId:upload_K.getID(), fileKey : dma_mast.get("open_attach_file_no"), mode: vMode, target:"IPMS"});
			return objs;			
		}else{
			var objs = [
				{uploadId:upload_A.getID(), fileKey : (dma_mast.get("plan_attach_file_no") == "")? scwin.gvar.typeCode:dma_mast.get("plan_attach_file_no"), mode: vMode, target:"IPMS"}, // 도면첨부
				{uploadId:upload_B.getID(), fileKey : (dma_mast.get("attach_file_no_1") == "")? scwin.gvar.typeCode : dma_mast.get("attach_file_no_1"), mode: vMode, target:"IPMS"}, // 기술설명서
				{uploadId:upload_C.getID(), fileKey : (dma_mast.get("pct_attach_file_no") == "")? scwin.gvar.typeCode:dma_mast.get("pct_attach_file_no"), mode: vMode, target:"IPMS"}, // 국외PCT사업화계획서
				{uploadId:upload_D.getID(), fileKey : (dma_mast.get("out_deed_attach_file_no") == "")? scwin.gvar.typeCode : dma_mast.get("out_deed_attach_file_no"), mode: vMode, target:"IPMS"}, // 외부인양도증
				{uploadId:upload_E.getID(), fileKey : (dma_mast.get("com_apply_attach_file_no") == "")? scwin.gvar.typeCode : dma_mast.get("com_apply_attach_file_no"), mode: vMode, target:"IPMS"}, // 공동출원동의서·사유서
				{uploadId:upload_F.getID(), fileKey : (dma_mast.get("etc_attach_file_no") == "")? scwin.gvar.typeCode : dma_mast.get("etc_attach_file_no"), mode: vMode, target:"IPMS"}, // 기타
				{uploadId:upload_G.getID(), fileKey : (dma_mast.get("one_invnt_attach_file_no") == "")? scwin.gvar.typeCode : dma_mast.get("one_invnt_attach_file_no"), mode: vMode, target:"IPMS"}, // 1인 발명지분 소명서
				{uploadId:upload_K.getID(), fileKey : (dma_mast.get("open_attach_file_no") == "")? scwin.gvar.typeCode : dma_mast.get("open_attach_file_no"), mode: vMode, target:"IPMS"}, // 사전공개파일
			];

			return objs;
		}		
	};

	/*
     * 신규 설정
     */ 
	scwin.init = function(){
		// 초기화	
		dma_mast.set("pat_clsf", "PAT070");
		dma_mast.set("pat_clsf_nm", "특허");
		rad_InvntClsf.setValue("PBT010");
		sbx_dom_oversea_clsf.setValue("PNT010");
		dma_mast.set("smitapp_hope_natn_nm", "대한민국");
		dma_mast.set("smitapp_hope_natn_cd", "KR");

		// 권리구분에 따른 셋팅
		scwin.setComp("PAT070");

		ta_tach_field.setValue("본 발명과 직접 관련된 종래의 기술(공백포함 300자 미만입력 가능)");
		ta_indst_use_clsf.setValue("구체적인 타깃분야 및 활용가능한 분야 기재(공백포함 300자 미만입력 가능)");
		ta_solve_task.setValue("종래 기술의 문제점 및 이를 해결하기 위한 본 발명의 수단(공백포함 300자 미만입력 가능)");
		ta_trans_nm.setValue("종래 기술과 대비되는 본 발명의 우수성을 중심으로 상세하게 설명(공백포함 1000자 미만입력 가능)");
		ta_invnt_efct.setValue("본 발명의 구성으로부터 나타나는 특유의 효과(공백포함 300자 미만입력 가능)");
		ta_relat_invnt.setValue("기존에 본인이 출원한 특허 중 본 발명과 관련이 있는 특허 기재(공백포함 300자 미만입력 가능)");

		// 출원인 셋팅
		var data={
			vend_type : "1", vend_nm : "한국생산기술연구원", charg :"이상목", vend_cd : "100000", vend_bsn_no : "119-82-01008", stake_rt : "100",
			contac_loc : "041-589-8101", addr : "충청남도 천안시 서북구 입장면 양대기로길 89 한국생산기술연구원"
		};

		dlt_vend.setRowJSON(0, data, false);	
		scwin.setGridVend(0);	

		// 첨부파일
		var objs = scwin.getAttachObjs("edit");
		com.file.createMulti(objs, objs.shift(), function() {
			scwin.gvar.attachCrtYn = "Y";
			scwin.getInvntPsn();
		});
	};

	/*
     * 최초 공동발명자 조회(세션이용)
     */ 
	scwin.getInvntPsn = function(){
		var data={empno : com.getLoginInfo("empno"),
		          work_clsf : "HAG010"
		         };

		com.pop.open(com.pop.TYPE.HUM, data, function(ret) {
			var empData= ret;
			empData.blong_nm = ret.dept_nm;
			empData.invnt_psn_clsf = "1";
			empData.nm_krn = ret.nm;
			empData.stake_rt = 100;
			empData.contac_loc = ret.ext_no;
			empData.resid_regst_no = ret.resid_no;
			empData.blong_cd = ret.dept_cd;
			empData.blong_clsf = "PDT010";
			empData.co_invnt_affir_ex = "1";
			empData.co_invnt_affir_ymd = com.getServerDateTime();

			dlt_invntPsn.setRowJSON(0, empData, false);	// 직무발명자
			dma_mast.set("invnt_syspayno", 	ret.syspayno);	

			grd_invntPsn.setCellDisabled(0, "nm_eng", true);
			grd_invntPsn.setCellDisabled(0, "resid_regst_no", true);
			grd_invntPsn.setCellDisabled(0, "syspayno", true);
			grd_invntPsn.setCellDisabled(0, "contac_loc", true);
			grd_invntPsn.setCellDisabled(0, "blong_nm", true);
			grd_invntPsn.setCellDisabled(0, "email", true);

			// 소연심의 담당자 조회
			scwin.getDeptManager();
		});			
	};

	/*
     * 부서별 담당자 조회
     */ 
	scwin.getDeptManager = function(){
		if(dma_mast.get("dom_oversea_clsf") == "PNT010"){
			if(rdo_interview_yn.getValue() == "Y"){
				dma_mast.set("charg_nm", "박헌수");
				dma_mast.set("charg_empno", "150642");
				dma_mast.set("charg_syspayno", "20150642");
			}else{
				// 소연심의 담당자가 이미 검색이 되어 있는 경우에는 더이상 검색하지 않고 기존에 설정되어있는 담당자 셋팅
				//if(scwin.gvar.charg_syspayno != ""){
				if(false){
					dma_mast.set("charg_nm", scwin.gvar.charg_nm);
					dma_mast.set("charg_empno", scwin.gvar.charg_empno);
					dma_mast.set("charg_syspayno", scwin.gvar.charg_syspayno);
				}else{

					dma_mast.set("charg_nm", "");
					dma_mast.set("charg_empno", "");
					dma_mast.set("charg_syspayno", "");

					// 부서별 담당자 조회	
					var data={
						roleType : "pat_manager",
						syspayno : dma_mast.get("invnt_syspayno")
					};

					com.biz.getDeptManager(data, function(retData){
						dma_mast.set("charg_syspayno", retData.syspayno);
						dma_mast.set("charg_empno", retData.empno);
						dma_mast.set("charg_nm", retData.nm);
						ibx_charg_nm.setValue(retData.nm);
						ibx_charg_empno.setValue(retData.empno);



						scwin.gvar.charg_syspayno = retData.syspayno;
						scwin.gvar.charg_nm = retData.nm;
						scwin.gvar.charg_empno = retData.empno;
					});
				}
			}
		}else{ // 해외출원인 경우
			dma_mast.set("charg_nm", "김훈");
			dma_mast.set("charg_empno", "140560");
			dma_mast.set("charg_syspayno", "20140560");
		}
	};

	/*
     * 조회
     */ 
	scwin.setForm = function(aprState){
		// 버튼 제어
		com.biz.disabledButtons(aprState, dma_mast.get("req_no"));
		btn_trnsPrint.setDisabled(false); // 양도증
		btn_print.setDisabled(false); // 발명신고서

		if(/PAT020|PAT070/.test(dma_mast.get("pat_clsf"))){
			sbx_pat_clsf.setItemDisabled(2, true);
			sbx_pat_clsf.setItemDisabled(3, true);
			sbx_pat_clsf.setItemDisabled(4, true);
		}else{
			sbx_pat_clsf.setItemDisabled(0, true);
			sbx_pat_clsf.setItemDisabled(1, true);
		}		

		if(aprState >= "XAD020"){
			btn_pSave.setDisabled(true); // 임시저장
			// 권리구분 등 disable
			grp_reqNo.setDisabled(true);
			spn_req_no.setDisabled(false);

			// 지재권관리자인 경우 결재완료 후에도 버튼 보이도록 함
			if(com.getLoginInfo("roleCode").indexOf("1016102") > 0){
				btn_save.setDisabled(false);
			}		

			// 추가, 삭제 버튼 보이지 않게 
			var addObj = document.querySelectorAll('.bt_plus');
			addObj.forEach(function(o){
				o.style ="display:none";
			});		

			var minusObj = document.querySelectorAll('.bt_minus');
			minusObj.forEach(function(o){
				o.style ="display:none";
			});	
		}else{			
			spn_req_no.setStyle("text-decoration", "");
			spn_req_no.setStyle("color", "");
			spn_req_no.setDisabled(true);
		}

		// 국외발명신고서 작성인 경우 PCT체크 강제적용
		if (dma_mast.get("dom_oversea_clsf") == "PNT020") {					
			chk_pct.setValue("P");
			chk_pct.setDisabled(true)
		}

		// 공동출원 유무
		if (dma_mast.get("co_smitapp_ex") == "Y") {		
			grp_com_attach.show("");
			grp_etc_attach.show("");
		}else{
			grp_com_attach.hide();
			grp_etc_attach.hide();
		}

		// 사전공개 확인서류 첨부		
		if(dma_mast.get("bef_open_yn") == "Y" && /PAT070|PAT020/.test(dma_mast.get("pat_clsf"))){ // 특허,실용신안
			grp_open_attach.show(""); 
		}else{
			grp_open_attach.hide();	
		}

		// 공동발명자
		for(var i=0; i<dlt_invntPsn.getRowCount(); i++){
			if(dlt_invntPsn.getCellData(i, "invnt_psn_clsf") == "1"){
				if(dma_mast.get("invnt_syspayno").trim() != dlt_invntPsn.getCellData(i, "syspayno")){
					dma_mast.set("invnt_syspayno", dlt_invntPsn.getCellData(i, "syspayno"));
				}	
			}

			if(aprState == "XAD020"){
				// 양도인 미결재시
				if(dlt_invntPsn.getCellData(i, "syspayno") == com.getLoginInfo("sn") && dlt_invntPsn.getCellData(i, "co_invnt_affir_ex").trim() != "0"){ 
					// 승인, 반려 버튼 보이게
					btn_prc.show("");	
					btn_ret.show("");
				}						
			}else if(aprState == "XAD100"){
				grd_invntPsn.setColumnVisible("resid_regst_no", false);
				grd_invntPsn.setColumnVisible("resid_no_dis", true);
			}
		}

		// 출원인 그리드 컬럼 활성화 여부 셋팅
		scwin.setGridVend();
		// 직무발명자 그리드 컬럼 활성화 여부 셋팅
		scwin.setGridPsn();
	};

	/*
     * 조회
     */ 
	scwin.search = function() {		
		com.execute(sbm_selectPatApp, {
			successCallback : function(e) { // 성공 콜백

				tabContainer1.setDisabled(true);
				// 출원번호 검색 후 조회한 경우
				if(dma_search.get("src_clsf") == "SMITAPP"){
					dma_mast.set("req_no", "");
					dma_mast.set("apr_state", "XAD005");
					dma_mast.set("charg_syspayno", "");
					dma_mast.set("charg_empno", "");
					dma_mast.set("charg_nm", "");
					dma_mast.set("smitapp_hope_natn_cd", "");
					dma_mast.set("smitapp_hope_natn_nm", "");
					dma_mast.set("dom_oversea_clsf", "PNT020");

					ibx_relat_dom_smitapp_no.setValue(scwin.gvar.smitapp_no);

					for(var i=0; i<dlt_invntPsn.getRowCount(); i++){
						dlt_invntPsn.setCellData(i, "co_invnt_affir_ex", "1");
					}

					dma_search.set("src_clsf", "");

					// 심의담당자 조회
					scwin.gvar.charg_syspayno = "";
					scwin.getDeptManager();
				}

				// 권리구분에 따른 컴포넌트 셋팅
				scwin.setComp(dma_mast.get("pat_clsf"));

				// 산업기술분류 중분류셋팅
				scwin.getMidTecClsf(dma_mast.get("indst_tech_big_clsf"), "SRC");				

				//첨부파일세팅
				var objs = scwin.getAttachObjs((dma_mast.get("apr_state") >= "XAD020")?"view":"edit");
				var func = function() {
					scwin.gvar.attachCrtYn = "Y";
					tabContainer1.setDisabled(false);

					// 컴포넌트 셋팅
					scwin.setForm(dma_mast.get("apr_state"));	

					// 첨부파일 컴포넌트 생성을 위해 탭이동 필요
					var index = 0;
					if(dma_mast.get("pat_clsf") == "PAT050"){
						index = 1;
					}else if(dma_mast.get("pat_clsf") == "PAT040"){
						index = 2;
					}else if(dma_mast.get("pat_clsf") == "PAT030"){
						index = 3;
					}
					if(index > 0){
						tabContainer1.setSelectedTabIndex(index);
						scwin.tabContainer1_ontabclick('', index);
					}
				};
				if (objs && objs.length > 0) {
					com.file.createMulti(objs, objs.shift(), function(){
						(func)();
					});
				} else {
					(func)();
				}
			}
		});
	};

	/*
     * 산업기술분류표 중분류 조회
     */ 
	scwin.getMidTecClsf = function(cd, clsf){
		dma_search.set("bigcls", cd);
		com.execute(sbm_selectTecMidClsf, {
			successCallback : function(e){				
				dlt_tecMidClsf.removeAll();
				dlt_tecMidClsf.setJSON(e.responseJSON.resIndTeccVoList);

				if(dma_mast.get("indst_tech_clsf").trim() != ""){
					sbx_indst_tech_clsf.setValue(dma_mast.get("indst_tech_clsf"));
				}

				// 조회 후 소분류 자동 조회	
				if(clsf == "SRC"){
					scwin.getSmlTecClsf(dma_mast.get("indst_tech_clsf"));
				}
			}
		});
	};

	/*
     * 산업기술분류표 소분류 조회
     */ 
	scwin.getSmlTecClsf = function(cd){
		dma_search.set("mid_clsf", cd);
		com.execute(sbm_selectTecSmlClsf, {
			successCallback : function(e){				
				dlt_tecSmlClsf.removeAll();
				dlt_tecSmlClsf.setJSON(e.responseJSON.resIndTeccVoList);

				if(dma_mast.get("indst_tech_sml_clsf").trim() != ""){
					sbx_indst_tech_sml_clsf.setValue(dma_mast.get("indst_tech_sml_clsf"));
				}
			}
		});
	};

	/*
     * 권리구분에 따른 컴포넌트 셋팅
     */ 
	scwin.setComp = function(patClsf){
		btn_prgPrint.hide();
		tabContainer1.hideTab(1);
		tabContainer1.hideTab(2);
		tabContainer1.hideTab(3);

		switch(patClsf){
			case "PAT030" : // 상표
				tabContainer1.showTab(3);
				break;
			case "PAT040" : // 디자인
				tabContainer1.showTab(2);
				break;
			case "PAT050" : // 프로그램	
				tabContainer1.showTab(1);	
				btn_prgPrint.show("");				
				break;
		}

		if(/PAT030|PAT040|PAT050/.test(patClsf)){
			grp_pat_grd.hide(); // 발명등급
			grp_tech_spec.hide(); // 기술적특징
			grp_open_attach.hide();	// 사전공개 확인서류

			if(patClsf == "PAT050"){
				grp_patofic.hide(); // 특허사무소				
			}else{
				grp_patofic.show(""); // 특허사무소				
			}
		}else{			
			grp_pat_grd.show(""); // 발명등급
			grp_tech_spec.show(""); // 기술적특징			
			grp_patofic.show(""); // 특허사무소

			if(dma_mast.get("bef_open_yn") == "Y"){ // 특허,실용신안
				grp_open_attach.show(""); 
			}else{
				grp_open_attach.hide();	// 사전공개 확인서류
			}
		}

		// 국외PCT 사업화계획서
		if(dma_mast.get("dom_oversea_clsf") == "PNT020" && patClsf != "PAT050"){
			grp_pct_attach.show(""); 
		}else{
			grp_pct_attach.hide();
		}

	};

	/*
     * 그리드 행 추가
     */ 
	scwin.addRow = function(type){
		if(type == "Vend"){ // 출원인
			var idx = com.addRow(grd_vend);
			dlt_vend.setCellData(idx, "stake_rt", "0");
			dlt_vend.setCellData(idx, "vend_type", "1");

		}else if(type == "InvntPsn"){ // 직무발명자
			var idx = com.addRow(grd_invntPsn);
			dlt_invntPsn.setCellData(idx, "stake_rt", "0");
			dlt_invntPsn.setCellData(idx, "co_invnt_affir_ex", "1");

		}else if(type == "Prj"){ // 관련연구과제
			var idx = com.addRow(grd_prj);
		}
	};

	/*
     * 그리드 행 삭제
     */
	scwin.removeGridRows = function(gridObj, type){
		var rows = gridObj.getCheckedIndex("chk");
		if(rows.length == 0){
			com.alert(com.getMsg("message.xom.co.com.0003", "삭제할 행"));
			return;
		}
		rows.reverse().forEach(function(rowIndex){
			if(type == "Vend"){ // 출원인				
				dlt_vend.removeRow(rowIndex);
			}else if(type == "InvntPsn"){ // 직무발명자
				dlt_invntPsn.removeRow(rowIndex);	
			}else if(type == "Prj"){ // 관련연구과제
				dlt_prj.removeRow(rowIndex);				
			}
		});
	};

	/*
     * 임시저장 전 체크
     */
	scwin.checkPreSave = function(){
		dma_mast.set("invnt_wrte_syspayno", com.getLoginInfo("sn"));
		dma_mast.set("indst_tech_clsf", sbx_indst_tech_clsf.getValue());
		dma_mast.set("indst_tech_sml_clsf", sbx_indst_tech_sml_clsf.getValue());

		if(dlt_invntPsn.getRowCount() == 0){
			alert ("직무 발명자를 입력해 주시기 바랍니다.");
			tabContainer1.setSelectedIndex(0);
			return false;
		}

		var validData =[
			{dataObj : dma_mast,
			 valInfoArr :[
						{ id : "pat_clsf", mandatory : true},
						{ id : "charg_syspayno", mandatory : true},
						{ id : "indst_tech_clsf", mandatory : true},
						{ id : "mandate_psn_syspayno", mandatory : true}
					],
			 isCheckModified : false},
			 {dataObj : dlt_invntPsn,
			 valInfoArr :[
						{ id : "blong_clsf", mandatory : true},
						{ id : "resid_regst_no", mandatory : true},
					],
			 isCheckModified : false},
		];
		if(!com.validateData(validData)) return false;

		// 직무발명자 체크		
		var bkEmpFlag = false; // 부발명자(위임자)가 그리드에 속해있는지 확인하는 Flag 
		var blongRt1 = 0;  //내부
		var blongRt2 = 0;  //외부
		var stakeRtSum = 0;
		var mainInvntCnt = 0; // 주발명자 체크
		var residNos = "";
		for(var i=0; i< dlt_invntPsn.getRowCount(); i++){
			if(residNos.indexOf(dlt_invntPsn.getCellData(i, "resid_regst_no")) >= 0){
				alert("발명자가 중복되었습니다. 확인하시기 바랍니다.");
				return false;
			}
			residNos += dlt_invntPsn.getCellData(i, "resid_regst_no") + ",";

			//주발명자 체크는 내부인 중에서 선택될 수 있도록 저장기능 보완 요청 : 20210408 진소영
			if(dlt_invntPsn.getCellData(i, "blong_clsf") == "PDT020"){
				if(dlt_invntPsn.getCellData(i, "invnt_psn_clsf") == "1"){
					alert("주발명자는 내부인 중에서만 선택 가능합니다.");						
					return false;
				}

				blongRt2 += Number(dlt_invntPsn.getCellData(i, "stake_rt"));
			}else{ // 내부인인 경우
				if(dlt_invntPsn.getCellData(i, "syspayno") == ""){
					alert("내부인인 경우에는 조회 버튼을 통하여 입력하여 주십시오.");
					return false;
				}

				blongRt1 += Number(dlt_invntPsn.getCellData(i, "stake_rt"));
			}

			stakeRtSum += Number(dlt_invntPsn.getCellData(i, "stake_rt"));

			if(dlt_invntPsn.getCellData(i, "empno") == ibx_mandate_psn.getValue()){
				bkEmpFlag = true;
			}

			if(dlt_invntPsn.getCellData(i, "invnt_psn_clsf") == "1") mainInvntCnt++;			
		}

		if(!bkEmpFlag){
			alert("부발명자는 직무발명자 리스트에 있는 발명자 중 한명이어야 합니다.");
			tabContainer1.setSelectedIndex(0);
			return false;
		}

		if(mainInvntCnt == 0){
			alert("주발명자를 선택하여 주시기 바랍니다.");
			tabContainer1.setSelectedIndex(0);
			return false;
		}

		if(stakeRtSum > 100){
			alert("지분율이 100%가 넘습니다. 확인하시기 바랍니다.");
			tabContainer1.setSelectedIndex(0);
			return false;
		}

		if(stakeRtSum < 100){
			alert("지분율을 100%로 맞춰 주시기 바랍니다.");
			tabContainer1.setSelectedIndex(0);
			return false;
		}
		if(stakeRtSum == 0){
			alert("지분율을 입력하시기 바랍니다.");
			tabContainer1.setSelectedIndex(0);
			return false;
		}

		if(sbx_dom_oversea_clsf.getValue() == "PNT020"){
			if(ibx_relat_dom_smitapp_no.getValue() == ""){
				alert("국외 출원번호를 입력해 주십시오.");	
				return false;
			}
		}

		// 출원인 체크
		var pdt010Rt = 0;
		var pdt020Rt = 0;
		for(var i=0; i<dlt_vend.getRowCount(); i++){
			if(dlt_vend.getCellData(i, "vend_cd")=="100000"){
				pdt010Rt += WebSquare.util.parseFloat(dlt_vend.getCellData(i, "stake_rt"),0);
			}else{
				pdt020Rt += WebSquare.util.parseFloat(dlt_vend.getCellData(i, "stake_rt"),0);
			}
		}

		if(rdo_co_smitapp_ex.getValue() == "Y"){
			if(blongRt1 > pdt010Rt){
				alert("내부인 지분율이 한국생산기술연구원의 권리지분율을 넘습니다. 확인하시기 바랍니다.");
				tabContainer1.setSelectedIndex(0);
				return false;
			}

			if(blongRt1 < pdt010Rt){
				alert("내부인 지분율을 한국생산기술연구원의 권리지분율로 맞춰 주시기 바랍니다.");
				tabContainer1.setSelectedIndex(0);
				return false;
			}

			if(scwin.gvar.isApproval){ // 결재신청시 체크
				if(	pdt020Rt != blongRt2){
					alert("외부인 지분율 총합 다릅니다.");
					return false;
				}
			}
		}else{ // 단독
			if((blongRt1 + blongRt2) != (pdt010Rt+pdt020Rt)){ //내부+외부 지분율체크
				alert("지분율 총합이 다릅니다.");
				return false;
			}
		}

		return true;		
	};

	/*
     * 필수값 체크
     */
	scwin.preMandatryCls = function(){
		// 01. 필수값 체크
		var validDataMast = [			
			    { id : "dom_oversea_clsf", mandatory : true } 
			   ,{ id : "tech_clsf", mandatory : true } 
			   ,{ id : "invnt_krn_nm", mandatory : true }

		];

		if (/PAT070|PAT020/.test(dma_mast.get("pat_clsf"))) {
			validDataMast.push(
					{ id : "kwd_1", mandatory : true, maxLength : 20 }
				  , { id : "kwd_2", mandatory : true, maxLength : 20 }
				  , { id : "kwd_3", mandatory : true, maxLength : 20 }
			);
		}

		if(dma_mast.get("dom_oversea_clsf") == "PNT020" && dma_mast.get("pat_clsf") == "PAT070"){
			validDataMast.push({ id : "relat_dom_smitapp_no", mandatory : true });
		}

		//기술이전이 Y인 경우
		if(dma_mast.get("trns_yn") == "Y"){
			validDataMast.push({ id : "tech_fee", mandatory : true, allowChar : "0-9"},{ id : "trns_ymd", mandatory : true});
		}

		if(dma_mast.get("pat_clsf") != "PAT050"){
			validDataMast.push({ id : "patofic_nm", mandatory : true}); // 프로그램이 아닐 경우 특허사무소 필수

			if(!/PAT030|PAT040/.test(dma_mast.get("pat_clsf"))){ // 상표, 디자인이 아닐 경우
				validDataMast.push({ id : "pat_grd", mandatory : true}
							 ,{ id : "tech_field", mandatory : true}
							 ,{ id : "indst_use_clsf", mandatory : true}
							 ,{ id : "trl_step_cd", mandatory : true}
							 ,{ id : "solve_task", mandatory : true}
							 ,{ id : "trans_nm", mandatory : true}
							 ,{ id : "invnt_efct", mandatory : true}
							 );				
			}
		}

		var validData =[
			{dataObj : dma_mast,
			 valInfoArr : validDataMast,
			 isCheckModified : false}
		];		

		// 프로그램 체크
		if(dma_mast.get("pat_clsf") == "PAT050"){
			var validDataProg = {
				dataObj : dma_invntBook,
				valInfoArr :[
					{ id : "nm", mandatory : true}, // 명칭	
					{ id : "crt_yr_mmdd", mandatory : true}, // 창작연월일
					{ id : "replica_shape", mandatory : true},
					{ id : "replica_qty", mandatory : true}, // 복제물수량	
					{ id : "pgm_knd_cd", mandatory : true}, // 프로그램종류코드	
					{ id : "apply_fild", mandatory : true}, // 적용분야	
					{ id : "spclmark", mandatory : true}, // 프로그램 특징
					{ id : "major_skill", mandatory : true}, // 주요기능	
					{ id : "use_way", mandatory : true}, // 사용방법 
					{ id : "sale_clsf", mandatory : true}, // 판매구분 코드
					{ id : "use_model", mandatory : true}, // 사용기종
					{ id : "use_os", mandatory : true},
					{ id : "use_lang", mandatory : true},
					{ id : "need_pgm", valFunc : function(value){
							if(com.isEmpty(value)){
								return "프로그램을 구동시키기 위하여 다른 프로그램이 \n필요하다면 필요한 프로그램을 기재하고, \n해당사항 없을 경우 '해당없음'이라고 기재";
							}	
						}
					},	
					{ id : "scale", valFunc : function(value){
							if(com.isEmpty(value)){
								return "규모[제출한 복제물의 용량을 byte 단위로 기재]\n를 입력해 주십시오.";
							}	
						}
					},					
				],
				isCheckModified : false
			};

			validData.push(validDataProg);
		}else if(dma_mast.get("pat_clsf") == "PAT030"){
			var validDataTm = {
				dataObj : dma_invntBook,
				valInfoArr :[
					{ id : "tm_mean", mandatory : true}, // 상표명
					{ id : "tm_desc", mandatory : true}, // 상표설명
					{ id : "appont_goods", mandatory : true}, //지정상품								
				],
				isCheckModified : false
			};

			validData.push(validDataTm);
		}

		return validData;
	};	

	/*
     * 첨부서류 체크 체크
     */
	scwin.checkAttachFile = function(){
		// 사전공개 확인 서류
		if(rdo_bef_open_yn.getValue() == "Y"){ // 사전공개유무
			if(com.file.action.getTotalFileCount(upload_K.getID()) < 1){
				alert("사전공개 확인 서류를 첨부하여 주시기 바랍니다.");
				return false;
			}
		}

		// 기술설명서
		if(!/PAT030|PAT040|PAT050/.test(dma_mast.get("pat_clsf"))){ // 상표, 디자인, 프로그램이 아닐 경우	
			if(com.file.action.getTotalFileCount(upload_B.getID()) < 1){
				alert("기술설명서를 첨부해 주시기 바랍니다.");
				return false;
			}
		}

		// PCT사업화 계획서
		if(dma_mast.get("pct_chk") == "P"){
			if(com.file.action.getTotalFileCount(upload_C.getID()) < 1){
				alert("PCT사업화 계획서를 첨부해 주시기 바랍니다.");
				return false;
			}
		}
		// 공동출원사유서
		if(dma_mast.get("co_smitapp_ex") == "Y"){
			if(com.file.action.getTotalFileCount(upload_E.getID()) < 1){
				alert("공동출원사유서를 첨부해 주시기 바랍니다.");
				return false;
			}
		}else{
			// 외부인 양도증 
			var rows = dlt_invntPsn.getMatchedJSON("blong_clsf", "PDT020", true); // 직무발명자에 외부인이 있는 경우
			if(rows.length > 0 && com.file.action.getTotalFileCount(upload_D.getID()) < 1){
				alert("외부인 양도증을 첨부해 주시기 바랍니다.");
				return false;
			}	
		}

		// 1인 발명지분 소명서
		var invntPsnStakeRt = dlt_invntPsn.getCellData(0, "stake_rt");
		if(invntPsnStakeRt == 100 && dma_mast.get("dom_oversea_clsf") == "PNT010"){
			if(com.file.action.getTotalFileCount(upload_G.getID()) < 1){
				alert("1인 발명지분 소명서를 첨부해 주시기 바랍니다.");
				return false;
			}
		}

		//  디자인 도면
		if(dma_mast.get("pat_clsf") == "PAT040"){
			if(com.file.action.getTotalFileCount(upload_I.getID()) < 1){
				alert ("디자인 도면을 첨부해 주시기 바랍니다.");
				tabContainer1.setSelectedIndex(2);
				return false;
			}
		}else if(dma_mast.get("pat_clsf") == "PAT050"){ // 프로그램 주요내용 첨부파일
			if(com.file.action.getTotalFileCount(upload_H.getID()) < 1){
				alert ("프로그램 관련 첨부파일을 입력해 주십시오.");
				tabContainer1.setSelectedIndex(1);
				return false;
			}
		}else if(dma_mast.get("pat_clsf") == "PAT030"){ // 상표 첨부파일
			if(com.file.action.getTotalFileCount(upload_J.getID()) < 1){
				alert ("상표 관련 파일을 첨부해 주시기 바랍니다.");
				tabContainer1.setSelectedIndex(3);
				return false;
			}
		}

		return true;
	};

	/*
     * 저장 전 체크
     */
	scwin.preSave = function(){
		// 01.필수값 체크
		if (!com.validateData(scwin.preMandatryCls())) return false;

		// 02. 첨부서류 체크
		if(!scwin.checkAttachFile()) return false;

		// 03. 저장전 체크
		var stakeRtSum = 0;
		if(dma_mast.get("co_smitapp_ex") == "Y"){ // 공동소유인 경우
			if(dlt_vend.getRowCount() <= 1){
				alert ("소유권 구분이 공동의 경우\n생기원 외 공동 출원 업체를 입력해 주시기 바랍니다.");
				return false;
			}

			var vendBsnNos = "";
			for(var i=0; i<dlt_vend.getRowCount(); i++){
				if(dlt_vend.getCellData(i, "vend_type") == ""){
					alert("공동소유권에서 소속구분이 지정되지 않은 항목이 있습니다. 확인하시기 바랍니다.");
					return false;
				}

				if(vendBsnNos.indexOf(dlt_vend.getCellData(i, "vend_bsn_no")) > 0){
					alert("공동소유권 업체가 중복되었습니다. 확인하시기 바랍니다.");
					return false;
				}

				if(WebSquare.util.parseFloat(dlt_vend.getCellData(i, "stake_rt"), 0) == 0){
					alert("권리지분율을 입력하시기 바랍니다.");
					return false;
				}

				vendBsnNos += dlt_vend.getCellData(i, "vend_bsn_no") + ",";

				stakeRtSum += Number(dlt_vend.getCellData(i, "stake_rt"));
			}

			//stakeRtSum = Math.round(stakeRtSum * 100) /100;
			console.log("권리지분 합:  ---> "+ stakeRtSum);
		}else{
			if(dlt_vend.getRowCount() == 0){
				alert ("소유권 구분이 단독인 경우 출원 업체 1개를 필히 입력해 주시기 바랍니다.");
				return false;
			}			
			stakeRtSum = Number(dlt_vend.getCellData(0, "stake_rt"));
		}

		if(stakeRtSum > 100 || stakeRtSum < 100){
			alert("권리지분율의 합이 100%가 아닙니다. 확인하시기 바랍니다.");
		}

		var psnStateRtSum = 0;
		for(var i=0; i<dlt_invntPsn.getRowCount(); i++){
			psnStateRtSum += Number(dlt_invntPsn.getCellData(i, "stake_rt"));
		}

		if(stakeRtSum != psnStateRtSum){
			alert("한국생산기술연구원의 권리지분과 발명자의 지분이 일치하지 않습니다.");
			return false;
		}

		//------------------------------ 관련 연구과제 계정번호 확인, 서브계정은 입력불가 ------------------------------//
		for(var i=0; i<dlt_prj.getRowCount(); i++){
			if(dlt_prj.getCellData(i, "accnt_no") == "" || dlt_prj.getCellData(i, "prj_no") == ""){
				alert("계정번호를 입력하세요.");
				return false;
			}

			if(dlt_prj.getCellData(i, "accnt_no").substr(7, 1) != "0"){
				alert("서브계정은 입력할 수 없습니다.");
				return false;
			}			
		}

		//--------------------------------------- 프로그램 -------------------------------------------//
		if(dma_invntBook.get("use_model").indexOf("PUT050") >= 0 && ibx_use_model_etc.getValue().trim() == ""){
			alert("사용기종 중 기타로 놓으신 경우에는 기타 텍스트를 입력하셔야합니다.");
			tabContainer1.setSelectedIndex(1);
			return false;
		}

		if(dma_invntBook.get("use_os").indexOf("PUA099") >= 0 && ibx_use_os_etc.getValue().trim() == ""){
			alert("사용OS 중 기타로 놓으신 경우에는 기타 텍스트를 입력하셔야합니다.");
			tabContainer1.setSelectedIndex(1);
			return false;
		}

		if(dma_invntBook.get("use_lang").indexOf("PUB099") >= 0 && ibx_use_lang_etc.getValue().trim() == ""){
			alert("사용언어중 기타로 놓으신 경우에는 기타 텍스트를 입력하셔야합니다.");
			tabContainer1.setSelectedIndex(1);
			return false;
		}	

		return true;	
	};

	/*
     * 출원인 그리드 컬럼 활성화 여부 셋팅
     */
	scwin.setGridVend = function(row){
		var fRow = (row == undefined)? 0 : row;
		var lRow = (row == undefined)? dlt_vend.getRowCount() : row+1;

		for(var i = fRow; i<lRow; i++){
			if(dlt_vend.getCellData(i, "vend_type") == "1"){ // 사업자
				grd_vend.setCellReadOnly(i, "vend_cd", false);
				grd_vend.setCellReadOnly(i, "vend_nm", true);
				grd_vend.setCellReadOnly(i, "vend_bsn_no", true);
				grd_vend.setCellReadOnly(i, "charg", true);
				grd_vend.setCellReadOnly(i, "contac_loc", true);
				grd_vend.setCellReadOnly(i, "addr", true);
			}else{ // 개인
				grd_vend.setCellReadOnly(i, "vend_cd", true);
				grd_vend.setCellReadOnly(i, "vend_nm", false);
				grd_vend.setCellReadOnly(i, "vend_bsn_no", false);
				grd_vend.setCellReadOnly(i, "charg", false);
				grd_vend.setCellReadOnly(i, "contac_loc", false);
				grd_vend.setCellReadOnly(i, "addr", false);
			}
		}		
	};

	/*
     * 직무발명자 그리드 컬럼 활성화 여부 셋팅
     */
	scwin.setGridPsn = function(row){
		var fRow = (row == undefined)? 0 : row;
		var lRow = (row == undefined)? dlt_invntPsn.getRowCount() : row+1;

		for(var i = fRow; i<lRow; i++){
			if(dlt_invntPsn.getCellData(i, "blong_clsf") == "PDT010"){ // 내부
				grd_invntPsn.setCellReadOnly(i, "empno", (dma_mast.get("apr_state") >= "XAD020")?true:false);
				grd_invntPsn.setCellReadOnly(i, "stake_rt", (dma_mast.get("apr_state") >= "XAD020")?true:false);

				grd_invntPsn.setCellReadOnly(i, "nm_krn", true);
				grd_invntPsn.setCellReadOnly(i, "nm_eng", true);
				grd_invntPsn.setCellReadOnly(i, "resid_regst_no", true);
				grd_invntPsn.setCellReadOnly(i, "syspayno", true);				
				grd_invntPsn.setCellReadOnly(i, "contac_loc", true);
				grd_invntPsn.setCellReadOnly(i, "blong_nm", true);
				grd_invntPsn.setCellReadOnly(i, "email", true);
				grd_invntPsn.setCellReadOnly(i, "addr", true);
			}else{ // 외부
				grd_invntPsn.setCellDisabled(i, "empno", true);

				grd_invntPsn.setCellReadOnly(i, "nm_krn", false);
				grd_invntPsn.setCellReadOnly(i, "nm_eng", false);
				grd_invntPsn.setCellReadOnly(i, "resid_regst_no", false);
				grd_invntPsn.setCellReadOnly(i, "syspayno", false);				
				grd_invntPsn.setCellReadOnly(i, "contac_loc", false);
				grd_invntPsn.setCellReadOnly(i, "blong_nm", false);
				grd_invntPsn.setCellReadOnly(i, "email", false);
				grd_invntPsn.setCellReadOnly(i, "addr", false);
			}
		}		
	};

	/*
     * 첨부파일 셋팅
     */
	scwin.setAttachFiles = function(attachObj){
		var attachList =[];

		if(attachObj.upload_A != undefined && attachObj.upload_A != ""){ // 도면첨부
			dma_mast.set("plan_attach_file_no", attachObj.upload_A);
			attachList.push(attachObj.upload_A);
		}		
		if(attachObj.upload_B != undefined && attachObj.upload_B != ""){ // 기술설명서
			dma_mast.set("attach_file_no_1", attachObj.upload_B);
			attachList.push(attachObj.upload_B);
		}		
		if(attachObj.upload_C != undefined && attachObj.upload_C != ""){ // 국외PCT사업화계획서
			dma_mast.set("pct_attach_file_no", attachObj.upload_C);
			attachList.push(attachObj.upload_C);
		}
		if(attachObj.upload_D != undefined && attachObj.upload_D != ""){ // 외부인양도증
			dma_mast.set("out_deed_attach_file_no", attachObj.upload_D);
			attachList.push(attachObj.upload_D)
		}
		if(attachObj.upload_E != undefined && attachObj.upload_E != ""){ // 공동출원동의서·사유서
			dma_mast.set("com_apply_attach_file_no", attachObj.upload_E);
			attachList.push(attachObj.upload_E)
		}
		if(attachObj.upload_F != undefined && attachObj.upload_F != ""){ // 기타
			dma_mast.set("etc_attach_file_no", attachObj.upload_F);
			attachList.push(attachObj.upload_F)
		}
		if(attachObj.upload_G != undefined && attachObj.upload_G != ""){ // 1인 발명지분 소명서
			dma_mast.set("one_invnt_attach_file_no", attachObj.upload_G);
			attachList.push(attachObj.upload_G)
		}
		if(attachObj.upload_K != undefined && attachObj.upload_K != ""){ // 사전공개파일
			dma_mast.set("open_attach_file_no", attachObj.upload_K);
			attachList.push(attachObj.upload_K)
		}

		if(dma_mast.get("pat_clsf") == "PAT050" && attachObj.upload_H != undefined && attachObj.upload_H != ""){ // 프로그램
			dma_invntBook.set("attach_file_no", attachObj.upload_H);
			attachList.push(attachObj.upload_H)
		}else if(dma_mast.get("pat_clsf") == "PAT040" && attachObj.upload_I != undefined && attachObj.upload_I != ""){ // 디자인
			dma_invntBook.set("attach_file_no_1", attachObj.upload_I);
			attachList.push(attachObj.upload_I)
		}else if(dma_mast.get("pat_clsf") == "PAT030" && attachObj.upload_J != undefined && attachObj.upload_J != ""){ // 상표
			dma_invntBook.set("attach_file_no", attachObj.upload_J);
			attachList.push(attachObj.upload_J)
		}		

		dma_mast.set("validAttachNo", attachList.join(","));		
	};

	/*
     * 저장
     */
	scwin.save = function(){
		if(dma_mast.get("pat_clsf") == "PAT050"){ // 프로그램
			dma_invntBook.set("auth_relat", "대리인");
    		dma_invntBook.set("auth_krn_nm", "한국생산기술연구원");
    		dma_invntBook.set("auth_eng_nm", "KITECH");
    		dma_invntBook.set("auth_natnlt", "대한민국");
    		dma_invntBook.set("auth_corp_regst_no", "115112-0000210");
    		dma_invntBook.set("auth_bsns_regst_no", "119-82-01008");
    		dma_invntBook.set("auth_addr", "충청남도 천안시 서북구 입장면 홍천리 35-3 한국생산기술연구원");
    		dma_invntBook.set("auth_tel", "041-589-8086");
    		dma_invntBook.set("auth_email", "moon11@kitech.re.kr");
    		dma_invntBook.set("auth_homepg", "www.kitech.re.kr");
		}

		for(var i=0;i<dlt_invntPsn.getRowCount();i++){
			if(dlt_invntPsn.getCellData(i,"invnt_psn_clsf")=="1"){
				dlt_invntPsn.setCellData(i,"stake_dept","1");
			}
		}	

		com.file.transport( function(attachObj){
			// 첨부파일 셋팅
			scwin.setAttachFiles(attachObj);				

			com.execute(sbm_saveData, {
				successCallback : function(e) { // 성공 콜백
					if(scwin.gvar.isApproval){
						// 결재화면 이동
						com.movePageMenu("A001", { "reqNo" :  dma_mast.get("req_no")});
					}else{
						com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function(){
							// 조회							
							scwin.search();
						});
					}
				}
			});
		});	
	};

	/*
     * 양도인의 승인 또는 반려
     */
	scwin.transApprovReq = function(flag){
		for(var i=0; i<dlt_invntPsn.getRowCount(); i++){
			if(dlt_invntPsn.getCellData(i, "syspayno") == com.getLoginInfo("sn")){
				dma_psnApr.set("req_no", dlt_invntPsn.getCellData(i, "req_no"));
				dma_psnApr.set("syspayno", dlt_invntPsn.getCellData(i, "syspayno"));
				dma_psnApr.set("co_invnt_affir_rmk", dlt_invntPsn.getCellData(i, "co_invnt_affir_rmk"));
				dma_psnApr.set("co_invnt_affir_ex", flag);
				dma_psnApr.set("co_invnt_affir_ymd", com.getServerDateTime());

				break;
			}	
		}

		com.execute(sbm_savePsnApr, {
			successCallback : function(e){
				com.movePageMenu("A005", { "reqNo" : dma_mast.get("req_no")});
			}
		});
	};

	/*
     * 직무발명자 검색
     */
	scwin.getEmpInfo = function(row, empNo){
		var data ={
			empno : empNo,
			work_clsf : "HAG010",
			ref_yn : (empNo == "")? "Y" : "N"
		};

		com.pop.open(com.pop.TYPE.HUM, data, function(ret) {
			dlt_invntPsn.setCellData(row, "nm_krn", ret.nm);
			dlt_invntPsn.setCellData(row, "nm_eng", ret.nm_eng);
			dlt_invntPsn.setCellData(row, "empno", ret.empno);
			dlt_invntPsn.setCellData(row, "syspayno", ret.syspayno);
			dlt_invntPsn.setCellData(row, "contac_loc", ret.ext_no);
			dlt_invntPsn.setCellData(row, "blong_nm", ret.dept_nm);
			dlt_invntPsn.setCellData(row, "blong_cd", ret.dept_cd);
			dlt_invntPsn.setCellData(row, "work_clsf", ret.work_clsf);
			dlt_invntPsn.setCellData(row, "email", ret.email);
			dlt_invntPsn.setCellData(row, "resid_regst_no", ret.resid_no);
			dlt_invntPsn.setCellData(row, "resid_no_dis", ret.resid_no.substring(0,7) + "*******");
			dlt_invntPsn.setCellData(row, "addr", ret.addr);
			if(dlt_invntPsn.getCellData(row, "invnt_psn_clsf") === '1'){
				dma_mast.set("invnt_syspayno", dlt_invntPsn.getCellData(row, "syspayno"));
				// 심의담당자
				scwin.getDeptManager();
			}
		});
	};

