     */
	scwin.rdo_use_model_onviewchange = function(info) {
		if(this.getValue().indexOf("PUT050") >= 0){
			ibx_use_model_etc.setReadOnly(false);
		}else{
			ibx_use_model_etc.setReadOnly(true);
			ibx_use_model_etc.setValue("");
		}
	};

