	/*
     * 탭클릭 이벤트
     */ 
	scwin.tabContainer1_ontabclick = function(tabID,idx) {	
		var vMode = (dma_mast.get("apr_state") >= "XAD020")? "view" : "edit";

		if(idx == 1){ // 프로그램
			if(dma_search.get("req_no") == ""){
				cbx_replica_shape.setValue("PUC001");
				dma_invntBook.set("replica_qty", 1);
			}
			if(com.file.isCreated(upload_H.getID())){
				if(com.file.action.getTotalFileCount(upload_H.getID()) < 1){
					com.file.create(upload_H.getID(), {fileKey: (dma_invntBook.get("attach_file_no") == "")? scwin.gvar.typeCode :dma_invntBook.get("attach_file_no") , mode: vMode});
				}
			}else{
				com.file.create(upload_H.getID(), {fileKey: (dma_invntBook.get("attach_file_no") == "")? scwin.gvar.typeCode :dma_invntBook.get("attach_file_no") , mode: vMode});
			}
		}else if(idx == 2){  // 디자인		
			if(com.file.isCreated(upload_I.getID())){
				if(com.file.action.getTotalFileCount(upload_I.getID()) < 1){
					com.file.create(upload_I.getID(), {fileKey: (dma_invntBook.get("attach_file_no_1") == "")? scwin.gvar.typeCode :dma_invntBook.get("attach_file_no_1") , mode: vMode});
				}
			}else{
				com.file.create(upload_I.getID(), {fileKey: (dma_invntBook.get("attach_file_no_1") == "")? scwin.gvar.typeCode :dma_invntBook.get("attach_file_no_1") , mode: vMode});
			}
		}else if(idx == 3){ // 상표
			if(com.file.isCreated(upload_J.getID())){
				if(com.file.action.getTotalFileCount(upload_J.getID()) < 1){
					com.file.create(upload_J.getID(), {fileKey: (dma_invntBook.get("attach_file_no") == "")? scwin.gvar.typeCode :dma_invntBook.get("attach_file_no") , mode: vMode});
				}
			}else{
				com.file.create(upload_J.getID(), {fileKey: (dma_invntBook.get("attach_file_no") == "")? scwin.gvar.typeCode :dma_invntBook.get("attach_file_no") , mode: vMode});
			}
		}else if(idx == 4){ // 양도증
			// 공통코드
			var codeOptions = [ { code : "PDT", compID :"grd_trnsfr:blong_clsf" }];

			com.setCmnCd(codeOptions);			

			if(dma_mast.get("apr_state") == "XAD020"){
				var row = dlt_invntPsn.getMatchedIndex("syspayno", com.getLoginInfo("sn"), true)[0];
				if(row >= 0){
					// 양도인 미결재시
					if(dlt_invntPsn.getCellData(row, "co_invnt_affir_ex").trim() != "0"){ 
						grd_trnsfr.setCellReadOnly(row, "co_invnt_affir_rmk", false);
					}	
				}				
			}
		}	 

	};

