	scwin.btn_smitNo_onclick = function(e) {
		biz.pat.pop.open(biz.pat.pop.TYPE.SMITAPP_NO, {sh_flag : "I110"}, function(ret) {
			scwin.gvar.smitapp_no = ret.smitapp_no;

			dma_search.set("req_no", ret.req_no);
			dma_search.set("mngmt_no", ret.mngmt_no);
			dma_search.set("src_clsf", "SMITAPP");
			scwin.search();
		});
	};

