	};

	/*
     * 국내/국외구분 변경 이벤트
     */
	scwin.sbx_dom_oversea_clsf_onviewchange = function(info) {
		if(this.getValue() == "PNT010"){
			dma_mast.set("smitapp_hope_natn_nm", "대한민국");
			dma_mast.set("smitapp_hope_natn_cd", "KR");
			ibx_relat_dom_smitapp_no.setValue("");

			dma_mast.set("pat_clsf", "PAT070");

			chk_pct.setValue("");

			rdo_interview_yn.setDisabled(false);

			btn_smitNo.setDisabled(true); // 출원번호	
			grp_pct_attach.hide(); // 국외PCT 사업화계획서
		}else{
			dma_mast.set("smitapp_hope_natn_nm", "");
			dma_mast.set("smitapp_hope_natn_cd", "");
			btn_smitNo.setDisabled(false); // 출원번호	

			chk_pct.setValue("P");

			rdo_interview_yn.setValue("N");
			rdo_interview_yn.setDisabled(true);

			if(sbx_pat_clsf.getValue() != "" && !/PAT070|PAT040/.test(sbx_pat_clsf.getValue())){
				alert("국외인 경우 권리구분은 특허 또는 디자인만 가능합니다.");
				sbx_pat_clsf.setValue("");
			}

			if(dma_mast.get("pat_clsf") != "PAT050"){
				grp_pct_attach.show(""); // 국외PCT 사업화계획서
			}else{
				grp_pct_attach.hide();	
			}
		}

		// 심의담당자
		scwin.getDeptManager();
	};

	/*
     * 발명등급 변경 이벤트
     */
	scwin.rdo_pat_grd_onviewchange = function(info) {
		if(sbx_dom_oversea_clsf.getValue() == "PNT010"){
			if(/PPG010|PPG020/.test(info.value)){ // S, A등급일 경우
				rdo_interview_yn.setValue("Y");
			}else{
				rdo_interview_yn.setValue("N");
			}
		}
	};

	/*
     * 권리구분 변경 이벤트
     */
	scwin.sbx_pat_clsf_onviewchange = function(info) {
