			dma_invntBook.set("auth_corp_regst_no", "115112-0000210");
			dma_invntBook.set("auth_bsns_regst_no", "119-82-01008");
			dma_invntBook.set("auth_addr", "충청남도 천안시 서북구 입장면 홍천리 35-3 한국생산기술연구원");
			dma_invntBook.set("auth_tel", "041-589-8086");
			dma_invntBook.set("auth_email", "moon11@kitech.re.kr");
			dma_invntBook.set("auth_homepg", "www.kitech.re.kr");
		}
	};

	/*
     * 소유권구분 변경 이벤트
     */
	scwin.rdo_co_smitapp_ex_onviewchange = function(info) {
		if(info.value == "Y"){
			grd_vend.setCellDisabled(0, "vend_cd", true);

			grp_com_attach.show("");
			grp_etc_attach.show("");
		}else{
			grd_vend.setCellDisabled(0, "vend_cd", false);

			grp_com_attach.hide();
			grp_etc_attach.hide();
		}
	};

	/*
     * 기술적특징 변경 이벤트
     */
	scwin.txt_techChrcs_onkeyup = function(clsf) {
		switch(clsf){
			case "1" :
				ta_tach_field.getValue().length;
				ibx_cnt1.setValue(ta_tach_field.getValue().length + "자");
				break;
			case "2" :
				ta_indst_use_clsf.getValue().length;
				ibx_cnt2.setValue(ta_indst_use_clsf.getValue().length + "자");
				break;
			case "3" :
				ta_solve_task.getValue().length;
				ibx_cnt3.setValue(ta_solve_task.getValue().length + "자");
				break;
			case "4" :
				ta_trans_nm.getValue().length;
				ibx_cnt4.setValue(ta_trans_nm.getValue().length + "자");
				break;
			case "5" :
				ta_invnt_efct.getValue().length;
				ibx_cnt5.setValue(ta_invnt_efct.getValue().length + "자");
				break;
			case "6" :
				ta_relat_invnt.getValue().length;
				ibx_cnt6.setValue(ta_relat_invnt.getValue().length + "자");
				break;				
		}
	};

	/*
     * 양도확인서 출력 버튼 클릭 이벤트
     */
	scwin.btn_trnsConfPrint_onclick = function(e) {
		var reportArr = [];
		for(var i=0; i<dlt_invntPsn.getRowCount(); i++){   
			if(dlt_invntPsn.getCellData(i, "blong_clsf") == "PDT020"){ // 외부인인 경우
				var node =  "inventNm=" + ("다음" + dma_mast.get("pat_clsf_nm") + "을(를) 위 양수인에게 양도함.")
				           + "&invntKrnNm=" + dma_mast.get("invnt_krn_nm")
				           + "&empNm=" + dlt_invntPsn.getCellData(i, "nm_krn")
				           + "&residNo=" + dlt_invntPsn.getCellData(i, "resid_regst_no")
				           + "&addrT=" + dlt_invntPsn.getCellData(i, "addr") + "(" + dlt_invntPsn.getCellData(i, "contac_loc") + ")";

	            reportArr.push({
					ozrCode : "I006",
					param: node
	            });
	    	}
        }

        // 오즈 뷰어 팝업 호출
        if(reportArr.length > 0){
        	com.oz.openPopup(reportArr);
        }
	};

	/*
     * 양도증출력 버튼 클릭 이벤트
     */
	scwin.btn_trnsPrint_onclick = function(e) {
		var node = "req_no="+ dma_mast.get("req_no");
		com.oz.openPopup({
			ozrCode : "I005",      
			param: node
        });
	};

	/*
     * 발명신고서 출력 버튼 클릭 이벤트
     */
	scwin.btn_print_onclick = function(e) {
		var node = "req_no="+ dma_mast.get("req_no");
		if(com.getLoginInfo("roleCode").indexOf("1016102") > 0){ // 지식재산권-관리자
			node += "&manager_yn=Y";
		}else{
			node += "&manager_yn=N";	
		}

		var itemCd = "";
		switch(dma_mast.get("pat_clsf")){
			case "PAT070" : // 특허
			case "PAT020" : // 실용신안
				itemCd = "I007"; break;
			case "PAT030" : // 상표
				itemCd = "I008"; break;
			case "PAT040" : // 디자인
				itemCd = "I009"; break;
			case "PAT050" : // 프로그램
				itemCd = "I009"; break;
		}

		com.oz.openPopup({
			ozrCode : itemCd,
			param : node
		});
	};

