	/*
     * 부발명자 검색버튼 클릭 이벤트
     */
	scwin.btn_mandate_psn_onclick = function(e) {
		var data={
			empno : ibx_mandate_psn.getValue(),
			work_clsf : "HAG010",
			ref_yn : (ibx_mandate_psn.getValue() == "")? "Y":"N"
		};

		com.pop.open(com.pop.TYPE.HUM, data, function(ret){
			if(/350010|351010|330020|354000|350020|351020/.test(ret.posi_cd)){
				alert("학생연구원을 부발명자(위임자)로 선택할 수 없습니다.");
				return;
			}
			dma_mast.set("mandate_psn_empno", ret.empno);
			dma_mast.set("mandate_psn_nm", ret.nm);
			dma_mast.set("mandate_psn_syspayno", ret.syspayno);
		});

	};

	scwin.btn_charg_onclick = function(e) {
		var data={
			empno : ibx_charg_empno.getValue(),
			work_clsf : "HAG010",
			ref_yn : (ibx_mandate_psn.getValue() == "")? "Y":"N"
		};

		com.pop.open(com.pop.TYPE.HUM, data, function(ret){
			ibx_charg_empno.setValue(ret.empno);
			ibx_charg_nm.setValue(ret.nm);
		});

	};

