     * 임시저장 버튼 클릭 이벤트
     */
	scwin.btn_pSave_onclick = function(e) {
		dma_mast.set("state", "10000");

		if(!scwin.checkPreSave()) return;		

		com.confirm("message.xom.wq.017"/*저장 하시겠습니까?*/, function(ret){
			if(ret){
				scwin.save();
			}
		});
	};

	/*
     * 저장 버튼 클릭 이벤트
     */
	scwin.btn_save_onclick = function(e) {
		if(!scwin.checkPreSave()) return;
		if(!scwin.preSave()) return;

		dma_mast.set("state", "10030");
		com.confirm("message.xom.wq.017"/*저장 하시겠습니까?*/, function(ret){
			if(ret){
				scwin.save();
			}
		});
	};

	/*
     * 인터뷰신청여부 변경 이벤트
     */
	scwin.rdo_interview_yn_onviewchange = function(info) {
		// 소연심의 담당자 조회
		scwin.getDeptManager();
	};

	/*
     * 결재신청 버튼 클릭 이벤트
     */
	scwin.btn_apr_onclick = function(e) {
		scwin.gvar.isApproval = true;

		if(!scwin.checkPreSave()) return;
		if(!scwin.preSave()) return;

		dma_mast.set("approval", "true");
		dma_mast.set("state", "10030");
		// 프로그램
		if(dma_mast.get("pat_clsf") == "PAT050"){
			dma_invntBook.set("replica_shape_nm", cbx_replica_shape.getText());
			// 사용OS
			var useOs = cbx_use_os.getValue();
			var useOsNm = cbx_use_os.getText();
			if(useOs.indexOf("PUA099") >= 0){
				useOsNm = useOsNm.replace("기타", ibx_use_os_etc.getValue());
			}
			dma_invntBook.set("use_os_nm", useOsNm);
			// 사용언어
			var useLang = cbx_use_lang.getValue();
			var useLangNm = cbx_use_lang.getText();
			if (useLang.indexOf("PUB099") > -1) {
				useLangNm = useLangNm.replace("기타", ibx_use_lang_etc.getValue());
			}
			dma_invntBook.set("use_lang_nm", useLangNm);
		}

		scwin.save();		
	};

	/*
     * 직무발명자 그리드 변경 이벤트
     */
	scwin.grd_invntPsn_onviewchange = function(info) {
		var row = info.rowIndex;
		var colId = grd_invntPsn.getColumnID(info.colIndex);
		if(colId == "blong_clsf"){
			// 컬럼 초기화
			var colNms = ["nm_krn", "nm_eng", "empno", "syspayno", "resid_regst_no","contac_loc","blong_nm","email", "addr"];
			colNms.forEach(function(colNm) {
				dlt_invntPsn.setCellData(row, colNm, "");				
			});

			// 소속에 따른 그리드 셋팅
			scwin.setGridPsn(row);
		}else if(colId == "invnt_psn_clsf"){
			if(info.newValue == '1'){
				if(dlt_invntPsn.getCellData("blong_clsf") == "PDT020"){
					alert("주발명자는 내부인 중에서만 선택 가능합니다.");						
					dlt_invntPsn.setCellData(row, "invnt_psn_clsf", '0');
				}else{
					dma_mast.set("invnt_syspayno", dlt_invntPsn.getCellData(row, "syspayno"));
					// 심의담당자
					scwin.getDeptManager();
				}
			}			
		}else if(colId == "empno"){
			if(info.newValue == ""){
				// 컬럼 초기화
				var colNms = ["nm_krn", "nm_eng", "empno", "syspayno", "resid_regst_no","contac_loc","blong_nm","email", "addr"];
				colNms.forEach(function(colNm) {
					dlt_invntPsn.setCellData(row, colNm, "");				
				});
			}else{
				if(dlt_invntPsn.getCellData(row, "blong_clsf") == ""){
					alert("소속구분을 선택하여 주시기 바랍니다.");
					dlt_invntPsn.setCellData(row, "empno", "");
					return;
				}
				scwin.getEmpInfo(row, dlt_invntPsn.getCellData(row, "empno"));
			}
		}
	};

	/*
     * 직무발명자 그리드 클릭 이벤트
     */
	scwin.grd_invntPsn_ontextimageclick = function(row,col) {
		if(dlt_invntPsn.getCellData(row, "blong_clsf") == ""){
			alert("소속구분을 선택하여 주시기 바랍니다.");
			return;
		}

		if(col == grd_invntPsn.getColumnIndex("empno")){
			scwin.getEmpInfo(row, dlt_invntPsn.getCellData(row, "empno"));
		}
	};

	/*
     * 출원인 그리드 변경 이벤트
     */
	scwin.grd_vend_onviewchange = function(info) {
		var row = info.rowIndex;
		var colId = grd_vend.getColumnID(info.colIndex);
		if(colId == "vend_type"){
			// 구분에 따른 출원인 그리드 셋팅
			scwin.setGridVend(row);
		}
	};

	/*
     * 출원인 그리드 클릭 이벤트
     */	
	scwin.grd_vend_ontextimageclick = function(row,col) {
		if(col == grd_vend.getColumnIndex("vend_cd")){
			com.pop.open(com.pop.TYPE.VEND, {ref_yn:"Y"}, function(ret) {
				dlt_vend.setCellData(row, "vend_nm", ret.vend_abbr);
				dlt_vend.setCellData(row, "vend_cd", ret.vend_cd);
				dlt_vend.setCellData(row, "vend_bsn_no", ret.bsns_psn_regst_no);
				dlt_vend.setCellData(row, "charg", ret.reprs_psn_nm);
				dlt_vend.setCellData(row, "contac_loc", ret.tel_no);
				dlt_vend.setCellData(row, "addr", ret.addr);
			});
		}
	};

	/*
     * 관련연구과제 그리드 계정번호검색 클릭 이벤트
     */	
	scwin.grd_prj_ontextimageclick = function(row,col) {
		if(col == grd_prj.getColumnIndex("accnt_no")){
			com.pop.open(com.pop.TYPE.ACNNO, {accnt_clsf : "RAR001", ref_yn:"Y"}, function(ret) {
				dlt_prj.setCellData(row, "accnt_no", ret.accnt_no);
				dlt_prj.setCellData(row, "prj_no", ret.prj_no);
				dlt_prj.setCellData(row, "prj_nm", ret.accnt_no_nm);
				dlt_prj.setCellData(row, "prj_term", ret.start_ymd + "~" + ret.cls_ymd);
				dlt_prj.setCellData(row, "resch_respn_syspayno", ret.accnt_rspns);
				dlt_prj.setCellData(row, "resch_respn_nm", ret.accnt_rspns_nm);
				dlt_prj.setCellData(row, "bsns_cd_inout", ret.bsns_cd_inout);
			});
		}
	};

