				mode : "view",
				ref_yn : "Y"
			};

			com.pop.open(com.pop.TYPE.ATTACH, data, function(ret) {				
			});
		}else if(colId == "attach"){
			var data={
				mode : "edit",
				ref_yn : "Y"
			};

			com.pop.open(com.pop.TYPE.ATTACH, data, function(ret) {		
				dlt_designFile.setCellData(row, "attach_file_no", ret.attach_file_no);
			});
		}
	};

