				mode : "view",
				ref_yn : "Y"
			};

			com.pop.open(com.pop.TYPE.ATTACH, data, function(ret) {				
			});
		}else if(colId == "attach"){
			var data={
				mode : "edit",
				file_key : scwin.gvar.typeCode,
				ref_yn : "Y"
			};

			com.pop.open(com.pop.TYPE.ATTACH, data, function(ret) {		
				dlt_invntPsn.setCellData(row, "attach_file_no", ret.attach_file_no);
			});
		}
	};

	/*
     * 특허사무소 검색 버튼 클릭 이벤트
     */
	scwin.btn_patofic_onclick = function(e) {
		var data ={
			mou_ex : "Y",
			patofic_nm : ibx_patofic_nm.getValue()	
		};

		biz.pat.pop.open(biz.pat.pop.TYPE.PAT_OFFICE, data, function(ret) {
			if(ret.patofic_cd == "0946" && (rdo_co_smitapp_ex.getValue() == "N" || sbx_pat_clsf.getValue() == "PAT050")){
				alert("권리구분이 프로그램이거나 소유권 구분이 단독일때 \n직접출원은 선택할 수 없습니다.");
				return;
			}

			dma_mast.set("patofic_cd", ret.patofic_cd);
			dma_mast.set("patofic_nm", ret.patofic_nm);
			dma_mast.set("patofic_charg_id", ret.charg_id);
			dma_mast.set("patofic_charg_nm", ret.charg_nm);
			dma_mast.set("patofic_contac_loc", ret.patofic_contac_loc);
		});
	};

