	/*
     * 반려 버튼 클릭 이벤트
     */
	scwin.btn_ret_onclick = function(e) {
		com.pop.open(com.pop.TYPE.RETRN_OPN,{ref_yn : "Y"}, function(ret){
			if(ret.comments){
				dma_psnApr.set("comments", ret.comments);
				scwin.transApprovReq("2");	
			}
		});
	};

	/*
     * 프로그램 종류코드 검색 버튼 클릭 이벤트
     */
	scwin.btn_pgmCd_onclick = function(e) {
		biz.pat.pop.open(biz.pat.pop.TYPE.PGM_CODE, {},function(ret) {
			dma_invntBook.set("pgm_knd_cd", ret.seq);
			dma_invntBook.set("pgm_knd_big", ret.big_cd_nm);
			dma_invntBook.set("pgm_knd_middle", ret.mid_cd_nm);
			dma_invntBook.set("pgm_knd_small", ret.cd_nm);
		});
	};

	/*
     * 국외PCT사업화계획서 버튼 클릭 이벤트
     */
	scwin.btn_pct_down_onclick = function(e) {
		scwin.downFileinfo("국외PCT사업화계획서.hwp","PCTplan");
	};

	/*
     * 외부인 양도증 버튼 클릭 이벤트
     */
	scwin.btn_out_down_onclick = function(e) {
		scwin.downFileinfo("(양식) 양도증(외부발명자).hwp","TransDoc");
	};

	/*
     * 공동출원동의서·사유서 버튼 클릭 이벤트
     */
	scwin.btn_cosmit_rsn_onclick = function(e) {
		scwin.downFileinfo("(양식) 공동출원 동의서·사유서.hwp","ComSmitappReason");
	};

	/*
     * 1인 발명지분 소명서 버튼 클릭 이벤트
     */
	scwin.btn_one_invnt_onclick = function(e) {
		scwin.downFileinfo("(양식) 1인 발명지분 소명서.hwp","ComOneInvent");
	};

	/*
     * 신청번호 더블클릭 이벤트
     */
	scwin.spn_req_no_ondblclick = function(e) {
		if(dma_mast.get("apr_state") < "XAD020") return;

		var data={
			reqNo : this.getValue(),
			ref_yn : "Y",
			viewMode : "read",
			instStatus : (dma_mast.get("apr_state") == "XAD100")? "Completed" : "Running"
		};
		com.pop.open(com.pop.TYPE.VIEW_APR, data);
	};

	/*
     * 기술이전 유무 변경 이벤트
     */
	scwin.rdo_trns_yn_onchange = function(info) {
		if(this.getValue() == "N"){
			ibx_tech_fee.setDisabled(true);
			sbx_trns_ymd.setDisabled(true);
		}else{
			ibx_tech_fee.setDisabled(false);
			sbx_trns_ymd.setDisabled(false);
		}
	};

	/*
     * 관리번호생성 클릭 이벤트
     */
	scwin.btn_crtMngNo_onclick = function(e) {
//		var param = "&req_no=" + dma_mast.get("req_no") +
//					"&pat_clsf=" + dma_mast.get("pat_clsf") +
//					"&syspayno=" + com.getLoginInfo("sn") +
//					"&dom_oversea_clsf=" + dma_mast.get("dom_oversea_clsf") +
//					"&pct_chk=" + dma_mast.get("pct_chk") +
//					"&smitappHopeNatnNm=" + dma_mast.get("smitapp_hope_natn_nm") +
//					"&smitappHopeNatnCd=" + dma_mast.get("smitapp_hope_natn_cd") ;
//					
//		var url = "/filter/index.jsp?serviceid=ID01" + param;
//		com.biz.openPopupByNative(url, '관리번호생성', {width: 550, height: 350}, 'BIZ2');

		var data ={
			req_no : dma_mast.get("req_no"),
			pat_clsf : dma_mast.get("pat_clsf"),
			syspayno : com.getLoginInfo("sn"),
			dom_oversea_clsf : dma_mast.get("dom_oversea_clsf"),
			pct_chk : dma_mast.get("pct_chk"),
			old_smitapp_hope_natn_nm : dma_mast.get("smitapp_hope_natn_nm"),
			old_smitapp_hope_natn_cd : dma_mast.get("smitapp_hope_natn_cd"),
		};

		biz.pat.pop.open(biz.pat.pop.TYPE.MNGNO_CRT, data, function(ret) {
			if(ret){
				com.openTabMenu("IA01", {req_no : dma_mast.get("req_no")});	
			}
		});
	};

	/*
     * 삭제 버튼 클릭 이벤트
     */
	scwin.btn_del_onclick = function(e) {
		com.confirm("message.xom.wq.022"/*삭제 하시겠습니까?*/, function(ret) {
			if (ret) {
				com.file.deleteAll( function(){
					com.execute(sbm_deleteData, {
						successCallback : function(e) { // 성공 콜백
							com.alert("message.xom.wq.023"/*정상적으로 처리되었습니다.*/, function() {
								scwin.movePage({});
							});
						}
					});
				});
			}
		});
	};
