						successCallback : function(e) {
							if(dma_manager.get("syspayno") === ""){
								com.alert("국내심의담당자가 등록안되어 있습니다.\n 심의담당자 등록 요청 부탁 드립니다.");
								return;
							}
							const empno = dma_manager.get("empno");
							const nm = dma_manager.get("nm");
							const syspayno = dma_manager.get("syspayno");
							inputChrgEmpno.setValue(empno);
							inputChrgEmpNm.setValue(nm);
							inputChrgSysNo.setValue(syspayno);
						}
					});
