			    if (row >= 0) {
			        start = row;
			        end = row + 1;
			    }

			    for (var i = start; i < end; i++) {

			        const blongClsf = dlt_invntPsn.getRowJSON(i).blong_clsf;

			        if (blongClsf === "PDT020") { // 외부

			            gridInvntPsn.setCellReadOnly(i, "nm_eng", false);
			            gridInvntPsn.setCellReadOnly(i, "resid_regst_no", false);
			            gridInvntPsn.setCellReadOnly(i, "syspayno", false);
