			            gridInvntPsn.setCellReadOnly(i, "addr", false);

			        } else {

			            gridInvntPsn.setCellReadOnly(i, "nm_eng", true);
			            gridInvntPsn.setCellReadOnly(i, "resid_regst_no", true);
			            gridInvntPsn.setCellReadOnly(i, "syspayno", true);
