			scwin.btn_add_del_row_onclick = function(e) {

			    const gridType = $p.getComponentById(e.target.id).getUserData('userData1');
			    const isAdd = $p.getComponentById(e.target.id).getUserData('userData2') || '';
			    let focusRowIdx = 0;

			    switch (gridType) {
				    case "gridDesnAttach":
				    	focusRowIdx = gridDesnAttach.getFocusedRealRowIndex();
				        isAdd ? com.addRow(gridDesnAttach) : scwin.add_unique_del_list(gridDesnAttach, gridType, focusRowIdx);
				        break;
				    case "gridSmitVend":
				    	focusRowIdx = gridSmitVend.getFocusedRealRowIndex();
				        isAdd ? com.addRow(gridSmitVend) : scwin.add_unique_del_list(gridSmitVend, gridType, focusRowIdx);
				        break;
				    case "gridInvntPsn":
				    	focusRowIdx = gridInvntPsn.getFocusedRealRowIndex();
				        isAdd ? com.addRow(gridInvntPsn) : scwin.add_unique_del_list(gridInvntPsn, gridType, focusRowIdx);
				        break;
				    default:
				        com.alert("그리드 정보가 존재하지 않습니다.");
				}

			};

			scwin.add_unique_del_list = function(gridObj, gridType, focusRowIdx){

			    function handleGrid(dltGrid, dltDelGrid, rowIdx, keyField) {
			        let data = [];
			        let rowData = dltGrid.getRowJSON(rowIdx);

			        if(rowData[keyField]){
			            data.push(rowData);
			            dltDelGrid.setJSON(data, true);

			            let distinctDataArray = dltDelGrid.getColumnDistinctDataArray(keyField);
			            if(distinctDataArray.length > 0){
			                let newDataArray = distinctDataArray.map(item => ({[keyField]: item}));
			                dltDelGrid.setJSON(newDataArray);
			            }
			        }
			        dltGrid.removeRow(rowIdx);
			    }

			    // gridType에 따른 분기 처리
			    switch (gridType) {
			        case "gridDesnAttach":
			            handleGrid(dlt_design_attach, dlt_del_design_attach, focusRowIdx, "attach_file_no");
			            break;
			        case "gridSmitVend":
			            handleGrid(dlt_smitVend, dlt_del_smitVend, focusRowIdx, "seq");
			            break;
			        case "gridInvntPsn":
			            handleGrid(dlt_invntPsn, dlt_del_invntPsn, focusRowIdx, "seq");
			            break;
			        default:
			            com.alert("그리드 정보가 존재하지 않습니다.");
			            return; // 존재하지 않는 gridType에 대해 함수를 종료
			    }

			    // 현재 그리드에서 선택된 행 삭제
			    // com.deleteRow(gridObj);
			};

			scwin.gridSmitVend_onclick = function(row, col, colId) {

				if(colId === "img_vend"){

					const data = {
						vend_abbr: dlt_smitVend.getRowJSON(row).vend_nm
					}

				    com.pop.open(com.pop.TYPE.VEND, data, function(ret) {
				    	const retData = {
				    		vend_nm: ret.vend_abbr,
							vend_cd: ret.vend_cd,
							vend_bsn_no: ret.bsns_psn_regst_no,
							charg: ret.reprs_psn_nm,
							contac_loc: ret.tel_no,
							addr: ret.addr,
							vend_type: ret.vend_clsf,
							stake_rt: 0
				    	}
				    	dlt_smitVend.setRowJSON(row, retData, true);
				    });
				}
			};

			scwin.gridInvntPsn_onclick = function(row, col, colId) {
				if(colId === "img_emp_nm"){
					const data = {
						empno: dlt_invntPsn.getRowJSON(row).nm_krn
					}
					com.pop.open(com.pop.TYPE.HUM, data, function(ret) {
				    	const retData = {
				    		empno: ret.empno,
							nm_krn: ret.nm,
							blong_nm: ret.dept_nm,
							blong_cd: ret.dept_cd,
							dept_new_ymd: ret.dept_new_ymd,
							syspayno: ret.syspayno,
							nm_eng: ret.nm_eng,
							email: ret.email,
							addr: ret.addr,
							resid_regst_no: ret.resid_no,
							contac_loc: ret.mobile,
							work_clsf: ret.work_clsf
				    	}
				    	dlt_invntPsn.setRowJSON(row, retData, true);
				    	scwin.gridInvntPsn_change(row, col, colId);
